INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(1, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Бедна любовь, если ее можно измерить', '', '', '', '5b0dc2f868f101407903c50bd07704ea'),
(2, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Все влюбленные клянутся исполнить больше, чем могут, и не исполняют даже возможного', '', '', '', '7861d81352eb2781f546e9a11344b973'),
(3, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Истинная любовь не может говорить, потому что истинное чувство выражается скорее делом, чем словами', '', '', '', '2ffc6ecb58ef8c03a4aac42f21ef93fc'),
(4, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Лишь та любовь - любовь, Которая чуждается расчета', '', '', '', '8b93d43f8c8e2060e6536d1398725193'),
(5, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Любовь - над бурей поднятый маяк, Не меркнущий во мраке и тумане, Любовь - звезда, которою морякОпределяет место в океане', '', '', '', 'a75efb87c1bdf1cf9b737935212c36e9'),
(6, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Любовь бежит от тех, кто гонится за нею, а тем, кто прочь бежит, кидается на шею', '', '', '', '10a2987a9b3bc4a0a8faca3244808c3b'),
(7, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Любовь всесильна: нет на Земле ни горя выше кары ее, ни счастья выше наслажденья служить ей', '', '', '', '98bdd49813c9c61d0150cd16d3ee3738'),
(8, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Любовь не рассуждает; с крыльями и без глаз, она эмблема слепой опрометчивости.', '', '', '', 'cac4dd6a69aeedde7446ef1b4a86c87e'),
(9, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Мужчины смахивают на апрель, когда ухаживают, и на декабрь, когда уже женаты', '', '', '', '895aa4f94e364ccc1240ae3eb7d5e0d0'),
(10, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Не вечная ли это насмешка любви, что женщина не может любить того, кто любит ее?', '', '', '', '931b61b8ee0145c601984ebc3a1800ad'),
(11, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Одним взглядом можно убить любовь, одним же взглядом можно воскресить ее', '', '', '', '9f11589fd242f22943200e2fc1f370ac'),
(12, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Опасней и вредней укрыть любовь, чем объявить о ней', '', '', '', 'c67b58137939b82793adbaa6d0e8e169'),
(13, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Трудно запугать сердце, ничем не запятнанное', '', '', '', '71fc952931c095923a3f0bf5d3bb6aa3'),
(14, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'У влюбленных обыкновенно часы бегут вперед', '', '', '', '63c54c7c44271b4f294043f5616bd5eb'),
(15, 1, 1, 1, 1448972754, 1448972754, 64, 0, 0, 'Хороша любовь искомая, еще лучше - рождающаяся без исканий', '', '', '', 'ba07bc83d1f1f83455b4f499f605dcc9'),
(16, 1, 1, 1, 1448972754, 1448972754, 63, 83, 0, 'Да не изрекают уста твои слов, которые не обдуманы в сердце. Ибо лучше споткнуться мысленно, чем споткнуться в разговоре.', '', '', '', '31e62bf142729786be0ccd0c6c11eedc'),
(17, 1, 1, 1, 1448972754, 1448972754, 63, 83, 0, 'Истинная красота женщины – в кротости ее характера, а прелесть ее – в кротости ее речей.', '', '', '', '20814a60d7205bc4669a27402840d459'),
(18, 1, 1, 1, 1448972754, 1448972754, 63, 83, 0, 'Как нет пользы от колец в ушах для дикого осла, нет пользы от женщины с важной осанкою, если она лукава в словах своих, лишена мудрости, словоохотлива и многоречива.', '', '', '', '1f6682857188ce3f97cead6214d5f402'),
(19, 1, 1, 1, 1448972754, 1448972754, 63, 83, 0, 'Многих сгубила женская красота, ибо любовь красавицы подобна испепеляющему пламени.', '', '', '', 'f679f2d5c5fda7ae6683646ee65b5847'),
(20, 1, 1, 1, 1448972754, 1448972754, 63, 83, 0, 'Никогда не принимай участия в обручении женщины, ибо, если ей придется плохо, она будет проклинать тебя, а если ей будет хорошо, она о тебе и не вспомнит.', '', '', '', '57d189ff00179af238d5a9367ecc1b10'),
(21, 1, 1, 1, 1448972754, 1448972754, 63, 83, 0, 'Я ворочал камни, но не нашел ничего столь тяжелого, как зять, живущий в доме тестя своего.', '', '', '', '1025e22ec6f0d49ce2852c77219640e1'),
(22, 1, 1, 1, 1448972754, 1448972754, 63, 83, 0, 'Подобно тому, как ветки и плоды украшают дерево, а густой лес – гору, так мужа украшают его дети и его жена. Человек, у которого нет ни братьев, ни жены, ни сыновей, незначителен в глазах своих врагов. Он подобен дереву, растущему на перекрестке дорог: каждый прохожий рвет его плоды, все звери полевые объедают его.', '', '', '', '666f30e7de18c190d08e68b721178d93'),
(23, 1, 1, 1, 1448972754, 1448972754, 62, 83, 0, 'Все побеждает любовь, покоримся ж и мы ее власти.', '', '', '', '3585050eb55e8825b0d4d9f9cf9b57dc'),
(24, 1, 1, 1, 1448972755, 1448972755, 62, 0, 0, 'Любовь превозмогает все.', '', '', '', '05a1935725a4b450359f64fd2c7fefb3'),
(25, 1, 1, 1, 1448972755, 1448972755, 62, 83, 0, 'Любовь у всех одна и та же.', '', '', '', 'c218d8c598f6cad0603fbf5adbcd941c'),
(26, 1, 1, 1, 1448972755, 1448972755, 60, 0, 0, 'Воинской службе подобна любовь Отойдите ленивцы! Тем, кто робок и вял, эти знамена невмочь', '', '', '', 'c83fd5f741d891b388ab2de70ebc807c'),
(27, 1, 1, 1, 1448972755, 1448972755, 60, 0, 0, 'Любовь и фортуна преклоняются перед смелыми', '', '', '', 'f22d3d03f2396c6606acc57d4ae3008e'),
(28, 1, 1, 1, 1448972755, 1448972755, 60, 83, 0, 'Мы всегда стремимся к запретному и желаем недозволенного.', '', '', '', 'f8672dbf8455d48d8d0fc3603e66a954'),
(29, 1, 1, 1, 1448972755, 1448972755, 60, 0, 0, 'Amor non est medicabilis herbis (Любовь травами не лечится)', '', '', '', '0e4b1a4e1d28c5eb8c4e47cdee895c35'),
(30, 1, 1, 1, 1448972755, 1448972755, 61, 0, 0, 'Любовь может изменить человека до неузнаваемости.', '', '', '', 'e21460e264d7551f46276c99a04ce540'),
(31, 1, 1, 1, 1448972755, 1448972755, 59, 0, 0, 'Как много прелести утратило бы наше счастье, если бы никто не радовался вместе с нами! Как трудно было бы перенести наши несчастия без друга, который испытывает их еще сильнее нас!', '', '', '', '55eb9c1236cbb13360038bbc21a01b5d'),
(32, 1, 1, 1, 1448972755, 1448972755, 59, 0, 0, 'Любовь следует измерять не так, как измеряют ее молодые, то есть по силе страсти, но по ее верности и прочности', '', '', '', '8d39e576081de10e371e91c5d1e07b60'),
(33, 1, 1, 1, 1448972755, 1448972755, 59, 0, 0, 'Нельзя любить ни того, кого ты боишься, ни того, кто тебя боится', '', '', '', '8ff0e15c7467b09deba072d59e11f0c3'),
(34, 1, 1, 1, 1448972755, 1448972755, 59, 0, 0, 'Ибо если есть любовь на свете, - а она есть! - то она недалека от безумия', '', '', '', '5974d28e992d9bdfd13466d1baad61dd'),
(35, 1, 1, 1, 1448972755, 1448972755, 35, 83, 59, 'Каждый, кого коснется Эрот, становится поэтом.', '', '', '', '95da602bb269fa5c667ae958d1aeadf9'),
(36, 1, 1, 1, 1448972755, 1448972755, 36, 83, 71, 'Влюбленные часто обманывают и обещают невозможное.', '', '', '', 'e4755789dd808deb34122909461ad275'),
(37, 1, 1, 1, 1448972755, 1448972755, 36, 83, 78, 'Добродетель и для мужчины, и для женщины одна.', '', '', '', '9710a55b1de419c5e6fa78c9ea825ce4'),
(39, 1, 1, 1, 1448972755, 1448972755, 36, 83, 0, 'Чтобы быть счастливым, достаточно быть добродетельным.', '', '', '', '50330aef7973c7c2f8e7068d1459dc29'),
(40, 1, 1, 1, 1448972755, 1448972755, 36, 83, 0, 'Сходиться нужно с теми женщинами, которые сами за это будут благодарны.', '', '', '', 'aca5ca6b6684d8be45e85d0c3047f4e4'),
(41, 1, 1, 1, 1448972755, 1448972755, 36, 83, 0, 'Мудрец женится, чтобы иметь детей, притом от самых красивых женщин; он не будет избегать и любовных связей – ибо только мудрец знает, кого стоит любить.', '', '', '', 'd0eb3ede734977195e0160e3c15b59e5'),
(42, 1, 1, 1, 1448972755, 1448972755, 37, 83, 0, 'Детей надо учить тому, что пригодится им, когда они вырастут.', '', '', '', '9786d187fc40ada6c7bd52520ca7b26b'),
(43, 1, 1, 1, 1448972755, 1448972755, 39, 83, 0, 'Любить – значит желать другому того, что считаешь за благо, и желать притом не ради себя, но ради того, кого любишь, и стараться по возможности доставить ему это благо.', '', '', '', 'a04dd6d3e0aa5260cfdd1923bce2cead'),
(44, 1, 1, 1, 1448972755, 1448972755, 39, 83, 0, 'Не любит тот, кто не любит всегда.', '', '', '', '7ef5afe8b896e454a2e9a40daae29d68'),
(48, 1, 1, 1, 1448972755, 1448972755, 39, 83, 133, 'Мужественные совершают поступки во имя прекрасного. (…) В противном случае мужественными, пожалуй, окажутся даже голодные ослы, ведь они и под ударами не перестают пастись.', '', '', '', '91f865df8fe953fdcff28469e9a89a17'),
(49, 1, 1, 1, 1448972755, 1448972755, 39, 83, 144, 'Телесных удовольствий, как сильнодействующих, ищут те, кто не способен наслаждаться иными: эти люди, конечно, сами создают себе своего рода жажду.', '', '', '', 'c8e77decaf5598d07e61ec2d7161d407'),
(50, 1, 1, 1, 1448972755, 1448972755, 39, 83, 146, 'Быть другом для многих при совершенной дружбе невозможно, так же как быть влюбленным во многих одновременно. (…) А нравиться многим (…) можно.', '', '', '', 'efaf116b1d436f92bca44d97192b4aab'),
(51, 1, 1, 1, 1448972755, 1448972755, 39, 83, 151, 'Бездетные скорее разводятся: дети – это общее обоим благо, а общее благо объединяет.', '', '', '', '94c5eae7511e69191a83cd70c6546c26'),
(52, 1, 1, 1, 1448972755, 1448972755, 39, 83, 154, 'Все больше дорожат доставшимся с трудом. (…) Не случайно матери (…) любят детей сильнее, чем отцы, ведь рождение ребенка требует от них больших усилий и они лучше отцов знают, что это их собственное создание.', '', '', '', '6855ce0ebff128f82310d2f9362256ea'),
(53, 1, 1, 1, 1448972755, 1448972755, 39, 83, 175, 'В чем разница: правят ли женщины, или должностные лица управляются женщинами? Результат получается один и тот же.', '', '', '', '4885a500c9f8246db584c6c5dda83119'),
(54, 1, 1, 1, 1448972755, 1448972755, 39, 83, 205, 'Мы всегда больше любим наши первые впечатления.', '', '', '', 'd6ffa66887cc4fb802543c2ab44be396'),
(55, 1, 1, 1, 1448972755, 1448972755, 39, 83, 224, 'Красота (…) различна для каждого возраста.', '', '', '', 'c062c4b33767ae04b9ec46c0da2a3b94'),
(56, 1, 1, 1, 1448972755, 1448972755, 39, 83, 237, 'И в горестях и в слезах есть (…) своего рода наслаждение: горечь является вследствие отсутствия любимого человека, но в припоминании и некоторого рода лицезрении его – что он делал и каков он был – заключается наслаждение.', '', '', '', 'f9c9bf707befc0130a09766bb12d50cb'),
(57, 1, 1, 1, 1448972755, 1448972755, 39, 83, 243, 'Никто не любит того, кого боится.', '', '', '', '601a0c16f8e8cc9b04372e3b8a30d05b'),
(58, 1, 1, 1, 1448972755, 1448972755, 39, 83, 270, 'Тело достигает цветущей поры от тридцати до тридцати пяти лет, а душа – около сорока девяти лет.', '', '', '', 'fecdb4763078436b5a66693c2166b45d'),
(59, 1, 1, 1, 1448972755, 1448972755, 38, 83, 0, 'Не насладится муж, когда жене не любо наслажденье.', '', '', '', 'e3d8ddbd03870c07289238e62c8ee4c2'),
(60, 1, 1, 1, 1448972755, 1448972755, 40, 83, 0, 'Острее жалит боль, когда ее причиняет кто-нибудь из близких.', '', '', '', '167143ed1ce3cba1a98eee92e9bd077e'),
(61, 1, 1, 1, 1448972755, 1448972755, 133, 83, 0, 'Следует любить друга, помня при этом, что он может стать врагом, и ненавидеть врага, помня, что он может стать другом.', '', '', '', '58338d2c0fb6d5d6e09e618af691ccf1'),
(62, 1, 1, 1, 1448972755, 1448972755, 42, 83, 290, 'Царство красоты может быть уничтожено одним волоском.', '', '', '', 'a833bd5a743cc7df950f2a7da131d441'),
(63, 1, 1, 1, 1448972755, 1448972755, 42, 83, 292, 'Великое несчастье – неумение переносить несчастье.', '', '', '', 'e613291dee23779bbf9ade15fd203122'),
(64, 1, 1, 1, 1448972755, 1448972755, 42, 83, 0, 'Уродливая жена будет тебе наказанием, красивая общим достоянием.', '', '', '', 'a278e0b4d5cc89423c892ea8b36ad503'),
(65, 1, 1, 1, 1448972755, 1448972755, 50, 83, 295, 'Если бы счастье заключалось в телесных удовольствиях, мы бы назвали счастливыми быков, когда они находят горох для еды.', '', '', '', 'f8b5afeff010fd4fd5d3d238e8967198'),
(66, 1, 1, 1, 1448972755, 1448972755, 50, 83, 307, 'Границ души тебе не отыскать, по какому бы пути ты ни пошел: столь глубока ее мера.', '', '', '', 'e39e6bd8e6357a31f90dee6b836407d2'),
(67, 1, 1, 1, 1448972755, 1448972755, 50, 83, 311, 'Вечность есть играющее дитя, которое расставляет шашки: царство над миром принадлежит ребенку.', '', '', '', '5e15e3140c4f9721c52e1bcce99fcc51'),
(68, 1, 1, 1, 1448972755, 1448972755, 50, 83, 313, 'Скрытая гармония лучше явной.', '', '', '', 'ee0efd8f520ab4ebc3afc110b5b0ec12'),
(69, 1, 1, 1, 1448972755, 1448972755, 50, 83, 321, 'Глаза – более точные свидетели, чем уши.', '', '', '', '196c7f5d976add069d6e48744ca1282f'),
(70, 1, 1, 1, 1448972755, 1448972755, 50, 83, 328, 'Взрослый муж слывет глупым у бога, как ребенок – у взрослого мужа.', '', '', '', 'fc8955f6634fea8631651ccc73a87686'),
(71, 1, 1, 1, 1448972755, 1448972755, 51, 83, 333, 'Женщины вместе с одеждой совлекают с себя и стыд.', '', '', '', 'ebcac04cf24b04074057a0d4e0d76e98'),
(72, 1, 1, 1, 1448972755, 1448972755, 51, 83, 349, 'Клевета двоих делает преступниками, а третьего – жертвой.', '', '', '', 'ef6e19141314223308a12e82934558f5'),
(73, 1, 1, 1, 1448972755, 1448972755, 52, 83, 0, 'Все огляди хорошо, чтоб не на смех соседям жениться.', '', '', '', 'da511dfc35dffbdd4fa82dc3c66282dd'),
(74, 1, 1, 1, 1448972755, 1448972755, 53, 83, 359, 'Человек не начинает прелюбодействовать в возрасте пятидесяти лет, или он всегда был таковым.', '', '', '', '6149a63d554f4bfd4a1fa1d949848249'),
(75, 1, 1, 1, 1448972755, 1448972755, 54, 83, 0, 'Сколько звезд на небе, столько обманов таит женское сердце.', '', '', '', 'd8193537d4898a8fd047e73ace890147'),
(76, 1, 1, 1, 1448972755, 1448972755, 55, 83, 0, 'Я – тебе, ты – мне.', '', '', '', '4d5da05257433fc04aeb25cc4985fe83'),
(77, 1, 1, 1, 1448972755, 1448972755, 55, 83, 0, 'Бог находит виновного.', '', '', '', '57fdc817cd6077abd723cf5457fe41f5'),
(78, 1, 1, 1, 1448972755, 1448972755, 55, 83, 0, 'Какое слово скажешь, такое в ответ и услышишь.', '', '', '', 'd4f3971cd8eeabe508cd6407b7110e13'),
(79, 1, 1, 1, 1448972755, 1448972755, 55, 83, 0, 'Об одном следует говорить, а о другом – молчать.', '', '', '', 'd0d65ac009725eb390c4e4de832b1f08'),
(80, 1, 1, 1, 1448972755, 1448972755, 55, 83, 0, 'Женщину украшает молчание.', '', '', '', 'fb657faeeabc4ef61af6ec3410ba8ec0'),
(81, 1, 1, 1, 1448972755, 1448972755, 55, 83, 0, 'Нет ничего пагубнее женщины.', '', '', '', '56ce2f5bd3fab2f838771ab49087843f'),
(82, 1, 1, 1, 1448972755, 1448972755, 43, 83, 396, 'Письмо пишется и посылается как своего рода подарок.', '', '', '', 'c41eacf6ff3050b11b96c6771a475f69'),
(83, 1, 1, 1, 1448972755, 1448972755, 43, 83, 397, 'Каждый, кто пишет письмо, дает почти что изображение своей души.', '', '', '', '115d5fe2439157afb1af9e09dc207738'),
(84, 1, 1, 1, 1448972755, 1448972755, 43, 83, 398, 'Во всяком сочинении словесного искусства можно разглядеть характер пишущего.', '', '', '', '458b141331db153a332519d73a6f3185'),
(86, 1, 1, 1, 1448972755, 1448972755, 43, 83, 400, 'Письмо – это выражение дружбы, сжато говорящее о простом деле простыми словами.', '', '', '', '690cd3b0d3d18841e445e120738add50'),
(87, 1, 1, 1, 1448972755, 1448972755, 44, 83, 419, 'Кто сам не любит никого, того (…) тоже никто не любит.', '', '', '', '56fedac8aa40941a60b79feb68ba0d01'),
(88, 1, 1, 1, 1448972755, 1448972755, 44, 83, 420, 'Кому попался хороший зять, тот приобрел сына, а кому дурной, тот потерял и дочь.', '', '', '', 'fc168b21040384de97b8b829445bbc1c'),
(89, 1, 1, 1, 1448972756, 1448972756, 44, 83, 421, 'Ложе уничтожает ссору любовников.', '', '', '', '34b440f6411ca05b62730784c9935774'),
(90, 1, 1, 1, 1448972756, 1448972756, 44, 83, 0, 'Истина в глубине.', '', '', '', '0ab7527a0cd89d443b41aeda54db0bdc'),
(91, 1, 1, 1, 1448972756, 1448972756, 44, 83, 0, 'Быть верным долгу в несчастье – великое дело.', '', '', '', '7255b183a74de16d5a03a5c2401d03ae'),
(92, 1, 1, 1, 1448972756, 1448972756, 44, 83, 0, 'Украшенье женщины – молчаливость; похвальна также и простота наряда.', '', '', '', '2320b7e62decb66a000ffe5f44dc75f2'),
(93, 1, 1, 1, 1448972756, 1448972756, 44, 83, 0, 'Только та любовь справедлива, которая стремится к прекрасному, не причиняя обид.', '', '', '', 'c9158401a1be1f371d18016f05a52791'),
(94, 1, 1, 1, 1448972756, 1448972756, 44, 83, 0, 'Счастлив тот, кто при малых средствах пользуется хорошим расположением духа, несчастлив тот, кто при больших средствах не имеет душевного веселья.', '', '', '', '57956e8c6220e1a9bbdee4f083ad1ff8'),
(95, 1, 1, 1, 1448972756, 1448972756, 44, 83, 0, 'Из удовольствий наиболее приятны те, которые случаются наиболее редко.', '', '', '', '8d7e77f18f28d099ad89e5d7a9afaa75'),
(96, 1, 1, 1, 1448972756, 1448972756, 45, 83, 0, 'Пользуйся чаще ушами, чем языком.', '', '', '', '45a216defc069ea80fd63c57fe788ab4'),
(97, 1, 1, 1, 1448972756, 1448972756, 46, 83, 447, 'Увидев однажды женщину, непристойным образом распростершуюся перед статуями богов, и желая избавить ее от суеверия, он [Диоген] подошел и сказал: «А ты не боишься, женщина, что, быть может, бог находится позади тебя, ибо все полно его присутствием, и ты ведешь себя непристойно по отношению к нему?»', '', '', '', 'b6b3f989637dba51615ca45eccf02d0c'),
(102, 1, 1, 1, 1448972756, 1448972756, 46, 83, 0, 'Отцы и дети не должны дожидаться просьбы друг от друга, а должны предупредительно давать потребное друг другу, причем первенство принадлежит отцу.', '', '', '', '503fe409f2b41bc3303b8745757be80b'),
(103, 1, 1, 1, 1448972756, 1448972756, 47, 83, 486, 'В разнообразии перемен красота остается вечно новой.', '', '', '', '2f27625465d0c82bccfa6992b4d31d7b'),
(104, 1, 1, 1, 1448972756, 1448972756, 49, 83, 492, 'Не верь женщине, даже если она говорит правду.', '', '', '', '7047c69e734fae8a66e0f9b61c68dd2b'),
(105, 1, 1, 1, 1448972756, 1448972756, 49, 83, 0, 'Когда двое говорят и один из них сердится – тот, кто уступает, – умнее.', '', '', '', 'dc4f4ca0f359b2b91bebef63d54204e7'),
(106, 1, 1, 1, 1448972756, 1448972756, 49, 83, 0, 'Не может быть добрым рожденный от злого отца.', '', '', '', 'cef1a02ad898500b8f0f6aa79d5e8c23'),
(107, 1, 1, 1, 1448972756, 1448972756, 49, 83, 0, 'Лишь ласковый имеет дар пленять.', '', '', '', 'f2429f3abaf61837842dee75b2490840'),
(108, 1, 1, 1, 1448972756, 1448972756, 56, 83, 0, 'К родителям относись так, как ты желал бы, чтобы твои собственные дети относились к тебе.', '', '', '', '92938aea350f519b2d04e8709c2c102f'),
(109, 1, 1, 1, 1448972756, 1448972756, 56, 83, 0, 'Не думай, что, сделавши что-либо нехорошее, ты можешь скрыться, так как, скрывшись от других, ты не скроешься от своей совести.', '', '', '', '36def68efae74bb6736e54c18fadf702'),
(110, 1, 1, 1, 1448972756, 1448972756, 56, 83, 0, 'Люди плачут над вымыслами поэтов, а на подлинные страдания… взирают спокойно и равнодушно.', '', '', '', 'b2abd2bd704b3efb8007513d3c3054bd'),
(111, 1, 1, 1, 1448972756, 1448972756, 57, 0, 0, 'Кто, наставляемый на пути любви, будет в правильном порядке созерцать прекрасное, тот, достигнув конца этого пути, вдруг увидит нечто удивительно прекрасное по природе(...)', '', '', '', 'e115d220e9905aca3ba51336c222380b'),
(112, 1, 1, 1, 1448972756, 1448972756, 58, 0, 0, 'Любовь, сладострастно-горькое чудовище, от которого нет защиты.', '', '', '', '438aa6b950373f7d29b352da46a14ee3'),
(113, 0, 1, 1, 1448972756, 1448972756, 48, 0, 0, 'Любовь, если можно любовью назвать,Безумной похоти женской власти,Опасней чудовищ, страшнее бури.', '', '', '', 'dfc43ca326d77fea11b5b4ac3cae64c1'),
(114, 1, 1, 1, 1448972756, 1448972756, 119, 0, 0, 'Природа сказала женщине: будь прекрасной, если можешь, мудрой - если хочешь; но благоразумной ты должна быть непременно', '', '', '', 'bdce48637b5e0a6f43bbf6f962d26f5e'),
(115, 1, 1, 1, 1448972756, 1448972756, 119, 0, 0, 'Так отрадно, когда тебя любят ради тебя самого', '', '', '', 'c7b7e64c4696243a0c3581fa128dc6b5'),
(116, 1, 1, 1, 1448972756, 1448972756, 119, 0, 0, 'Могут ли покой и любовь ужиться в одном сердце?', '', '', '', 'e6b96e5974368da727596113d8a43122'),
(117, 1, 1, 1, 1448972756, 1448972756, 120, 0, 0, 'Любовь не должна изготавливаться при помощи увеличительных стекол', '', '', '', 'a0a47fc7b5cc8a35e096e6d54f80b68d'),
(118, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Либо это столь глупая вещь, что она не способна познать самое себя, либо столь отвратительная, что она должна скрывать себя под гримом. Сцена многим обязана любви, жизнь - ничем. Любовь всегда означает слишком узкий взгляд на вещи.', '', '', '', '17e4fec8664068a1c15f4785a14f3004'),
(119, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Любить и быть мудрым - невозможно.', '', '', '', '0c4e3131ac789b93656fca80b16db48c'),
(120, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Мужчина уже наполовину влюблен в каждую женщину, которая слушает, как он говорит', '', '', '', '3dfc10c5fba9460b2dfd8bd2291a6e29'),
(121, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Никакая страсть не околдовывает человека, как любовь или зависть.', '', '', '', '2ca85fccc6046eb152523a344eb5a796'),
(122, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Всякий, кто любит одиночество, либо - дикий зверь, либо - Господь Бог.', '', '', '', '5d6ad01cb351aa916d56b26cc49ea01d'),
(123, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Дети приумножают наши житейские заботы и тревоги, но в то же время благодаря им смерть не кажется нам такой страшной.', '', '', '', '4f90df9c3bd1640d5d20cc9a936d8b45'),
(124, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Для молодых людей жены - любовницы; для людей средний лет - спутницы жизни, для стариков - сиделки.', '', '', '', '8c748a08e7b7fd0061bd2a264a0bdea8'),
(125, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Жена и дети учат человечности; холостяки же мрачны и суровы.', '', '', '', '7cf218f3ca8a76408898602c87c7e455'),
(126, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Женитьба - умная вещь для дурака и глупая для умного.', '', '', '', '4a29d44f49f8d833bead43b0b5288036'),
(127, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Имеющий жену и детей - заложник судьбы.', '', '', '', '400df284964400df2f72ba30593b7f5c'),
(128, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Красивое лицо является безмолвной рекомендацией.', '', '', '', '6840c3a57b679b5d4ea2432750a433ec'),
(129, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Любовь к родине начинается с семьи.', '', '', '', '4fa47be03493a7ec0c28cbaa3b6ca933'),
(130, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Семейные интересы почти всегда губят интересы общественные.', '', '', '', '1914a1e005ac0b5b8ca16509c37e5786'),
(131, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Только то наслаждение естественно, которое не знает пресыщения.', '', '', '', 'fa26e491b1b3169f473f642355f0cdfe'),
(132, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Увы! Многие в бытность женихом и невестой не знали ничего, кроме поэзии, а после женитьбы жили всегда в прозе.', '', '', '', 'c7c95b3d6a4aec28e04a426fe65e9606'),
(133, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Супружеская любовь размножает человеческий род, дружеская - совершенствует его, а безнравственная развращает и унижает.', '', '', '', 'b3d0859974c34c963eecb682e4e04b64'),
(134, 1, 1, 1, 1448972756, 1448972756, 118, 0, 0, 'Дети делают труд радостным, но неудачи кажутся из-за них более огорчительными; благодаря детям жизнь кажется более приятной, а смерть - менее страшной.', '', '', '', '34af29e700eae1dbc46ddbba849de241'),
(135, 1, 1, 1, 1448972756, 1448972756, 121, 0, 0, 'Разлука для любви - что ветер для огня: слабую она гасит, а большую раздувает', '', '', '', 'ff7c3abe76304e1f00c4e91b2c15a50d'),
(136, 1, 1, 1, 1448972756, 1448972756, 122, 0, 0, 'Повесничать так же небезопасно, как играть с огнем. Любовь - это девушка, у которой есть друзья в гарнизоне.', '', '', '', 'c4ddb52afdf5845ac9cf236600bbe97f'),
(137, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'В жизни нередки случаи, когда женщина изо всех сил скрывает страсть, которую испытывает к мужчине, в то время как он так же прилежно разыгрывает любовь, которой вовсе к ней не чувствует', '', '', '', '4dfa32b32eead5be843109e4a94bb5f4'),
(139, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Время укрепляет дружбу, но ослабляет любовь', '', '', '', '0054b2b473f4e45d80286bf9c6300746'),
(140, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Главное для женщины не в том, чтобы иметь духовного наставника, а в том, чтобы жить разумно и не нуждаться в нем', '', '', '', 'aff63bf3eeb7b5bf310abdb9f39d163d'),
(141, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Женщина, которую все считают холодной, просто еще не встретила человека, который пробудил бы в ней любовь', '', '', '', 'a19d8f517fc03919dbde296e6601fb5f'),
(142, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Женщины не любят друг друга, и причина этой нелюбви - мужчина', '', '', '', 'ec598c693fc8bb9f2681c963f337ed01'),
(143, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Женщины ни в чем не знают середины: они или намного хуже, или намного лучше мужчин', '', '', '', 'ce39ec310b853bcb9dd0374562c5273c'),
(144, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Жизнь - трагедия для того, кто живет чувствами, и комедия для того, кто живет мыслями', '', '', '', 'af8eab5b5584d9d27c9c403745a3ea4c'),
(145, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'И при зарождении, и на закате любви люди всегда испытывают замешательство, оставаясь наедине друг с другом', '', '', '', '8df4a0248056b200b1a75b3947ba147e'),
(146, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Когда женщина не спускает глаз с мужчины или все время отводит их от него, все сразу понимают, что это значит', '', '', '', '124921229228543da2746fc9570061b0'),
(147, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Когда женщина перестает любить мужчину, она забывает все - даже милости, которыми его дарила', '', '', '', '180f5c14664d91e299241d47dff9dc38'),
(148, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Любовь начинается с любви; даже самая пылкая дружба способна породить лишь самое слабое подобие любви', '', '', '', '800812d718621ac73e083b96ea8a94c8'),
(149, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Мужчина громко негодует на женщину, которая его разлюбила, и быстро утешается; женщина не столь бурно выражает свои чувства, когда ее покидают, но долго остается безутешной', '', '', '', 'dcc4c4f9ddc4c44703b6e209f11c8ea0'),
(150, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Мужчина соблюдает чужую тайну вернее, чем собственную, а женщина лучше хранит свою, чем чужую', '', '', '', 'a1b9ba88490eab7beda50f4031a991a4'),
(151, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Мы хотим быть источником всех радостей, или, если это невозможно, всех несчастий того, кого мы любим', '', '', '', 'dde4acce2eec45be81f0f3181821a5bd'),
(152, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'На свете нет зрелища прекраснее, чем лицо любимой, и нет музыки слаще, чем звук любимого голоса', '', '', '', '477cfee561e12d1f582b94fcd5ff656e'),
(153, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Начало и ослабление любви чувствуется по затруднению, которое испытываем мы, оставаясь одни"', '', '', '', '47199213ea750535e6673a6ed8d1eef4'),
(154, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Непостоянная женщина - та, которая уже не любит; легкомысленная - та, которая любит уже другого; ветреная - та, которая не знает, любит ли она и кого любит; равнодушная - та, которая никого не любит', '', '', '', '64c5c0307b8a7a54f641767e44b29da8'),
(155, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Полюбить - значит проявить слабость; разлюбить - значит иной раз проявить не меньшую слабость', '', '', '', '209380fa269045e78a0c9a24966887c7'),
(156, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Тосковать о том, кого любишь, много легче, нежели жить с тем, кого ненавидишь', '', '', '', '908ec005f48654251abcf2a19799ff7c'),
(157, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Тот, кто любит так сильно, что хотел бы любить в тясячу раз сильнее, все же любит меньше, нежели тот кто любит сильнее, чем сам того хотел бы', '', '', '', '1b8653680ba942b259727773378a550f'),
(158, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Труднее всего исцелять ту любовь, которая вспыхнула с первого взгляда', '', '', '', 'f917089dd2cf1a60fca0fedbe7756e69'),
(159, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'У друзей мы замечаем те недостатки, которые могут повредить им, а у любимых те, от которых страдаем мы сами', '', '', '', '89fe99df53434c983111708aa898e757'),
(160, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Угасание любви - вот неопровержимое доказательство того, что человек ограничен и у сердца есть пределы', '', '', '', 'd5eac828c79917af8711fa9344a48737'),
(161, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Умирает любовь от усталости, а хоронит ее забвение', '', '', '', '9a95cc3a39e0644d7673875509036904'),
(162, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Умное выражение лица у мужчин - то же, что правильность черт у женщин; это род красоты, которую и самые тщеславные люди желали бы иметь', '', '', '', '8eba4a2c3d963b69049123ff6cd5bca5'),
(163, 1, 1, 1, 1448972756, 1448972756, 123, 0, 0, 'Чем больше милостей женщина дарит мужчине, тем сильнее она его любит и тем меньше любит ее он', '', '', '', '9a6d923aee207a3f54fca1a046a877dc'),
(217, 1, 1, 1, 1448972756, 1448972756, 107, 0, 0, 'Когда женщина охвачена страстью, лицом она почему-то делается похожей на девочку Правда, эта страсть может быть обращена и на зонтик', '', '', '', 'a310cd6032c5c73adbbc22dddac68ae5'),
(218, 1, 1, 1, 1448972756, 1448972756, 107, 0, 0, 'Мужчина искони больше любви ценит работу Если кто-нибудь усомнится в этом факте, пусть почитает письма Бальзака Бальзак писал графине Ганской: "Если б это письмо обратить в рукопись, сколько франков оно стоило бы!"', '', '', '', '014ffd13f29bfa0640be83995942ed0e'),
(219, 1, 1, 1, 1448972757, 1448972757, 107, 0, 0, 'Брак полезен для успокоения чувственности. Для успокоения любви он бесполезен.', '', '', '', 'a56472cef2106aaaec926a792fc31d57'),
(220, 1, 1, 1, 1448972757, 1448972757, 108, 0, 0, 'Влюбленные, принимающие идеалы друг друга, с годами становятся всё более привлекательными друг для друга', '', '', '', '1f8827390ea6008eca66e25f3c9b0cd9'),
(221, 1, 1, 1, 1448972757, 1448972757, 108, 0, 0, 'Если тебе когда-нибудь захочется найти такого человека, который сможет одолеть любую, даже самую тяжелую беду и сделать тебя счастливым, когда этого не может больше никто: ты просто посмотри в зеркало и скажи: "Привет!"', '', '', '', 'c7a8a8c3225052e23e76d109a19a4a2b'),
(222, 1, 1, 1, 1448972757, 1448972757, 109, 0, 0, 'Почему, когда любишь женщину и все у вас с ней чудесно, она непременно хочет превратить тебя и себя в воспитателей целой кучи сопливых младенцев, которые орут с утра до ночи и путаются у вас под ногами, мешая наслаждаться уединением?', '', '', '', '2b54bad96a6a11bb42d9340d64b2e0e6'),
(223, 1, 1, 1, 1448972757, 1448972757, 110, 0, 0, 'Женщиной не рождаются, ею становятся', '', '', '', 'dbfd63a814a914ce97885843c290ae3c'),
(224, 1, 1, 1, 1448972757, 1448972757, 116, 0, 0, 'Умная женщина - та, в обществе которой можно держать себя как угодно глупо', '', '', '', 'fbcb23477a43ca20fe3a7a028b2799a0'),
(225, 1, 1, 1, 1448972757, 1448972757, 112, 0, 0, 'Любовь не должна просить и не должна требовать, любовь должна иметь силу увериться в самой себе Тогда не ее что-то притягивает, а притягивает она сама', '', '', '', '68bdd7a9d9be5436db3c67db44dc7542'),
(226, 1, 1, 1, 1448972757, 1448972757, 117, 0, 0, 'Нужно быть немного схожими, чтобы понимать друг друга, но нужно быть немного разными, чтобы друг друга любить.', '', '', '', '9384a98950cde4e6fa216b73c2622953'),
(227, 1, 1, 1, 1448972757, 1448972757, 113, 0, 0, 'Безрассудство любви в том, что любящий стремится, чтобы дни ожидания поскорее прошли и пропали Так он стремится приблизить конец Так любовь одной из граней соприкасается со смертью', '', '', '', '8d16b2a562d666e9f23ad5a37f46517e'),
(228, 1, 1, 1, 1448972757, 1448972757, 113, 0, 0, 'Не быть любимым - это всего лишь неудача, не любить - вот несчастье', '', '', '', 'a5cc9d61e9a98492fbbbca94a08166cd'),
(229, 1, 1, 1, 1448972757, 1448972757, 113, 0, 0, 'Одни созданы для того, чтобы любить, другие - для того, чтобы жить', '', '', '', '43c9c58d3897084746e9768105a2c64a'),
(230, 1, 1, 1, 1448972757, 1448972757, 113, 0, 0, 'У любви есть своя честь. Стоит потерять ее - и любви приходит конец', '', '', '', '13db58e0b7d0a4cf19b97d94c48f4b18'),
(231, 1, 1, 1, 1448972757, 1448972757, 114, 0, 0, 'Нам нравятся за что-то; мы любим несмотря ни на что.', '', '', '', '2c0f4db6481b42880d1415b774950886'),
(232, 1, 1, 1, 1448972757, 1448972757, 115, 0, 0, 'Кто не тратит себя, становится пустым местом', '', '', '', '44ecbbb60b8428715942bf59bf1fc122'),
(233, 1, 1, 1, 1448972757, 1448972757, 115, 0, 0, 'Мы в ответе за тех, кого приручили', '', '', '', '58204f1fc7b7a76bf6234a19d7bfa653'),
(234, 1, 1, 1, 1448972757, 1448972757, 115, 0, 0, 'Мы любим того, о ком заботимся', '', '', '', '3f7ed6d606139bebb977f86ae2e3334e'),
(235, 1, 1, 1, 1448972757, 1448972757, 115, 0, 0, 'Настанет день, и в юной девушке просыпается женщина И тогда появляется какой-нибудь дурак И неизменно проницательный ее взор впервые обманывается и видит дурака в самом розовом свете Если дурак прочитает стихи, его принимают за поэта Отдают ему свое сердце - дикий сад, а ему по вкусу только подстриженные газоны И дурак уводит принцессу в рабство', '', '', '', '183d498d3e7b9709642ba1798a149e49'),
(236, 1, 1, 1, 1448972757, 1448972757, 115, 0, 0, 'Зорко одно лишь сердце Самого главного глазами не увидишь', '', '', '', 'fc757f5b04a5a2233c59c0f513a0272b'),
(237, 1, 1, 1, 1448972757, 1448972757, 115, 0, 0, 'Не смотреть друг на друга, но смотреть в одном направлении - вот что значит любить', '', '', '', 'd0de997d8affcfddc4201bcf494fa9af'),
(238, 1, 1, 1, 1448972757, 1448972757, 111, 0, 0, 'Родственники даны нам судьбою. Как хорошо, что друзей мы выбираем сами.', '', '', '', '0bd1da0f647d02a13ccdabd001dcc670'),
(239, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'А уходить мужчина тоже должен красиво, как и приходить. Т.е. навсегда', '', '', '', 'c6f66a3e723fa8dace4aeb2500f1c522'),
(240, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Большинство мужчин любят лесть вследствие скромного мнения о себе, большинство женщин - по противоположной причине', '', '', '', '4dbad5287b7738130a30c6f6a732609f'),
(241, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'В безопасности не тот, кто любит, а тот, кто позволяет себя любить', '', '', '', '2d64b0dd1ae8b2c35e46bf006a08fb4c'),
(242, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'В доме, где есть дети, поселяется добрый ангел, в доме без детей - раздор', '', '', '', '217a4ecf72f6ba2b03914f0b7dc1ba9a'),
(243, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'В каждой женщине должна быть своя безyминка', '', '', '', '0f4a61af7f1c306f8daa25a101ba712d'),
(244, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'В любви, как и в искустве, не нужно читать того, что было сказано другими, нужно говорить то, что чувствуешь, и тот, кто торопиться говорить, когда ему нечего сказать, очень рискует никогда ничего не сказать', '', '', '', '9d39ac6e9abb017d33453911fde63d34'),
(245, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'В ревности больше себялюбия, чем любви', '', '', '', 'bafe5ca9dc37b08d76eac4c8cc125acf'),
(246, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Влюблённость для любви - то же самое, что детство для жизни', '', '', '', 'eccbb0b80e3c8a2518e7421ab81e4f5d'),
(247, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Воображение женщин богаче нашего; это устроено для того, чтобы они могли восхищаться нами', '', '', '', '3d8543ee0f4d5f5b3f59cf7bf1f34622'),
(248, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Все рассуждения мужчин не стоят одного чувства женщины', '', '', '', '40cf1ddbb8026f982736515c0efb3353'),
(249, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Встреча двух людей – это как встреча двух химических элементов. Реакция может и не произойти, но если она происходит – изменяются оба', '', '', '', 'a8ad4350c1999638ef97ee8b68cbb3d7'),
(250, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Всякая бывает любовь. Не может быть только любви с гарантией', '', '', '', '2e1ee0104da02e7ca36cae91dbc7bd68'),
(251, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Глубоко любящие не состарятся', '', '', '', 'ae2a695f4ca0f2d1417baa4b01983ef0'),
(252, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Гордость - это роскошь, которую влюбленная женщина не может себе позволить', '', '', '', '313e576748f216c2b9937a864b944d15'),
(253, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Горькая сладость надежд и сладкая горечь разочарований - все это любовь', '', '', '', '9f23a860a3c026722b9692f4df8b2953'),
(254, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Девиз для женщин: "Изменяйся, оставаясь незаменимой!"', '', '', '', 'b27ae2d1e67a58cc6bebf0d20315e2cc');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(255, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Для поцелуя недостаточно одного, слишком много троих, двое же - в самый раз', '', '', '', '48df226cab19ee11052dd369fba6d413'),
(256, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Для того, чтобы любовное письмо было тем, чем оно должно быть, надо, начиная его, не знать, что скажешь, а оканчивая - не знать, что сказал', '', '', '', '6c5e3a853caa70e5b3c46917eb80abc5'),
(257, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Душа, которая может говорить глазами – может целовать взглядом', '', '', '', 'a16a16e3f259d966b8dc3c648343c742'),
(258, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Если бы мужчины знали, что думают женщины, то были бы в двадцать раз нахальнее, точно так же, как если бы женщины знали поближе мужчин, то стали бы еще кокетливее', '', '', '', '4335b6394c469557d7fd2e9dfdb2132a'),
(259, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Если вы нашли женщину своей мечты - с остальными мечтами можете уже распрощаться', '', '', '', 'c92b4818c1f50235bbf87203bca152e4'),
(260, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Если вы нашли настоящую любовь – дайте ей крылья летать и если она останется с вами – так тому и быть!', '', '', '', '24db166f848e312acd05fc94aa8855aa'),
(261, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Если мужчина дает женщине все, что она просит, значит, она просит слишком мало', '', '', '', '95c27c3a59014c3fd677ee0c298ef8b1'),
(262, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Если мы еще можем любить тех, кто заставил нас страдать, то любовь наша лишь становится сильнее', '', '', '', 'fa7789467d7d3ac5de4bf26081b612cc'),
(263, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Если он - это первое, о чем ты думаешь, когда просыпаешься; единственное - когда бодрствуешь; и последнее, о чем ты думаешь, перед тем как заснуть - значит он действительно особенный!', '', '', '', '36d9a7c32cb9e60ca66d6fe60a3fe1bb'),
(264, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Если сердце не смотрит, то и глаза не видят', '', '', '', 'c29cc883e1a3d9d790b00ed54b964118'),
(265, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Есть женщины, в которых никто не влюбляется, но которых все любят. Есть женщины, в которых все влюблены, но которых никто не любит. Счастлива только та женщина, которую все любят, но в которую влюблен лишь один', '', '', '', '888528afa7e840f7ed74493a4a782d47'),
(266, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женоненавистник - мужчина, который ненавидит женщин не меньше, чем женщины ненавидят друг друга', '', '', '', '22b06e6e4b51d73173273a15501dfb96'),
(267, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женская логика: "если уж и быть половиной, то большей"', '', '', '', '2e895b38dcd518b84ed32cd8d5029358'),
(268, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женские слезы ничего не стоят, но много значат', '', '', '', '901f52c06ab9d723cc2521e799d0c082'),
(269, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина - великая воспитательница мужчины', '', '', '', 'f87ce98e7106438b055a640bcf30abd5'),
(270, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина - самый крепкий напиток', '', '', '', '3bc7cb808a090b0a0475564de7121f45'),
(271, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина больше мечтает о том, чтобы доставить счастье, нежели о том, чтобы самой быть счастливой', '', '', '', '869cb38e513e657d440f31b246aee498'),
(272, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина даёт секс, чтобы получить любовь, а мужчина даёт любовь, чтобы получить секс', '', '', '', 'a2d7de10c7016cf29d3cea440d6c3ac6'),
(273, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина должна быть настолько умна, чтобы нравиться глупым мужчинам, и настолько вульгарна, чтобы нравиться умным', '', '', '', 'd2358a919c0b695b03484abb3f399191'),
(274, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина достается тому, кто ее хвалит', '', '', '', 'd786e78ee0655c18f79c944a446dd72f'),
(275, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина любит, когда привыкнет; мужчина - до тех пор, пока не привыкнет', '', '', '', '1a1019b01218d9b6d6c776e38d01ac48'),
(276, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина может создать из ничего три вещи: шляпку, салат и ссору', '', '', '', '038b5f3cf69d0ec482c9b473ec756b13'),
(277, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина не изменяет в двух случаях: если считает, что ее мужчина лучший, или если считает, что все они одинаковые', '', '', '', '937d337bcb1c53ace6ac61af37f87fc8'),
(278, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина свои недостатки считает изюминками, а мужские - пороками', '', '', '', '88e13468543aedf07aaf1a5c076171ff'),
(279, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина скорее полюбит человека, которого она ненавидит, чем того, к которому равнодушна', '', '', '', '39c1bb0a1f3cc3b3506f15ceb7df66cd'),
(280, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщина хочет многого от одного, мужчина - одного от многих', '', '', '', '48522917d05647903f5d9a68185a088f'),
(281, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщине невозможно подарить слишком много цветов, а ребенку - слишком много игрушек', '', '', '', 'd5b7b147b609908bfcbb5d1c63c2a1f8'),
(282, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщины без мужского общества блекнут, а мужчины без женского глупеют', '', '', '', '0b1ca79bb8cdab1834eb7d3e4ebc5f26'),
(283, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщины вдохновляют нас на многое великое. Они же мешают это творить', '', '', '', 'b759b8a47cc6e6f3de8e2dadb15ba5f8'),
(284, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщины и кошки одинаковы - ты только думаешь, что ими владеешь', '', '', '', '73b9d9caa00f0482d4d7ca148f3b6605'),
(285, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщины не более нравственны, чем мужчины, но меньше испорчены властью', '', '', '', '6d14a6f16127e78e5404bb0fb8352d47'),
(286, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщины слишком не доверяют мужчинам вообще и слишком доверяют им в частности', '', '', '', '386128a59bad84cfea2a513f6584102d'),
(287, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщины способны на все, мужчины - на все остальное', '', '', '', '273bb4ac1448a0331825e0a20e2f4b9e'),
(288, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Женщины учёны от природы, мужчины - от книг', '', '', '', '75de21d929a8777ab79cb19f30bf5158'),
(289, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Задача каждого человека - прожить свое простое будничное так, чтобы внести в свое и в чужое существование каплю мира и радости', '', '', '', 'fbde6277a616cc5eaa279df21198315f'),
(290, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Зачем печалиться о том, что могло бы быть и чего не будет. Радуйся, вспоминая то, что было. Это останется с тобой навсегда', '', '', '', 'cc1d012fea5456c38da60d988a49dce9'),
(291, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Идеальный подарок мужчине, у которого все есть, - женщина, которая знает, что со всем этим делать', '', '', '', '287673626138c5bb4403f8d2a907dba4'),
(292, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Им нельзя сделать замечание: они либо огрызаются и позволяют себе оскорбительные выражения, что делает их малопривлекательными, либо начинают истерично рыдать', '', '', '', '10ad4dd275716ad065df40a3a8fa7e88'),
(293, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Интуиция - поразительное чутье, подсказывающее женщине, что она права, независимо от того, права она или нет', '', '', '', '960be8c81309883367b7ae08c8eb8a0b'),
(294, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Итак, не спрашивайте, почему я люблю вас. Ответ на такой вопрос может дать только обусловленная любовь. Я же люблю вас не потому, что вы имеете ту или иную внешность или совершаете те или иные поступки, или обладаете теми или иными добродетелями. Спрашивайте только: "Вы любите меня?" И я смогу ответить: "Да, да, конечно!"', '', '', '', 'a8c90b5a011d020ff4ccff23b8327186'),
(295, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Когда Бог оценивает человека, то меряет его сердце, а не голову', '', '', '', '51f13dfec710b462e883bc0cee4c2cde'),
(296, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Когда женщина выходит замуж, она меняет внимание многих мужчин на невнимательность одного', '', '', '', 'fe94f0e2534523f87a451c688c4f0b37'),
(297, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Когда любишь, то такое богатство открываешь в себе, столько нежности, ласковости, даже не верится, что так умеешь любить', '', '', '', 'e1608ecc57064071b75576f5a5c01142'),
(298, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Кокетство - это искусство сделать первый шаг так, чтобы мужчине казалось, что это он его сделал', '', '', '', 'e1d1289d8ac40a8a2c06d2577c97f6fe'),
(299, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Кокетство есть желание нравиться без потребности любить', '', '', '', '5242dc6448edff927f53ed0492ac228d'),
(300, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Конечно, мое решение никогда не должно нарушать твоей свободы. Предлагая тебе дар своей любви, я остаюсь самим собою, но я должен предоставить такую же возможность и тебе, - быть свободным в принятии или отвержении моего дара. Вероятно, это самый трудный путь, по которому должна идти истинная любовь, - быть самим собой и предлагать свою помощь в соответствии с тем, как я считаю нужным это сделать, никогда не принуждая тебя к принятию ее или желательному для меня ответу', '', '', '', 'd1cbaacf74bd1d3c7e12c0e9735deb15'),
(301, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Крестьянину нужна земля, дворянину - почет, солдату - война, купцу - деньги, женщине - весь мир', '', '', '', 'c5c1f49177421e868b8672d9db947d0a'),
(302, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Кто знает женщин, жалеет мужчин; но тот, кто знает мужчин, готов извинить женщин', '', '', '', '8fafc278cd3c2b814cb5aba618071da7'),
(303, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'ЛЕДИ - женщина, создающая такие условия, при которых мужчине остается лишь одно - быть джентльменом', '', '', '', '0193e0acec911f6aec90c5855be2b203'),
(304, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Лишь надо быть с чуткою, доброй душой, чтоб были вокруг все счастливы', '', '', '', 'fb5937d10c63b3ec759cd69313f40b6f'),
(305, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Лучшая женщина - та, что заставляет забыть об идеальной', '', '', '', '567b626089a614cbc7213fd3c7f2756f'),
(306, 1, 1, 1, 1448972757, 1448972757, 271, 0, 2814, 'Лучше блюдо зелени, и при нем любовь, нежели откормленный бык, и при нем ненависть', 'Прит., 15, 17', '', '', 'c51502137339ca3a8e3e3de7c8ddbcbb'),
(307, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любите - все остальное у вас уже есть!', '', '', '', '98974e141127b06eadb971077520f80f'),
(308, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любите тех с кем живете, если не можете жить с теми кого любите!', '', '', '', '462f4ef5cb95c42bd4f64eb934d97d0c'),
(310, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любовь - единственная глупость мудрого и единственная мудрость глупца', '', '', '', 'cb8c060db99ba3164d3bbef762e41712'),
(311, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любовь - единственная игра, не прекращающаяся из-за темноты', '', '', '', '35662fbe63feeef3e62cbeebc1252947'),
(312, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любовь - как ветер в моих руках. Так легко почувствовать, но так сложно поймать', '', '', '', 'f751f5e3b28d4e65b329c84c814827b4'),
(313, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любовь - последняя и самая тяжелая детская болезнь', '', '', '', '64fdee87e7713844a334b72b5b379ee2'),
(314, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любовь - это единственная ценность, которая не поддается власти и не продается за деньги', '', '', '', '4222cde8af37040c62636611c8e7862e'),
(315, 1, 1, 1, 1448972757, 1448972757, 27, 0, 0, 'Любовь - это когда ты не хочешь засыпать, потому что реальность лучше, чем сон', '', '', '', '435a37b03e0046716872c62f7aa37d2c'),
(316, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь - это не сказка, любовь - это просто любовь', '', '', '', 'cc849800fef71bdcf266ff503897fa9b'),
(317, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь – это щекотка где-то у сердца, которую ты не можешь почесать', '', '', '', '3f48904a3b77c2ce4d94c4326ea8aace'),
(318, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь есть кража порядка естественного у порядка общественного', '', '', '', '7caf97a662be85dd482c6857c7094c36'),
(319, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь мужчины развивается от чувственности к духовности. Любовь женщины развивается от духовности к чувственности', '', '', '', '0d2939f347d393cb17de748bf19ca2b4'),
(320, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь обычно предпочитает контрасты подобиям', '', '', '', '6ede4c30e5325ab73a581d0378e4fbf2'),
(321, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь понимает и разделяет все - сочувствием. Любовь любит находить во всем добро, она в него верит. Его везде ищет. Любовь прощает даже жестокосердие и извиняет осуждающего. Самая тяжелая задача любви - это прощать отсутствие ее в других, находить извинение для нетерпимости, прощать того, кто сам прощать не умеет. Нет на свете более утешительного и чудного зрелища, чем любовь, заслоняющая собою величайшее преступление - отсутствие любви', '', '', '', '81dede228c0937cb27dd7c834c593a6d'),
(322, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь проскальзывает в сердце через глаза (Это у мужчин. А у женщин - через уши)', '', '', '', '18f64d34a46e3bd89e314185d2be4892'),
(323, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь - это когда счастье и горе сочитается в одном чувстве', '', '', '', '282edb309b098800d1e04e80f28234f4'),
(324, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь, как ртуть: ее можно удержать в открытой ладони, но не в сжатой руке', '', '', '', 'dee4ece35a09c37adba65c8200ec87c4'),
(325, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь, которая выражается в ласковых словах, в тихом ровном общении, которая наполняет собою весь дом, облегчает жизнь и вносит свет и радость в каждое сердце, подобно солнцу, заглядывающему в каждый уголок скромного жилища, чтобы согреть и освятить его', '', '', '', 'af50fa266530187e7cc34135ca566d94'),
(326, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Любовь, подобно огню, не знает покоя: она перестает жить, как только перестает надеяться и бояться', '', '', '', 'b6633f3dd9e8daa0e74629c68b0003eb'),
(327, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Люди часто остаются одинокими потому, что строят стены вместо мостов', '', '', '', '7cb52e1e32d0469c0da612080d8d2094'),
(328, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мало какая женщина согласна признать свой возраст; мало какой мужчина ведет себя соответственно своему возрасту', '', '', '', 'f9c8d1e315be4e1f392f354b85a78a75'),
(329, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина бывает зол в двух случаях - когда он голоден и когда унижен, женщина - если несчастна в любви', '', '', '', 'db992065edda53a3b0a5fb062d93d0d8'),
(330, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина занимается женщиной, как химик своей лабораторией: он наблюдает в ней непонятные ему процессы, которые сам же и производит', '', '', '', 'c9417fb85fde9031bc332cc698384c71'),
(331, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина ищет кого-нибудь, перед кем он мог бы гордиться; женщина ищет плечо, к которому она бы могла прислониться', '', '', '', '27171da476c5710981135aae34173951'),
(332, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина лучше женщины философствует о сердце человеческом, но женщина лучше его читает в сердце мужчин', '', '', '', '7afc7d5370b0315083d7c1d36dd5c983'),
(333, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина настолько молод, насколько чувствует его женщина', '', '', '', '86b68499ae80e053624013ce93755d6d'),
(334, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина ради секса играет в любовь, а женщина ради любви играет в секс', '', '', '', '00c5b970c9fc3456c67f17da18a064e3'),
(335, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина терпит брак из любви к женщине; женщина терпит мужчину из любви к браку', '', '', '', '41e657adf02560e639ec7d40900c40d5'),
(336, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина, если бы и смог понять, что думает женщина, всё равно не поверил бы', '', '', '', 'e82bf99b88c4e3a5aa56ef088881708f'),
(337, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчина, который может спокойно вести машину, одновременно целуя красотку, просто не уделяет поцелую должного внимания', '', '', '', '75422c3aa24a5daf58f298ddf757ef4b'),
(338, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчинам живется гораздо лучше, чем женщинам Во-первых, они женятся позже; во-вторых, умирают раньше', '', '', '', 'b111dd7e7a9c7ec62c87de92fea73940'),
(339, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчины всегда правы, а женщины никогда не ошибаются', '', '', '', 'e442fa915d1f8ddf50fde535d22ff805'),
(340, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчины обнажают свою душу, как женщины - тело, постепенно и лишь после упорной борьбы', '', '', '', 'd34f2045ac5a56c598c30983a656d1be'),
(341, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчины объясняются в любви прежде чем почувствуют ее, женщины - после того, как испытали ее', '', '', '', '6b5a63bf9950722662af3c8dea817ab4'),
(342, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчины только делают вид, что не понимают женщин! Им это обходится дешевле', '', '', '', 'a92d03b71c295cc776a361bfd50c856c'),
(343, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мужчины убеждены в слабости женщин, а сами состоят у них же невольниками', '', '', '', 'b67c35b4c31412807ade61b6280dc243'),
(344, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Мы можем отдавать не любя, но мы не можем любить, не отдавая', '', '', '', '7f3a960a1b6462c720566a1672333f0b'),
(345, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Настоящего мужчину видно по женщине', '', '', '', '39eab9db0f2ca6b15c15ebdadbd2dfaf'),
(346, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Начинается всегда всё с улыбки женщины, которая тебя любит', '', '', '', 'cffce39e990ac29625763de0293b812d'),
(347, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Не верь женщине, когда ты ее любишь, верь, когда она тебя любит', '', '', '', 'ac01fbd203b25dd75a4067a2a9ee0c5d'),
(348, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Не упрекай себя, если не можешь дать что-то любимому Упрекай, если можешь, но не даешь!', '', '', '', 'ff8c1e685f35b03c24386a42c123262d'),
(349, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Не хмурься, потому что ты никогда не знаешь кто может влюбиться в твою улыбку', '', '', '', '9515b1a9df92496190584e4d73ad61ec'),
(350, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Некоторые женщины достойны, чтобы драться за них… Но некоторые - чтобы умереть', '', '', '', '9d3f3ecf0f72f51cfbe548ad38038f1c'),
(351, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Нельзя доверять женщине, которая не скрывает свой возраст. Такая женщина не постесняется сказать все что угодно', '', '', '', '4227a791f9c3de42b699cd45f6a1347a'),
(352, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Ни один мужчина не стоит женщины, если она по-настоящему хорошая женщина', '', '', '', '2a855b12ca9151f50f649cb818c7f2ca'),
(353, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Никогда не спрашивай у женщины - любит ли она тебя. Если любит, рано или поздно сама об этом скажет', '', '', '', '766f3c36cad858557770be5a7a9b52b5'),
(354, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Никогда не теряйте надежды и веры в лучшее, потому что после самой черной ночи всегда бывает светлый день и даже после самого сильного ливня ярко светит солнце', '', '', '', '12473b5cc7f3ff449836a32bb15caf6e'),
(355, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Однажды, во время прогулки я остановился на минутку и подумал о множестве вещей, которых у меня нет. Но внезапно я осознал, что у меня есть Ты и почувствовал себя цельным и счастливым', '', '', '', '3153eaaec50379c08da26a53baf84a68'),
(356, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Одни люди приходят в нашу жизнь, но быстро уходят. Другие же остаются дольше и оставляют следы в наших сердцах и мы уже становимся другими', '', '', '', 'b1d06d6813343e1c94dcc6e01a5f439c'),
(357, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Оставьте трех мужчин вместе после обеда, и вы можете быть уверены, что разговор зайдет о женщинах и что заведет его тот из них, кто постарше', '', '', '', 'e8b34a7f224f775165c583ebad0f7238'),
(358, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'От рождения до смерти мужчина остается дитятей женщины, которому постоянно от нее что-то нужно и который никогда ей ничего не дает, разве только подержать и сохранить что-нибудь, что может пригодиться ему самому', '', '', '', '0de14b20e5bff19efb9cc02e40e8db8d'),
(359, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Отвергая любовь, человек не только отвергает Бога, но и громко зовет дьявола', '', '', '', 'df8f34ee872382e177b923d59ee048ee'),
(360, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Отец охраняет ее в детстве, муж - в молодости, сын - в старости: нельзя предоставлять женщину самой себе', '', '', '', 'ed26604536c355c0c7ab2e5a0c5ec78a'),
(361, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Покажи свою жену, и я скажу, что ты за человек', '', '', '', '4fcf453fd5ebedb19fbc941224c8033d'),
(362, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Понятно, почему мужчина и женщина не могут понять друг друга - ведь они хотят совершенно разного Мужчина хочет женщину, а женщина - мужчину', '', '', '', '8b4fe751221343ac960ecf7068c210ff'),
(363, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'ПОЦЕЛУЙ - это когда две души встречаются между собой кончиками крыльев', '', '', '', '3402ff1920c51afb56c2b8274683a141'),
(364, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Поцелуй - это то, что вы не можете дать, не взяв, и взять, не отдав', '', '', '', '84f2e81f0bcccdd3753eb0bb33e24193'),
(365, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Привлекательные женщины отвлекают', '', '', '', 'f5336d803f6310541663ea580ffb910e'),
(366, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Путь любви к самому себе — основа любви ко всем без исключения. Любящий себя (и нелюбящий — тоже!) человек относится к другим так, как относится к себе', '', '', '', '93ee6f137400578353d88175c2673217'),
(367, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Ревнует не тот, кто любит, а тот кто хочет, чтобы его любили', '', '', '', '22419792f4619f372cff424bbe0bb631'),
(368, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Ревнуют того, кого любят или уважают', '', '', '', '5046849cca5ba5569a5328a3e9f6ee28'),
(369, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Романтизировать несчастную любовь можно только не испытав счастливую', '', '', '', '666e38a066ae1f24c831aa40102160da'),
(370, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'С одним женщина болтает, на другого кокетливо смотрит, о третьем думает. Кто же ей мил?', '', '', '', '8725237834899e014ad906d219736a1d'),
(372, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Самое важное чаще всего невесомо. Здесь как будто всего важней была улыбка. Часто улыбка и есть главное. Улыбкой благодарят. Улыбкой вознаграждают', '', '', '', '416cdbaa17ccfa8287dfbf363e9168b2'),
(373, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Самое дорогое у человека - жизнь, но любовь - это тоже жизнь, Не будет любви, не будет и жизни', '', '', '', 'd77f669a71baa835871d5f1d3d51449c'),
(374, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Самые невыносимые люди - это мужчины, считающие себя гениальными, и женщины, считающие себя неотразимыми', '', '', '', '1713fb30b39b129a8ad2a41bcb98d5e0'),
(375, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Сказать "Я тебя люблю" займет несколько секунд, показать как - всю жизнь', '', '', '', '8ea61693ee83265fe229441a54e7cd89'),
(376, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Слабый пол силен ввиду слабости сильного пола к слабому полу', '', '', '', '2e5f9641abf1f9778e11f79eb24b1dc2'),
(377, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Смотря на твою красоту, я думаю, что никогда не видел, чтобы ангелы летали так низко', '', '', '', '2ebefbce3ecdc41214f210cb4318568d'),
(378, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Супружество - это когда сначала говорит мужчина, а женщина слушает, потом говорит женщина, а мужчина слушает, и наконец мужчина и женщина говорят вместе, а соседи слушают', '', '', '', 'dd2c2033020ea00089a37a85d118e106'),
(379, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Счастлив тот, кто добивается цели, а не получает ее в подарок', '', '', '', '36d07ebc3c60a0a6d34d0929282a89eb'),
(380, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Счастье - это не владеть, тем что ты хочешь, а хотеть, то чем ты владеешь', '', '', '', '79cd538b7c119e354ff6c232c51a319e'),
(382, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Тоpопить женщину - то же самое, что пытаться ускоpить загpузку компьютеpа. Пpогpамма всё pавно должна выполнить все очевидно необходимые действия и ещё многое такое, что всегда остаётся сокpытым от вашего понимания', '', '', '', '3e44e305d75804d7f7cd1844c9f4accd'),
(383, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Только мужчина может сделать женщину по-настоящему женственной. Сделать так, чтобы она почувствовала себя красивой и желанной', '', '', '', '75f490b30302837481a9bcc4e28defe7'),
(384, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Тот, кто знает, чего хочет, или слишком мало хочет, или слишком много знает', '', '', '', '26e9bdf467ca4f0872bc800dbd21de0c'),
(385, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Удел женщины - владычествовать, удел мужчины - царить, потому что владычествует страсть, а правит ум', '', '', '', '976431eaba00ea9039445d59fee00f51'),
(386, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Ум дан женщине для того, чтобы скрывать свой характер', '', '', '', '6c9be9c501126f3798e03284c5d69b51'),
(387, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Успешный мужчина - тот, кто зарабатывает больше денег, чем может потратить его жена', '', '', '', '130e676b0b387faf84c7432b4bf0e5b7'),
(388, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Физическая жизнь на земле – это командировка, чтобы найти свою любовь', '', '', '', 'fedf2a5a69640fef11488ecbb892939a'),
(389, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Человеческая ревность есть страх сравнения', '', '', '', '33275dcb5ad641b100e1894633e8cea0'),
(390, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Честная женщина - это женщина, которая никогда не лжет, если, разумеется, речь не идет о ее возрасте, ее весе и заработках ее мужа', '', '', '', 'f336f3d7062dfa323b0465f90abb0c18'),
(391, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Что такое счастливая супружеская жизнь? Это постоянные, ежедневные взаимные компромиссы', '', '', '', '2f368e7532727a3c20557a40f56cfa35'),
(392, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Чтобы не надоесть мужчине, женщина меняет платья, а чтобы не надоесть женщине, мужчина меняет женщин', '', '', '', '3f247c70ad3ff6af96b86e4542bbc916'),
(393, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Шути любя, но не люби шутя!', '', '', '', 'a2d53136921101db40645b3a964c23a2'),
(394, 1, 1, 1, 1448972758, 1448972758, 27, 0, 0, 'Я могу лишь любить. Сказать, как ты любима, может лишь вечность одна', '', '', '', '50317ea343ee95da056191c393edf0fb'),
(395, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'В необходимом - единение, в сомнительном - свобода, во всем - любовь.', '', '', '', 'bcd8bef75cc8de1ef2d98e0ae3a2c94b'),
(396, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'Не входят в истину иначе как через любовь', '', '', '', '04a3abef10f5f34098da299b3a4a7999'),
(397, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'Люби - и делай что хочешь.', '', '', '', '335a1168d6b1724c01a194f8fc490ca4'),
(398, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'Любовь к ближнему ограничена тем, насколько каждый человек любит самого себя.', '', '', '', '8585638420c776bc79aec5c89c5b0fa9'),
(399, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'На что похожа любовь? У нее есть руки, чтобы помогать другим, у нее есть ноги, чтобы спешить на помощь к бедным и нуждающимся, у нее есть глаза, чтобы видеть горе и нужду, у нее есть уши, чтобы слышать людские вздохи и жалобы, - вот на что похожа любовь.', '', '', '', 'df60e1284df402939ecdf82c074fa547'),
(400, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'Существует высшая дружба, основанная не на привычке, а на разуме, при которой человек любит своего друга благодаряверности и доброй воле.. Если мы можем найти что-либо выше такой дружбы - это божественная любовь. Человек начинает любить Бога и любит Его в каждом другом человеке.', '', '', '', 'c0caf22f373c9da6ebb736a4feb735f9'),
(401, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'Тот, кто полон любви, исполнен самим Богом.', '', '', '', '722c7532e9618f100b6db905ed9ffd62'),
(402, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'Если бы Бог назначил женщине быть госпожой мужчины, он сотворил бы ее из головы; если бы рабою, то сотворил бы из ноги; но так как Он назначил ей быть подругой и равной мужчине, то сотворил из ребра', '', '', '', 'd98238b5d6468430bdfe141377106134'),
(403, 1, 1, 1, 1448972758, 1448972758, 28, 0, 0, 'Совершенно неизвестную вещь любить никто никогда не может', '', '', '', '62f39295c8df2fdd1ed0525feb7ee596'),
(404, 1, 1, 1, 1448972758, 1448972758, 32, 0, 0, 'Даже языческий брак бывает свят и чист, если в нем присутствует подлинная любовь и супруги навечно отданы друг другу в нескончаемой верности и взаимном поклонении.', '', '', '', 'd6606ae7dd3c455aabf63e1e506d74b2'),
(405, 1, 1, 1, 1448972758, 1448972758, 32, 0, 0, 'Разврат происходит не от чего иного, как от недостатка любви.', '', '', '', 'f377f3ec6371f204e367b58778a3bf3c'),
(406, 1, 1, 1, 1448972758, 1448972758, 29, 0, 0, 'Люди очень нужны друг другу. Нужны они друг другу общностью природы своей, которая есть любовь, и различием даров своих призваны ко взаимному восполнению.', '', '', '', 'cc14335f61b9bd41708d83d32b2d3f0a'),
(407, 1, 1, 1, 1448972758, 1448972758, 30, 0, 0, 'На высочайшем уровне любовь является песней сердца, которое открыто другим людям, которое вышло из тюрьмы самостности, замкнутости, изолированности, одиночества.', '', '', '', '13d82c326921f9e4b227a335e6f3999a'),
(408, 1, 1, 1, 1448972759, 1448972759, 31, 0, 0, 'Любовь только тогда может давать, когда она забывает о себе.', '', '', '', '70e9897b877395ab05b1e00abeebfb62'),
(409, 1, 1, 1, 1448972759, 1448972759, 31, 0, 0, 'Мы все думаем, будто знаем, что такое любовь, и умеем любить. На самом деле, очень часто мы умеем только лакомиться человеческими отношениями.', '', '', '', 'fbe8f479b813bed2cdd717057b0bf74a'),
(410, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Два человека, избравшие друг друга среди всех прочих людей ради того, чтобы приносить друг другу любовь и поддержку, обязаны проявлять чувство юмора, приветливость, благоразумие, умение прощать, терпение и радушие, должны помнить о том, как хрупка и непрочна жизнь человеческого существа, и уважать друг друга до конца своих дней.', '', '', '', '31fff9b3fd332bf9d30f87a9bbab39ce'),
(411, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Мужчины, которые относятся к женщинам с наибольшим почтением, редко пользуются у них наибольшим успехом.', '', '', '', '512a97675037f48f4d82d4b4a7aad484'),
(412, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Без постоянства не может быть ни любви, ни дружбы, ни добродетели.', '', '', '', '52ac3560e34cdfd35fb9bba4ad35ca29'),
(413, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Болезнь ревнивца столь злокачественна, что решительно все превращается ею в пищу для себя.', '', '', '', 'ce4d0b792eddcaebd1a985c1aa886eb7'),
(414, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Брак отличается той особенностью, что с ним прекращается поклонение идолу. Когда мужчина ближе присмотрится к своей богине, она снова становится простой женщиной.', '', '', '', 'ebe7e6c763b2af29ca80f8a55f17f1c3'),
(415, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Всегда есть что сказать в оправдание обеих сторон.', '', '', '', '4d4078335314dbe57b79676ce3742147'),
(416, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Все мы что-то делаем для потомства; хотелось бы увидеть, что потомство делает для нас.', '', '', '', '2ff10cf60b177728bcc703870b61b9cf'),
(417, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Если я люблю свой сад, то не потому, что там растет вишня, а потому, что туда залетают дрозды; они распевают песни, а я за это кормлю их вишнями.', '', '', '', 'e405eb5b6ec41dba00b5381a2350149e'),
(418, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Женщина редко обращается за советом, прежде чем купить подвенечное платье.', '', '', '', '1ff0470a4a4bdd0d409c479a7a6cf3da'),
(419, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Женщина слишком чистосердечна и принципиальна, чтобы внять голосу рассудка(...)', '', '', '', 'a4bdf0d3481b4f388e086f33ef351698'),
(420, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Истинное счастье по природе своей любит уединение; оно - враг шума и роскоши и рождается главным образом из любви к самому себе.', '', '', '', '691016b9d28a61d99504e175274b68e4'),
(421, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Нет в природе явления более разнообразного и многоликого, чем женский головной убор.', '', '', '', 'f6ac220ca21b1beea055ce7386487938'),
(422, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Раздельный кошелек у супругов - вещь столь же неестественная, как и раздельное ложе.', '', '', '', '61b944e59109ae642294b2738e16aeaa'),
(423, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Расчетливому и равнодушному хитрецу проще убедить женщину, что он ее любит, и преуспеть, чем страстному влюбленному с его пылкими выражениями чувств.', '', '', '', '8e47db827abbb0785b6a9011e93c9af8'),
(424, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Самая необузданная страсть у всех живых существ - похоть и голод; первая вызвана постоянным стремлением к воспроизводству себе подобных, вторая - к самосохранению.', '', '', '', '885043f36985d4050914b1eb31f04da4'),
(425, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Самое важное и самое трудное для мощного духа - это уметь сдерживать себя: пруд спокойно стоит в долине, но чтобы сдерживать его, нужны горы.', '', '', '', 'af24dfde5dee9c3aa6f47d7fba5f7e57'),
(426, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Уверенность в том, что ты любим, смягчает страдания разлуки. Последнее "прости" даже теряет свою горечь, когда в нем еще слышится отзвук любви.', '', '', '', 'c9a895bcf7f5bb0196a02677d9c9b0b9'),
(427, 1, 1, 1, 1448972759, 1448972759, 66, 0, 0, 'Женщины, счастливые в первом браке, чаще решаются на второй.', '', '', '', 'f11c4a426a7031b1e812769cddde1fb0'),
(428, 1, 1, 1, 1448972759, 1448972759, 67, 0, 0, 'В любви, как и в природе, первые холода чувствительнее всего.', '', '', '', 'e305966e7fee79cb1b22cb98568d3e74'),
(429, 1, 1, 1, 1448972759, 1448972759, 67, 0, 0, 'Величайшим доказательством женской привязанности бывает жертвование модой', '', '', '', 'd6d38baabcceb594fc06f324b2e57caa'),
(430, 1, 1, 1, 1448972759, 1448972759, 67, 0, 0, 'Любовники могут полюбить друг друга прежде, нежели узнают один другого; супруги должны узнать друг друга, прежде чем полюбить', '', '', '', '5b30058fa6d36ed2d7db72c4059eb9b3'),
(431, 1, 1, 1, 1448972759, 1448972759, 67, 0, 0, 'Любовники тщательно скрывают свои недостатки; супруги слишком часто выказывают их друг перед другом.', '', '', '', 'dc93ac81b18ca0109f08438343ba1adb'),
(432, 1, 1, 1, 1448972759, 1448972759, 67, 0, 0, 'Прежде чем клясться женщине никого не любить, кроме нее, следовало бы увидеть всех женщин, или же видеть только ее одну.', '', '', '', '5084786b22b29c18ef24ca19c556f54b'),
(433, 1, 1, 1, 1448972759, 1448972759, 67, 0, 0, 'Самое жестокое одиночество - это одиночество сердца', '', '', '', 'ee4ada28e0e147a2976f17cef5e32de7'),
(434, 1, 1, 1, 1448972759, 1448972759, 67, 0, 0, 'Часто встречаются ледяные сердца под пылкими головами, но редко холодные головы под пылкими сердцами', '', '', '', '6111f3da079ae4c986e742075a6b305a');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(435, 1, 1, 1, 1448972759, 1448972759, 71, 0, 0, 'Кто не постучался в сердце, тот стучится в дверь напрасно', '', '', '', '627c5829a6d3f82b1ef60d918eecf045'),
(436, 1, 1, 1, 1448972759, 1448972759, 71, 0, 0, 'Любовь, деньги и заботы скрыть невозможно', '', '', '', 'b2520ef79cfb105991799766a1c4542a'),
(437, 1, 1, 1, 1448972759, 1448972759, 71, 0, 0, 'Любовью оскорбить нельзя,Кто б ни был тот, кто грезит счастьем;Нас оскорбляют безучастьем', '', '', '', '17407caadb7c7a33421e0372c0333f3e'),
(438, 1, 1, 1, 1448972759, 1448972759, 71, 0, 0, 'Ничто не усиливает любви так, как неодолимые препятствия', '', '', '', '6c6a9d6fd7dac83ee9a04de91dd14837'),
(439, 1, 1, 1, 1448972759, 1448972759, 71, 0, 0, 'Половиной своей красоты женщины обязаны портнихам', '', '', '', '04b01d23c8a51d8651e352bc5523691f'),
(440, 1, 1, 1, 1448972759, 1448972759, 71, 0, 0, 'Проклятая любовь всему винойКто ей поддастся, тот утратит разом Свою свободу, мужество и разум', '', '', '', '5cab2a2001219fdfe50925bff8ed5b0e'),
(441, 1, 1, 1, 1448972759, 1448972759, 73, 0, 0, 'Любовь сильнее самолюбия: женщину можно любить, даже когда она презирает вас.', '', '', '', '248f33f1f6d9e6f835e489652734cd17'),
(442, 1, 1, 1, 1448972759, 1448972759, 73, 0, 0, 'Постоянство - это всегдашняя мечта любви.', '', '', '', '2c086ab902c925cee8aef6effe9d1fa0'),
(443, 1, 1, 1, 1448972759, 1448972759, 72, 0, 0, 'Бог создал женщин лишь для того, чтобы приручить мужчин', '', '', '', '4582afec51f6d1545b1ea04d4faeba34'),
(444, 1, 1, 1, 1448972759, 1448972759, 72, 0, 0, 'Достаточно было одному мужчине влюбиться в   женщину, чтобы мир стал таким, каков есть.', '', '', '', 'e13b67590ea600aacc5a283ae39de44b'),
(445, 1, 1, 1, 1448972759, 1448972759, 72, 0, 0, 'Зависть - яд для сердца', '', '', '', '2903cd86c90bd3e4b5b67328f8f7b9ec'),
(446, 1, 1, 1, 1448972759, 1448972759, 72, 0, 0, 'Любовь - самая сильная из всех страстей, потому что она одновременно завладевает головою, сердцем и телом', '', '', '', '3bfdf2950276e55d628da20fd17ebc3f'),
(447, 1, 1, 1, 1448972759, 1448972759, 72, 0, 0, 'Мрачная ревность неверною поступью следует за руководящим ее подозрением; перед нею с кинжалом в руке идут ненависть и гнев, разливая свой яд За ними следует раскаяние', '', '', '', '1be43849871734dc8d946673a5dd8cb4'),
(448, 1, 1, 1, 1448972759, 1448972759, 72, 0, 0, 'Целую кончики ваших крыльев', '', '', '', 'c1a05e6aced72d6af77b9f9023b45607'),
(449, 1, 1, 1, 1448972759, 1448972759, 72, 0, 0, 'Что бы ты ни делал, попирай все препятствия и люби тех, кто любит тебя.', '', '', '', '780f4820beedeeba096f133c5638455b'),
(450, 1, 1, 1, 1448972759, 1448972759, 68, 0, 0, 'Лишь в восторгах любви ощущают счастье существования и, прижимая губы к губам, обмениваются душами', '', '', '', '1ef3cb72e4a1b918f8b1dabb8fc4334b'),
(451, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'Да разве любовь имеет что-либо общее с умом!', '', '', '', 'fa52fdc585ec92a5a565386c5f422a33'),
(452, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'Если мы принимаем людей такими, какие они есть, мы делаем их хуже Если мы относимся к ним так, как будто они уже таковы, какими им следует быть, мы помогаем им стать такими, какими они в состоянии стать', '', '', '', '7922ccc398950ca8941d340da483ce92'),
(453, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'Если твое сердце и твой ум беспокойны, чего же тебе больше? Кто перестал любить и делать ошибки, тот может похоронить себя заживо', '', '', '', '92e13bc94ffaffa2c65be2bb843a8d49'),
(454, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'Любовь - вещь идеальная, супружество - реальная; смешение реального с идеальным никогда не проходит безнаказанно.', '', '', '', '5bb5408a7ea592758196b4bfb48e7985'),
(455, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'Любовь не может властвовать над людьми, но она может изменять их', '', '', '', '4656b20d07d79892548f9497597e5bde'),
(456, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'Обращайтесь с женщиной осторожно! Она сделана из кривого ребра, Бог не сумел создать ее прямее; если захочешь выпрямить ее, она поломается; оставишь ее в покое, она станет еще кривее', '', '', '', '8c519f7893c55236fd7f3189105fcace'),
(457, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'ПОЦЕЛУЙ - это когда две души встречаются между собой кончиками губ', '', '', '', '8cdbd7ee4ad4150589cc8115c919a1f2'),
(459, 1, 1, 1, 1448972759, 1448972759, 69, 0, 0, 'Радость и горе, волнение дум,Сладостной мукой встревоженный ум,Трепет восторга, грусть тяжкая вновь;Счастлив лишь тот, кем владеет любовь!', '', '', '', '6f173fb40fac5f428081bf0d8b3bc6b3'),
(460, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Без женщин начало нашей жизни было бы лишено поддержки, середина - удовольствий и конец - утешения', '', '', '', 'a844aae10527b915ecf0714317cb73b6'),
(461, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Брак - слишком совершенное состояние для несовершенного человека', '', '', '', 'dd3798cac99f8fa72f751ef47791c1fd'),
(462, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Брак следует за любовью, так же как дым за пламенем', '', '', '', 'cec496d5544cfb33cce8008058abd6b6'),
(463, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Как бы плохо мужчины не думали о женщинах, любая женщина думает о них еще хуже', '', '', '', '3f57607d31dff3b07c253ef10adb4ebd'),
(464, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Когда женщина выбирает себе любовника, ей не так важно, нравится ли он ей, как нравится ли он другим женщинам', '', '', '', '1457d34c8140b809f1519470ff70a32d'),
(465, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Любовь - единственное чувство, в котором все истинно и все лживо', '', '', '', 'a1d90e9aac9918974c0150aed6cb60c6'),
(466, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Любовь - как прилипчивая болезнь: чем больше ее боишься, тем скорее подхватишь', '', '', '', 'd3d12baac792130da9f04ad101c35a03'),
(467, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Любовь - милое безумие; честолюбие - опасная глупость', '', '', '', 'd2b7537ab5d4a910a35588ea0a9c88e1'),
(468, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Любовь приятнее брака по той же причине, по какой романы занимательнее исторических сочинений', '', '', '', 'e61e8f353509fa12b440d1f323d00191'),
(469, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Люди в большинстве своем не столько хотят испытать любовь, сколько ее внушить', '', '', '', '73c17ea5c3d0757326a462e6490e4658'),
(470, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Мужчина охладевает к женщине, которая слишком сильно его любит, и наоборот Видимо, с сердечными чувствами дело обстоит, как с благодеяниями: кто не в состоянии отплатить за них, тот становится неблагодарным', '', '', '', '4a997e0f0843609faa632e27aaa5cb57'),
(471, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Наряд - предисловие к женщине, а иногда и вся книга', '', '', '', 'd0de2bc9a3c3533d1f782ae52d2cd2f2'),
(472, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Одна умная женщина бросила мне как-то фразу, которая, возможно, проливает свет на природу слабого пола: "Когда женщина выбирает себе любовника, ей не так важно нравиться ли он ей, как нравиться ли он другим женщинам"', '', '', '', '2219589fcc55faf96f6ca4009a2c4eb6'),
(473, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Повторение одних и тех же слов может наскучить нашим ушам, уму, но только не сердцу', '', '', '', 'a83b7270f78fbfdbb709dfc5810c0e02'),
(474, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Со счастьем дело обстоит, как с часами: чем проще механизм, тем дольше он служит', '', '', '', '485869ae04b16d0272ba6915b5b906ff'),
(475, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Сперва любовь, потом брак: сперва пламя, потом дым', '', '', '', 'ed3f889b57edff93d04f61145080688b'),
(476, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Та женщина, которая ценит себе не столько красоту, сколько свойства души и ума, на голову выше других женщин; та, что больше всего ценит красоту, похожа на своих сестер; а та, что свою знатность или титул ценит больше, чем даже красоту, ниже других женщин, да, пожалуй, и не женщина вовсе', '', '', '', 'fa81f2d2d53bb94c3118201b743de8a1'),
(477, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Чтобы связь мужчины с женщиной была по-настоящему увлекательна, их должны соединять наслаждение, воспоминание или желание', '', '', '', '0a79c42107d89c8a1e59c557ee827820'),
(478, 1, 1, 1, 1448972759, 1448972759, 70, 0, 0, 'Я мысленно нарисовал себе образ женщины, каких мало, и он ограждает меня от женщин, каких много', '', '', '', '515e8af8162de41cc6180605c293ec19'),
(479, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'В колчане дьявола нет лучшей стрелы для сердца, чем мягкий голос', '', '', '', '4f59bbe1d4970b56cd13f3c53b9e75fd'),
(480, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Дружба – это любовь без крыльев', '', '', '', '4fae4859683b76f94928e7efe0f40c68'),
(481, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Из всех тропинок, ведущих к сердцу женщины, жалость - самая короткая', '', '', '', '4170179f3510dc818ce94656ae639ffd'),
(482, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Неужели вы думаете, что если бы Лаура была женой Петрарки, он стал бы всю жизнь писать сонеты?', '', '', '', '3cb82480d6c7481072efe3139c48f378'),
(483, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Ночь придает блеск звездам и женщинам', '', '', '', 'be771c2dc1bc27f92459d135f0ec0f1f'),
(484, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Влюбляешься как-то автоматически, как плаваешь.', '', '', '', '63669fa656281ae94f46203f696c28c3'),
(485, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Любовь стара, как мирозданье, Но кто пришел и кто придет на свет,Приходит обновить ее завет.', '', '', '', '636056d3ce62f983f6c3530f3f164cef'),
(486, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Правда, что в пятьдесят лет редко можно рассчитывать на взаимность в любви, но не менее верно, что в этом возрасте ее можно иметь очень много за пятьдесят золотых.', '', '', '', '0dc5c1b8e0578a82e29cdb17e8a11169'),
(487, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Раны от любви не убивают, но никогда не заживают.', '', '', '', '887e2fb94ddcfe1e3564df395e09051d'),
(488, 1, 1, 1, 1448972759, 1448972759, 93, 0, 0, 'Слезу страданья осушить с любовью славнее, чем весь мир забрызгать кровью.', '', '', '', '3bc00371e43f37e31d5cb2d7202ca899'),
(489, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'В одном часе любви – целая жизнь.', '', '', '', '7c3c337cc455ce68b0ab450d85690269'),
(490, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'В пожилом возрасте любовь превращается в порок.', '', '', '', '71b2ab561e5107cc6eb59364a3e7d209'),
(491, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'В пятьдесят лет мужчина более опасен, чем во всяком другом возрасте, ибо обладает дорогостоящим опытом и часто состоянием -', '', '', '', '103d0bdbb1d6bc8f99b07236477e4dbc'),
(492, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Женщина любит победу над мужчиной, который принадлежит другой!', '', '', '', '7559123142d037606be4432aeff7c03d'),
(493, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Женщина, руководящаяся рассудком, а не сердцем, настоящая общественная зараза: она имеет все недостатки страстной и любящей женщины, но ни одного ее достоинства; она без жалости, без любви, без добродетели, без пола', '', '', '', 'efa0bb6a050bc85cb1dcab06cd5591c8'),
(494, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Женщина, смеющаяся над своим мужем, не может   более его любить.', '', '', '', 'a75e25746674b65eb5bdafce5dc7e640'),
(495, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Кто способен управлять женщиной, способен управлять государством', '', '', '', '6161470fe675e59949c52a30cc5816d7'),
(496, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Любовь - единственная страсть, не признающая ни прошлого, ни будущего.', '', '', '', 'bd8b3c0e371d0bcfcd34718c4f141578'),
(497, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Любовь - это удивительный фальшивомонетчик, постоянно превращающий не только медяки в золото, но нередко и золото в медяки.', '', '', '', '59a7fc11bc22307507e1161f7b155a3c'),
(498, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Любовь для высоконравственной натуры - то же, что Солнце для Земли.', '', '', '', '30b5f90f7f2b7f9d1cd4edfb3f44e6d9'),
(499, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Любовь слышится в голосе раньше, чем угадывается во взгляде.', '', '', '', '7f5d383193dcb79e6036563efa890d3c'),
(500, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Любовь соединяет в себе все добрые качества человека.', '', '', '', '5b529f28dafe51439ce514664bcf3560'),
(501, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Любовь так плохо выносит домашние дрязги, что для прочного счастья нужно найти друг у друга выдающиеся качества.', '', '', '', '25d798342d894c1fb2e5748fb07b0254'),
(502, 1, 1, 1, 1448972759, 1448972759, 91, 0, 0, 'Никто не становится другом женщины, если может быть ее любовником', '', '', '', 'c9b88154cc7bd360bd6ddb8236af8d3a'),
(503, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Поиски разнообразия в любви - признак бессилия.', '', '', '', '83b0082fed7d93c7cded68827e5d1975'),
(504, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Ревновать женщину, которая вас любит, было бы по меньшей мере нелогичным Что-нибудь из двух: вас любят или не любят В обоих этих крайних случаях ревность оказывается совершенно бесцельной', '', '', '', 'c12f7fa0c66a9144a626285bf0c7521c'),
(505, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Страсть плохо рассуждает.', '', '', '', '60d5d59b6583bf8b63030892b46cc232'),
(506, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Талант в мужчине - то же, что красота в женщине, - всего лишь обещание Для того, чтобы быть подлинно великим, его сердце и характер должны быть равны его таланту', '', '', '', '3a1290ac2a961aece7ccaed274a45c99'),
(507, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Только последняя любовь женщины может сравниться с первой любовью мужчины.', '', '', '', 'c04ccb5f7e9649ca4dd0faf135992bc8'),
(508, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Тот, кто может управлять женщиной, справится и с государством', '', '', '', '58241b2eb187eaaf7a51ad15d94f2007'),
(509, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Благородное сердце не может быть неверным.', '', '', '', '2b2199298948251dbda8c24056597974'),
(510, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Брак не может быть счастливым, если супруги до вступления в союз не узнали в совершенстве нравы, привычки и характеры друг друга.', '', '', '', 'fc2ddd3ac6026fcf9697e4eb1372ed06'),
(511, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Будущее нации - в руках матерей.', '', '', '', '12ae66c8b12cd7cf5cf6eaa2b328165f'),
(512, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Все приходит в свое время для тех, кто умеет ждать.', '', '', '', '98a74656fd1dd6dfa29b50beea8f5501'),
(513, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Идеальная красота, самая восхитительная наружность ничего не стоят, если ими никто не восхищается.', '', '', '', '3c2b187ba545ccf7de1ce7c6b59090ff'),
(514, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Испивая чашу удовольствия до дна, мы обнаруживаем там больше гравия, чем перлов.', '', '', '', 'ffa52427069c65764e6468ec3070a5e4'),
(515, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Мы признаем человеком только того, чья душа мечтает в любви настолько же о духовном наслаждении, как и о телесном удовольствии.', '', '', '', '8f1fae4d618498f40dad4198f918360d'),
(516, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Не будет другом женщины тот, кто может быть ее любовником.', '', '', '', '4f6a0121dbbfc4fea3d2b799a01818df'),
(517, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Равнодушие к прекрасному полу в старости - наказание за то, что слишком умел нравиться в молодости.', '', '', '', '5cf844b5760ae95cd2f4f775b380a8ae'),
(518, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Ревность у мужчины складывается из эгоизма, доведенного до чертиков, из самолюбия, захваченного врасплох, и раздраженного ложного тщеславия.', '', '', '', '5344b26fd5e8641f7c6ee563c764fa01'),
(519, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Семья всегда будет основой общества.', '', '', '', '134de5d74aa6758fdfc8d768f083f4a3'),
(520, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Сердце матери - это бездна, в глубине которой всегда найдется прощение.', '', '', '', '829f97ff8ca759f3553ef122b839b108'),
(521, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Возможно, равенство - это право, но никакая сила на земле не сделает его фактом.', '', '', '', '58c8504679d9cc4670970fd4c56f0fa7'),
(522, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Встречаются мужчины, чья глупость и упорство поистине превосходят все, чего можно ожидать от Божьего создания.', '', '', '', '1beaf535a63f3ce523c4b6ad4281e581'),
(523, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Говорить о любви - значит заниматься любовью.', '', '', '', '9e34efb12540bad4adce54f6ea381bab'),
(524, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Если жена заговорила об экономии, значит, ваши акции начали падать.', '', '', '', '3570ecbcb771a8fae3933437b5895c14'),
(525, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Если мужчина остроумен, хорош собою и общежителен, женщины интересуются не тем, откуда он вышел, а тем, куда он хочет прийти.', '', '', '', '1bc2fef7c5e9ef49a4d82c387298e7c0'),
(526, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Замужняя женщина - это рабыня, которую надо уметь посадить на трон.', '', '', '', 'e2dd72009441018c3b1ea67b6c704694'),
(527, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Каждая ночь должна иметь свое меню.', '', '', '', 'c1041fb52d30de710fb071b191a65600'),
(528, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Когда женщина уже слишком стара, чтобы нравиться мужчинам, она обращается к Богу.', '', '', '', '56efc7d39f3282b9e23d0a2e15e9ec62'),
(529, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Легче быть любовником, чем мужем, ибо труднее быть остроумным каждый день, чем шутить от случая к случаю.', '', '', '', '41db2258dd22ba394559ff8c257f206c'),
(530, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Любовь - единственная возможность для дурака вырасти в собственных глазах.', '', '', '', '59546793f5bc4d6ae4f12bb069cf2370'),
(531, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Муж, как и правительство, ни за что не должен признаваться в ошибках.', '', '', '', 'bb2d044ef304965b557c66d202eb38a5'),
(532, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Мужчина не имеет права жениться, не изучив предварительно анатомии и не сделав вскрытия хотя бы одной женщины.', '', '', '', '15d35741dcea8ff77b60fe24150125f6'),
(533, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Не станем подсчитывать женщин, добродетельных по глупости, ибо известно, что в любви все женщины умны.', '', '', '', 'f9c2cce070ab7cc9cb31bade5b2a3d36'),
(534, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Печать для рукописи то же, что театр для женщины: все достоинства и изъяны выставляются напоказ.', '', '', '', '81335d9b8ff1023844658a82388f9b1a'),
(535, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Погружаясь на самое дно наслаждения, мы достаем больше песка, чем жемчужин.', '', '', '', 'b5f5a20f341416910650a2c87398aa27'),
(536, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Порядочная женщина - та, которую любовник боится скомпрометировать.', '', '', '', '57114a9b1e5087eac754e83899da195c'),
(537, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Ревнивец сомневается на самом деле не в своей жене, а в себе самом.', '', '', '', 'f6d9538737159f8716fe180e3a03f74b'),
(538, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Совершенная красота почти всегда отмечена либо холодностью, либо глупостью.', '', '', '', 'c867e5f6c677907a5764c2f499875c1f'),
(539, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Так уж устроены мужчины: могут устоять против самых умных доводов и не устоять перед одним-единственным взглядом.', '', '', '', 'c93463da8505e23fb4f52a2261c41a6d'),
(540, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Утверждать, что невозможно всегда любить одну и ту же женщину, также бессмысленно, как полагать, что прославленному музыканту для исполнения разных мелодий потребны разные скрипки.', '', '', '', 'dd6f311a445667cbc54f764163767389'),
(541, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Ухаживая за женщинами, многие, так сказать, подсушивают дрова, которые будут гореть не для них.', '', '', '', '0860aaa665abb88860777830694063fd'),
(542, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Быть повсюду дома могут только короли, девки и воры.', '', '', '', '8d90139f0b54e9fd037841c557155ac1'),
(543, 1, 1, 1, 1448972760, 1448972760, 91, 0, 0, 'Там, где замешано честолюбие, нет места чистосердечию.', '', '', '', '7bdad93fd33365099061c84da9a2cd90'),
(544, 1, 1, 1, 1448972760, 1448972760, 92, 0, 0, 'Простить - хорошо, но лучше всего - забыть', '', '', '', '2c7def1970b927a919c89b97c1e7be01'),
(545, 1, 1, 1, 1448972760, 1448972760, 106, 0, 0, 'И ребенка, и друга мы любим лишь в том случае, если мы вообще уже умеем любить. А этому мужчина научается у женщины.', '', '', '', 'f37a648054f1603f7e7c2e079cbb5fe5'),
(546, 1, 1, 1, 1448972760, 1448972760, 96, 0, 0, 'Истинная сущность любви состоит в том, чтобы отказаться от сознания самого себя, забыть себя в другом Я и, однако, в этом же исчезновении и забвении впервые обрести самого себя', '', '', '', '8a3ab0f008d2173bf7a8dfcee8e7b5ac'),
(547, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'Ангелы зовут это небесной отрадой, черти - адской мукой, а люди - любовью.', '', '', '', 'a13fbb1687a1cb8aa43514893125d781'),
(548, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'Для любви не существует вчера, любовь не думает о завтра.', '', '', '', 'ad5afdc6b70e9149393c9c785928e0ca'),
(549, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'Женщина - одновременно яблоко и змея', '', '', '', '78221e1c5c4eebd5978d9803de04b583'),
(550, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'Любовь - это зубная боль в сердце.', '', '', '', 'e69001d267a60087e72b11206219690d'),
(551, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'Любовь! Это самая возвышенная и победоносная из всех страстей.', '', '', '', '79d009639ceccfbf89e7f682db377dae'),
(552, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'Мотылек не спрашивает у розы: лобызал ли кто тебя? И роза не спрашивает у мотылька: увивался ли ты около другой розы?', '', '', '', '98448ea7c96effd353a8304beb8521dd'),
(553, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'Принцип гомеопатии, по которому от женщины излечивают нас женщины, может быть, самый доказанный опытом', '', '', '', 'eb469c092c0859053d28fe1c6dfb1646'),
(554, 1, 1, 1, 1448972760, 1448972760, 98, 0, 0, 'У женщин не бывает второй любви; их природа слишком нежна, чтобы быть в состоянии дважды перенести это страшное потрясение чувств.', '', '', '', '5474a88e85a1aca709b4a0b6b9a5b6a1'),
(555, 1, 1, 1, 1448972760, 1448972760, 97, 0, 0, 'Любовь, которая боится препятствий, - не любовь', '', '', '', '0065339958ed69f987ab878597a75157'),
(556, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Вы созерцаете звезду по двум причинам: потому, что она сверкает, и потому, что она непостижима Но рядом с вами - сияние более нежное и тайна более глубокая: женщина', '', '', '', 'cc5eaadfb7706b3da9f20e8b38ad8a28'),
(557, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Высшее счастье в жизни - это уверенность в том,  что вас любят; любят ради вас самих, вернее сказать - любят вопреки вам.', '', '', '', 'a82cfc25c8c99b4f2670f05b29a06ba5'),
(558, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Если человек может сказать, что такое любовь, значит, он никого не любил', '', '', '', '986b6590a961d420e965114a4d79e30b'),
(559, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Жизнь - это цветок, а любовь - нектар.', '', '', '', '177ccdfc08f712a26dc041a0fff0b903'),
(560, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Иллюзия - главное основание грез; отнимите у любви иллюзию, и вы отнимете у нее пищу', '', '', '', '7aa76092ac4554e5b69489289ec8dd66'),
(561, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Истинная любовь не знает пресыщения Будучи всецело духовной, она не может охладиться', '', '', '', '1b310eb381fbfebba9361cc74a6b8903'),
(562, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Любви всего мало Она обладает счастьем, а хочет рая, обладает раем - хочет неба О любящие! Все это есть в вашей любви Сумейте только найти', '', '', '', '1f3857b04aff7e9c37fde39facd2fb5c'),
(563, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Любовь - как дерево: она вырастает сама собой, пускает глубоко корни во все наше существо и нередко продолжает зеленеть и цвести даже на развалинах нашего сердца', '', '', '', '31881db3a26151af61990af2877eac9b'),
(564, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Любовь к женщине имеет для нас великое, ничем не заменимое значение; она подобна соли для мяса: пропитывая сердце, предохраняет его от порчи.', '', '', '', '52fd419d92b818b5eed42a951f8bb39c'),
(565, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Однажды я встретил на улице влюбленного нищего. На нем была старая шляпа, пальто потерлось на локтях, башмаки его протекли, а в душе сияли звезды.', '', '', '', '8459af071d16b25049f8cc83d41c7855'),
(566, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Первый признак любви : у мужчин - несмелость, у женщин - смелость', '', '', '', 'e920a8f07dbd7ab286ed449c41e43c94'),
(567, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Тот, кто отстаивает права женщины, тот отстаивает права ребенка, иначе говоря, - отстаивает будущее', '', '', '', '3abc0210319aad6921e3043f66bcc6aa'),
(568, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Уважение имеет границы, любовь - никогда', '', '', '', '323cbaf89b47cbce54ed730e48aa29f8'),
(569, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Умирать от любви - значит жить ею.', '', '', '', 'fb9f142313a4234ddfbd1225d211699e'),
(570, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Всегда любите друг друга всей душой. В мире нет почти ничего, кроме любви.', '', '', '', 'a31bac6ef49f5e9129531dc682293e08'),
(571, 1, 1, 1, 1448972760, 1448972760, 99, 0, 0, 'Чтобы быть вполне счастливым, недостаточно обладать счастьем, надо еще заслуживать его', '', '', '', '764ff9da50f58b20ccf9bde3d04258a7'),
(572, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Воля к любви означает готовность к смерти', '', '', '', '433f7148c4c85ab21e6dd99774615038'),
(573, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Есть два пути избавить вас от страдания: быстрая смерть и продолжительная любовь', '', '', '', '130fa2223956efbd32955c5edb6bed6a'),
(574, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Кто беден любовью, тот скупится даже своей вежливостью', '', '', '', '0792eea70690d585b0ab44ec3d559ffc'),
(575, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Много кратких безумий - вот что вы называете любовью И ваш брак кладет предел множеству кратких безумий - одной большой и долгой глупостью', '', '', '', '16b359c6275247b8b244b28e3c2f72e8'),
(576, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Надо учиться любить себя - любовью здоровой и святой, чтобы оставаться верным себе и не терять себя', '', '', '', '3e2bce9cf29a32ce04261601bc5e71dd'),
(577, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Начинаешь с того, что отучиваешься любить других, и кончаешь тем, что не находишь больше в себе ничего достойного любви', '', '', '', 'd2d05e8ccea09775cf36d6d03e045a9c'),
(578, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Пусть мужчина боится женщины, когда она любит: ибо она приносит любую жертву и всякая другая вещь не имеет для нее цены', '', '', '', 'ad7dd6277815a420f463b6171a4c2dc6'),
(579, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Слишком долго таились в женщине раб и тиран Поэтому неспособна она к дружбе: ей ведома только любовь', '', '', '', '5e9ff7c2be39bf257d236c6c3811e78e'),
(580, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Счастье мужчины зовется "Я хочу". Счастье женщины - "он хочет"', '', '', '', '7593397eb5b9f1a46d15f049acc6866f'),
(581, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'Там, где нельзя больше любить, там нужно пройти мимо!', '', '', '', 'dbf5db933cf2c2c778f9cce788a05f9a'),
(582, 1, 1, 1, 1448972760, 1448972760, 100, 0, 0, 'В мире и без того недостаточно любви и благости, чтобы их еще можно было расточать воображаемым существам', '', '', '', 'aca0f8243ca95ba6527fa83176109c01'),
(583, 1, 1, 1, 1448972760, 1448972760, 101, 0, 0, 'Когда-нибудь надо осознать, что даже между самыми близкими людьми существует бесконечность, и прекрасная жизнь двоих может продолжаться, если они сумеют в любви сохранить дистанцию между собой, которая дает каждому возможность видеть мир другого человека во всей его необъятной полноте.', '', '', '', '486a7e6357c934ab47523a45d2dc18b0'),
(584, 1, 1, 1, 1448972760, 1448972760, 101, 0, 0, 'Любовь есть высокое побуждение индивидуума к росту и зрелости, к тому, чтобы стать чем-то внутри себя, стать неким миром, стать миром внутри себя ради другого Это великое, почти непомерное требование, нечто такое, что избирает нас и призывает к великому. Любовь заключается в том, что двое уединяются для того, чтобы защищать, поддерживать и радоваться друг другу', '', '', '', '25035e4c43d2b9ba34f801c9cd6f6b1c'),
(585, 1, 1, 1, 1448972760, 1448972760, 102, 0, 0, 'Любовь способна извинить все, кроме скупости', '', '', '', '9aedf94454f1cf3b3ff6ed89855aecce'),
(586, 1, 1, 1, 1448972760, 1448972760, 102, 0, 0, 'Мужчина, который умно говорит о любви, не очень влюблен', '', '', '', 'd25ebf85d0cfcb508da9a1194d2261bb'),
(587, 1, 1, 1, 1448972760, 1448972760, 102, 0, 0, 'Неразделенная любовь так же отличается от любви взаимной, как заблуждение от истины', '', '', '', '1fc8dddde1303ee75c1d197b95894e32'),
(588, 1, 1, 1, 1448972760, 1448972760, 102, 0, 0, 'Сопротивляться любви значит снабжать ее новым оружием', '', '', '', 'aa4cc1f84129b370e9ad7d3d3553aab2'),
(589, 1, 1, 1, 1448972760, 1448972760, 104, 0, 0, 'Любящие дерзают', '', '', '', 'ab1543df12b5934d4e495626201a400f'),
(590, 1, 1, 1, 1448972760, 1448972760, 105, 0, 0, 'В любви есть некоторая романтика, в помолвке - никакой, ведь помолвка большей частью кончается свадьбой.', '', '', '', '2750dbee83983d0cd2ce37ef1d89bf15'),
(591, 1, 1, 1, 1448972760, 1448972760, 105, 0, 0, 'В поведении людей, которых разлюбили, всегда есть что-то нелепое.', '', '', '', 'ecc7411e1d3886aae935ccddf3ab7cfc'),
(592, 1, 1, 1, 1448972760, 1448972760, 105, 0, 0, 'Верность в любви - это всецело вопрос физиологии, она ничуть не зависит от нашей воли. Люди молодые хотят быть верны - и не бывают, старики хотели бы изменить, но где уж им.', '', '', '', '4aa50d977bd053d301f7cd430b1b7ffe'),
(593, 1, 1, 1, 1448972760, 1448972760, 105, 0, 0, 'Дружба трагичнее любви - она умирает гораздо  дольше.', '', '', '', '9c3f59f38b2d58007f52b7449c74f7c7'),
(594, 1, 1, 1, 1448972760, 1448972760, 105, 0, 0, 'Единственный способ избавиться от искушений - это поддаться ему', '', '', '', '019545c828769e45bf8d202b27967a69'),
(595, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Женственность - это качество, которым я больше всего восхищаюсь в женщинах', '', '', '', 'ea59bf2aab412e636fe983edff7ce844'),
(596, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Женщина - это примат дела над мыслью; мужчина - примат мысли над моралью', '', '', '', 'a8da3b7ab8524ec8f4de04289265e7fd'),
(597, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Женщина создана для того, чтобы ее любить, а не для того, чтобы ее понимать', '', '', '', '79f0819a4a40811a98c4229c027e0c42'),
(598, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Женщину никогда нельзя обезоружить комплиментом, мужчину можно всегда', '', '', '', '317b07952f0b8e033023202602e10584'),
(599, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Женщины относятся к нам, мужчинам, так же, как человечество к своим богам: они нам поклоняются - и надоедают, постоянно требуя чего-то', '', '', '', '93878e961eab5e0c06124d300984928c'),
(600, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Женщины созданы для того, чтобы их любили, а не для того, чтобы их понимали', '', '', '', 'f0534f4850ca5c0a6573d55e852057d7'),
(601, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Каждая женщина - бунтарь по натуре, причем бунтует она исключительно против себя самой', '', '', '', '955a1ac45000ceeb427b6facf6c26575'),
(602, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Когда во второй раз выходит замуж женщина, это означает, что она ненавидела своего первого мужа Когда вторично женится мужчина, это происходит потому, что он обожал свою первую жену', '', '', '', '3f235f0c58757e72040884c7fdb54580'),
(605, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Любовь к себе - вот роман, который длится всю жизнь', '', '', '', '7a6c720725ad3941c2c2fc471253ad09'),
(606, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Между капризом и «вечной любовью» разница только та, что каприз длится несколько дольше', '', '', '', 'ef1bc16a2526f84759fd1413bc5e870f'),
(607, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Мужчин можно анализировать, женщин только обожать', '', '', '', 'faa0919130adebd81d7db6b36b473c99'),
(608, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Никогда не следует доверять женщине, которая называет вам свой возраст Женщина, способная на такое, способна на все', '', '', '', '435927a24332cd53a5d894f38fd738a9'),
(609, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Ничто так не мешает роману, как чувство юмора у женщины или отсутствие его у мужчины', '', '', '', 'db836ab15918bd580c742f64ce4e0eaf'),
(610, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Оставаясь закоренелым холостяком, человек являет собой постоянный соблазн для других членов общества', '', '', '', '05f64b286062583860da2945c3763a14'),
(611, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Своих мужей всегда ревнуют некрасивые женщины; красивым - не до этого: они ревнуют чужих', '', '', '', '7e51515ec049300fa8bc675185169cf2'),
(612, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Только по-натоящему хорошая женщина способна совершить по-настоящему глупый поступок', '', '', '', '881327e4b108eefb011a9744bbffc3f4'),
(613, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Когда человек влюбляется, то начинает с того, что обманывает себя самого, а кончает тем, что обманывает других.', '', '', '', '6db9a7e03a4e4442bb47f6600b92ac90'),
(614, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Любовь должна прощать все грехи, только не грех против любви.', '', '', '', 'b77ad9903505c3436f7a5b6ad0f19487'),
(615, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Любовь замужней женщины - великая вещь. Женатым мужчинам такое и не снилось.', '', '', '', '4b2464474f8029c6a04ed872d6667a84'),
(616, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Между капризом, увлечением и любовью до гроба разница только в том, что каприз длится несколько дольше.', '', '', '', 'a5fbdbb6895b086ebff3578f6ff14af9'),
(617, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Мужчина может быть счастлив с любой женщиной, если только он не влюблен в нее.', '', '', '', 'ec165fd7ca88625863f29e5a231411a1'),
(618, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Мужчины всегда хотят быть первой любовью женщины. Женщины мечтают быть последним романом мужчины.', '', '', '', 'b76f074c9e257bb7483be43bc1b02497'),
(619, 1, 1, 1, 1448972761, 1448972761, 105, 0, 0, 'Храни любовь в сердце своем. Жизнь без нее подобна бессолнечному саду с мертвыми цветами.', '', '', '', '96eee38f5b17c37cdcc4af22302f8477'),
(620, 1, 1, 1, 1448972761, 1448972761, 95, 0, 0, 'Женщина молода до тех пор пока ее любят', '', '', '', '9dbb224fb84240fe314d6fcdd5ae4c5b'),
(621, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Брак - это лавина, которую юноша и девушка обрушивают себе на голову, потянувшись за цветком', '', '', '', '32af83b225bbaa268f7f70cf3fb32318'),
(622, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Брак пользуется популярностью потому, что сочетает наибольшее искушение с максимальными возможностями', '', '', '', 'cf89bb0d0cc0fc3478ab6f687895d9be'),
(623, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Все без исключения женщины думают, что обладают чувством юмора, потому что любят похихикать над подружкой; но только у одной из тысячи хватает ума посмеяться над собой', '', '', '', 'a9fc8f1d419d91d66ca98c375469285a'),
(624, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Все мужчины одинаковы перед женщиной, которой они восхищаются', '', '', '', 'd59c10b37285ce46b8302528069b28b0'),
(625, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Вся любовь в мире жаждет заговорить, только она не смеет, потому что она стыдится.', '', '', '', '6661bc6f8fbddad38827bb09d5e9fae5');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(626, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Если вы начинаете с самопожертвования ради тех,   кого любите, то закончите ненавистью к тем, кому  принесли себя в жертву.', '', '', '', 'c6193ed8544e367e903d257397e48748'),
(627, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Если когда-нибудь, гоняясь за счастьем, вы найдете его, вы, подобно старухе, искавшей свои очки, обнаружите, что счастье было все время увас на носу', '', '', '', 'ad24d1fd995d09564a6388ae0698619b'),
(629, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Если человек не влюблялся до сорока лет, то лучше ему не влюбляться и после', '', '', '', '0632c741543474a37e324912e953da97'),
(630, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Есть любовь сильнее, а мечты чудесней, чем у    домашнего очага.', '', '', '', 'c72b0eaff3e804e3334cff6f8678837d'),
(631, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Желание мужчины прожить всю жизнь с красивой женщиной похоже на желание насладиться вкусом вина, которым до предела заполнен его рот', '', '', '', '90d2382da37c12c21846eb2ac32572e6'),
(632, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Женщины как-то сразу угадывают с кем мы готовы им изменить Иногда даже до того, как это придет нам в голову', '', '', '', 'da20af4c95265843a4d924b9f7a68f8e'),
(633, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'И в дружбе, и в любви рано или поздно наступает  срок сведения счетов.', '', '', '', 'b2b5db135d9c82582193fd868e0c6538'),
(634, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Идеальная любовь возможна только по переписке.', '', '', '', '73132c30e6b24c8a682ec0870c5eb5be'),
(635, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Идеальный муж - это мужчина, считающий, что у него идеальная жена', '', '', '', 'f3ecaaf595075a4f21fcf190d8cb462a'),
(636, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Испорченные люди - это те, у которых нет любви.', '', '', '', '746832e2feee22764f43e1ae61f3a45a'),
(637, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Когда двое находятся под влиянием самой неистовой, самой безрассудной, самой обманчивой и самой кратковременной из страстей, им приходится заверять друг друга, что они останутся в этом возбуждающем, ненормальном и изматывающем состоянии до тех пор, пока смерть не разлучит их', '', '', '', 'e8653673950d8fa9397af9ed0aba2b03'),
(638, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Когда мужчина холост, все женщины палят по нему, как развеселившиеся охотники по дикой утке', '', '', '', 'f608d1fd3a86f5a76501acf748d86a80'),
(639, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Любовь – это грубое преувеличение различия между одним человеком и всеми остальными.', '', '', '', 'a05f528d28afc62ba1a4c5329e0109c4'),
(640, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Любовь слишком великое чувство, чтобы быть только личным, интимным делом каждого.', '', '', '', '48110af91d824dd32cdc6beb49fd1702'),
(641, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'На свете нет и не может быть ничего более страшного, чем вечное счастье', '', '', '', 'f78e24a3a58b30098de6d21e34dfd712'),
(642, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Нет такой женщины, которой удалось бы сказать "до свидания" меньше, чем в тридцати словах', '', '', '', 'c0318167065ad7840ee241b855019300'),
(643, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Основания для развода, принятые в нашем обществе, смехотворны, ведь дурной нрав - куда более веская причина, чем супружеская измена', '', '', '', '332ff4cdaa296b6a7c444ece5057574e'),
(644, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Строптивых женщин легче переносить, чем спокойных Их, правда, иногда убивают, зато никогда не бросают', '', '', '', '3c818bf89a32773d0c3cdfb388d2506a'),
(645, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Я взял себе за правило влюбляться только в ненакрашенных и очень просто одетых Минимум разочарования', '', '', '', '6c9ac9fbdfc9925c464eb48b0fa8f938'),
(646, 1, 1, 1, 1448972761, 1448972761, 103, 0, 0, 'Я два дня прожил, не видя Вас, и тем доказал, что способен вынести все', '', '', '', 'bfebb68a7dae6ed3c6644aed0c4ba659'),
(647, 1, 1, 1, 1448972761, 1448972761, 94, 0, 0, 'Вера и доверие женщины писаны на песке', '', '', '', '4b074219cade10f0ba6d45c07a9a313a'),
(648, 1, 1, 1, 1448972761, 1448972761, 94, 0, 0, 'Женщины думают, что дважды два не будет четыре, если они станут проливать обильные слезы или кричать известное время', '', '', '', '4be8c74504a75baf92d03b970aa41bdb'),
(649, 1, 1, 1, 1448972761, 1448972761, 74, 0, 0, 'Я спросила: "Чего ты хочешь?"Он сказал: "Быть с тобой в аду:"', '', '', '', 'a8df50100dd38a2d179d14078ece9d66'),
(650, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Всякая любовь истинна и прекрасна по-своему, лишь бы только она была в сердце, а не в голове', '', '', '', '8973ecf58aca404a787c8b31078b8aeb'),
(651, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Если вы знаете, за что любите женщину, значит вы её не любите', '', '', '', '802df4eeab5d6c323230b6a5ebfb3331'),
(652, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Истинно человеческая любовь может быть основана только на взаимном уважении друг в друге человеческого достоинства, а не на одном капризе чувства и не на одной прихоти сердца', '', '', '', 'c33bd0f23cdd49a7e44cbd91500886bd'),
(653, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Меркою достоинства женщины может быть мужчина, которого она любит', '', '', '', '529e07f434adb1c2c766f998c8fcf8f5'),
(654, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Не преступление любить несколько раз в жизни и не заслуга любить только один раз: упрекать себя за первое и хвастаться вторым - равно нелепо', '', '', '', '169a2be72fabdb8fedbdc6801bdf4498'),
(655, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Нельзя отрицать влияние нравственных качеств на чувство любви, но когда любят человека, любят его всего, не как идею, а как живую личность, любят в нем особенно то, чего не умеют ни определить ни назвать', '', '', '', '8257c1a6216e8626b1239d60e4421eed'),
(656, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Первый и основной смысл любви заключается в заботливости природы о поддержании и размножения рода человеческого Но если б в любви людей все ограничивалось только этим расчетом природы, люди не были бы выше животных Следственно, это чувственное стремление, а любви человека одного пола к человеку другого пола есть только один из элементов чувства любви, его первый момент, за которым в развитии следует высшие, более духовные и нравственные моменты', '', '', '', '172a1a39d2cd810912889aacdbccfe4b'),
(657, 1, 1, 1, 1448972761, 1448972761, 75, 0, 0, 'Человек влюбляется просто, без вопросов, даже прежде нежели поймет и осознает, что он влюбился У человека это чувство зависит не от головы, у него оно - естественное, непосредственное стремление сердца к сердцу', '', '', '', '3e0e775578e8786ef68cabdda43f3349'),
(658, 1, 1, 1, 1448972761, 1448972761, 76, 0, 0, 'Тот, кто любит, должен разделять участь того, кого он любит.', '', '', '', '4ec91373a82dbd43bd9c9525c5672777'),
(659, 1, 1, 1, 1448972761, 1448972761, 77, 0, 0, 'Есть женские души, которые вечно томятся какой-то печальной жаждой любви и которые от этого самого никогда и никого не любят', '', '', '', 'cde39520b7be9fc33e0c75e5f82f2642'),
(660, 1, 1, 1, 1448972761, 1448972761, 77, 0, 0, 'Женщина прекрасная должна занимать вторую ступень; первая принадлежит женщине милой Сия-то делается владычицей нашего сердца: прежде нежели мы отдадим о ней отчет сами себе, сердце наше делается невольником любви навеки', '', '', '', '024e994353818bc92225dba225cae78b'),
(661, 1, 1, 1, 1448972761, 1448972761, 77, 0, 0, 'Позабыв про горе и страданья, Верю я, что кроме суеты,На Земле есть мир очарованья, Чудный мир любви и красоты!', '', '', '', '768f311c674aeec8916908115e3cf6a3'),
(662, 1, 1, 1, 1448972761, 1448972761, 80, 0, 0, 'Женщина лишь тогда верит слову "люблю", когда оно сказано тихо и просто', '', '', '', 'a38bb55d31d51391bd833006919b6b04'),
(663, 1, 1, 1, 1448972761, 1448972761, 85, 0, 0, 'На мой взгляд, самое главное – это уметь жить вместе с кем-то, уметь постоянно идти на компромисс, учитывать интересы другого человека, ведь в семье невозможно жить, думая только о себе и своих желаниях Так что в семье мы учимся многому: характеру, твердости и в то же время уступчивости, мягкости, терпимости', '', '', '', '310962e10c593966e140df50ae95a355'),
(664, 1, 1, 1, 1448972761, 1448972761, 81, 0, 0, 'Любит, потому что любит, не любит, потому что не любит, - логика чувств и страстей коротка.', '', '', '', '17cf92aa9f7c60f1feee12dc313e52f5'),
(665, 1, 1, 1, 1448972761, 1448972761, 81, 0, 0, 'Любовь - высокое слово, гармония создания требует ее, без нее нет жизни и не может быть.', '', '', '', '26983e01471a3052d93918abf0407ee5'),
(666, 1, 1, 1, 1448972761, 1448972761, 81, 0, 0, 'Любовь и дружба - взаимное эхо: они дают столько, сколько берут.', '', '', '', '54f16c59e432f2676bd97b4eaa2718ae'),
(667, 1, 1, 1, 1448972761, 1448972761, 81, 0, 0, 'Любовь раздвигает пределы индивидуального существования и приводит в сознание все блаженсто бытия; любовью жизнь восхищается собой; любовь - апофеоз жизни.', '', '', '', '8b1fa423c09a0ec044ba52fafa2e88e5'),
(668, 1, 1, 1, 1448972761, 1448972761, 81, 0, 0, 'Мучительная любовь не есть истинная.', '', '', '', 'e29d28ace8be70ccd55b9ab9f6c2f2d6'),
(669, 1, 1, 1, 1448972761, 1448972761, 81, 0, 0, 'Первая любовь потому так благоуханна, что она забывает различие полов, что она страстная дружба.', '', '', '', '54446afbe3f85247b5d77edc70bff1bb'),
(670, 1, 1, 1, 1448972761, 1448972761, 81, 0, 0, 'Только любовь создает прочное и живое, а гордость бесплодна, потому что ей ничего не нужно вне себя.', '', '', '', 'a52d6d24a4df7ac42a79f7b1ae2cb6fd'),
(671, 1, 1, 1, 1448972761, 1448972761, 82, 0, 0, 'Когда человек влюбился, то он все равно, что подошва, которую, коли размочишь в воде, возми согни - она и согнется.', '', '', '', '5fe16fd38444dbea2e7e9494abeaa32c'),
(672, 1, 1, 1, 1448972761, 1448972761, 83, 0, 0, 'Жизнь без любви - не жизнь, а существование Без любви жить невозможно, для того и дана душа человеку, чтобы любить', '', '', '', 'bbefe98710a2f4afcbfc61b2f4dc2e05'),
(673, 1, 1, 1, 1448972761, 1448972761, 83, 0, 0, 'Жизнь устроена так дьявольски искусно, что, не умея ненавидеть, невозможно искренне любить.', '', '', '', '7eed518902e0cd8e5fdec9659762d115'),
(674, 1, 1, 1, 1448972761, 1448972761, 83, 0, 0, 'Любовь - это желание жить', '', '', '', '4e9c9b62492973cd43e029c835d61504'),
(675, 1, 1, 1, 1448972761, 1448972761, 83, 0, 0, 'Нужно жить всегда влюбленными во что-нибудь недоступное тебе. Человек становится выше ростом от того, что тянется вверх', '', '', '', '24397e77df9ca839b4296defabe2ff5c'),
(676, 1, 1, 1, 1448972762, 1448972762, 83, 0, 0, 'Любовь - как огонь железу, которое хочет быть сталью(...)', '', '', '', 'b337b1d82bc2c4d317c9f4a0c70ebf9e'),
(677, 1, 1, 1, 1448972762, 1448972762, 83, 0, 0, 'Людям любовь в корень дана.', '', '', '', '518fe7e17db9d39e22365a1b9c7142fe'),
(678, 1, 1, 1, 1448972762, 1448972762, 83, 0, 0, 'Не меньше чумы губит людей любовь.', '', '', '', '7ea22e95ed26e829b2b7c1b312a9e4a5'),
(679, 1, 1, 1, 1448972762, 1448972762, 83, 0, 0, 'Нужно жить всегда влюбленным во что-нибудь недоступное тебе. Человек становится выше ростом от того, что тянется вверх.', '', '', '', '46b8ccf455f3ddb9fd29f0142c471312'),
(680, 1, 1, 1, 1448972762, 1448972762, 83, 0, 0, 'От любви к женщине родилось все прекрасное на земле Любовь к женщине всегда плодотворна для мужчин, какова бы она ни была, даже если она дает только страдания, и в них всегда есть много ценного', '', '', '', '8f7b0f1387468a3cd80f55d1cf224e45'),
(681, 1, 1, 1, 1448972762, 1448972762, 84, 0, 0, 'В мужчине ум - решающая ценность,И сила - чтоб играла и кипела;А в женщине плееняет нас душевность…И многие другие части тела', '', '', '', 'da68a7ea26539d4af7bef6403eb7d6f0'),
(682, 1, 1, 1, 1448972762, 1448972762, 84, 0, 0, 'Мы дарим женщине цветы,Звезду с небес, круженье балаИ переходим с ней на ты…А после дарим очень мало', '', '', '', '5cabb35464b1324167e48f08361f5a7c'),
(683, 1, 1, 1, 1448972762, 1448972762, 78, 0, 0, 'В истинно любящем сердце или ревность убивает любовь, или любовь убивает ревность', '', '', '', '589600fa3d85d2c54d18866fb58346af'),
(684, 1, 1, 1, 1448972762, 1448972762, 78, 0, 0, 'Любовь столь всесильна, что перерождает и нас самих', '', '', '', '559849636246715f814a4bdb23305299'),
(685, 1, 1, 1, 1448972762, 1448972762, 78, 0, 0, 'Нельзя любить то, чего не знаешь!', '', '', '', '15921abb73028f7ec160ef9f626adf37'),
(686, 1, 1, 1, 1448972762, 1448972762, 78, 0, 0, 'Влюбиться – не значит любить… Влюбиться можно и ненавидя', '', '', '', '490c358797efa1273ca8785d3d0f6427'),
(687, 1, 1, 1, 1448972762, 1448972762, 79, 0, 0, 'Мы думаем о себе, что все мы причастны этой любви: каждый из нас любит что-либо, кого-либо(...) Но та ли это любовь, которой ожидает от нас Христос?.. Из бесконечного количества явлений и лиц мы выбираем родственные нам, включаем их в свое расширенное "я" и любим их. Но стоит им отойти немного от того, за что мы их избрали, как мы изольем на них полную меру ненависти, презрения, в лучшем случае - равнодушия. Это человеческое, плотское, природное чувство, часто очень ценное в этом мире, но теряющее свой смысл в свете жизни вечной. Оно непрочно, легко переходит в свое противоположение, принимает демонический характер.', '', '', '', 'e95811fa363d5c5475414a2996c732ab'),
(688, 1, 1, 1, 1448972762, 1448972762, 79, 0, 0, 'Человек в браке погружается в жизнь, входя в нее через другую личность. Это наслаждение настоящим познанием и настоящей жизнью дает то чувство завершенной полноты и удовлетворения, которое делает нас богаче и мудрее.', '', '', '', 'a75607f9dac21fea55b249a90f0bf60b'),
(689, 1, 1, 1, 1448972762, 1448972762, 87, 0, 0, 'Была без радостей любовь, разлука будет без печали', '', '', '', '39f9cf4067c594dfa11ec379b6b969b6'),
(690, 1, 1, 1, 1448972762, 1448972762, 87, 0, 0, 'Все ясно ревности - а доказательств нет!', '', '', '', 'fef87b6898cf637f0356df28e63c2c04'),
(691, 1, 1, 1, 1448972762, 1448972762, 87, 0, 0, 'Любовь, как огонь, без пищи гаснет', '', '', '', 'e46c0ac57590eec2e1a196db6d5efb09'),
(692, 1, 1, 1, 1448972762, 1448972762, 87, 0, 0, 'Мы часто себя очень обманываем, думая, что женщина любит за наши физические или нравственные достоинства; конечно, они приготовляют, располагают её сердце к принятию священного огня, а всё-таки первое прикосновение решает дело', '', '', '', '69fe2678ee7aaa56fcafe5ea8557de0a'),
(693, 1, 1, 1, 1448972762, 1448972762, 87, 0, 0, 'Нет ничего парадоксальнее женского ума Женщин трудно убедить в чем-нибудь: надобно их довести до того, чтобы они убедили себя сами Чтобы выучиться их диалектике, надо опрокинуть в уме своем все школьные правила логики', '', '', '', 'e428d5927d19480a2705bc3d588789fa'),
(694, 1, 1, 1, 1448972762, 1448972762, 87, 0, 0, 'Поверь мне - счастье только там Где любят нас, где верят нам', '', '', '', 'c3d6c2d07e55b37bb90520cceeeb15b7'),
(695, 1, 1, 1, 1448972762, 1448972762, 87, 0, 0, 'У уважения есть границы, в то время как у любви таковых не имеется', '', '', '', 'c52f964fd625b2b28e25baca71f59cea'),
(696, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Большая часть мужчин требует от своих жен достоинств, которых сами они не стоят', '', '', '', '65a6d9eb82bf5ac9c10e22f87286b8aa'),
(697, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Если сколько голов - столько умов, то сколько сердец - столько родов любви', '', '', '', 'ff70ca7cdf3b44baea1e9086620a9150'),
(698, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Женщина знает, что наш брат все врет о высоких чувствах, - ему нужно только тело, и потому он простит все гадости, а уродливого, безвкусного, дурного тона костюма не простит', '', '', '', '99a016da9e6c64bb854e813ece882a37'),
(699, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Женщина, видишь ли, это такой предмет, что сколько ты ни изучай ее, все будет совершенно новое', '', '', '', '696f7a1cd9287438ba734790bb3272c8'),
(700, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Женщины устроили из себя такое орудие воздействия на чувственность, что мужчина не может спокойно обращаться с женщиной Как только подошел к ней, так и попал под ее дурман и ошалел', '', '', '', 'efd4164ce2836db2ca7777a8428e612a'),
(701, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Любить - значит жить жизнью того, кого любишь', '', '', '', 'd414bc0e0e5e98fc02a68cea2d11d112'),
(702, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Любовь - это бесценный дар Это единственная вещь, которую мы можем подарить и все же она у тебя остается', '', '', '', 'efc29bf6dcff5af026415d993c53784b'),
(703, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Надо не ставить себе задачу жениться, а задавать себе одну всегдашнюю задачу - жить хорошо; и тогда придет время, и обстоятельства сделают то, что нельзя не жениться. Так вернее не ошибешься', '', '', '', 'ad3d30d740201b299532b66360787960'),
(704, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Нам всегда кажется что нас любят за то, что мы хороши. А не догадываемся, что любят нас от того, что хороши те, кто нас любят', '', '', '', 'b0e525d494819a18aad6fc263fadd839'),
(705, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Существует странное, укоренившееся заблуждение в том, что стряпня, шитье, стирка, нянчанье составляют исключительно женское дело и что делать это мужчине даже стыдно. А между тем стыдно обратное: стыдно мужчине, часто незанятому, проводить время за пустяками или ничего не делать в то время, как усталая, часто слабая, беременная женщина через силу стряпает, стирает или нянчит больного ребенка', '', '', '', '114a6e723f7dab9534a8a03dd79af550'),
(706, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Счастье есть удовольствие без раскаяния', '', '', '', 'ac0112f5bc531162dea91731bf7a61aa'),
(707, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Счастье не в том, чтобы делать всегда, что хочешь, а в том, чтобы всегда хотеть того, что делаешь', '', '', '', 'bbf3d7698fada6e0960e9200644c9df5'),
(708, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Удивительное дело, какая полная бывает иллюзия того, что красота есть добро. Красивая женщина говорит глупости, ты слушаешь, а не слышишь глупости, а слышишь умное. Она говорит, делает гадости, а ты видишь что-то милое', '', '', '', '70088c99cb0f25a69af4f723baf0605f'),
(709, 1, 1, 1, 1448972762, 1448972762, 88, 0, 0, 'Всякая разодетая женщина представляет опасность для общества. Поэтому, когда я вижу разодетую женщину, я хочу закричать: "Городовой!"', '', '', '', 'e6e19c163b2c6121798a489957c7958d'),
(710, 1, 1, 1, 1448972762, 1448972762, 89, 0, 0, 'Женщина не только способна понять самопожертвование: она сама умеет пожертвовать собой', '', '', '', 'f7ea47d21ecd555815d6aaaa5d2db4c5'),
(711, 1, 1, 1, 1448972762, 1448972762, 89, 0, 0, 'Любовь сильнее смерти и страха смерти. Только ею, только любовью держится и движется жизнь', '', '', '', 'c2b87c0c644286cd11fe44777a5a55ad'),
(712, 1, 1, 1, 1448972762, 1448972762, 89, 0, 0, 'Хочешь быть счастливым? Выучись сперва страдать', '', '', '', 'b435badf9a0fef73db5333fd44189a39'),
(713, 1, 1, 1, 1448972762, 1448972762, 89, 0, 0, 'Человек, который расстается с женщиной, некогда любимой, в тот горький час и великий миг, когда он невольно сознает, что его сердце не вполне проникнуто ею, этот человек, поверьте мне, лучше и глубже понимает святость любви, чем те малодушнае люди, которые от скуки, от слабости продолжают играть на полупорванных струнах своих вялых и чувствительных сердец', '', '', '', '892ba62fb4c1c58aee5d47140bd0863f'),
(714, 1, 1, 1, 1448972762, 1448972762, 90, 0, 0, 'О, как убийственно мы любим,Как в буйной слепоте страстейМы то всего вернее губим,Что сердцу нашему милей!', '', '', '', 'b1ea796570a9549b63bf80c1d2bf1bac'),
(715, 1, 1, 1, 1448972762, 1448972762, 86, 0, 0, 'Интимная близость мужа и жены является частью сотворенной Богом человеческой природы, замыслом Божиим о человеческой жизни. Именно поэтому такое общение не может осуществляться случайно, с кем угодно, ради собственного удовольствия или страсти, но всегда должно быть связано с полной отдачей себя и полной верностью другому, только тогда оно становится источником духовного удовлетворения и радости для любящих.', '', '', '', 'ce2fd2f6137dafb462d758a029a86b28'),
(716, 1, 1, 1, 1448972762, 1448972762, 268, 83, 2728, 'Не прелюбодействуй.', '', '', '', '22c7b4140feb37c81f464b0a83b6d3db'),
(717, 1, 1, 1, 1448972762, 1448972762, 33, 0, 0, 'В любви нет страха, но совершенная любовь изгоняет страх, потому что в страхе есть мучение; боящийся не совершенен в любви.', '1-е Иоанна 4:18', '', '', '2dbc7632cd806a4e1e830ef241ff95c3'),
(719, 1, 1, 1, 1448972762, 1448972762, 125, 83, 0, 'Кто видит слишком далеко, не спокоен сердцем. Не печалься же ни о чем заранее и не радуйся тому, чего еще нет.', '', '', '', '6373e5b35c88954370575b3ce50c2680'),
(720, 1, 1, 1, 1448972762, 1448972762, 125, 83, 0, 'Помни: только эта жизнь имеет цену!', '', '', '', 'af3f92e82d69b1b5d3662472511a05f9'),
(722, 1, 1, 1, 1448972762, 1448972762, 125, 83, 0, 'Истина рано или поздно все равно выйдет на свет, и ложь будет повержена в прах.', '', '', '', '2e1ce19bd18b0cd026f3712cb59a0273'),
(723, 1, 1, 1, 1448972762, 1448972762, 125, 83, 0, 'Поступай с человеком так, как он сам поступает с другими.', '', '', '', 'a82bae836d416e5736af73c2fb41b2ab'),
(724, 1, 1, 1, 1448972762, 1448972762, 125, 83, 0, 'Как ни скор человек на слово, он порою слишком торопится. Как ни быстро откликается на все его сердце, ему не поспеть за его страстями.', '', '', '', 'f571e68ec58d09a99e659cc9bf325b90'),
(725, 1, 1, 1, 1448972762, 1448972762, 125, 83, 0, 'Нужно быть благодарным тому, кто оказал тебе благодеяние.', '', '', '', '47d27699506268eed2d08165a507cd87'),
(726, 1, 1, 1, 1448972762, 1448972762, 125, 83, 0, 'Что бы тебе ни сказали, ни с кем не говори так, словно он не имеет права с тобой разговаривать.', '', '', '', '8b26b99cb4fef91e44a5d6075a140d7a'),
(727, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Разве бывает что-либо прекрасно или безобразно само по себе? Что нравится человеку, то для него и прекрасно.', '', '', '', 'c05e9a77a8d15e2e1fba5fe0759fa4d1'),
(728, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Не было, нет и не будет человека, достойного одного лишь осуждения или одной лишь хвалы.', '', '', '', '4901b7fb28a631a84a1c20b2c55224eb'),
(729, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Как две щепки встречаются в великом океане и, встретившись, расходятся, так встречаются и люди.', '', '', '', '7d609a5e163cba5fef9241b3d8bdd10e'),
(730, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Словно отблеск луны в воде, непрочна жизнь смертных; зная это, непрестанно твори добро.', '', '', '', 'bbf56cd21ce29aca8551d8ccac864222'),
(731, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Все наши дела, мысли и речи следуют за нами творите же добро!', '', '', '', 'ed6ddc15036f10ced3344b6afa5a1031'),
(732, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Богатство и постоянное здоровье, друг и сладкоречивая жена, послушный сын и полезное знание - вот шесть благ в этом мире.', '', '', '', 'c80f85c61fefa117a8a88c3fa9cb7f1d'),
(733, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Хороши новая одежда, новый зонт, новобрачная и новый дом. Но хороши также старый слуга и старый рис.', '', '', '', 'f9ceda0a66bfde5a15dff3f56b3d235b'),
(734, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Друг, жена, слуга, рассудок и отвага познаются в беде.', '', '', '', '5a55047741e51bd77125755ec101f085'),
(735, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Друг познается в беде, герой - в битве, честный - в уплате долга, жена - в бедности, родственники - в невзгодах.', '', '', '', 'e0561e9f81fd9c68690b06025e30c676'),
(736, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Пропадает знание без дела, пропадает человек без знания, пропадает войско без полководца, пропадают женщины без мужчин.', '', '', '', 'ac3117db996f180a0c82a4e9c1d18164'),
(737, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Несбыточны сны, что приходят к больным, опечаленным, ожесточенным, влюбленным и пьяным.', '', '', '', 'ca7de18a47aaa06826e5541ba8b110a6'),
(738, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Безумный утешается прошедшим, слабоумный будущим, умный - настоящим.', '', '', '', 'ec0df59ba5c2137b98431c1d83379a1a'),
(739, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'В детстве глупец думает лишь об отце с матерью, в молодости - лишь о возлюбленной, в старости лишь о детях. Так и не успевает он подумать о самом себе.', '', '', '', 'c54e94488f99aff42ef29c4f2b93802c'),
(740, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Тот, чье сердце не стремится ни к наукам, ни к битвам, ни к женщинам, напрасно родился на свет, похитив юность матери.', '', '', '', 'f5f9e98ffba0c24152205f315c5dd110'),
(741, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Юность мчится невозвратимым потоком. Так предадимся радости - ведь дарованы радости смертным!', '', '', '', 'f8e2e95b419943b29b75640446820a76'),
(742, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Два пути перед мудрыми в этом бесплодном и непостоянном мире: вкушать нектар высшего знания или наслаждаться юными красавицами.', '', '', '', 'd8c70313699f5c978da4ecfc8db783c1'),
(743, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'За счастьем следует несчастье, за несчастьем - счастье. Не бывает непрерывного несчастья, не бывает непрерывного счастья.', '', '', '', 'e9b03e76828afd2adc0c7d24aa5f69a9'),
(744, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'То, что судьба судила нам получить, мы получим и лежа в постели. То, чего не судила, - не получим, каких бы усилий ни прилагали.', '', '', '', 'd60c3bc0f79d08fe9ecad9cb9dc71dde'),
(745, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Чему суждено быть - то и будет со мной. Неотвратим путь событий, мое дело - следовать ему.', '', '', '', 'fcc0a0522aa13ce18bcdd686d71b612b'),
(746, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Радуйся выпавшей тебе радости, переноси выпавшее тебе горе, жди всего, что приносит время, как земледелец ждет урожая.', '', '', '', '0c10555c4fc3ab20b616418bb9784c63'),
(747, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Раб своих надежд - раб всех людей, повелитель своих надежд - повелитель всего мира.', '', '', '', 'a8bcf90c005828f33f3c12f16973f0df'),
(748, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Надежда - источник великой тревоги, свобода от надежд - источник великого покоя.', '', '', '', '69b538c3a2d21a44b167b89377bda4ce'),
(749, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Плод победы - ненависть, удел побежденного - печаль. Спокоен и счастлив тот, кто отказался и от победы, и от поражения.', '', '', '', '386a99c04f830f4d988d63a61eff7a9f'),
(750, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Нет добродетели выше правдивости, нет порока хуже лжи.', '', '', '', 'ec8323b93a85deedfdcf78bbd8196fa4'),
(751, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Дела должны вести нас к счастью. Но где нет справедливости, нет счастья. Поступай же справедливо.', '', '', '', 'c00fb2bb37e5ff251ba730cc5f4d4cb2'),
(752, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Даже о правде следует умолчать, если она принесет несчастье.', '', '', '', 'a6dca35732937b4d5743a04e21bcd3d9'),
(753, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Говори правдивое и приятное; не говори правдивого, но неприятного; не говори приятного, но неправдивого - вот извечная заповедь.', '', '', '', '07d1f4990f9943d0ca7584e496e8d1ec'),
(754, 1, 1, 1, 1448972762, 1448972762, 126, 0, 299, 'Что невозможно, то невозможно; что возможно, то возможно. Повозка не движется по воде, корабль не плывет по суше.', '&quot;О природе&quot;, фрагм. 12', '', '', 'd47072e61f4c5321027a874b26ff3b83'),
(755, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Пусть драгоценность валяется под ногами, а стекло украшает голову - драгоценность остается драгоценностью, а стекло - стеклом.', '', '', '', 'df612e53c8f74256eae63a8a24149aec'),
(756, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Исполняй свой долг, не думая об исходе; исполняй свой долг, принесет ли это тебе счастье или несчастье. Кто исполняет долг и спокойно, не радуясь и не печалясь, встречает любые последствия, тот поистине велик душою.', '', '', '', 'ee9746302b5121f6cc9b55daedee065c'),
(757, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Нельзя посеять одно, а получить другое. Какое семя посеяно, то и даст всходы.', '', '', '', '27d5589f8185a625eb1558f580669327'),
(758, 1, 1, 1, 1448972762, 1448972762, 126, 0, 0, 'Лучше худо исполнять свой долг, чем хорошо - чужой. Не совершает греха тот, кто следует собственной природе.', '', '', '', '903c4ea366aa55d84db1b8068b16bceb'),
(759, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не сегодня, так завтра погибнет наше тело среди горестей и болезней. Что пользы преступать ради него долг?', '', '', '', 'eb17f7b27d8088b9cf22f15e77152355'),
(760, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Дело стоит рядом со стоящим, идет за идущим, творит с творящим. Оно следует за нами подобно тени.', '', '', '', 'cbaf6b4a3b1314e0117cad93d65b1aa8'),
(761, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Все задуманное можно осуществить человеческими усилиями. То, что зовем мы судьбою, есть лишь незримые свойства людей.', '', '', '', '9874b236c1197c4b17a05300612c5a5e'),
(762, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не катится колесница на одном колесе, не совершается судьба без людских усилий.', '', '', '', 'c3bd02a23159f5975ab9d1f3dafa8ca0'),
(763, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Того посещает богиня счастья, кто трудится подобно льву. Лишь ничтожные говорят: "Все от судьбы!" Одолевай судьбу мужественными делами; если же усердие твое не увенчается успехом, то нет на тебе вины!', '', '', '', 'c5aefd0502ecc99741bda285b9f86154'),
(764, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не приносят пользы мудрые наставления тому, кто страшится действия. Что проку от светильника в руках слепого?', '', '', '', 'fd0465760639c01b0d7c12fcafada027'),
(765, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Человек должен трудиться - плохо ему, когда он бездействует.', '', '', '', 'cb9f58e51cb0b6032c5a2800a0baa2bf'),
(766, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Мир и труд - вот источники благополучия.', '', '', '', '07d9f7ddce573a88ae5210f12a87ec54'),
(767, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Что пользы от учености, направленной на зло? Что пользы в светильнике, накрытом горшком?', '', '', '', '9ffd3ffb94546f153f45b401f4d8feb2'),
(768, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Сами собой разрастаются масляное пятно на воде, чужой секрет в устах сплетника, малый дар у достойного и знание у разумного - такова природа вещей.', '', '', '', '9c2a5f952828278b7b8e6571b387164e'),
(769, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Только богам открыты предначертания судьбы.', '', '', '', '0f7dcbabb4f3ee93e6aa3313971efd4d'),
(770, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Не рассчитывай на завтрашний день, пока он не наступил, ибо никто не знает, какие беды этот день принесет.', '', '', '', '85b959cdc7515e072483550c2725d2d8'),
(771, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Спокойствие страны – в справедливости.', '', '', '', 'c98aea0fa2586f9fc369e802625f9e34'),
(772, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Ложь кормится истиной, на ней она расцветает, но жизнь ее недолга.', '', '', '', 'a39c10a7f0e7083575a8c0f7c5521c40'),
(773, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Добро хорошо тогда, когда это истинное добро. Справедливость же всегда бессмертна. Кривыми путями не добраться барке до гавани. Лишь тот, кто честен и добр, благополучно достигает берега.', '', '', '', 'bb6c65dc92dd6a5df0cf3dc7113f0c82'),
(774, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Кто расставляет сети, сам в них попадется; кто роет яму, сам в нее свалится; кто точит меч, сам от меча погибнет.', '', '', '', 'bffea491f70b9cd5d46f74804b6cab34'),
(775, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Даже разум глупца мирится с истиной.', '', '', '', 'bbf3c78c98b7a8e45c79e75d8c4ba826'),
(776, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Если не исправишь зло, оно удвоится.', '', '', '', '8a53ec95815a058a6a0254cb16896a0c'),
(777, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Не сделаешь косноязычного красноречивым, не превратишь лентяя в прилежного, не сделаешь мудрецом невежду, не заставишь поумнеть дурака.', '', '', '', '4cea20842f8ca289a91a3bcd3c3dc654'),
(778, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Полезно слушать слова других!', '', '', '', '17c60e8c7a71758d12a4cd324408511b'),
(779, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Кто будет предотвращать злодеяния, если тот, кто обязан бороться с пороками, сам им подвержен?', '', '', '', '6f40bcb56f14b3f55e9302e8c873b749'),
(780, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Вкус пищи знает лишь тот, кто ест; ответить может лишь тот, кого спрашивают; сны видит лишь тот, кто спит; а для злодея наилучшим примером служит судья, сам достойный суда.', '', '', '', '8fad9cfc28bb0b604ed34b47412dbf76'),
(781, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Всякое беспристрастно решенное дело лишает ложь силы, утверждает истину, создает добро и уничтожает зло подобно пище, которая уничтожает голод, подобно одежде, которая прикрывает наготу, подобно тому, как небо после свирепой грозы проясняется и солнце согревает всех замерзших, подобно огню, который жарит то, что было сырым, подобно воде, которая утоляет жажду.', '', '', '', '852e19c7eac8884e7cd93774319a400f'),
(782, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Как несчастен приходящий с жалобой! Удел несчастного быть просителем.', '', '', '', '6f06d9243b524347cc66339901c9465c'),
(783, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Мошенничество несовместимо с правосудием. Лишь когда всё меряешь правильной мерой, правосудие торжествует. Преувеличения и излишества к добру не ведут. Если можешь сделать добро ближнему, делай его сразу. Если же будешь молчать и слишком долго глотать слова, даже истина может устареть и оказаться некстати.', '', '', '', '677f80829084b2396064a6526f2274cd'),
(784, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Чтобы сохранить друзей, нужно прощать.', '', '', '', 'eef4b2d7def58693b7aeaba16b7bf805'),
(785, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Что может быть хуже неверных весов, фальшивой гири или справедливого и честного человека, сделавшегося обманщиком?', '', '', '', '0f885553c3e7f3ee8b60ef06d0c442ee'),
(786, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Вспыльчивый никогда не познает истину.', '', '', '', '2154aa95d03502a52b5bab9f3227f276'),
(787, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Кто убивает, тоже будет убит. Кто приказывает убить, тоже будет убит по приказу.', '', '', '', '4d25f47ec6969ecf617493c62e7bad5f'),
(788, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Истинно великий не может быть жадным.', '', '', '', 'c2d8c2eea34a6512df12176a12f0b98e'),
(789, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Жадность бездонна – в эту пропасть можно падать бесконечно. Корыстный часто не знает, чего он хочет, и единственное, что его ждет, – это конечная неудача.', '', '', '', 'f0c27c927c53c553aeeab0ece4b221e6');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(790, 1, 1, 1, 1448972763, 1448972763, 125, 83, 0, 'Нет приятного прошлого для немилостивого, нет друзей у того, кто глух к справедливости, нет праздничного дня для корыстолюбца.', '', '', '', 'a027a9f042fc298b822b36c984e929f7'),
(791, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Лучше получить от умного сто ударов, чем разрешить глупцу вылить на твою голову благовонные масла.', '', '', '', '8536c0f9674a8e7e4f18b43f70b83560'),
(792, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Если доведется тебе услышать дурное слово, закопай его в землю на глубину в семь локтей.', '', '', '', '2942421d2aff38be1d29ccc45733dfd0'),
(793, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Если услышишь слово секретное, то пусть в душе твоей оно и умрет. Никому не открывай того секрета, дабы он не стал пылающим углем во рту твоем, не обжег языка твоего, не причинил страданий душе твоей и не заставил тебя возроптать против бога.', '', '', '', '1bfab3900f835978ef60fcea3fbfa9c3'),
(794, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Не следует слишком часто навещать своего друга, дабы он не пресытился тобой и не возненавидел тебя.', '', '', '', '2ae7d8fe2de52dbefa5301330452707a'),
(795, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Не забывайся с глупцом и не имей общения с нецеломудренным.', '', '', '', 'b15cee791af2e7b47cc8b48c55436873'),
(796, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Не пребывай там, где люди ссорятся, ибо ссора часто ведет к убийству.', '', '', '', 'b53ec39808a08cae08dcc27b75fc2d44'),
(797, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Спеши бежать из того места, где спорят, и душа твоя будет умиротворенной.', '', '', '', '8a6ece22c0ed2bdec9f12833dc8dff8f'),
(798, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Я поднимал железо и свинец, но не нашел ничего тяжелее хулы и клеветы.', '', '', '', '1d635b526172940a4618e2fcc35e6e53'),
(799, 1, 1, 1, 1448972763, 1448972763, 63, 83, 0, 'Чьи одеяния великолепны, того и слова весомы, но кто бедно одет, словам того не придают значения.', '', '', '', '4570d12be7af668ac12a627488c214a6'),
(800, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'И злым, и добрым, и заслуживающим смерти должен сочувствовать благородный. Ибо нет человека, свободного от вины.', '', '', '', '6d1cc890abd0dde5a4f5986e2a31813b'),
(801, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Высшей власти достигает тот, кто видит во всех существах себя и все существа - в себе.', '', '', '', '1f9458540891cd87c46849c47bbc4e01'),
(802, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Каковы те, кто ему служат, каковы те, кому он служит, таков и сам человек.', '', '', '', 'e60021147ca8eaef65370a262deced8d'),
(803, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Нередко заблуждается наш разум, и вещи кажутся иными, чем они есть. Плоским представляется нам небо и огоньком - светлячок, но небо не плоское, а светлячок не огонь.', '', '', '', 'e543915fd20ff0f386e7f6ac21eb5117'),
(804, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Женщины учены от природы, мужчины - от книг.', '', '', '', '15bb849df3ab26e8726d8d5be5c6b2da'),
(805, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Лишь ученому ведомо утомление ученых, не поймет бесплодная болей роженицы.', '', '', '', '7ec24f5abed8cbf080551f3385ffe6c1'),
(806, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Нет преграды, равной преграде невежества.', '', '', '', 'd9f01f5739468805c3dd3e159983db3d'),
(807, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Сладость губ, упругость грудей, острота женских взоров и красота поэзии понятны лишь знатоку.', '', '', '', 'd25a73a4a7e3dcd28dc9a5d7a4bbbbc8'),
(808, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Конь, оружие, наука, лютня, речь, мужчина и женщина бывают хороши или дурны - все зависит от того, как с ними обращаются.', '', '', '', 'ffd9a564961dec00488981211d83886b'),
(809, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Науки ведут к скромности, лишь глупец кичится ученостью. Так свет усиливает наше зрение и делает слепыми сов.', '', '', '', 'c1f6561330a6864f301c50ac19d23ef5'),
(810, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Что пользы от науки тому, у кого нет ума? На что зеркало тому, кто лишен глаз?', '', '', '', '4a06bda97ccd8e12b201bc5f476a2a4c'),
(811, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Когда время действовать, то знание, сокрытое в книгах, - не знание, а деньги в чужих руках - не деньги.', '', '', '', '842702bab958b339bf20321fec55bf97'),
(812, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Что проку от знаний тому, кто, изучив все науки и проникнув в их суть, не извлечет из них пользы? Одно утомление.', '', '', '', '4ee9140124abe3ce6de41a6a3e1fb5ea'),
(813, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Даже в гнусном теле мудрец найдет добродетель, которую надо почитать и уважать.', '', '', '', 'c8c14a30d42eca0e60fc40ebde2f9a21'),
(814, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Украшение человека - мудрость, украшение мудрости - спокойствие, украшение спокойствия - отвага, украшение отваги - мягкость.', '', '', '', 'b66e042c6c16aada9b1cf13af19121c6'),
(815, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Давать советы глупцу - только злить его.', '', '', '', 'f7beef2829b9f67a4b7185c2b74f98b6'),
(816, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Неистинное представляется истинным, а истинное неистинным - таково многообразие бытия. Будьте же разумны!', '', '', '', '46c700fc9e57e171788d920fda8629be'),
(817, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Жизнь одна - тел много, истина одна - заблуждений много, знание одно - ересей много. Какой мудрец разберется во всех противоречиях?', '', '', '', '8afdc966d9db40b5e89d239a92808a17'),
(818, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Разумный оценивает по собственному суждению, глупец доверяет молве.', '', '', '', '0d462839a86b0909738db6aef8d5d916'),
(819, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не берись ни за какие дела - вот первый признак мудрости. Взявшись же за дело, доводи его до конца вот второй признак мудрости.', '', '', '', '5e0862958afb8319e560e17d0b5da040'),
(820, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Когда все гибнет, мудрец сберегает половину и с этой половиной достигает своей цели.', '', '', '', 'ed662be4a0b61ccd518cac99ff3aae12'),
(821, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не станет разумный ради малого губить великое. Малым сберечь великое - вот истинная мудрость.', '', '', '', '07cc5f9296e93bb1cf03a36f2fd9ef3e'),
(822, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Если ты мудр, не противоречь богачу, правителю, ребенку, старику, аскету, мудрецу, женщине, дураку и учителю.', '', '', '', 'bc17e22f41939d93d96c8e38a6208bae'),
(823, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Когда надо давать советы другим, каждый - кладезь премудрости. Когда надо самому следовать этим советам, то и мудрец не умнее дурака.', '', '', '', 'aa1be867b2c0c218cc94be282dee7b3d'),
(824, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'У кого не хватит ума поучать другого? Лишь великий разумом сам способен следовать надлежащим путем.', '', '', '', '7ba98b9d59d2caf495e97d760ce60cdd'),
(825, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Мудрые отдают время поэзии и наукам, глупые порокам, сну и ссорам.', '', '', '', 'eb9997a0475805eac97574f5ac5a2bab'),
(826, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Мудрый не горюет о потерянном, об умершем и о прошлом. Тем он и отличается от глупца.', '', '', '', '9f468920f65ce535a250bc66615de2f4'),
(827, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Огонь заливают водой, от солнца укрываются зонтом, от болезни спасаются лекарством. От всего есть средство - нет средства лишь от глупости.', '', '', '', '4e2d7552ee9df963a7103a1b86497781'),
(828, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Кого боги задумали погубить, того они лишают рассудка, и всё представляется ему в ложном свете.', '', '', '', '54ba8053a8fea6f14817bdc292af0f49'),
(829, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Беспричинная ссора - вот признак глупости.', '', '', '', '0340bb6092c7109ebfa945bfaa07cc1a'),
(830, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Умен человек - и все же он служит царю, поглощает отраву и наслаждается женщинами.', '', '', '', '7e562f2440a2ebaacb2f8941c66e8804'),
(831, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Радости полон мир для того, кто смотрит на всех без вражды и предубеждения.', '', '', '', 'd2500a9e4c29fc8dbb455bcce35873a4'),
(832, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Одно полезное слово, услышав которое становятся спокойными, лучше тысячи речей, составленных из бесполезных слов.', '', '', '', 'dfa5f750af37f75e5716726907930961'),
(833, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Хорошо сказанное слово человека, который ему не следует, столь же бесплодно, как и прекрасный цветок, но лишенный аромата.', '', '', '', 'b32a987222c925f6b8594800a970befe'),
(834, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Все определяется словом, коренится в слове, вышло из слова. Кто лжет в слове, тот лжет во всем.', '', '', '', '138d42ead72f292529e8dabf02304aff'),
(835, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Заживает рана, нанесенная стрелой, вновь поднимается лес, вырубленный топором, но не заживает рана, нанесенная злым словом.', '', '', '', '0375cdbfdcc6943775e99ab94200b69a'),
(836, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Нельзя остановить пения безумца, лепета ребенка и слов женщины.', '', '', '', 'fc18835e882bc9dff3fefa1b394ec669'),
(837, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Молчание лучше слов.', '', '', '', '35f901887011eb44eb8b6c13fdb88328'),
(838, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Тело старится от странствий, гора - от воды, женщина - от неудовлетворенных желаний, сердце от злых речей.', '', '', '', '86fb4ac3bd1310242325a98aa030ca6c'),
(839, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не рассказывай о том, что задумал: не бывает успеха у замысла, что открыт другому.', '', '', '', '7074b7196708618cba1689d756b67c7b'),
(840, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Лучше мгновение, прожитое в законе, чем миллионы лет беззаконной жизни.', '', '', '', '8886d9aa347cf9730801636bbfb114c4'),
(841, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не живи в стране, которой никто не правит, правят многие, правит женщина или правит дитя.', '', '', '', 'bb86d514e598de28bbe9d3c2446074ba'),
(842, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Тогда лишь стоит родиться, когда тебе уготована свобода. Если рабство - жизнь, что же такое смерть?', '', '', '', '99150292da7ce5b6e0e888a2c446023b'),
(843, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Где чтут недостойных и презирают достойных, там находят себе прибежище трое: голод, смерть и страх.', '', '', '', 'd37de3230b298f58151eacff531069e6'),
(844, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Добродетель в том, чтобы делать ближним добро и не причинять зла. А делать ближним добро - это делать для них то, чего желал бы самому себе.', '', '', '', 'd1b692c0d866b13b07e6a99a200a0069'),
(845, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Не видно следа птицы в воздухе и рыбы в воде таков и путь добродетельных.', '', '', '', '027a68c926bafbbe4bbd8dbe34c76692'),
(846, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'С великим трудом поднимаем мы камень на гору, а вниз он падает мгновенно - так же влекут нас вверх добродетели, а вниз пороки.', '', '', '', 'a7ea16d76b21981a3038344d3cc196c2'),
(847, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Поборов гордость, человек становится приятным. Поборов гнев, он становится веселым. Поборов страсть, он становится преуспевающим. Поборов алчность, он становится счастливым.', '', '', '', '783a765a653c945df32182e1138b229f'),
(848, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Сердце злодея твердо в счастье и мягко в несчастье; сердце достойного мягко в счастье и твердо в несчастье.', '', '', '', '014df161ceea230828a678bab89814f9'),
(849, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Неизменно сердце доброго - пусть его гневают; не нагреется океан от горящей соломы.', '', '', '', 'db258c75c184b654813b9de5bca6fda8'),
(850, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Терпение - добродетель бессильного и украшение сильного.', '', '', '', 'ac32c035d8d1b92cf6fa3c2a49760a68'),
(851, 1, 1, 1, 1448972763, 1448972763, 126, 0, 0, 'Обилие плодов клонит ветвь книзу, пышный хвост замедляет походку павлина, быстроногого коня стреножат. Так сами достоинства причиняют зло.', '', '', '', '595a14f86e482bb6229b58d90c9a4d9b'),
(852, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Герой, ученый и красавица куда бы ни пошли, везде найдут приют.', '', '', '', '42643ae16579cda7b226e7613d41c2dd'),
(853, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Разум без отваги - свойство женщины; отвага без разума - свойство скотины.', '', '', '', '7ec6787a6465762b066918ea91a90807'),
(854, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Когда один смел в битве, все войско делается смелым; когда один труслив, все становятся трусами.', '', '', '', 'cc956ce4aacd66d9ee80da2baaf2ba12'),
(855, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Если нельзя избежать опасности, что толку в трусости, которая все равно не защитит тебя?', '', '', '', 'bdc349f370c37487a7426299d55986b1'),
(856, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Бойся опасности, пока ее нет; когда же опасность пришла, не бойся, а борись с ней.', '', '', '', '4caf9ff36e8b885e5bef3cbb59d460dc'),
(857, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Пусть никто не соединяется с плохими друзьями, пусть никто не соединяется с плохими людьми. Привяжитесь к хорошим друзьям, привяжитесь к благородным людям.', '', '', '', '62cdaf6826fe70ed7801fc14d489d12c'),
(858, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, '"Он оскорбил меня, он ударил меня, он одержал верх надо мной, он обобрал меня". У тех, кто таит в себе такие мысли, ненависть не прекращается. Ибо никогда в этом мире ненависть не прекращается ненавистью, но отсутствием ненависти прекращается она(...)', '', '', '', '537b970a964fc508ee6b550bc70d749b'),
(859, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Ненависти можно положить конец не ненавистью, а только любовью.', '', '', '', 'e14b1d8cd3e261b930bafc7732497c07'),
(860, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Даже тот, кто далеко, стоит рядом, если он в твоем сердце; даже тот, кто стоит рядом, далек, если твои мысли далеки от него.', '', '', '', 'bc790a8a0fb8af9bc82abab479c9d831'),
(861, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Лучше враждовать с умным, чем дружить с дураком.', '', '', '', '2bc347c1c3a82cd0b0b23eca53b1e5a3'),
(862, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Ни матери, ни жене, ни брату, ни собственным детям нельзя довериться так, как истинному другу.', '', '', '', '8e20deebd91ef71f3f239ccf4a3c7014'),
(863, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Давать, брать, делиться тайной, расспрашивать, угощать, принимать угощение - вот шесть признаков дружбы.', '', '', '', '181800dadb0573121ce1018de99bd508'),
(864, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не занимай у друга и не одалживай другу. Долг, словно ножницы, разрезает дружбу.', '', '', '', 'bb08b2b50c9a2d5bf3d9ed2265716f23'),
(865, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Пройдет время, и друг станет врагом, а враг - другом. Ибо собственная выгода сильнее всего.', '', '', '', '68f710d94d3dc6d8de01a169b9d1e0e9'),
(866, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не победить сильным слабых, если те держатся вместе; не страшен ураган кустам, что растут близко друг к другу.', '', '', '', 'ad7c93dae25e6495a7cd4296df562c1e'),
(867, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Любовь к женщине острее крюка, которым укрощают диких слонов, горячее пламени; она подобнее стреле, вонзающейся в душу человека.', '', '', '', 'e25f449bbea08b3aa928dbaa3b8e7637'),
(868, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Здоровье, стойкость, могущество - великие блага, но они бесполезны, если нет наслаждения любовью.', '', '', '', 'b5ba6f987f3f0b234bf46d005bed209a'),
(869, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'При любовном наслаждении уста женщины всегда чисты.', '', '', '', 'a6bf337f3d0ee05b2ff219ccc941ff09'),
(870, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Томимый страстью не уснет и на мягком ложе; удовлетворивший страсть сладко уснет и на шипах.', '', '', '', 'da33f866dad26e7bbc0d772591283c76'),
(871, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Кокетство с любимым заменяет у женщины признание в любви.', '', '', '', 'd5349229817c53d53083af9d16e57d4a'),
(872, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Можно поймать тигра в чаще, птицу в небесах, рыбу в пучине вод, но нельзя поймать непостоянное сердце женщины.', '', '', '', 'afebd9617f57726e60c30dd5277fd186'),
(873, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Женщина создана рожать, мужчина - производить потомство.', '', '', '', '3c9e3b8501ceac33122ddad0bafbbe77'),
(874, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Хорошая жена трудится на тебя, как слуга; дает советы, как советник; прекрасна, как богиня красоты; спокойна и вынослива, как земля; кормит тебя, как мать, и услаждает тебя, как гетера. Хорошая жена шесть лиц в одном.', '', '', '', '006b70de991332e938e88a3607be77f4'),
(875, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Женщина сияет - весь дом сияет, женщина мрачна весь дом погружен во мрак.', '', '', '', 'dd8f62e6583141383946bfbc153fe968'),
(876, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Хозяйка дома - вот что такое дом. А дом без хозяйки подобен лесу.', '', '', '', 'debd0a382a6906abb48d8963fe8de030'),
(877, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не звучит лютня без струн, не катится повозка без колес, несчастлива женщина без мужа - пусть у нее сотня родичей.', '', '', '', '40244d97758973c9f0614d5116d9ec31'),
(878, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Как ни плох родной сын - невоспитан, уродлив, глуп, распутен, зол, - он все же радует сердце.', '', '', '', 'ad9ae1ac8a4d467662d825f65ff39d6b'),
(879, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не вмешивайся в дела мужа с женой, не вмешивайся в дела отца с сыном.', '', '', '', '879828dbbcdc31de7b82e1504a4c80fc'),
(880, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'До пяти лет обращайся с сыном, как с царем, с пяти до пятнадцати - как с слугой, после пятнадцати - как с другом.', '', '', '', 'ea60ec28b764f801034a1c9e9ebae488'),
(881, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Нет места, нет случая, нет пожелавшего - тогда и чиста женщина. Пусть достанется ей сам бог любви она пожелает другого мужчину. Такова уж природа всех жен.', '', '', '', '3bf1505c3e2bf85f80596d2319dfda87'),
(882, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Уже одна ревность мужа влечет жену к другому. Разумный охраняет жену, не показывая ревности.', '', '', '', 'a5827ea6665689823b6a528a0366c15b'),
(883, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'До тех лишь пор бывает человек хозяином своих дел, пока в ухо ему не вонзается стрекало женских речей.', '', '', '', 'f675e916c19ac14cf31b879847f8feb5'),
(884, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Что сделает злодей тому, в чьей руке меч спокойствия? Когда огонь не находит травы, он сам успокаивается.', '', '', '', 'af5e2dfd75542445734721cfb2f1170f'),
(885, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Мягкость побеждает мягких, мягкость побеждает суровых. Нет ничего недостижимого для мягкости. Мягкость сильнее всего.', '', '', '', 'e33c10c82dadb1d488882a9ac83653cc'),
(886, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Победи сначала самого себя, а потом - врагов. Как может владеть другими не владеющий собой?', '', '', '', '411deae02daa08383550e9f3c68f62de'),
(887, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Когда случается упасть, достойный человек падает как мяч, ничтожный - как ком глины.', '', '', '', 'bccdbc5272d62974d43e096bc173be95'),
(888, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Смотрите благожелательно на все сущее: пусть каждое ваше слово будет спокойно, приветливо, благосклонно; пусть каждое действие ваше служит к исправлению, к развитию добра.', '', '', '', 'c8626cf0a1d5eec3a6638696eccc8e4f'),
(889, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Избегайте легкомыслия, чуждайтесь страсти и наслаждения, ибо лишь серьезный и вдумчивый достигает великого счастья.', '', '', '', '7d668a4fe228cd729e7e7a6d6901d45b'),
(890, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не совершай того, чего не следует делать - даже под угрозой смерти; не откладывай того, что следует сделать, - вот извечная заповедь.', '', '', '', '43928a57ec5cb5f09d34d3ecc2a5ce2d'),
(891, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'He делай другому того, что было бы неприятно тебе самому.', '', '', '', 'aeeabc3a565ccaf33087c00cfafb95be'),
(892, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Как ни мало благодеяние, оно пустит сотню ростков, если оказать его достойному.', '', '', '', '77781097b4de906ba3a353366b1245f2'),
(893, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Ты думаешь: "Я один" - и не знаешь, что сердце твое не покидает мудрец, взирающий на добро и зло, творимые тобою.', '', '', '', '6507846cd3951838750e43d7c9ce721e'),
(894, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Никакое добро не пропадает даром.', '', '', '', 'dd66cd4b312deb9ea03fb9c5fda757f6'),
(895, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Побеждай гнев мягкостью, зло - добром, жадность щедростью, ложь - правдой.', '', '', '', '44d78e50d8d94754fdbfa737011c22ce'),
(896, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Нет подвижничества, равного терпеливости; нет счастья, равного удовлетворенности; нет дара, равного дружбе; нет добродетели, равной состраданию.', '', '', '', 'd2e4a09299471b1d657aac95290085b1'),
(897, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Говори приятное, но не льсти; будь героем, но без хвастовства; будь щедрым, но не к недостойным; будь смелым, но без дерзости.', '', '', '', '248e283435d9d70c7ff29d79060e0dd9'),
(898, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'На восходе солнце красно, красно оно и на закате. И в счастье и в несчастье неизменны великие.', '', '', '', '63e474eb6b1e1ef5a926891efac7aba8'),
(899, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Тот велик, кто становится слепым перед чужими женами, хромым в погоне за чужим богатством, немым, слыша хулу на ближних.', '', '', '', '5a9183ae2b7442922ce6c917fd504107'),
(900, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Суть миллионов книг - в одном стихе: заслуга добре ближнему, грех - в зле ближнему.', '', '', '', 'c6bfe0461d6886800ff5e7a22b749836'),
(901, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Как ловок благородный, когда надо скрыть чужой порок! Как неловок он, когда приходится обнаруживать свои достоинства.', '', '', '', '980d9365fba45e1809d4581d00955a8a'),
(902, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Подражай хорошему даже во врагах, не подражай дурному даже в родителях.', '', '', '', 'd003d3e0240ef4034f1a34d518c95d82'),
(903, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Внимай полезной речи, пусть она исходит от ребенка; не слушай дурных речей, пусть они исходят от старца.', '', '', '', '5efa6098098c41119aaee579bee7261f'),
(904, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Кто, зная о злодействе и имея силу ему воспрепятствовать, не сделал этого - виноват наравне со злодеем.', '', '', '', 'af55b92b103ee402a70bd90b8cbb7b49'),
(905, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Шестеро не помнят тех, кто помогал им раньше: ученик - учителя, женатый сын - мать, разлюбивший муж - жену, достигший цели - помощника, выбравшийся из чащи - проводника, больной - врача.', '', '', '', '67e1b207e92543eb02a077d536bb6dbf'),
(906, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не сердись на рассерженного, отвечай мягкостью на грубость, не говори суетного и лживого.', '', '', '', '19a3702a405e1de1e5e3a2e9f041e438'),
(907, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Тот настоящий человек, кто сбрасывает подступивший гнев, как змея сбрасывает старую кожу.', '', '', '', '857b700f2fb9bce9eeb36fa24e09dcc6'),
(908, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Кто не отвечает гневом на гнев, спасает обоих - и себя, и другого.', '', '', '', 'dc0e76c2739ad71d0d5d1f3222b9f374'),
(909, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не будь груб ни с кем - тебе ответят тем же. Сердитая речь причиняет боль, и тебя постигнет возмездие.', '', '', '', '78d7414bf21685ba446e3100114ba305'),
(910, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Кто не отвечает ни на излишнюю хвалу, ни на порицание, кто не отвечает ударом на удар, кто не желает зла обидчику, тому завидуют боги.', '', '', '', 'c303421bf9853610807b350773f52ac5'),
(911, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Будь внимателен к гостю - пусть он твой враг. Даже лесорубу с топором дерево не отказывает в тени.', '', '', '', '08a144d01850cce02b4aa26bcfa53dd6'),
(912, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Человек, творящий зло, - враг самому себе: ведь сам он и вкусит плоды своего зла.', '', '', '', 'e92f40fc5f7eed7c7b1ed8e2288e91d7'),
(913, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Ничтожен тот, кто способен говорить о чужих недостатках или о своих добродетелях. Ни о чужих недостатках, ни о своих добродетелях не говорит истинно достойный.', '', '', '', '5516f87df78b2f5caff1816d7ceebf83'),
(914, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Благородный говорит лишь о достоинствах ближнего, хоть тот лишен их; низкий - лишь о недостатках. И пусть оба они лгут - первый идет на небо, второй - в преисподнюю.', '', '', '', '5c0d913d5724b751e5e7d843fe08f3ae'),
(915, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Не подобает разумному вести в собрании дурные речи о другом.', '', '', '', 'c77b99e8412cc1d867e6a0de9db61fe3'),
(916, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Один лишь раз приказывает царь, один лишь раз высказывает свое суждение достойный, один лишь раз отдают замуж девушку - три эти дела делают лишь раз.', '', '', '', '94da120f5c641d80bf9082de9e2f7c7b'),
(917, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Два средства есть от телесных и душевных недугов: или примени противодействие, или не думай о них.', '', '', '', '22cd1ed4c00c078a1346e256cabaea74'),
(918, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Лекарство от несчастья - не думать о нем. Когда думаешь о несчастье, оно не проходит, а возрастает.', '', '', '', 'a2d747b7bfa307ceabb8428e7c254ce7'),
(919, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Терпение - украшение мужчины, скромность украшение женщины; однако получивший оскорбление должен ответить силой, а предающаяся любовным наслаждениям - оставить сдержанность.', '', '', '', 'd3340cddc61aaa12587d0384a1a230d5'),
(920, 1, 1, 1, 1448972764, 1448972764, 126, 0, 0, 'Когда книга, жена или деньги попадают в чужие руки, то они пропадают для нас; если же возвращаются, то книга - истрепанной, жена испорченной, а деньги - по частям.', '', '', '', '141c40d2a4885d0fc3162f94b94928e2'),
(921, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Глупец суетится вовсю, затеяв пустяк, - умный сохраняет спокойствие, берясь за великое дело.', '', '', '', '704452140f3b3c6b08c0b39d082a053e'),
(922, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'К умному и честному стремись, с умным и лживым будь настороже, честного и глупого жалей, лживого и глупого избегай.', '', '', '', '550658a6f70da133179c3f085a1c4bca'),
(923, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Кое-что можно открыть женам, кое-что друзьям, кое-что детям - ведь все они достойны доверия. Но всего каждому открывать нельзя.', '', '', '', '4f805f481857817096801b4211bfbffc'),
(924, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Не будь ни слишком близко, ни слишком далеко от царей, огня, старших и женщин: окажешься слишком близко - они погубят тебя; окажешься слишком далеко - они будут бесполезны для тебя.', '', '', '', '6ee8759ff15cce1e7fc4d6ccccba0811'),
(925, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Не будь ни слишком грубым, ни слишком упрямым ни слишком мягким, ни слишком склонным к доказательствам, ни слишком гневливым. Излишек в чем бы то ни было опасен: грубость раздражает людей, упрямство отталкивает, мягкость вызывает презрение, излишние доказательства обижают, слепая вера делает смешным, неверие ведет к пороку.', '', '', '', '3a62a877c8074686a2aa7bdb0e8cc5f3'),
(926, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Занозу, расшатанный зуб и дурного советника надо удалять с корнем - вот условие покоя.', '', '', '', '8e8a7d2c19dfb64385b99ed9b55b2692'),
(927, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Пусть тебя озолотят, не доверяй врагу и нелюбящей женщине - иначе ты погиб.', '', '', '', '5ef7d437a060f96db1a2ae5fa529a850'),
(928, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Избегай дел, зависящих от других; стремись лишь к тому, что зависит от тебя самого.', '', '', '', 'fbe41f305a8d16fcf51c812854b7ec6c'),
(929, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Не следуй неумеренным желаниям, но не подавляй всех желаний.', '', '', '', 'f84d47d9faf8606d42ca0afd066b2608'),
(930, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Не успокаивается желание от наслаждения желанным; еще сильнее вспыхивает огонь, в который подлили масла.', '', '', '', '85e26bc9ef52a3b1a325383aea84cac1'),
(931, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Побеждай жадного деньгами, гордого - мольбой, глупого - потворством, мудрого - правдивостью.', '', '', '', '0b4b0cdcd96d90d9dc479d925ef9d5e4'),
(932, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Сколько достоинств у человека, столько и пороков. Никто не родится на свет без пороков.', '', '', '', 'fe5692052632ba5e4ad184fd8c582019'),
(933, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Достоинства не так бросаются в глаза, как пороки.', '', '', '', 'dbce8c01690f7051d5afd66e1b31d40a'),
(934, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Чаше весов подобен ничтожный человек: чуть что он возносится, чуть что - падает.', '', '', '', '586592212d62aee095bb5a47cb5b58b6'),
(935, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Жестокость - даже к злым - ведет в ад. Что же говорить о жестокости к добрым?', '', '', '', '5c6d6e18f1c80e17aa65097526bfb464'),
(936, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Невелика мудрость - обмануть доверчивого. Невелико мужество - убить заснувшего на твоей груди.', '', '', '', '2a5b546676d377dfa3cdd7572c5bea63'),
(937, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Не одолеть сильному слабого, когда тот недоверчив. Но даже слабый одолеет сильного, когда тот доверчив.', '', '', '', '6865ff5876da6ef7fd67103ec2ec2393'),
(938, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Вялость, женолюбие, болезненность, привязанность к родным местам, довольство жизнью, боязливость вот шесть преград на пути к величию.', '', '', '', '4881a0788d90636d63dc7596bbcf6422'),
(939, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Знание зависит от ученья, почет - от дел, благополучие - от усердия, награда - от судьбы.', '', '', '', '9db0420488e6670c445f4dde537ed153'),
(940, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Никто не бывает от природы ни высоким, ни низким лишь собственные дела ведут человека к почету или презрению.', '', '', '', '57184a76bfbb303023d955b2c590a751'),
(941, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Два упущения возможны при владении богатством: давать недостойному и не давать достойному.', '', '', '', '4c127931b134a4d82dcef88705b96435'),
(942, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Лучше всего деньги, добытые собственным трудом, хуже - добытые по наследству, еще хуже - от брата, хуже всего - от женщины.', '', '', '', 'cbc41539a897bc69113db6557e959fb7'),
(943, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Человек служит не человеку - человек служит деньгам.', '', '', '', 'afb3c487052c93d788c36ea3e5f6a92d'),
(944, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Для жадного нет дельного и не дельного, нет славного и позорного, нет доброго и злого - для него есть только выгодное и невыгодное.', '', '', '', 'c0377101fdf92090e4ad73d4ccdfb3b4'),
(945, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Две ошибки совершил творец: создал женщин и золото.', '', '', '', '7b7a52969b4b34fcd569a904acc09fe0'),
(946, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Нет друга, равного здоровью; нет врага, равного болезни.', '', '', '', '042461185acf22628d713809f43343ef'),
(947, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Остатки долга, огня и болезни способны снова возрастать - уничтожайте их до конца.', '', '', '', '9e4454dbdbaa0f9e4a7eee2209f3742c'),
(948, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Живи в молодости так, чтобы быть счастливым в старости.', '', '', '', '0353a2da611fecfcdaa13d3ddd2b9cab'),
(949, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Ничто не грозит спелому плоду, кроме падения; ничто не грозит рожденному на свет, кроме смерти.', '', '', '', '1e950c5f29b51303e01269259c10af20'),
(950, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Время никого не любит и никого не ненавидит, время ни к кому не равнодушно - оно уносит всех.', '', '', '', 'a66d7e59937442024b55bcc04c773c69'),
(951, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Даже с собственным телом невозможно быть вечно что же говорить о других существах?', '', '', '', '6ce5f0f848140497c6fb75b0bc83e065'),
(952, 1, 1, 1, 1448972765, 1448972765, 126, 0, 0, 'Мудрый не оплакивает ни умерших, ни живых.', '', '', '', '88fefc29e0d7bc543b5c901e00c87a4a'),
(953, 1, 1, 1, 1448972765, 1448972765, 65, 0, 0, 'Никто не познает точно добродетели, если не имеет понятия о пороке, в особенности когда некоторые пороки до такой степени близки к добродетели, что легко обманывают своим подобием.', '', '', '', 'f69e5e9cead5f6db2a4023cf301d505f'),
(954, 1, 1, 1, 1448972765, 1448972765, 65, 0, 0, 'То, чем мы грешим в молодости, приходится искупать в старости.', '', '', '', 'd9657bc1a0a942e455c39a5c12e99bc6'),
(955, 1, 1, 1, 1448972765, 1448972765, 65, 0, 0, 'С сильной любовью не могут ужиться черные подозрения.', '', '', '', '3105204e7448b162f9c2a31d5bf9558e'),
(956, 1, 1, 1, 1448972765, 1448972765, 65, 0, 0, 'Любовь одно из зол, которых нельзя скрыть; одно слово, один нескромный взгляд, иногда даже молчание выдают ее.', '', '', '', '8c0fb678657814885fe237d8cb34b532'),
(957, 1, 1, 1, 1448972765, 1448972765, 65, 0, 0, 'Одно дело - лгать, другое - заблуждаться в речах и отступать от истины в словах в силу заблуждения, а не злого умысла.', '', '', '', '8119bb6347f7a5878a1db8fb9633d562'),
(958, 1, 1, 1, 1448972765, 1448972765, 65, 0, 0, 'Человеческие чувства часто сильнее возбуждаются или смягчаются примерами, чем словами.', '', '', '', '5404f1208afd6b211722846f19338648'),
(971, 1, 1, 1, 1448972765, 1448972765, 127, 83, 1, 'Все полно богов.', '', '', '', 'f3c6aad4c35ba3f236d35fd64a0613da'),
(972, 1, 1, 1, 1448972765, 1448972765, 127, 83, 2, 'Мудрее всего – время, ибо оно раскрывает все.', '', '', '', '1df8e2ee7d0534ae9cadc3fcc02443dc'),
(973, 1, 1, 1, 1448972765, 1448972765, 127, 83, 3, '[Фалес] сказал, что между жизнью и смертью нет разницы. – «Почему же ты не умрешь?» – спросили его. «Именно поэтому», – сказат Фалес.', '', '', '', '493ab22ff7c1f23249514b4c6f715e63'),
(974, 1, 1, 1, 1448972765, 1448972765, 127, 83, 4, '[Фалеса] спросили, что на свете трудно? – «Познать себя». Что легко? – «Советовать другому».', '', '', '', 'dfd5f950262b40eddac937abdb452e3f'),
(975, 1, 1, 1, 1448972765, 1448972765, 127, 83, 5, 'Какая жизнь самая лучшая? – «Когда мы не делаем того, что осуждаем в других».', '', '', '', '099e3ff1a8843350b1f39fbb575d7c9b'),
(976, 1, 1, 1, 1448972765, 1448972765, 127, 83, 6, 'На вопрос, что он видел самого удивительного, Фалес ответил: «Тирана в старости».', '', '', '', '6348da767e64ca42423c347ff5076e01'),
(977, 1, 1, 1, 1448972765, 1448972765, 127, 83, 7, 'Из диких [животных] хуже всех тиран, из домашних – льстец.', '', '', '', '6009b9a8fe301fab82dc83d135099a5b'),
(978, 1, 1, 1, 1448972765, 1448972765, 127, 83, 8, 'Врагам и в вероятном не верь, друзьям и в невероятном верь.', '', '', '', 'e50aaf34db1b93bd1b3e231da96ad8f8');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(979, 1, 1, 1, 1448972765, 1448972765, 127, 83, 9, 'Фалес, которому мать неотступно советовала жениться, уклонялся от ее настояний, говоря сначала: «Еще не время, матушка», а позднее: «Уже не время, матушка».', '', '', '', '61f6bdd985792cedf4b30387e2c593e6'),
(980, 1, 1, 1, 1448972765, 1448972765, 127, 83, 10, 'Фалес (…) будто бы (…) утверждал, что за три вещи благодарен судьбе: во-первых, что он человек, а не животное; во-вторых, что он мужчина, а не женщина; в-третьих, что он эллин, а не варвар.', '', '', '', '6e79e69175bb4b3e0ad1910f9488bf25'),
(981, 1, 1, 1, 1448972765, 1448972765, 128, 83, 11, 'Никого при жизни нельзя считать счастливым.', '', '', '', 'c365a2ff06909ffa71784e0ba3eda4fe'),
(982, 1, 1, 1, 1448972765, 1448972765, 128, 83, 12, 'Во всяком деле нужно иметь в виду его исход, чем оно кончится.', '', '', '', 'f1cf68975988d082544069a511275f10'),
(983, 1, 1, 1, 1448972765, 1448972765, 128, 83, 13, 'Ничего слишком!', '', '', '', '9db97552aa6230e01032884395ac2930'),
(984, 1, 1, 1, 1448972765, 1448972765, 128, 83, 14, 'Чего не клал, того не бери.', '', '', '', '31a06a82975b5b2830b942561f14bd7e'),
(985, 1, 1, 1, 1448972765, 1448972765, 128, 83, 15, 'Не советуй угодное, советуй лучшее.', '', '', '', 'd2aecb1457fc75ce87fd079b11b17b9a'),
(986, 1, 1, 1, 1448972765, 1448972765, 128, 83, 16, 'О тайном догадывайся по явному.', '', '', '', 'd50681b1c62d3a56fff06395ad45ed57'),
(987, 1, 1, 1, 1448972765, 1448972765, 128, 83, 17, 'Прежде чем приказывать, научись повиноваться.', '', '', '', 'e5a10a9d06b64e05a6a73bda7ec61f6c'),
(988, 1, 1, 1, 1448972766, 1448972766, 128, 83, 18, 'Когда его [Солона] спросили, самые ли лучшие законы он дал афинянам, он ответил: «Да, самые лучшие из тех, какие они могли принять».', '', '', '', '073a985a7d810f7e68f5295f67fef2b1'),
(989, 1, 1, 1, 1448972766, 1448972766, 128, 83, 19, 'Законы подобны паутине: если в них попадется бессильный и легкий, они выдержат, если большой – он разорвет их и вырвется.', '', '', '', 'e8c7f15d984231b2617f33964ace0b6b'),
(990, 1, 1, 1, 1448972766, 1448972766, 128, 83, 20, 'На вопрос, почему он [Солон] не установил закон против отцеубийц, он ответил: «Чтобы он не понадобился».', '', '', '', 'e2fccd0a1760bf073ab510d86166d4b2'),
(991, 1, 1, 1, 1448972766, 1448972766, 128, 83, 21, 'Знаешь – так молчи.', '', '', '', 'cb4c23fec5744bad72025c95c95f42e5'),
(992, 1, 1, 1, 1448972766, 1448972766, 128, 83, 22, 'Когда Питтак спросил молчавшего за столом Солона, отчего он не говорит – оттого ли, что не находит предмета для разговора, или по глупости, он получил ответ: «Ни один дурак не может молчать за столом».', '', '', '', '21fe49c7348853daf7a3ab3af8a94535'),
(993, 1, 1, 1, 1448972766, 1448972766, 128, 83, 23, 'Когда он оплакивал сына, кто-то ему сказал: «Ведь это бесполезно!» – «Оттого и плачу, что бесполезно», – ответил Солон.', '', '', '', 'cced16e1c5ebb78658849dfef6dfa866'),
(994, 1, 1, 1, 1448972766, 1448972766, 128, 83, 24, 'Питтак (…) уселся у алтаря как проситель и требовал освободить его от власти. На расспросы митиленцев о причине этого Питтак ответил, что трудно быть благородным. Узнав об этом, Солон сказал: «Прекрасное – трудно», и отсюда эти слова вошли в поговорку.', '', '', '', 'b9837fa983f753ff6ac58c8c8a100dad'),
(996, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Голос истины неизящен, а изящная речь лжива. Нравственный человек не красноречив, а красноречивый - лжец.', '', '', '', '2fe02d75d8283929b1f6797ed4ee06ba'),
(997, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Закон достойных - творить добро и не ссориться.', '', '', '', '8dbf8bfedc91ce7e0c22d8e4b061abe1'),
(998, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Великий человек держится существенного и оставляет ничтожное. Он все делает по правде, но никогда не будет опираться на законы.', '', '', '', '44eacde9dc45594dde81292f95a0045e'),
(999, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Легко достигнутое согласие не заслуживает доверия.', '', '', '', '49dd6c833467121c1afde94ef38abb97'),
(1000, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Голос истины противен слуху.', '', '', '', '3049035de15f05164b49b417fde8159c'),
(1001, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто, предпринимая дело, спешит наскоро достичь результата, тот ничего не сделает. Кто осторожно оканчивает свое дело, как начал, тот не потерпит неудачи.', '', '', '', '132b76f29628b74727220124d658bd59'),
(1002, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Хотя в мире нет предмета, который был бы слабее и нежнее воды, но она может разрушить самый твердый предмет.', '', '', '', 'eb7e4203a8f6cf9e53fd11ea595b6f90'),
(1003, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто делает вид, что много знает и ко всему способен, тот ничего не знает и ни к чему не способен.', '', '', '', '9980a92804d342fb7f26d76211263bde'),
(1004, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто думает, что постиг всё, тот ничего не знает.', '', '', '', 'bf570f894c8ad749c0be6129ce2a78a7'),
(1005, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Знающий людей разумен, а знающий себя самого прозорлив.', '', '', '', '97f07ad7574f82f74a00613a8d9a0e49'),
(1006, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Знающий много молчалив, а говорящий много не знает ничего.', '', '', '', '1051d5e759e4cef23a198e7921f15b77'),
(1007, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Истинно просвещенный человек никогда не воюет.', '', '', '', '1bbeada09ecc9e2987bcda9e762eae80'),
(1008, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто, зная много, держит себя, как не знающий ничего, тот - нравственный муж.', '', '', '', 'cf976c3876662cda96a40a88a2576f87'),
(1009, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Умные не бывают учены; ученые не бывают умны.', '', '', '', '4c385bb9f89d9dbbd904b17ac1fe7161'),
(1010, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Достойный муж всегда старается быть беспристрастным, не придавать ценности труднодобываемым вещам и не слушать бесплодного учения.', '', '', '', '34ee940b525310d5d315ad0de06868ae'),
(1011, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Достойный муж делает много, но не хвалится сделанным; совершает заслуги, но не признает их, потому что он не желает обнаружить свою мудрость.', '', '', '', '54e5d048f7ffac0d7a335fe13deba8f5'),
(1012, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Мудрый человек(...) не выставляет себя на свет, поэтому блестит; он не говорит о себе, поэтому он славен; он не прославляет себя, поэтому он заслужен; он не возвышает себя, поэтому он является старшим среди других.', '', '', '', '773f1ae68296c8122fbeeb1e8b96f80f'),
(1013, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Мудрец избегает всякой крайности.', '', '', '', '98c39bbcc5224bd0836f4b6690183f66'),
(1014, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто много говорит, тот часто терпит неудачу.', '', '', '', '015dabdf87fceb5460d1639339058861'),
(1015, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Хотя война ставит, быть может, целью спокойствие, но она несомненное зло.', '', '', '', 'bd2475baeb1d45018e1a28303de501dd'),
(1016, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Причина того, что трудно управлять народом, заключается в том, что народ просвещается и в нем много умных.', '', '', '', '8ca1174e633cbedd44e641b84aa2b6a5'),
(1017, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Когда множатся законы и приказы, растет число воров и разбойников.', '', '', '', 'bcdc6d7fd35d4547675e5c293f8164a7'),
(1018, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Люди высшей нравственности не считают себя нравственными; поэтому они имеют высшую нравственность.', '', '', '', 'c6e16871022e682ad23a913a46612168'),
(1019, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Когда вы благополучны, то подумайте, что нужно предпринять во время беды, так как великая беда начинается с незначительной.', '', '', '', 'db8db83a7cbf750b015c820553c448da'),
(1020, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Беда всего мира происходит из мелочи, как великое дело - из малых.', '', '', '', 'fc39b873fd74939bad563c889fc5a049'),
(1021, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Достойный муж надевает на себя худую одежду, но в себе имеет драгоценный камень.', '', '', '', '31394adface1ff646f024c55a5f78401'),
(1022, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Только что распустившееся растение нежно и слабо. Засохшее растение твердо и не гибко. Отсюда ясно, что нежное и слабое живет.', '', '', '', '530392a577355035de02de64da9e5d6d'),
(1023, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Побеждающий других силен, а побеждающий самого себя могуществен.', '', '', '', '20951a1c91c7fb9c00c13d7777b23d9c'),
(1024, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Превосходный воин никогда не разгневается.', '', '', '', '1ae813073b178254420b806d54b2ae9f'),
(1025, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Не ссорящийся не осуждается.', '', '', '', '0daaaac75205a2b5478eac94195371ed'),
(1026, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто, зная границы своей деятельности, не приблизится к опасностям, тот будет жить долго.', '', '', '', '012583d5e0c4c08e6cabf27db21a1ef2'),
(1027, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Нет греха тяжелее страстей.', '', '', '', '6df163e7c6e8503bb6505345ac1558d0'),
(1028, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Нет беды тяжелее незнания удовлетворения.', '', '', '', '29a21459112b6a3d68186b103de4963d'),
(1029, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Знающий меру доволен своим положением.', '', '', '', '09de0535ce2cfbf263a26d23e30916ef'),
(1030, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Воздержание - это первая ступень добродетели, которая и есть начало нравственного совершенства.', '', '', '', 'ba0139ed30fc52a59cf39c9c6d130f84'),
(1031, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто ведет войну ради человеколюбия, тот победит врагов.', '', '', '', 'f551cd8bb0f5ca5029f3cd9bf8b65e01'),
(1032, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто храбр, не зная человеколюбия, кто щедр, не зная бережливости, кто идет вперед, не зная смирения, тот погибнет.', '', '', '', '4993717006fc318b92445c631105e3c2'),
(1033, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Нет беды тяжелее, чем презирать врагов.', '', '', '', 'cf7a10503f86a8d04d9e2ed766102f9e'),
(1034, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Кто, не зная ничего, держит себя как знающий много, тот болен.', '', '', '', '967248ffbdf4b6640455c9170ca4ca43'),
(1035, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Почесть и позор от сильных мира (для мудреца одинаково странны.', '', '', '', '1b7874f63c4e58e539fe383c0d8f1a7b'),
(1036, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Довольствующийся самим собой - богач.', '', '', '', 'ae94beadfbb2eabcd53606363d030497'),
(1037, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Потеря есть начало размножения, множество - начало потери.', '', '', '', 'a27a7bdd9ce2a8f13e898899b348593f'),
(1038, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Если вещь не годна для одной цели, ее можно употребить для другой.', '', '', '', 'e92be986374d62094b768475459588b1'),
(1039, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Когда нет врагов, то не бывает войны.', '', '', '', 'cafbb3f620b906bbaeb451f5597a52ca'),
(1040, 1, 1, 1, 1448972766, 1448972766, 129, 0, 0, 'Будьте внимательны к своим мыслям - они начало поступков.', '', '', '', '17ab1783ba3898ccdbe3aa6ed3dcbd87'),
(1041, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Тот, кто красиво говорит и обладает привлекательной наружностью, редко бывает истинно человечен.', '', '', '', '45062ff74879931e843015f531a6df45'),
(1042, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Достойный человек не может не обладать широтой познаний и твердостью духа. Его ноша тяжела, а путь его долог.', '', '', '', '5458aa3994b429518205bac1ffda0a3b'),
(1043, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Мудрый не знает волнений, человечный не знает забот, смелый не знает страха.', '', '', '', 'ede5fb1889228f1a1a6a55857eea5089'),
(1044, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Превозмогать себя и возвращаться к должному в себе вот что такое истинная человечность. Быть человечным или не быть - это зависит только от нас самих.', '', '', '', '21421917050988acc7ef0aec4d408b0b'),
(1045, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'По своим природным задаткам люди друг другу близки, а по своим привычкам друг от друга далеки.', '', '', '', '9140ce624f2910c03a70393240891a71'),
(1046, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Совершенный человек все ищет в себе, ничтожный - в других.', '', '', '', '4458ef211c0978a70f64407598d6a761'),
(1047, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Учиться и, когда придет время, прикладывать усвоенное к делу - разве это не прекрасно! Беседовать с другом, приехавшим издалека, - разве это не радостно! Не быть по достоинству оцененным светом и не таить обиду - разве это не возвышенно!', '', '', '', '2b78933a0f2ba184102fa1968cc6615c'),
(1048, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Самые достойные мужи избежали оков целого света, за ними шли те, которые избежали привязанности к определенному месту, за ними - те, которые избежали соблазнов плоти, за ними - те, которые смогли избежать злословия.', '', '', '', '8b73c17f913ffc24b02e9f9b8bf583f9'),
(1049, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Того, кто не задумывается о далеких трудностях, непременно поджидают близкие неприятности.', '', '', '', 'cdb90ba05c9fe552d09eb2e9c2b780e0'),
(1050, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Благородный муж с достоинством ожидает велений Неба. Низкий человек суетливо поджидает удачу.', '', '', '', '61ca69b4df9f287fb82f1422596c6bef'),
(1051, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Достойный человек не идет по следам других людей.', '', '', '', '9e442db6f4ff54239983a517267fd54b'),
(1052, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Благородный муж в душе безмятежен. Низкий человек всегда озабочен.', '', '', '', '65ace44c19bb4d9480a4aed8e327dba2'),
(1053, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Ученый, ищущий истину, но стыдящийся бедной одежды и грубой пищи! О чем тут еще говорить!', '', '', '', 'c132456cd88c567d543095417ad3aa28'),
(1054, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Утром познав истину, вечером можно умереть.', '', '', '', '4416b80444ba75299f78bcc5116ce111'),
(1055, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Стрельба из лука учит нас, как надо искать истину. Когда стрелок промахивается, он не винит других, а ищет вину в самом себе.', '', '', '', '58d89d457de148c9e49db863ad04748d'),
(1056, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Оценивая мирские дела, благородный муж ничего не отвергает и не одобряет, а все меряет справедливостью.', '', '', '', '59761a1a55ba71be88f8ba5523d15dbb'),
(1057, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Истинно человечный муж добивается всего собственными усилиями.', '', '', '', '80ce32f8c6e69cd4be1be184482f8f69'),
(1058, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Благородный муж превыше всего почитает долг. Благородный муж, наделенный отвагой, но не ведающий долга, может стать мятежником. Низкий человек, наделенный отвагой, но не ведающий долга, может пуститься в разбой.', '', '', '', '50a264cc370023f1ccaff291598b6514'),
(1059, 1, 1, 1, 1448972766, 1448972766, 130, 0, 0, 'Каждый может стать благородным мужем. Нужно только решиться им стать.', '', '', '', 'f57a9a6c89d8313baaa3a14d6fd35a1c'),
(1060, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Слово должно быть верным, действие должно быть решительным.', '', '', '', '95df3fd86914d9675248ac7c86c7e424'),
(1061, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный человек знает только долг, низкий человек знает только выгоду.', '', '', '', '3726a6b1a8ed2f5fb9f3165d500fb1e4'),
(1062, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Если в человеке естество затмит воспитанность, получится дикарь, а если воспитанность затмит естество, получится знаток писаний. Лишь тот, в ком естество и воспитанность пребывают в равновесии, может считаться достойным мужем.', '', '', '', '14c0403f6983469cb6225be2dbe78e7a'),
(1063, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Тот, кто учится, не размышляя, впадет в заблуждение. Тот, кто размышляет, не желая учиться, окажется в затруднении.', '', '', '', 'c27f124932b9a1d7a12473cc927873e0'),
(1064, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Давай наставления только тому, кто ищет знаний, обнаружив свое невежество. Оказывай помощь только тому, кто не умеет внятно высказать свои заветные думы. Обучай только того, кто способен, узнав про один угол квадрата, представить себе остальные три.', '', '', '', '09fa43a529b78c3fa731f72ff30176a7'),
(1065, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Даже в обществе двух человек я непременно найду, чему у них поучиться. Достоинствам их я постараюсь подражать, а на их недостатках сам буду учиться.', '', '', '', '4856890b59963bb8b941b139509f0cf4'),
(1066, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Почтительность без знания должного превращается в самоистязание. Осторожность без знания должного превращается в трусость. Храбрость без знания должного превращается в безрассудство. Прямодушие без знания должного превращается в грубость.', '', '', '', 'f3c093b1bf3edea38f607c3b99c5d3f9'),
(1067, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Нелегко встретить человека, который, отдав учению три года жизни, не мечтал бы занять высокий пост.', '', '', '', 'a0352a862547ba167c82f92e8e89959a'),
(1068, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'В древности люди учились для того, чтобы совершенствовать себя. Ныне учатся для того, чтобы удивить других.', '', '', '', '6ab6779180c3c23889094abc31cfdabb'),
(1069, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Учитесь так, словно вы постоянно ощущаете нехватку своих знаний, и так, словно вы постоянно боитесь растерять свои знания.', '', '', '', '4d92b163fc629234a9dc844b8270c3cd'),
(1070, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Только самые мудрые и самые глупые не поддаются обучению.', '', '', '', '8b3ad141c266322dd5f35c7a48179ed3'),
(1071, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Не познав судьбы, нельзя стать благородным мужем. Не познав должного, нельзя обрести опору в жизни. Не научившись понимать истинный смысл слов, нельзя знать людей.', '', '', '', 'f6ad891b373f3b218183572eee591184'),
(1072, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Блажен тот, кто ничего не знает: он не рискует быть непонятым.', '', '', '', '1e138e24e1da9322ab71885921760df8'),
(1073, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Не беспокойся о том, что люди тебя не знают, но беспокойся о том, что ты не знаешь людей.', '', '', '', '885e90b11da9b8ed0b2001979dab95f0'),
(1074, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж не стремится есть досыта и жить богато. Он поспешает в делах, но медлит в речах. Общаясь с людьми добродетельными, он исправляет себя.', '', '', '', '36187d5a240abb4e4e96a9d3be10ffcf'),
(1075, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Тот, кто, обращаясь к старому, способен открывать новое, достоин быть учителем.', '', '', '', '2a763f287e274c6bdf521ab6d96c4a78'),
(1076, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Учитель сказал: &quot;Мое дело, кажется, безнадежно. Я еще не встречал человека, который, зная о своих ошибках, признал бы свою вину перед самим собой&quot;.', '', '', '', '7ec17d0b55c2c1509e2bd647df4e5fef'),
(1077, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Единственная настоящая ошибка - не исправлять своих прошлых ошибок.', '', '', '', '0cb42b77a2a697c22cae25b1f003a07f'),
(1078, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж ни от кого не ожидает обмана, но когда его обманывают, он первый замечает это.', '', '', '', '2b03efe2f26078d4c3e2f5ac3291f786'),
(1079, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Не поговорить с человеком, который достоин разговора, - значит потерять человека. А говорить с человеком, который разговора не достоин, - значит терять слова. Мудрый не теряет ни людей, ни слов.', '', '', '', '23e1d173ef67fb99c322c6d46bbae231'),
(1080, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Каждый ошибается в зависимости от своей пристрастности. Вглядись в ошибки человека познаешь степень его человечности.', '', '', '', '6e836e76ff9af1f9da8d0babf41986a2'),
(1081, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Не огорчаюсь, если люди меня не понимают, огорчаюсь, если я не понимаю людей.', '', '', '', '555b59965c331cff20eb318ec4d5fe5b'),
(1082, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Радом с благородным мужем допускают три ошибки: с ним говорить, когда слова не доходят до него, - это опрометчивость; не говорить, когда слова бы до него дошли, - это скрытность; и говорить, не наблюдая за выражением его лица, - это слепота.', '', '', '', '4cd5b2dbe735a6dc7a6dbc9d33f744be'),
(1083, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Не происходит изменений лишь с высшей мудростью и низшей глупостью.', '', '', '', '73e1433a5419bfae84826b3c842b72c0'),
(1084, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Три пути у человека, чтобы разумно поступать: первый, самый благородный, - размышление; второй, самый легкий, - подражание; третий, самый горький, - опыт.', '', '', '', '8240d40a7cbdd249c0f3856ae0507d11'),
(1085, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Люди в древности не любили много говорить. Они считали позором для себя не поспеть за собственными словами.', '', '', '', '62ba225c6dc7bdca61bf9d070e51e890'),
(1086, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Достаточно, чтобы слова выражали смысл.', '', '', '', '4e9bc55a54c455d30eee730ee1fafc9e'),
(1087, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Молчание - верный друг, который никогда не изменит.', '', '', '', 'c612e8d4b48257babb13b110de150b4d'),
(1088, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Жаловаться на неприятную вещь - это удваивать зло; смеяться над ней - это уничтожить его.', '', '', '', 'af4effa0a42f694427afa7d356098edd'),
(1089, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Когда не знаешь слов, нечем познавать людей.', '', '', '', '24ec72d9689bc4a1130681ac5052c623'),
(1090, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж стремится говорить косноязычно, а действовать искусно.', '', '', '', 'a0e338840b464261aef51f5422a897af'),
(1091, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'В стране, где есть порядок, будь смел и в действиях, и в речах. В стране, где нет порядка, будь смел в действиях, но осмотрителен в речах.', '', '', '', '39c06292edef7e7a1b328e5f8aa3febe'),
(1092, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Однажды Конфуций проезжал неподалеку от горы. Какая-то женщина в голос рыдала над могилой. Склонившись в знак почтения на передок колесницы, Конфуций слушал ее рыдания. А затем послал к той женщине своего ученика, и тот спросил ее:- Вы так убиваетесь - похоже, скорбите не впервой?- Так оно и есть, - ответила женщина. - Когда-то от когтей тигра погиб мой свекор. После от них же погиб мой муж. А теперь вот от них погиб и мой сын.- Отчего же вы не покинете эти места? - спросил Конфуций.- Здесь нет жестоких властей, - ответила женщина.- Запомни это, ученик, - сказал Конфуций. - Жестокая власть - свирепее тигра.', '', '', '', '26ea22330d9f28817985d910a85ee8b9'),
(1093, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Добродетель не останется в одиночестве. У нее обязательно найдутся соседи.', '', '', '', '88eaac32de1abad7c78a168c000ae55a'),
(1094, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж помогает людям увидеть то, что есть в них доброго, и не учит людей видеть то, что есть в них дурного. А низкий человек поступает наоборот.', '', '', '', '8b2b6d80e543f04f985183be9ed0abb0'),
(1095, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Если человек тверд, решителен, прост и несловоохотлив, то он уже близок к человечности.', '', '', '', 'c30e93dd5293a1c4ba97cb429a275f62'),
(1096, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Попытайтесь быть хотя бы немного добрее, и вы увидите, что окажетесь не в состоянии совершить дурной поступок.', '', '', '', '1db2f69f6755810063614b3c87f365a3'),
(1097, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Прекрасно там, где пребывает милосердие. Разве достичь мудрости, если не жить в его краях?', '', '', '', 'ba12df8185e9c14ae2da432ea7b0945b'),
(1098, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Посылать людей на войну необученными - значит предавать их.', '', '', '', '4ba7a424e627cb28b066cd42c741e523'),
(1099, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Не имей друзей, которые бы уступали тебе в моральном отношении.', '', '', '', 'bcc3a8832efce0bf12d587f0ffcd7337'),
(1100, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Только истинно человечный человек способен и любить, и ненавидеть.', '', '', '', '30816b49845cf5f941b26512084ef8cd'),
(1101, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Почтительный сын - это тот, кто огорчает отца и мать разве что своей болезнью.', '', '', '', '97e1fc79f731359641ca0cfab312deca'),
(1102, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Служа отцу с матерью, увещевайте их как можно мягче. Если ваши советы не возымеют действия, будьте по-прежнему почтительны и смиренны. Даже если вы раздосадованы в душе, не высказывайте своего недовольства.', '', '', '', '2028ddb26a2c46b1062ee2297cab5c7b'),
(1103, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Тот, кто не может наставить к добру своих домашних, не может учиться сам.', '', '', '', 'be18b37372221fe0146778f43f8d0333'),
(1104, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Ныне почитать родителей - это значит уметь кормить. Но и лошади, и собаки могут тоже получить пропитание. Как же отличить одно от другого, если не будет самой почтительности?', '', '', '', '986f63f06979430ded750df00949642f'),
(1105, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж стойко переносит беды, а низкий человек в беде распускается.', '', '', '', 'c1bf18f83cb1b8fc635e09db66c3d976'),
(1106, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Кто не может сосредоточиться в себе или увлекается чем-нибудь, тот видя не увидит, слыша не услышит, вкушая не различит вкуса.', '', '', '', '45b82f3194f3e275e6c80d913aac2097'),
(1107, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'He беспокойся о том, что у тебя нет высокого чина. Беспокойся о том, достоин ли ты того, чтобы иметь высокий чин. Не беспокойся о том, что тебя не знают. Беспокойся о том, достоин ли ты того, чтобы тебя знали.', '', '', '', '7326c6b00890a5bf75135a8166d4759d'),
(1108, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'При встрече с достойным человеком думай о том, как сравняться с ним. Встречаясь с низким человеком, присматривайся к самому себе и сам себя суди.', '', '', '', '86ad2fadd9494e241b3a1c5afa2e637c'),
(1109, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Если будешь чрезмерно усерден на службе, потеряешь расположение государя. Если будешь чрезмерно радушен в дружбе, потеряешь расположение друзей.', '', '', '', '31eaf031f9242c9a84aa535881957fe3'),
(1110, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'У сдержанного человека меньше промахов.', '', '', '', 'bd7cdc4e838e92a3aa2e53cd6f5d033d'),
(1111, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Будучи вне дома, держите себя так, словно вы принимаете почетных гостей. Пользуясь услугами людей, ведите себя так, словно совершаете торжественный обряд. Не делайте другим того, чего себе не пожелаете. Тогда ни в государстве, ни в семье не будет недовольства.', '', '', '', 'c9ec6212e4c163487352274bdced89a0'),
(1112, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'В отношениях с друзьями советуй им делать лишь то, что они способны сделать, и веди их к добру, не нарушая приличий, но не пытайся действовать там, где нет надежды на успех. Не ставь себя в унизительное положение.', '', '', '', '8563de31a04bedf7753f74bc4b8388f3'),
(1113, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Некто спросил: &quot;Правильно ли говорят, что за зло нужно платить добром?&quot; Учитель сказал: &quot;А чем же тогда платить за добро? За зло надо платить по справедливости, а за добро - добром&quot;.', '', '', '', '5b38bcb842a78c0259ece6e7619fee29'),
(1114, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Будьте строги к себе и мягки к другим. Так вы оградите себя от людской неприязни.', '', '', '', '8027676dd681e318633c108c9898397f'),
(1115, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж знает о своем превосходстве, но избегает соперничества. Он ладит со всеми, но ни с кем не вступает в сговор.', '', '', '', '9ca9b6a977d9641a8ee6f714348d6854'),
(1116, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж в своей жизни должен остерегаться трех вещей: в юности, когда жизненные силы обильны, остерегаться увлечения женщинами; в зрелости, когда жизненные силы могучи, остерегаться соперничества; в старости, когда жизненные силы скудны, остерегаться скупости.', '', '', '', 'b51e7889584c942aeab09098126eacb4'),
(1117, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Строить правильно отношения труднее всего с женщинами и низкими людьми. Если приблизишь их к себе - они станут развязными, если удалишь от себя - возненавидят.', '', '', '', '999dcc8b64eb32e914834261ca4ff203'),
(1118, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Люди хотят для себя богатства и славы; если то и другое нельзя обрести честно, следует их избегать.', '', '', '', '4c65d2ce435b6d25edf747ff6d73e150'),
(1119, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Если у вас есть возможность явить милосердие, не пропускайте вперед даже учителя.', '', '', '', '203c57fc961080d662e843436dd42f6f'),
(1120, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Люди страшатся бедности и безвестности; если того и другого нельзя избежать, не теряя чести, следует их принять.', '', '', '', '5637139351c735c8c850d4b8000e8cc0'),
(1121, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Благородный муж живет в согласии со всеми, а низкий человек ищет себе подобных.', '', '', '', '06d2f0d0e7c993dc8f0452ca1c735785'),
(1122, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Секрет доброго правления: правитель да будет правителем, подданный - подданным, отец - отцом, а сын - сыном.', '', '', '', 'dc7f5702eb9397bfa9bee5ecb41e0cb4'),
(1123, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Не делай другому того, чего себе не пожелаешь.', '', '', '', '7dd1ff09ff45b4f1a5c60170719de877'),
(1124, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Когда пути неодинаковы, не составляют вместе планов.', '', '', '', '2c8cc09448f9fc4cbb9ef7c03453c889'),
(1125, 1, 1, 1, 1448972767, 1448972767, 130, 0, 0, 'Уважать всякого человека, как самого себя, и поступать с ним, как мы желаем, чтобы с нами поступали, - выше этого нет ничего.', '', '', '', 'e1797d4af796f49744587a94bc81e15d'),
(1126, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Не усвоив приличий, не утвердишься.', '', '', '', 'cb07e5d7be1fe2f0fa6d045e8e7f3e36'),
(1127, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Плати за зло чистосердечием, а за добро плати добром.', '', '', '', '813ab6cd41b4b36d38bcf41f1e70cbb6'),
(1128, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Не жалуйтесь по поводу того, что на крыше соседа лежит снег, если ваш собственный порог не очищен.', '', '', '', '28c33b39fef1a35eef3f51d5865f5b4a'),
(1129, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Благородные люди живут в согласии с другими людьми, но не следуют за другими людьми, низкие следуют за другими людьми, но не живут с ними в согласии.', '', '', '', 'd969e9d5a103608667f5fadda7871485'),
(1130, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Владеть собой настолько, чтоб уважать других, как самого себя, и поступать с ними так, как мы желаем, чтобы с нами поступали, - вот что можно назвать учением о человеколюбии.', '', '', '', '8555f5bf879f3c3992e3ba9eeee78d3c'),
(1131, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Если сам прям, то всё исполнят и без приказания. А если сам не прям, то слушаться не будут, даже если им прикажут.', '', '', '', 'a787cf52a046869cb122627310793bf8'),
(1132, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Благородный муж, привязанный к домашнему уюту, не достоин зваться таковым.', '', '', '', 'fbe5e263d9e23d86b05431fb10ae2b4a'),
(1134, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Изощренные слова губят добродетель. Несдержанность в мелочах погубит великое дело.', '', '', '', 'ed834c2cd20f8ec5f901005a5203870c'),
(1135, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Тот, кто, дожив до сорока лет, вызывает лишь неприязнь, конченый человек.', '', '', '', '51b996e23a75ab2aec16b7581083dd99'),
(1136, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Как можно иметь дело с человеком, которому нельзя доверять? Если в повозке нет оси, как можно в ней ездить?', '', '', '', '255a5943af0d866ee2cd6320cc86670d'),
(1137, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Из всех преступлений самое тяжкое - это бессердечие.', '', '', '', '0e336e4d701f7fe065a08e844f7f8278'),
(1138, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Если у тебя не будет дурных мыслей, не будет и дурных поступков.', '', '', '', 'f7a82cf8de77fa9d2fd107898a7554ee'),
(1139, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Сердитый человек всегда полон яда.', '', '', '', '611b553cc887e5ec3a0fed35d7e979b8'),
(1140, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Посещать и слушать злых людей - это уже начало злого дела.', '', '', '', '2aba85b9992a0d63cf522bdeef243fc9'),
(1141, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Благородный муж винит себя, малый человек винит других.', '', '', '', '30d7d2d5242d7e2235d127505cd65c32'),
(1142, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Когда исходят лишь из выгоды, то множат злобу.', '', '', '', '7995895f435ba75f172a50f0f33cc322'),
(1143, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Когда государство управляется согласно с разумом, постыдны бедность и нужда; когда государство не управляется согласно с разумом, то постыдны богатства и почести.', '', '', '', 'b737561d2693e5981eba85c121fd6a11'),
(1144, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'Если мы так мало знаем о жизни, что можем мы знать о смерти?', '', '', '', '634c7ca2dc622f78cca44f4a625c164b'),
(1145, 1, 1, 1, 1448972768, 1448972768, 130, 0, 0, 'К молодым людям нельзя относиться свысока. Очень может быть, что, повзрослев, они станут выдающимися мужами. Только тот, кто ничего не достиг, дожив до сорока или пятидесяти лет, не заслуживает уважения.', '', '', '', '492ed6c7f743f3b6607c864375081440'),
(1146, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Если не знаешь, каковы твои дети, посмотри на их друзей.', '', '', '', '9c44dbc35303b15e578f7fb65b5f8dd2'),
(1147, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Тот, кто правильно указывает на мои ошибки, - мой учитель; тот, кто правильно отмечает мои верные поступки, - мой друг; тот, кто мне льстит, - мой враг.', '', '', '', 'e5a9f35bcaaf267ed54437d9ef905fb0'),
(1148, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Когда совершенный человек обладает большими знаниями да к тому же ежедневно проверяет себя и анализирует свое поведение, тогда он мудр и не совершает ошибок.', '', '', '', '8de03c4ea2ed6460051e07047a3f036c'),
(1149, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Не поднявшись на высокую гору, не узнаешь высоты неба. Не взглянув в глубокое ущелье в горах, не узнаешь толщины земли. Не услышав заветов предков, не узнаешь величия учености.', '', '', '', 'ccef9701def57b20dc066fffd77853c6'),
(1150, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Новорожденные везде плачут одинаково. Когда же они вырастают, у них оказываются неодинаковые привычки. Это - результат воспитания.', '', '', '', 'c553f7231a48b74a7d04b45e3b2610f6'),
(1151, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Беды приходят тогда, когда люди в своей лени забывают заботиться о себе.', '', '', '', 'c4c1a112ed0ed8b4c2364ef3fe777a3c'),
(1152, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'В учении нельзя останавливаться.', '', '', '', '6d245ca9aaaef584d2654a05679f7715');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(1153, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Если будешь поступать слишком жестоко, тебя постигнет неудача; если же будешь действовать слишком мягко, сам окажешься в оковах.', '', '', '', 'ac4a04edb1ae4604349654282a00c3b1'),
(1154, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Дурные качества и поступки человека зависят от него самого.', '', '', '', '1992916319eeeaa5eedce1de482c08cc'),
(1155, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Породистый скакун одним прыжком не сможет покрыть расстояния в тысячу ли. На кляче можно преодолеть это расстояние за десять дней, если не останавливаться на полпути.', '', '', '', '62c3e9f7f738dc8d049b8bdd5deb8fce'),
(1156, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Нет более быстрого пути к овладению знаниями, чем искренняя любовь к мудрому учителю.', '', '', '', '7efa49a6a80df48f30a5f530ccb92dd7'),
(1157, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Земляной червь не имеет острых когтей и клыков, у него нет крепких мышц и костей, и все же на поверхности он питается пылью, а под землей - пьет подземную воду. Это происходит потому, что он весь - старание! Краб имеет восемь ног и две клешни, однако он поселяется в уже готовых ходах, сделанных змеями и угрями, другого пристанища у него нет. Это происходит потому, что краб нетерпелив. Поэтому тот, кто не имеет глубоко скрытых желаний, не сможет обладать блестящей мудростью; тот, кто не отдается целиком делу, не будет иметь блестящих успехов.', '', '', '', '7b5fcb6333fc4610b6d1a4de32e12544'),
(1158, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Учиться надо всю жизнь, до последнего дыхания!', '', '', '', '6639a15a89e5d7adbb43f9320ee4dc22'),
(1159, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Когда задают неприличный вопрос, отвечать на него не следует. Когда рассказывают о неприличном, не следует задавать вопросов. Когда рассказывают неприличные вещи, не следует слушать.', '', '', '', 'ab4b303641fc6796982fdaea0d2d2679'),
(1160, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'С тем, кто любит спорить, не надо вступать в пререкания.', '', '', '', 'd35d392c478e68f6cb10d368abb45cd1'),
(1161, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Когда видишь хорошее, следует отнестись к нему почтительно и проверить, есть ли у тебя эти качества. Когда видишь плохое, следует отнестись к нему с презрением и проверить, нет ли у тебя этих качеств.', '', '', '', '0974980b76d53e52d90f3e614e45faab'),
(1162, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Человек должен делать те дела, которые хотя и требуют тяжелого физического труда, но успокаивают его разум.', '', '', '', 'f4af064e80367b27bfb5cfd8d1a75030'),
(1163, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Когда у человека много свободного времени, он немногого достигнет.', '', '', '', '9e2517f1a604796f251354347222c497'),
(1164, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Полагаться на законы и к тому же понимать их положения - только так можно добиться согласия.', '', '', '', '65b6c2e78f5d36e2f9b97ab99f87720c'),
(1165, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Мудрых и способных людей надо выдвигать на должности независимо от их положения; ленивых и неспособных людей нужно немедленно отстранять от должности; главных злодеев нужно казнить, не дожидаясь их перевоспитания; обычных, средних людей нужно воспитывать, не дожидаясь, когда к ним придется применить меры наказания.', '', '', '', 'e6d6124394c15d7a429274f8f10b6836'),
(1166, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Правителя можно сравнить с лодкой, а народ - с водой: вода может нести лодку, а может ее и опрокинуть.', '', '', '', '052bd56c6e424f9fb13b1ca09d2e87db'),
(1167, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Взимание непомерных налогов - это путь, вызывающий разбой, обогащение врага, приводящий к гибели государства.', '', '', '', 'edbc6713befae9a9edc937da21922fcc'),
(1168, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Заниматься делом - это то, что ненавидят люди; заслуги и власть - это то, что они любят.', '', '', '', '06e7a8c3c5e8009651eccc5c6487d07b'),
(1169, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Физический труд - это то, что служит добродетели.', '', '', '', '544da3bb4bf0e8fd58a58983db1e2e4c'),
(1170, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Когда не обращают внимания на критику и похвалу народа и спокойно мирятся с потерей его симпатий - это недостойный путь управления страной.', '', '', '', 'df00a1f0743f7a2afd0f2fe76d8b1d3f'),
(1171, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'То, что совершается без участия труда человека, и то, что он получает помимо своих желаний, составляет деятельность неба(...) Когда человек отказывается делать то, что ему предназначено, и ждет, что небо сделает все за него, - он заблуждается.', '', '', '', '8dd63da73eebba54f2bed6d9ab237639'),
(1172, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Вместо того чтобы возвеличивать небо и размышлять о нем, не лучше ли самим, умножая вещи, подчинить себе небо? Вместо того чтобы служить небу и воспевать его, не лучше ли, преодолевая небесную судьбу, самим использовать небо в своих интересах?', '', '', '', '83bdb28f7c48d68c659d1f902b2fa4d5'),
(1173, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Готовность пожертвовать собой ради выполнения долга есть основа поддержания жизни.', '', '', '', '1e94d3d077609cd54f6d8046339ac5a8'),
(1174, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Музыка - источник радости мудрых людей, она способна вызывать в народе хорошие мысли, она глубоко проникает в его сознание и легко изменяет нравы и обычаи.', '', '', '', '2658ae7a989f3f46109506bd82db20e8'),
(1175, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Человек по своей природе зол, его добродетельность порождается практической деятельностью.', '', '', '', '0917ff54288221cea59577b077ddbd0b'),
(1176, 1, 1, 1, 1448972768, 1448972768, 131, 0, 0, 'Пусть даже человек от природы и обладает прекрасными свойствами и мудростью - он должен еще получить мудрого учителя и следовать ему, он должен выбирать себе в друзья хороших людей и дружить с ними.', '', '', '', '88b49994626a6d9cf843ad52c85368e7'),
(1177, 1, 1, 1, 1448972768, 1448972768, 132, 83, 25, 'Дело умных – предвидеть беду, пока она не пришла, дело храбрых – управляться с бедой, когда она пришла.', '', '', '', '33ea8f5b0696c6b398ccc6e2fdc3111b'),
(1178, 1, 1, 1, 1448972768, 1448972768, 132, 83, 26, 'На вопрос, что лучше всего, он ответил: «Хорошо делать, что делаешь».', '', '', '', '42480f1d95750d7908ce5f53b2d2ed30'),
(1179, 1, 1, 1, 1448972768, 1448972768, 132, 83, 27, 'На вопрос Креза, какая власть сильней, [Питтак ответил:] «Та, что резана на дереве», то есть законы.', '', '', '', 'ab5ad0342706cc2ddd71b98806e64ece'),
(1180, 1, 1, 1, 1448972768, 1448972768, 132, 83, 28, 'Митиленяне дали ему [Питтаку] надел земли. (…) Он отделил себе от нее лишь малую часть и сказал: «Половина – больше целого!»', '', '', '', '4a9c6ac7c7fb75d114ce6f1814218e91'),
(1181, 1, 1, 1, 1448972768, 1448972768, 132, 83, 29, 'Кто совершит проступок во хмелю, с того взыскание вдвое против трезвого.', '', '', '', '709dfdcc093d95eee8a23aea6ad002a8'),
(1182, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Самое великое, заключающееся в самом малом, есть здравый ум в теле человека.', '', '', '', 'b312c9ea36830aeb38614478277aa51f'),
(1183, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Победы должны быть бескровными.', '', '', '', '709097149a57bac8c9051419cb7c14ff'),
(1184, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Человека выказывает власть.', '', '', '', '096ed658bfafe9593de155f9192a5134'),
(1185, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Задумав дело, не говори о нем: не удастся – засмеют.', '', '', '', 'abf7340dcf40333c4274f53a49023c8c'),
(1186, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Неудачей не кори – бойся себе того же.', '', '', '', 'd6969c536d4cfe6e8f2606f1ec46de3b'),
(1187, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Не злословь ни о друге, ни даже о враге.', '', '', '', '5d95d8e1c98c23f8c9115d21c2566ee7'),
(1188, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Храни правду, верность, опытность, ловкость, товарищество, усердие.', '', '', '', 'e1448e494fdc9e40d00088cfdf1aca5f'),
(1189, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Знай всему пору.', '', '', '', 'e35e46ebca061155dcffd1c82fede453'),
(1190, 1, 1, 1, 1448972768, 1448972768, 132, 83, 0, 'Трудно быть хорошим.', '', '', '', 'ce79d3dafea51889dd20e505d0fa9ac5'),
(1191, 1, 1, 1, 1448972768, 1448972768, 132, 83, 859, 'С неизбежностью и боги не спорят.', '', '', '', '5b0ea637e15f2e050eac8d65d78c7579'),
(1192, 1, 1, 1, 1448972768, 1448972768, 133, 83, 30, 'Жизнь надо размеривать так, будто жить тебе осталось и мало и много.', '', '', '', 'f0c7658aed76801bd02551836d793d80'),
(1193, 1, 1, 1, 1448972768, 1448972768, 133, 83, 31, 'Лучше разбирать спор между своими врагами, чем между друзьями, – ибо заведомо после этого один из друзей станет твоим врагом, а один из врагов – другом.', '', '', '', '3832bb5c2fedab854236be05b9dc396a'),
(1194, 1, 1, 1, 1448972768, 1448972768, 133, 83, 32, 'Друзей [надо] любить так, будто они тебе ответят ненавистью, – ибо большинство людей злы.', '', '', '', '9116352c63b6ad413534af35a6f21a5c'),
(1195, 1, 1, 1, 1448972768, 1448972768, 133, 83, 33, 'Однажды он плыл на корабле среди нечестивых людей; разразилась буря, и они стали взывать к богам. «Тише! – крикнул Биант, – чтобы боги не услышали, что вы здесь!»', '', '', '', '86e79f63e9dc5b78148c31bc4ae45671'),
(1196, 1, 1, 1, 1448972768, 1448972768, 133, 83, 34, 'Один нечестивец стал его спрашивать, что такое благочестие, – Биант промолчал. Тот спросил, почему он молчит. «Потому что ты спрашиваешь не о своем деле», – сказал ему Биант.', '', '', '', '0bf1272540c4ad0294a524b1fd2c61f5'),
(1197, 1, 1, 1, 1448972769, 1448972769, 133, 83, 35, 'Когда его родная Приена была захвачена врагом и все жители бежали, захватив с собой чуть ли не все свое имущество, [Биант] на обращенный к нему чей-то совет поступить таким же образом, ответил: «А я так и поступаю, потому что все свое ношу с собой».', '', '', '', '3798ad28f6508261bbbb791b49aa86f0'),
(1198, 1, 1, 1, 1448972769, 1448972769, 134, 83, 36, 'Дочерей надобно выдавать замуж по возрасту девицами, по разуму женщинами.', '', '', '', '09e79582fe3fe594b3fe788b51075912'),
(1199, 1, 1, 1, 1448972769, 1448972769, 134, 83, 37, 'Нужно услужать друзьям, чтобы укрепить их дружбу, и врагам, чтобы приобрести их дружбу.', '', '', '', '257390d70ac0297797cdb2365af58b41'),
(1200, 1, 1, 1, 1448972769, 1448972769, 134, 83, 38, 'С женой при чужих не ласкайся и не ссорься: первое – знак глупости, второе – бешенства.', '', '', '', 'f48c33f9e2620ed31621d4fde98aa165'),
(1201, 1, 1, 1, 1448972769, 1448972769, 134, 83, 39, 'Пьяного раба не наказывай: покажешься пьян.', '', '', '', 'a6f086d18e6cc4c10ce4cb623c19dc06'),
(1202, 1, 1, 1, 1448972769, 1448972769, 134, 83, 40, 'В счастье не возносись, в несчастье не унижайся.', '', '', '', 'c460672b237e787adc83d3839eefd2c4'),
(1203, 1, 1, 1, 1448972769, 1448972769, 134, 83, 41, 'Лучшее – мера.', '', '', '', 'ca76d9ee9f90d6e897f74b11f6de65ed'),
(1204, 1, 1, 1, 1448972769, 1448972769, 134, 83, 42, 'Бери жену из ровни, ибо, если возьмешь из тех, кто знатнее тебя, приобретешь не родственников, а господ.', '', '', '', '6eb9040f1177cc3f7d7df071793d54ff'),
(1205, 1, 1, 1, 1448972769, 1448972769, 134, 83, 43, 'Насмешкам остряка не смейся, не то будешь ненавистен тем, на кого они направлены.', '', '', '', 'eabb407ecffc978a49605a6e0e1acfb3'),
(1206, 1, 1, 1, 1448972769, 1448972769, 134, 83, 0, 'Добродетели свойственно, а пороку не свойственно избегать несправедливости.', '', '', '', '2b2899760e5dcdf0d6e98a56d7c6eda3'),
(1207, 1, 1, 1, 1448972769, 1448972769, 134, 83, 0, 'В счастье не следует быть чрезмерно самоуверенным, а в беде не следует терять уверенность.', '', '', '', 'dde997328c20e75a52fd8a9c3be9fe82'),
(1208, 1, 1, 1, 1448972769, 1448972769, 134, 83, 0, 'Превратности судьбы умей выносить с благородством.', '', '', '', '57ec2b3379cbe54b04c1eb8f8502dad0'),
(1209, 1, 1, 1, 1448972769, 1448972769, 134, 83, 0, 'Больше слушай, чем говори.', '', '', '', '255c0992e5dfab859637b6f62c06d9b4'),
(1210, 1, 1, 1, 1448972769, 1448972769, 134, 83, 0, 'Силой ничего не верши.', '', '', '', '1bf104e0163cde679a5be9c9f53ff9f4'),
(1211, 1, 1, 1, 1448972769, 1448972769, 134, 83, 0, 'С враждой развязывайся.', '', '', '', 'a85a11b5190f03695d317a0b5ca5b5a7'),
(1212, 1, 1, 1, 1448972769, 1448972769, 134, 83, 0, 'Над осмеиваемыми не тешься – наживешь в них врагов.', '', '', '', '0cdebae74a328a61e84d8d6007bbb1fb'),
(1213, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Ничего сверх меры.', '', '', '', '89b58dc1cf106991190a4ea162e55a84'),
(1214, 1, 1, 1, 1448972769, 1448972769, 135, 83, 44, 'Познай самого себя.', '', '', '', 'eca91fece99c5d3d6df23a3ddd59b127'),
(1215, 1, 1, 1, 1448972769, 1448972769, 135, 83, 45, 'Если тебе причинили ущерб – примирись, если оскорбили – отомсти.', '', '', '', 'aa62aff9ac20260ed137cd7e8e7c63f8'),
(1216, 1, 1, 1, 1448972769, 1448972769, 135, 83, 46, 'Не грозись: это дело бабье.', '', '', '', 'fc877972b11bc60f1afc5dc13ac65b6f'),
(1217, 1, 1, 1, 1448972769, 1448972769, 135, 83, 47, 'Мертвых не хули.', '', '', '', '59cc42bd3134e40cedb65463129d6030'),
(1219, 1, 1, 1, 1448972769, 1448972769, 135, 83, 48, 'Языком не упреждай мысль.', '', '', '', '45e258ffe13425119af6c2e659e7915e'),
(1220, 1, 1, 1, 1448972769, 1448972769, 135, 83, 49, 'Когда говоришь, руками не размахивай – это знак безумства.', '', '', '', '0f5e2459c41e41a9318e3129a7275b27'),
(1221, 1, 1, 1, 1448972769, 1448972769, 135, 83, 50, 'Счастлив тиран, который умрет у себя дома своей смертью!', '', '', '', '57687c454f68c5370b37c8d925132701'),
(1222, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Пробным камнем испытывают золото, а золотом испытывается человек.', '', '', '', '36a168be70d0ea331c6284cf089ffce7'),
(1223, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Не позволяй твоему языку опережать твою мысль.', '', '', '', '92f9baf88d99a0341b15d13c6325bc1a'),
(1224, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Сдерживай язык, особенно в застолье.', '', '', '', '2da54d7d54adb53bf9ecdc1c8216fb64'),
(1225, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Не злословь о ближнем, чтобы не услышать такого, чему сам не порадуешься.', '', '', '', '93d7924038f9f277de917e9f9192f82a'),
(1226, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'За порукой – расплата.', '', '', '', '2d005ae1bc502276b57100988cfa33f9'),
(1227, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Самому умному философу трудно отвечать на глупые вопросы.', '', '', '', '9835d60b707826bc0d40919b0289b758'),
(1228, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'К друзьям спеши проворнее в несчастье, чем в счастье.', '', '', '', '2fe1a6b86d70ee833b9da9a9079cff84'),
(1229, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Чужой беде не смейся.', '', '', '', 'c500a477779117af74ceb90d62f510f1'),
(1230, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Истрачивая деньги на ненужное, ты мало будешь иметь их на необходимое.', '', '', '', '8ab6a30d3dd711ebe91d5860ce812383'),
(1231, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Хорошо начальствовать учись на своем доме.', '', '', '', 'd127aadc375b7b3ca6823b01dd23b444'),
(1232, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Лучше избирай наказание, нежели гнусную корысть, ибо первое огорчит на один раз, а второе навсегда.', '', '', '', 'e0be6db29fae8a18d2f68a9b11d6f13d'),
(1233, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'Избегай постыдного.', '', '', '', '6fbde51527c9eb2a8a1dc74786eeabdb'),
(1234, 1, 1, 1, 1448972769, 1448972769, 135, 83, 0, 'О мертвых следует говорить или хорошо, или ничего.', '', '', '', 'eb73c44f1c88fdef59a8188e66ea619b'),
(1235, 1, 1, 1, 1448972769, 1448972769, 136, 83, 51, 'Анахарсис на вопрос, почему не заводит детей, сказал: из любви к детям.', '', '', '', '376f47508ac8f3fe3458a524b43932c2'),
(1236, 1, 1, 1, 1448972769, 1448972769, 136, 83, 52, 'Анахарсис, посетив Народное собрание [в Афинах], выражал удивление, что у эллинов говорят мудрецы, а дела решают невежды.', '', '', '', '88df79fa13e3d81ea8b717e9cafa368b'),
(1237, 1, 1, 1, 1448972769, 1448972769, 136, 83, 53, 'В Элладе участвуют в состязаниях люди искусные, а судят их неискусные.', '', '', '', '625ffd829262900a410ebdcf1a10b4d3'),
(1238, 1, 1, 1, 1448972769, 1448972769, 136, 83, 54, 'Удивительно (…), как эллины при начале пира пьют из малых чаш, а с полными желудками – из больших.', '', '', '', '91cc3895e121fb6aaf9c8282e0699729'),
(1239, 1, 1, 1, 1448972769, 1448972769, 136, 83, 55, 'Лоза приносит три грозди: гроздь наслаждения, гроздь опьянения и гроздь омерзения.', '', '', '', 'bd18b825a465aa252cb3bd0c2fe5e701'),
(1240, 1, 1, 1, 1448972769, 1448972769, 136, 83, 56, 'Афинянин попрекал его [Анахарсиса], что он скиф; он ответил: «Мне позор моя родина, а ты позор своей родине».', '', '', '', '417b44c10f8af3b59a6a6fbb2b52757d'),
(1241, 1, 1, 1, 1448972769, 1448972769, 136, 83, 0, 'Чтобы не стать пьяницей, достаточно иметь перед глазами пьяницу во всем его безобразии.', '', '', '', '0506250cff417cb7b9fe8b90eef831d3'),
(1242, 1, 1, 1, 1448972769, 1448972769, 136, 83, 0, 'Первая чаша принадлежит жажде, вторая – веселью, третья – наслаждению, четвертая – безумию.', '', '', '', '6d3d493b8cdb9596317d3da4fc3ca48d'),
(1243, 1, 1, 1, 1448972769, 1448972769, 136, 83, 0, 'Безопасные корабли – это вытащенные на берег корабли.', '', '', '', 'd9b1001572ec2632021ddeafdcf7e97c'),
(1244, 1, 1, 1, 1448972769, 1448972769, 136, 83, 0, 'Лучше иметь одного друга многоценного, чем многих малоценных.', '', '', '', 'd72b09722a716bb85c5e81c562a36064'),
(1245, 1, 1, 1, 1448972769, 1448972769, 136, 83, 0, 'Рынок – это место, нарочно назначенное, чтобы обманывать и обкрадывать друг друга.', '', '', '', 'ee983b5154c3259e6bb10f806cf40765'),
(1246, 1, 1, 1, 1448972769, 1448972769, 136, 83, 0, 'Злой человек похож на уголь: если не жжет, то чернит тебя.', '', '', '', 'edab2fa17f42bb5ae4b33ccc07d833b7'),
(1247, 1, 1, 1, 1448972769, 1448972769, 142, 83, 387, 'Ничто не существует; (…) если и существует, то оно не познаваемо (…); если оно и познаваемо, то (…) непередаваемо.', '', '', '', 'ea24cc3253e9b0ca6016da02f0d76b39'),
(1248, 1, 1, 1, 1448972769, 1448972769, 142, 83, 388, 'Трагедия – это обман, где обманщик справедливее честного, а обманутый мудрее необманутого.', '', '', '', 'a994da1b9e112088745185d94530fe16'),
(1249, 1, 1, 1, 1448972769, 1448972769, 142, 83, 389, 'Следует серьезность противника отражать шуткой, а шутку – серьезностью.', '', '', '', 'dd79fb8dfc270b28e12c4eeab9b62f57'),
(1250, 1, 1, 1, 1448972769, 1448972769, 142, 83, 390, 'Кто посеет позор, пожнет несчастье.', '', '', '', '56f5a811a13e7f3af7e6a782661565e1'),
(1251, 1, 1, 1, 1448972769, 1448972769, 142, 83, 391, 'Искусство убеждать людей много выше всех искусств, так как оно делает всех своими рабами по доброй воле, а не по принуждению.', '', '', '', '3f2fb62d0b89527e142c6c9f0502006e'),
(1252, 1, 1, 1, 1448972769, 1448972769, 142, 83, 0, 'Серьезные доводы противника опровергай шуткой, шутки – серьезностью.', '', '', '', 'a3bac3902ec2653a6323cd06d36470bf'),
(1253, 1, 1, 1, 1448972769, 1448972769, 142, 83, 0, 'Слово – величайший владыка: видом малое и незаметное, а дела творит чудесные – может страх прекратить и печаль отвратить, вызвать радость, усилить жалость.', '', '', '', '25a9d8911bdd2f3fe8b86c878bb4a70a'),
(1254, 1, 1, 1, 1448972769, 1448972769, 138, 83, 96, 'Законченные работы он [Апеллес] выставлял в открытой беседке и сам, скрываясь за картиной, выслушивал замечания проходящих, так как считал народ более внимательным судьей, чем самого себя. Однажды, говорят, зритель-сапожник отметил, что на сапоге изображено с внутренней стороны одной петлей меньше должного. На следующий день, возгордившись тем, что указанное им упущение исправлено, сапожник стал изощряться об изображении ноги. Тогда разгневанный художник вышел из укрытия и воскликнул: «Сапожник, суди не выше сапога».', '', '', '', '208f3bf921c970230a6f8e0f6d08140c'),
(1255, 1, 1, 1, 1448972769, 1448972769, 139, 83, 0, 'Несравненный дар – могучая стойкость души. С нею в жизни ничего не страшно.', '', '', '', '1df2888938f342948006bcc251082e85'),
(1256, 1, 1, 1, 1448972769, 1448972769, 140, 83, 289, 'Будь в моем распоряжении другая земля, на которую можно было бы встать, я сдвинул бы с места нашу.', '', '', '', 'e06b158dfd5d21855864a0d9b11fc782'),
(1257, 1, 1, 1, 1448972769, 1448972769, 140, 83, 0, 'Дайте мне точку опоры, и я сдвину Землю.', '', '', '', '3881719dabb2c814312490a4a68c4daf'),
(1258, 1, 1, 1, 1448972769, 1448972769, 139, 0, 0, 'Все созидает для смертных забота и труд человека.', '', '', '', '6b136b02bbc1b642fb1162e635711a5e'),
(1259, 1, 1, 1, 1448972769, 1448972769, 139, 0, 0, 'Все человеку, Перикл, судьба посылает и случай.', '', '', '', 'ff1e629fdf2494d55caa5683b0cb380d'),
(1260, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'То, что мы принимаем за  добродетель,  нередко  оказывается  сочетанием корыстных  желаний  и  поступков,  искусно  подобранных  судьбой  или  нашей собственной хитростью; так, например, порою женщины бывают  целомудренны,  а мужчины - доблестны совсем  не  потому,  что  им  действительно  свойственны целомудрие и доблесть.', '<b>"Максимы и моральные размышления"</b> - № 1', '', '', '242aca6d85e999b8c77c737e39b766bb'),
(1261, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Ни один льстец не льстит так искусно, как себялюбие.', '<b>"Максимы и моральные размышления"</b> - № 2', '', '', 'de9c2f24acb7da4d9844985c77e69c0e'),
(1262, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Сколько ни сделано  открытий  в  стране  себялюбия,  там  еще  осталось вдоволь неисследованных земель.', '<b>"Максимы и моральные размышления"</b> - № 3', '', '', '94a457608b51166311a8a4b4aa3f8c68'),
(1263, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Ни один хитрец не сравнится в хитрости с самолюбием.', '<b>"Максимы и моральные размышления"</b> - № 4', '', '', '9277f6204b0c0472bd247d983c543bd8'),
(1264, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Долговечность наших страстей не более зависит от нас, чем долговечность жизни.', '<b>"Максимы и моральные размышления"</b> - № 5', '', '', '259e19a34bfcf6f262e2edf4ca98f056'),
(1265, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Страсть часто превращает умного человека в глупца, но  не  менее  часто наделяет дураков у мои.', '<b>"Максимы и моральные размышления"</b> - № 6', '', '', 'a69914b272c529177f8bb793b39b4c03'),
(1266, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Великие исторические деяния, ослепляющие нас своим блеском и  толкуемые политиками как следствие великих замыслов, чаше всего являются  плодом  игры прихотей и страстей. Так, война между Августом и Антонием, которую объясняют их честолюбивым желанием властвовать  над  миром,  была,  возможно,  вызвана просто-напросто ревностью.', '<b>"Максимы и моральные размышления"</b> - № 7', '', '', '628bb28c90313913a79633b94f9260f2'),
(1267, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Страсти - это единственные ораторы, доводы которых всегда  убедительны; их искусство рождено  как  бы  самой  природой  и  зиждется  на  непреложных законах.  Поэтому  человек  бесхитростный,  но  увлеченный  страстью,  может убедить скорее, чем красноречивый, но равнодушный.', '<b>"Максимы и моральные размышления"</b> - № 8', '', '', '9330da5d08fd806ea903f38375c6cb3f'),
(1268, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Страстям присущи  такая  несправедливость  и  такое  своекорыстие,  что доверять им опасно и следует их остерегаться даже тогда, когда  они  кажутся вполне разумными.', '<b>"Максимы и моральные размышления"</b> - № 9', '', '', '9b645b1ff9736f27f776e72cb40ab30b'),
(1269, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'В человеческом сердце происходит непрерывная смена страстей, и угасание одной из них почти всегда означает торжество другой.', '<b>"Максимы и моральные размышления"</b> - № 10', '', '', '000192565036acfd5c5bb8bb795d1692'),
(1270, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Наши страсти часто  являются  порождением  других  страстей,  прямо  им противоположных: скупость порой ведет к расточительности, а расточительность - к скупости; люди  нередко  стойки  по  слабости  характера  и  отважны  из трусости.', '<b>"Максимы и моральные размышления"</b> - № 11', '', '', '03d8ede5dd2890c9f144e7056abde6a9'),
(1271, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Как бы мы ни старались скрыть наши страсти под  личиной  благочестия  и добродетели, они всегда проглядывают сквозь этот покров.', '<b>"Максимы и моральные размышления"</b> - № 12', '', '', 'a1f60618da7a00ffd42f94142977fb03'),
(1272, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Наше самолюбие больше страдает, когда порицают наши  вкусы,  чем  когда осуждают наши взгляды.', '<b>"Максимы и моральные размышления"</b> - № 13', '', '', '844c897cc60618e21ff1470d36ef3441'),
(1273, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Люди не только забывают благодеяния и обиды, но даже склонны ненавидеть своих благодетелей и прощать обидчиков. Необходимость отблагодарить за добро и отомстить за зло кажется им рабством, которому они не желают покоряться.', '<b>"Максимы и моральные размышления"</b> - № 14', '', '', '16e5d95c3cf67afa9639a08ac6355075'),
(1274, 1, 1, 1, 1448972769, 1448972769, 124, 0, 0, 'Милосердие сильных мира сего чаще  всего  лишь  хитрая  политика,  цель которой - завоевать любовь народа.', '<b>"Максимы и моральные размышления"</b> - № 15', '', '', 'd0f7bdba389badc36d68596469371ef6'),
(1275, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Хотя  все  считают  милосердие  добродетелью,  оно   порождено   иногда тщеславием, нередко ленью, часто страхом, а почти всегда - и тем, и  другим, и третьим.                                      17         Умеренность счастливых  людей  проистекает  из  спокойствия,  даруемого неизменной удачей.', '<b>"Максимы и моральные размышления"</b> - № 16', '', '', '3bef291ef4ba9e6f86d5ee4f6dfe9545'),
(1276, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Умеренность - это боязнь  зависти  или  презрения,  которые  становятся уделом всякого, кто ослеплен своим счастьем; это суетное  хвастовство  мощью ума; наконец, умеренность людей,  достигших  вершин  удачи,  -  это  желание казаться выше своей судьбы.', '<b>"Максимы и моральные размышления"</b> - № 18', '', '', '859ad677c4db870753a1949d51cc221a'),
(1277, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'У нас у всех достанет сил, чтобы перенести несчастье ближнего.', '<b>"Максимы и моральные размышления"</b> - № 19', '', '', '5b9c35ba0d73f0e77aee48abb512cff9'),
(1278, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Невозмутимость мудрецов - это всего лишь умение скрывать свои чувства в глубине сердца.', '<b>"Максимы и моральные размышления"</b> - № 20', '', '', 'd20a24fdfc50e64ecf77826ca6409556'),
(1279, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Невозмутимость, которую проявляют порой осужденные на казнь, равно  как и презрение к смерти, говорит лишь о боязни  взглянуть  ей  прямо  в  глаза; следовательно, можно сказать, что то и другое для их разума - все равно  что повязка для их глаз.', '<b>"Максимы и моральные размышления"</b> - № 21', '', '', '51f49f66b9c5eaeb0971b5f1efcb0dcc'),
(1280, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Философия торжествует над горестями прошлого  и  будущего,  но  горести настоящего торжествуют над философией.', '<b>"Максимы и моральные размышления"</b> - № 22', '', '', 'e36326089514940a1e2f563d15f68baa'),
(1281, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Немногим людям дано постичь, что такое смерть; в большинстве случаев на нее идут не по обдуманному намерению, а по глупости и по заведенному обычаю, и люди чаще всего умирают потому, что не могут воспротивиться смерти.', '<b>"Максимы и моральные размышления"</b> - № 23', '', '', 'e88b7148b68c4acc73bfeb2280d8d28c'),
(1282, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Когда великие люди наконец сгибаются под тяжестью  длительных  невзгод, они этим показывают, что  прежде  их  поддерживала  не  столько  сила  духа, сколько сила честолюбия, и что герои отличаются от обыкновенных людей только большим тщеславием.', '<b>"Максимы и моральные размышления"</b> - № 24', '', '', '9be307e8ed0ccc636b55a1c2dae0fb6c'),
(1283, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Достойно вести себя, когда судьба благоприятствует, труднее, чем  когда она враждебна.', '<b>"Максимы и моральные размышления"</b> - № 25', '', '', '9e7e2205234fd6367718a423361276a0'),
(1284, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Ни на солнце, ни на смерть нельзя смотреть в упор.', '<b>"Максимы и моральные размышления"</b> - № 26', '', '', '6ff794beaefaab8a93eff94de5bb03b6'),
(1285, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Люди часто похваляются самыми  преступными  страстями,  но  в  зависти, страсти робкой и стыдливой, никто не смеет признаться.', '<b>"Максимы и моральные размышления"</b> - № 27', '', '', 'f6219d440b9e86ef280a31934d529984'),
(1286, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Ревность до некоторой степени разумна  и  справедлива,  ибо  она  хочет сохранить нам наше достояние или то, что мы считаем таковым, между  тем  как зависть слепо негодует на то, что какое-то достояние есть и у наших ближних.', '<b>"Максимы и моральные размышления"</b> - № 28', '', '', '0028b2a303680f424ce9950c405355c1'),
(1287, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Зло,  которое  мы  причиняем,  навлекает  на  нас  меньше  ненависти  и преследований, чем наши достоинства.', '<b>"Максимы и моральные размышления"</b> - № 29', '', '', 'ec4e49b8fa7cc8d00796cd968a24dd4c'),
(1288, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Чтобы оправдаться в собственных глазах, мы нередко убеждаем  себя,  что не в силах достичь цели; на самом же деле мы не бессильны, а безвольны.', '<b>"Максимы и моральные размышления"</b> - № 30', '', '', '91ab76962558e6059401cd284355ddb5'),
(1289, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Не будь у нас недостатков, нам было бы не так приятно  подмечать  их  у ближних.', '<b>"Максимы и моральные размышления"</b> - № 31', '', '', '1104752bc0c1b7d2ef96740b416a61cc'),
(1290, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Ревность питается сомнениями; она умирает или переходит в  неистовство, как только сомнения превращаются в уверенность.', '<b>"Максимы и моральные размышления"</b> - № 32', '', '', '58c61fe9d0dd54c6d8e784f90b863ed1'),
(1291, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Гордость всегда возмещает свои убытки и ничего не теряет,  даже  когда, отказывается от тщеславия.', '<b>"Максимы и моральные размышления"</b> - № 33', '', '', 'f4bd9a2d8108f40e986c9f5b629d0b31'),
(1292, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Если бы нас не одолевала гордость, мы  не  жаловались  бы  на  гордость других.', '<b>"Максимы и моральные размышления"</b> - № 34', '', '', '3b0b8ac09a903a3f8f5935b1dc0a762c'),
(1293, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Гордость свойственна всем людям; разница лишь в том, как и когда они ее проявляют.', '<b>"Максимы и моральные размышления"</b> - № 35', '', '', '9586d1bb8a29f708b765dbb3d6091b29'),
(1294, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Природа, в заботе о нашем счастии, не только  разумно  устроила  opганы нашего тела, но еще  подарила  нам  гордость,  -  видимо,  для  того,  чтобы избавить нас от печального сознания нашего несовершенства.', '<b>"Максимы и моральные размышления"</b> - № 36', '', '', '673cc19382d9d5813465ba05b5c41998'),
(1295, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Не доброта, а гордость обычно побуждает нас читать  наставления  людям, совершившим проступки; мы укоряем их не столько для того,  чтобы  исправить, сколько для того, чтобы убедить в нашей собственной непогрешимости.', '<b>"Максимы и моральные размышления"</b> - № 37', '', '', '12cb68d2eb2511dd2043de96dd538a2a'),
(1296, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Мы обещаем соразмерно нашим расчетам, а выполняем обещанное  соразмерно нашим опасениям.', '<b>"Максимы и моральные размышления"</b> - № 38', '', '...нашим опасениям. - Максима имеет прямую связь  с  поведением  АнныАвстрийской, которая давала Ларошфуко большие обещания, но не сдерживала  ихиз-за страха перед Мазарини.', 'ff74d42c7255768ba80f460924a33258'),
(1297, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Своекорыстие говорит на всех языках и разыгрывает  любые  роли  -  даже роль бескорыстия.', '<b>"Максимы и моральные размышления"</b> - № 39', '', '', '4fe54242937e3e8db2b0d90dd4c8701d'),
(1298, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Одних своекорыстие ослепляет, другим открывает глаза.', '<b>"Максимы и моральные размышления"</b> - № 40', '', '', '69227f25f3aa03f2662baad8e87dac93'),
(1299, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Кто слишком усерден  в  малом,  тот  обычно  становится  неспособным  к великому.', '<b>"Максимы и моральные размышления"</b> - № 41', '', '...неспособным к великому. - Обычно эту максиму  связывают  с  именем Людовика XIII, но и узость эгоистических  претензий  друзей-фрондеров  могла вызвать появление этого афоризма Ларошфуко.', '537caae52428a1d70cd626e3b8df2dd6'),
(1300, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'У нас не хватает силы характера, чтобы покорно следовать всем  велениям рассудка.', '<b>"Максимы и моральные размышления"</b> - № 42', '', '', '269896011b1e9b7e4cf0491e1e2b54f5'),
(1301, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Человеку нередко кажется, что он владеет собой, тогда как на самом деле что-то владеет им; пока разумом он стремится к одной цели, сердце  незаметно увлекает его к другой.', '<b>"Максимы и моральные размышления"</b> - № 43', '', '', '9857a2a5fb2cbd0ddfb5b91acb9e90f0'),
(1302, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Сила  и  слабость  духа  -  это  просто   неправильные   выражения:   в действительности же существует лишь хорошее  или  плохое  состояние  органов тела.', '<b>"Максимы и моральные размышления"</b> - № 44', '', '', '0055ade0dc63d86638f22e604bd71cbf'),
(1303, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Наши прихоти куда причудливее прихотей судьбы.', '<b>"Максимы и моральные размышления"</b> - № 45', '', '', '1b9b6cecce57c3af598d5c312bc7a030'),
(1304, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'В привязанности  или  равнодушии  философов   к  жизни  сказывались особенности их себялюбия, которые так же нельзя оспаривать, как  особенности вкуса, как склонность к какому-нибудь блюду или цвету.', '<b>"Максимы и моральные размышления"</b> - № 46', '', '...философов... - имеются в виду древние философы-язычники.', 'a7a1f6e176184a2258c0dc7e7399c2fc'),
(1305, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Все,  что  посылает  нам  судьба,  мы  оцениваем   в   зависимости   от расположения духа.', '<b>"Максимы и моральные размышления"</b> - № 47', '', '', '84004733e993fbd1c4b60b6c99e001e9'),
(1306, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Нам дарует радость  не  то,  что  нас  окружает,  а  наше  отношение  к окружающему, и мы бываем счастливы, обладая тем, что любим, а  не  тем,  что другие считают достойным любви.', '<b>"Максимы и моральные размышления"</b> - № 48', '', '', 'b223339a004100060d485639236d377c');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(1307, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Человек никогда не бывает так счастлив  или  так  несчастлив,  как  это кажется ему самому.', '<b>"Максимы и моральные размышления"</b> - № 49', '', '', 'e0ff3e01ed1d737897e7fd5a07294ade'),
(1308, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Люди, верящие в свои достоинства, считают долгом быть несчастными, дабы убедить таким образом и других и себя в том, что судьба еще не воздала им по заслугам.', '<b>"Максимы и моральные размышления"</b> - № 50', '', '', 'f5b5a6c4b34cab74cb314cade57d8b42'),
(1309, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Что может быть сокрушительнее  для  нашего  самодовольства,  чем  ясное понимание того, что сегодня мы порицаем вещи, которые еще вчера одобряли.', '<b>"Максимы и моральные размышления"</b> - № 51', '', '', '5a379c056cd3d0197392e8c561ef94da'),
(1310, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Хотя  судьбы  людей  очень   несхожи,   но   некоторое   равновесие   в распределении благ и несчастий как бы уравнивает их между собой.', '<b>"Максимы и моральные размышления"</b> - № 52', '', '', 'b18906e1193417c68324c1924dafd89c'),
(1311, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Какими бы преимуществами природа ни наделила человека, создать из  него героя она может, лишь призвав на помощь судьбу.', '<b>"Максимы и моральные размышления"</b> - № 53', '', '', 'd1a8b9b1028ddbd50ea6b813642c722c'),
(1312, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Презрение философов к богатству было вызвано  их  сокровенным  желанием отомстить  несправедливой  судьбе  за  то,  что  она  не  наградила  их   по достоинствам жизненными благами; оно было  тайным  средством,  спасающим  от унижений  бедности,  и  окольным  путем  к  почету,   обычно   доставляемому богатством.', '<b>"Максимы и моральные размышления"</b> - № 54', '', '', '0ed0b91de2341e0793a6173286cd8adf'),
(1313, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Ненависть к людям,  попавшим  в  милость,  вызвана  жаждой  этой  самой милости. Досада на ее отсутствие смягчается и умиротворяется  презрением  ко всем, кто ею пользуется; мы отказываем им в уважении, ибо  не  можем  отнять того, что привлекает к ним уважение всех окружающих.', '<b>"Максимы и моральные размышления"</b> - № 55', '', '', '246fd1717c304be7a6a6738c4988d0e4'),
(1314, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Чтобы упрочить свое положение в свете, люди старательно делают вид  что оно уже упрочено.', '<b>"Максимы и моральные размышления"</b> - № 56', '', '...уже упрочено. -  В  "Мемуарах"  Ларошфуко  говорит  о  герцоге  де Бофоре, который, "чтобы закрепить свое возвышение,  умело  пользовался  этимотличием..., стараясь создать впечатление, что оно уже прочно закреплено".', '14b188684a7f8cb51c755ae1f2087fdb'),
(1315, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Как бы ни кичились люди величием своих деяний, последние  часто  бывают следствием не великих замыслов, а простой случайности.', '<b>"Максимы и моральные размышления"</b> - № 57', '', '', 'b65d30dc1fd895f3d3ffb3a1cd9c6b54'),
(1316, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Наши  поступки  словно  бы  рождаются  под  счастливой  или  несчастной звездой; ей они и обязаны большей частью похвал или порицаний, выпадающих на их долю.', '<b>"Максимы и моральные размышления"</b> - № 58', '', '', 'e47e20ac6279bf811fbe45e660d09f93'),
(1317, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Не бывает обстоятельств столь несчастных, чтобы умный  человек  не  мог извлечь из них какую-нибудь выгоду, но не бывает и столь  счастливых,  чтобы безрассудный не мог обратить их против себя.', '<b>"Максимы и моральные размышления"</b> - № 59', '', '', 'a967fd8dbc7bf69aa8427e04b78f5c58'),
(1318, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Судьба все устраивает к выгоде тех, кому она покровительствует.', '<b>"Максимы и моральные размышления"</b> - № 60', '', '', 'ba35cc9635ce191d3bc8a6567a84a691'),
(1319, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Счастье и несчастье человека в такой, же степени зависят от его  нрава, как от судьбы.', '<b>"Максимы и моральные размышления"</b> - № 61', '', '', '9bada39d230aed7e485e89b67095c8b0'),
(1320, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Искренность - это чистосердечие. Мало кто обладает  этим  качеством,  и то, что мы принимаем за него, чаще всего  просто  тонкое  притворство,  цель которого - добиться откровенности окружающих.', '<b>"Максимы и моральные размышления"</b> - № 62', '', '', '9a283137f9b0c9ada08a87653f265877'),
(1321, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'За отвращением ко лжи нередко кроется  затаенное  желание  придать  вес нашим утверждениям и внушить благоговейное доверие к нашим словам.', '<b>"Максимы и моральные размышления"</b> - № 63', '', '', 'a9d7bbe06a2c5c5ddf085deb187ac87a'),
(1322, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Не так благотворна истина, как зловредна ее видимость.', '<b>"Максимы и моральные размышления"</b> - № 64', '', '', 'd493fd6e19df555a3a07dbaac433c8a1'),
(1323, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Каких только похвал не возносят благоразумию! Однако  оно  не  способно уберечь нас даже от ничтожнейших превратностей судьбы.', '<b>"Максимы и моральные размышления"</b> - № 65', '', '', '60fb98ea5b0ce8d2db27a9c8ceced7c8'),
(1324, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Дальновидный человек должен  определить  место  для  каждого  из  своих желаний и затем осуществлять их по порядку.  Наша  жадность  часто  нарушает этот порядок и заставляет  нас  преследовать  одновременно  такое  множество целей, что в погоне за пустяками мы упускаем существенное.', '<b>"Максимы и моральные размышления"</b> - № 66', '', '', 'b81e83dd60c1888225c452d33d5c090f'),
(1325, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Изящество для тела - это то же, что здравый смысл для ума.', '<b>"Максимы и моральные размышления"</b> - № 67', '', '', '8564e8ca5dae07c4b3eba60f24976b38'),
(1326, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Трудно дать определение любви; о ней можно лишь сказать, что для души - это жажда властвовать, для ума - внутреннее сродство, а для тела - скрытое и утонченное желание обладать, после многих околичностей, тем, что любишь.', '<b>"Максимы и моральные размышления"</b> - № 68', '', '', '6845b35a893d9031e48e26e7b9040486'),
(1327, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Чиста и свободна от влияния других страстей только та  любовь,  которая таится в глубине нашего сердца и неведома нам самим.', '<b>"Максимы и моральные размышления"</b> - № 69', '', '', '324458a051861524f58e9099ab6ff1ac'),
(1328, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Никакое притворство не поможет долго скрывать любовь, когда  она  есть, или изображать - когда ее нет', '<b>"Максимы и моральные размышления"</b> - № 70', '', '', 'e76a0d54ad62255402057d80c69a7d20'),
(1329, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Нет таких людей, которые,  перестав  любить,  не  начали  бы  стыдиться прошедшей любви.', '<b>"Максимы и моральные размышления"</b> - № 71', '', '', '99abf99e9521fd4d23de1c2eb8deaa4f'),
(1330, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Если судить о любви по обычным ее проявлениям,  она  больше  похожа  на вражду, чем на дружбу.', '<b>"Максимы и моральные размышления"</b> - № 72', '', '', '003c1f01e57ad973478668a46c525e0f'),
(1331, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'На свете немало таких женщин, у  которых  в  жизни  не  было  ни  одной любовной связи, но очень мало таких, у которых была только одна.', '<b>"Максимы и моральные размышления"</b> - № 73', '', '', 'ebf444d476ef676666229b15218b4657'),
(1332, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Любовь одна, но подделок под нее - тысячи.', '<b>"Максимы и моральные размышления"</b> - № 74', '', '', '8f944be25cde74a8a0306cfcac6beee3'),
(1333, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Любовь, подобно огню, не знает покоя: она перестает  жить,  как  только перестает надеяться или бояться.', '<b>"Максимы и моральные размышления"</b> - № 75', '', '', '9c52c4dd9f9668e8f58024be4941b3f9'),
(1334, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Истинная любовь похожа на привидение: все о ней говорят, но мало кто ее видел.', '<b>"Максимы и моральные размышления"</b> - № 76', '', '', '800e7e855a02ead064f115a643a65382'),
(1335, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Любовь  прикрывает  своим  именем  самые   разнообразные   человеческие отношения, будто бы связанные с нею, хотя на самом деле она участвует в  них не более, чем дож в событиях, происходящих в Венеции.', '<b>"Максимы и моральные размышления"</b> - № 77', '', '', 'c78c3c95dd5e800e8505294edb8f2f3f'),
(1336, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'У большинства  людей  любовь  к  справедливости  -  это  просто  боязнь подвергнуться несправедливости.', '<b>"Максимы и моральные размышления"</b> - № 78', '', '', 'dd9ca0b14657993547f271e7f9e9ebdf'),
(1337, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Тому, кто не доверяет себе, разумнее всего молчать.', '<b>"Максимы и моральные размышления"</b> - № 79', '', '', '2d99ee18f32567d0499955ef9d744eda'),
(1338, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Мы потому так непостоянны в дружбе, что трудно  познать  свойства  души человека и легко познать свойства его ума.', '<b>"Максимы и моральные размышления"</b> - № 80', '', '', '9b2b124ea6273fa909023afc0990b8db'),
(1339, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Мы способны любить  только  то,  без  чего  не  можем  обойтись;  таким образом, жертвуя собственными интересами  ради  друзей,  мы  просто  следуем своим вкусам и склонностям. Однако именно эти жертвы делают дружбу подлинной и совершенной.', '<b>"Максимы и моральные размышления"</b> - № 81', '', '', '509fb439fa6ab7e37270baa2af9286eb'),
(1340, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Примирение с врагами говорит лишь об  усталости  от  борьбы,  о  боязни поражения и о желании занять более выгодную позицию.', '<b>"Максимы и моральные размышления"</b> - № 82', '', '...выгодную  позицию.  -  Видимо,  Ларошфуко  вспоминал   примирение заклятых врагов, которым заканчивался каждый эпизод Фронды', '8e97767a6260607d81bddeb1bb353e0a'),
(1341, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Люди обычно называют дружбой совместное  времяпрепровождение,  взаимную помощь в  делах,  обмен  услугами  -  одним  словом,  такие  отношения,  где себялюбие надеется что-нибудь выгадать.', '<b>"Максимы и моральные размышления"</b> - № 83', '', '', '7cf4d594e30edf9ca9dcab4000813311'),
(1342, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Не доверять друзьям позорнее, чем быть ими обманутым.', '<b>"Максимы и моральные размышления"</b> - № 84', '', '', 'f46e1d9a1673e9ffab6ae51348ccbd9b'),
(1343, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Мы часто убеждаем себя в том, что действительно  любим  людей,  стоящих над нами; между тем  такая  дружба  вызвана  одним  лишь  своекорыстием:  мы сближаемся с этими людьми не ради того, что хотели бы им дать, а ради  того, что хотели бы от них получить.', '<b>"Максимы и моральные размышления"</b> - № 85', '', '', '454badf7094e44de5caf5513d83ff6d0'),
(1344, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Своим недоверием мы оправдываем чужой обман.', '<b>"Максимы и моральные размышления"</b> - № 86', '', '', 'e6b2c24b6f8f79c5dc17509fd47bd84e'),
(1345, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Люди не могли бы жить в обществе, если бы не водили друг друга за нос.', '<b>"Максимы и моральные размышления"</b> - № 87', '', '', 'a4eb8ce15126773b4da905d15a9b987e'),
(1346, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Самолюбие  увеличивает  или  умаляет   добродетели   наших   друзей   в зависимости от того, насколько мы довольны этими людьми: об их  достоинствах мы судим по их отношению к нам.', '<b>"Максимы и моральные размышления"</b> - № 88', '', '', '7600042a2af5ff539e72fcfa435b2166'),
(1347, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Все жалуются на свою память, но никто не жалуется на свой разум.', '<b>"Максимы и моральные размышления"</b> - № 89', '', '', '8ab91c250114cfa813e49c07ad7bc7ed'),
(1348, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'В   повседневной   жизни   наши   недостатки   кажутся   порою    более привлекательными, чем наши достоинства.', '<b>"Максимы и моральные размышления"</b> - № 90', '', '', '2314ce9fbb2176898f100c513c3faa98'),
(1349, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Самое большое честолюбие прячется и становится незаметным,  как  только его притязания наталкиваются на непреодолимые преграды.', '<b>"Максимы и моральные размышления"</b> - № 91', '', '', '07b0e8bb7e0bea170885a956e3e06fa6'),
(1350, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Вывести   из   заблуждения   человека,   убежденного   в    собственных достоинствах, значит оказать ему  такую  же  дурную  услугу,  какую  некогда оказали  тому  афинскому  безумцу,  который  считал  себя  владельцем   всех кораблей, прибывающих в гавань.', '<b>"Максимы и моральные размышления"</b> - № 92', '', '', '803e197a503fe49bfdf3b64d11cc44f6'),
(1351, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Старики потому так любят давать хорошие советы,  что  уже  не  способны подавать дурные примеры.', '<b>"Максимы и моральные размышления"</b> - № 93', '', '', 'd6d7492cdb4fb67dd5ca390055162c27'),
(1352, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Громкое имя не возвеличивает, а лишь унижает того, кто не умеет  носить его с честью.', '<b>"Максимы и моральные размышления"</b> - № 94', '', '', 'd4ac1021068bdf14fddb128e175e7b1e'),
(1353, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Поистине необычайными достоинствами обладает тот, кто  сумел  заслужить похвалу своих завистников.', '<b>"Максимы и моральные размышления"</b> - № 95', '', '', 'fd5e2796ca8747a2872584b5f98cbeb1'),
(1354, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Неблагодарность остается неблагодарностью даже и в  том  случае,  когда облагодетельствованный повинен в ней меньше, чем благодетель.', '<b>"Максимы и моральные размышления"</b> - № 96', '', '', 'e23b6e64c2e23527a03a1e8e7c8550bf'),
(1355, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Неправ, тот, кто считает,  будто  ум  и  проницательность  -  различные качества. Проницательность - это просто  особенная  ясность  ума,  благодаря которой он добирается до сути вещей, отмечает  все,  достойное  внимания,  и видит невидимое другим. Таким образом, все, приписываемое  проницательности, является лишь следствием необычайной ясности ума.', '<b>"Максимы и моральные размышления"</b> - № 97', '', '', '708a1409a3e3b0e2d43ba3c6cb4e035b'),
(1356, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Все расхваливают свою доброту, но никто не решается похвалить свой ум.', '<b>"Максимы и моральные размышления"</b> - № 98', '', '', '0658da6b83a40b67875715a09560ce68'),
(1357, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Учтивость ума заключается в способности думать достойно и утонченно.', '<b>"Максимы и моральные размышления"</b> - № 99', '', '', 'd9709761cd189b543e3ff0a6acb116f6'),
(1358, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Изысканность ума сказывается в умении тонко льстить.', '<b>"Максимы и моральные размышления"</b> - № 100', '', '', 'f89e5719ebed563ee514a6502dd69db0'),
(1359, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Порою в нашем уме рождаются мысли в форме уже такой  отточенной,  какую он никогда не смог бы придать им, сколько бы ни ухищрялся.', '<b>"Максимы и моральные размышления"</b> - № 101', '', '', 'a75276d5346c754e8c6c2659225eb87c'),
(1360, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Ум всегда в дураках у сердца.', '<b>"Максимы и моральные размышления"</b> - № 102', '', '', '18f69c3d5d7191324f576d58fdef3cd8'),
(1361, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'Не всякий человек, познавший глубины своего ума, познал глубины  своего сердца.', '<b>"Максимы и моральные размышления"</b> - № 103', '', '', '1049177002d011d1d4f90abb1ab5fac8'),
(1362, 1, 1, 1, 1448972770, 1448972770, 124, 0, 0, 'На каждого человека, как и на  каждый  поступок,  следует  смотреть  с. определенного расстояния. Иных можно понять, рассматривая их вблизи,  другие же становятся понятными только издали.', '<b>"Максимы и моральные размышления"</b> - № 104', '', '...только издали. - В  "Мемуарах"  мы  находим  подобные  рассуждения относительно Ришелъе и Бофора.', 'c8d09f817785936fd641906aa8cdce10'),
(1363, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Умен не тот, кого случай делает умным, а тот, кто понимает,  что  такое ум, умеет его распознать и любуется им.', '<b>"Максимы и моральные размышления"</b> - № 105', '', '', '7762b5cff9ef13832f248eb370571570'),
(1364, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Чтобы постичь окружающий нас мир, нужно знать его во всех подробностях, а так как этих подробностей почти бесчисленное множество, то и  знания  наши всегда поверхностны и несовершенны.', '<b>"Максимы и моральные размышления"</b> - № 106', '', '', '2eae2ce78fcd6bfe827e42f5429748aa'),
(1365, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди кокетничают, когда делают вид, будто им чуждо всякое кокетство.', '<b>"Максимы и моральные размышления"</b> - № 107', '', '', '6b1c1e8ff27a2bf41966835d8fb54dad'),
(1366, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Уму не под силу долго разыгрывать роль сердца.', '<b>"Максимы и моральные размышления"</b> - № 108', '', '', 'b6be7d7e1e02bd85f15b93df4a587aff'),
(1367, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Юность меняет свои вкусы из-за пылкости чувств, а старость сохраняет их неизменными по привычке.', '<b>"Максимы и моральные размышления"</b> - № 109', '', '', '811466379531043baacde21d035fd102'),
(1368, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы ничего не раздаем с такой щедростью, как советы.', '<b>"Максимы и моральные размышления"</b> - № 110', '', '', '1105e5f7f52e617b4f2dd5ca079c943b'),
(1369, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Чем сильнее мы любим женщину, тем больше склонны ее ненавидеть.', '<b>"Максимы и моральные размышления"</b> - № 111', '', '', 'f8355e197a2a854df6450be0703cc829'),
(1370, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'К старости недостатки ума становятся все  заметнее,  как  и  недостатки внешности.', '<b>"Максимы и моральные размышления"</b> - № 112', '', '', 'aeff225b39815d9f6f00c24134cbd756'),
(1371, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Бывают удачные браки, но не бывает браков упоительных.', '<b>"Максимы и моральные размышления"</b> - № 113', '', '', '600c81a44fac2dde5a61a106a65b7eac'),
(1372, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди безутешны, когда их обманывают враги или предают  друзья,  но  они нередко испытывают удовольствие, когда обманывают или предают себя сами.', '<b>"Максимы и моральные размышления"</b> - № 114', '', '', '53d5bcc231a0180e9302b8e61f320252'),
(1373, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Так же легко обмануть себя и не заметить  этого,  как  трудно  обмануть другого и не быть изобличенным.', '<b>"Максимы и моральные размышления"</b> - № 115', '', '', '7166c5ede52e7b4fdce5c14264cb6f9e'),
(1374, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Сколько лицемерия  в  людском  обычае  советоваться!  Тот,  кто  просит совета, делает вид, что относится  к  мнению  своего  друга  с  почтительным вниманием, хотя в действительности ему нужно лишь, чтобы кто-то одобрил  его поступки и взял на себя ответственность за них. Тот  же,  кто  дает  советы, притворяется, будто платит за оказанное доверие пылкой и бескорыстной жаждой услужить, тогда как на самом деле обычно рассчитывает  извлечь  таким  путем какую-либо выгоду или снискать почет.', '<b>"Максимы и моральные размышления"</b> - № 116', '', '', 'b2e4281edd898705dd291db08b20629b'),
(1375, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Притворяясь, будто мы попали в расставленную нам ловушку, мы  проявляем поистине утонченную хитрость,  потому  что  обмануть  человека  легче  всего тогда, когда он хочет обмануть нас.', '<b>"Максимы и моральные размышления"</b> - № 117', '', '...обмануть нас. - Подобная мысль была изложена ранее в "Мемуарах".', '70db9409c4a2a49e24a747a47be8d067'),
(1376, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Если мы решим никогда  не  обманывать  других,  они  то  и  дело  будут обманывать нас.', '<b>"Максимы и моральные размышления"</b> - № 118', '', '', '241fcc2eef0af6309f7d84b00f8565a3'),
(1377, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы так привыкли притворяться перед  другими,  что  под  конец  начинаем притворяться перед собой.', '<b>"Максимы и моральные размышления"</b> - № 119', '', '', '28227cb223679878f8196945b5a9b4de'),
(1378, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Предательства совершаются чаще всего не по обдуманному намерению, а  по слабости характера.', '<b>"Максимы и моральные размышления"</b> - № 120', '', '', '99b107c4c3834644555794d9fed5de74'),
(1379, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди делают добро  часто  лишь  для  того,  чтобы  обрести  возможность безнаказанно творить зло.', '<b>"Максимы и моральные размышления"</b> - № 121', '', '', '5a0f4b327396bb4610539904ba0bdda8'),
(1380, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы сопротивляемся нашим страстям не потому, что мы  сильны,  а  потому, что они слабы.', '<b>"Максимы и моральные размышления"</b> - № 122', '', '', 'd7656572617ae520868300c2885174d1'),
(1381, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди не знали бы удовольствия в жизни, если бы никогда себе не льстили.', '<b>"Максимы и моральные размышления"</b> - № 123', '', '', '48280d2b8f97c5f7354053c959d2d475'),
(1382, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Истинно ловкие люди всю жизнь делают вид, что гнушаются хитростью, а на самом деле они просто приберегают ее для исключительных  случаев,  обещающих исключительную выгоду.', '<b>"Максимы и моральные размышления"</b> - № 124', '', '', '60269704d7a933e09c3845a6958fde48'),
(1383, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Злоупотребление  хитростью  говорит  об   ограниченности   ума;   люди, пытающиеся прикрыть таким способом свою  наготу  в  одном  месте,  неизбежно разоблачают себя в другом.', '<b>"Максимы и моральные размышления"</b> - № 125', '', '', '56aed2f5034b10e28f04541b24fd03e4'),
(1384, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Хитрость и предательство свидетельствуют лишь о недостатках ловкости.', '<b>"Максимы и моральные размышления"</b> - № 126', '', '', 'e9d6eaf4118f4b83d7fc6b556a43eb1e'),
(1385, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Вернейший способ быть обманутым - это считать себя хитрее других.', '<b>"Максимы и моральные размышления"</b> - № 127', '', '', 'ed61d09fc2f3b7b52455715a69b64685'),
(1386, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Преувеличенная тонкость ведет к пустой щепетильности; только в истинной щепетильности скрыта настоящая тонкость', '<b>"Максимы и моральные размышления"</b> - № 128', '', '', 'fadde3a482209b399246c535bc8a1e87'),
(1387, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Иногда достаточно быть грубым, чтобы избегнуть ловушки хитреца.', '<b>"Максимы и моральные размышления"</b> - № 129', '', '...ловушки хитреца. - Намек на герцога де Бофора, который "достаточно искусно достигал своих целей, идя напролом" ("Мемуары").', '5c552c1fd0cca142efc55318ea405ecd'),
(1388, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Слабость характера - это единственный  недостаток,  который  невозможно исправить.', '<b>"Максимы и моральные размышления"</b> - № 130', '', '', '5eb2b446666ff25443d96ba7d0406f86'),
(1389, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Легкое поведение - это наименьший недостаток  женщин,  известных  своим легким поведением.', '<b>"Максимы и моральные размышления"</b> - № 131', '', '', '882324efe0de791fbab8d0afcea2748b'),
(1390, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Проявить мудрость в чужих делах куда легче, нежели в своих собственных.', '<b>"Максимы и моральные размышления"</b> - № 132', '', '', 'e7f171f523884734e9257e6ccbee3508'),
(1391, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Копии хороши лишь тогда, когда они открывают нам смешные стороны дурных оригиналов.', '<b>"Максимы и моральные размышления"</b> - № 133', '', '', '1529bde8c166b4d9fdf39bf669d297fb'),
(1392, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'В людях не так смешны те качества, которыми они обладают,  как  те,  на которые они претендуют.', '<b>"Максимы и моральные размышления"</b> - № 134', '', '', 'c0c60c82ab3f1910f710c894a6310024'),
(1393, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Порою человек так же мало похож на себя, как и на других.', '<b>"Максимы и моральные размышления"</b> - № 135', '', '', 'd03d0a37f4bd1b18c740d55e0a2ca6c2'),
(1394, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Иные люди только потому и влюбляются, что они наслышаны о любви.', '<b>"Максимы и моральные размышления"</b> - № 136', '', '', '373f08094451f33678f4d4b9a5c468c4'),
(1395, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди охотно молчат, если тщеславие не побуждает их говорить.', '<b>"Максимы и моральные размышления"</b> - № 137', '', '', '08d0217ee9e12fd857aa6973c725fc34'),
(1396, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди скорее согласятся себя чернить, нежели молчать о себе.', '<b>"Максимы и моральные размышления"</b> - № 138', '', '', '42205275eb8d404fb6fe26d0b80936f5'),
(1397, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Одна из причин того,  что  умные  и  приятные  собеседники  так  редки, заключается в обыкновении большинства людей отвечать не на чужие суждения, а на  собственные  мысли.  Тот,  кто  похитрее  и  пообходительнее,   пытается изобразить на своем  лице  внимание,  но  его  глаза  и  весь  облик  выдают отсутствие интереса к тому,  что  говорит  другой,  и  нетерпеливое  желание вернуться к тому, что намерен сказать он сам. Мало кто понимает,  что  такое старание угодить себе - плохой способ угодить другому или убедить его и что, только умея слушать и отвечать, можно быть хорошим собеседником.', '<b>"Максимы и моральные размышления"</b> - № 139', '', '', 'a49be7fd33995a9bb1ae970fd7eb756e'),
(1398, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Умный человек нередко попадал бы в затруднительное положение,  не  будь он окружен дураками.', '<b>"Максимы и моральные размышления"</b> - № 140', '', '', '6d9904e09574a8588e8b200b9ee0178b'),
(1399, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы  любим  похваляться  тем,  что  никогда  не  скучаем;  тщеславие  не позволяет  нам  признать,  что  в  обществе   нас   могут   счесть   плохими собеседниками.', '<b>"Максимы и моральные размышления"</b> - № 141', '', '', '99b56405a71ec03769a7792a37ae7128'),
(1400, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'В то время как люди умные умеют выразить многое в немногих словах, люди ограниченные, напротив, обладают способностью много говорить - и  ничего  не сказать.', '<b>"Максимы и моральные размышления"</b> - № 142', '', '', 'c4bc0d11379c6160f0cff5c8bf7b2853'),
(1401, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Преувеличивая чужие добродетели, мы отдаем дань не столько им,  сколько нашим собственным чувствам; мы ищем  похвал  себе,  делая  вид,  что  хвалим других.', '<b>"Максимы и моральные размышления"</b> - № 143', '', '', 'c6cc35615a7c03fdb648d707d6a919ea'),
(1402, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди не любят хвалить и никогда не хвалят бескорыстно.  Похвала  -  это искусная, скрытая, изящная лесть, приятная и тому, кто льстит, и тому,  кому льстят:  один  принимает  ее  как  награду  за  свои   достоинства,   другой преподносит, чтобы доказать свою справедливость и проницательность.', '<b>"Максимы и моральные размышления"</b> - № 144', '', '', '1bc3ae0a9855b550483217f62c4e5be1'),
(1403, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы часто выискиваем отравленные похвалы, косвенно  открывающие  в  тех, кого мы хвалим, такие недостатки, на  которые  мы  не  осмеливаемся  указать прямо.', '<b>"Максимы и моральные размышления"</b> - № 145', '', '', 'acd9363030b2ab66ed3a18c2b4cee650'),
(1404, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы хвалим других обычно лишь для того, чтобы услышать похвалу себе.', '<b>"Максимы и моральные размышления"</b> - № 146', '', '', '27f6af63184e942a1af2e8f4d951081f'),
(1405, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди  редко  бывают  достаточно  разумны,  чтобы  предпочесть  полезное порицание опасной похвале.', '<b>"Максимы и моральные размышления"</b> - № 147', '', '', 'e5f43113a8961b45c583b0cff1c85078'),
(1406, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Иные упреки звучат как похвала, зато иные похвалы хуже злословия.', '<b>"Максимы и моральные размышления"</b> - № 148', '', '', 'd74ad53a91be557ddb4ae7684fad32db'),
(1407, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Уклонение от похвалы - это просьба повторить ее.', '<b>"Максимы и моральные размышления"</b> - № 149', '', '', '55913f9d403dffd35c4108d61a4ebea4'),
(1408, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Жажда заслужить расточаемые нам  похвалы  укрепляет  нашу  добродетель; таким образом, похвалы нашему уму, доблести  и  красоте  делают  нас  умнее, доблестнее и красивее.', '<b>"Максимы и моральные размышления"</b> - № 150', '', '', '865daa95452b07b238b620c1178faf87'),
(1409, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Нам легче управлять людьми, чем помешать им управлять нами.', '<b>"Максимы и моральные размышления"</b> - № 151', '', '', 'a1078bdca4bd05544f87e9d12ce2b1bf'),
(1410, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Если бы мы не льстили себе сами, нас не портила бы чужая лесть.', '<b>"Максимы и моральные размышления"</b> - № 152', '', '', 'e4e299062ec22b5fec7e65e60e03b9b9'),
(1411, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Наделяет нас достоинствами природа, а помогает их проявить судьба.', '<b>"Максимы и моральные размышления"</b> - № 153', '', '', 'e7a2a76388fb7d6727bfd95c6f0a90eb'),
(1412, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Судьба исправляет такие наши недостатки, каких не мог бы исправить даже разум.', '<b>"Максимы и моральные размышления"</b> - № 154', '', '', 'dd47a091c05b51125f074dc58bb23bda'),
(1413, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Иные люди  отталкивают,  невзирая  на  все  их  достоинства,  а  другие привлекают при всех их недостатках.', '<b>"Максимы и моральные размышления"</b> - № 155', '', '', 'fa05cc321b6365a1034decf3653a46d6'),
(1414, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Есть люди, все достоинства  которых  основаны  на  способности  уместно говорить и делать глупости; если бы они  изменили  поведение,  все  было  бы испорчено;', '<b>"Максимы и моральные размышления"</b> - № 156', '', '', '173daa099ca1410413dafe9b246af0b6'),
(1415, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Слава великих людей всегда должна измеряться способами, какими она была достигнута.', '<b>"Максимы и моральные размышления"</b> - № 157', '', '', '2a05b6c3d3d2df6dde5e01c340ae22e4'),
(1416, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Лесть - это фальшивая  монета,  которая  имеет  хождение  только  из-за нашего тщеславия.', '<b>"Максимы и моральные размышления"</b> - № 158', '', '', '794b55ec8a585a30a86a11271271d230'),
(1417, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мало обладать выдающимися качествами, надо еще уметь ими пользоваться.', '<b>"Максимы и моральные размышления"</b> - № 159', '', '', 'c648b236be3810d64898ee6f6284d3bf'),
(1418, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Любое, даже самое громкое деяние нельзя назвать великим,  если  оно  не было следствием великого замысла.', '<b>"Максимы и моральные размышления"</b> - № 160', '', '', 'd686e468548f6b02f852e5d56c511a0a'),
(1419, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Деяние и замысел должны соответствовать друг другу, не то заложенные  в них возможности так и останутся неосуществленными.', '<b>"Максимы и моральные размышления"</b> - № 161', '', '', '006325bc5bbe899d1fe4ac73e5b6cbc7'),
(1420, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Умение ловко  пользоваться  посредственными  способностями  не  внушает уважения - и все же  нередко  приносит  людям  больше  славы,  чем  истинные достоинства.', '<b>"Максимы и моральные размышления"</b> - № 162', '', '...истинные достоинства. - Намек на герцога де Бофора, подобная мысль высказывалась и в "Мемуарах".', 'd4241afcf035a299da4deb191aeba8bf'),
(1421, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'В очень многих случаях поведение людей только потому  кажется  смешным, что причины его, вполне разумные и основательные, скрыты от окружающих.', '<b>"Максимы и моральные размышления"</b> - № 163', '', '', '43162ebef9314f07bc9fc66be01bb9b6'),
(1422, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Человеку  легче  казаться  достойным  той  должности,  которой  он   не занимает, нежели той, в которой состоит.', '<b>"Максимы и моральные размышления"</b> - № 164', '', '', '63dfd8249a8a1264a37dc59cdacc1fb3'),
(1423, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Порядочные  люди  уважают  нас  за  наши  достоинства,  а  толпа  -  за благосклонность судьбы.', '<b>"Максимы и моральные размышления"</b> - № 165', '', '', '59e88daffa0551d7d3d004eb6a069cb4'),
(1424, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Свет чаще награждает видимость достоинств, нежели сами достоинства.', '<b>"Максимы и моральные размышления"</b> - № 166', '', '', 'f7678bd481448c8a67ed4d3af0935728'),
(1425, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Скупость дальше от бережливости, чем даже расточительность.', '<b>"Максимы и моральные размышления"</b> - № 167', '', '', 'e18e0741edb691d59b481434de035747'),
(1426, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Как ни обманчива надежда, все же до конца  наших  дней  она  ведет  нас легкой стезей.', '<b>"Максимы и моральные размышления"</b> - № 168', '', '', '939b5ab1033a769edfb16186c80514ec'),
(1427, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Хотя мы храним верность своему долгу нередко лишь из лени  и  трусости, все лавры за это достаются на долю наших добродетелей.', '<b>"Максимы и моральные размышления"</b> - № 169', '', '', '6cee69c0a01e356ef82dc598c869d55e');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(1428, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Нелегко разглядеть, чем вызван честный, искренний, благородный поступок - порядочностью или дальновидным расчетом.', '<b>"Максимы и моральные размышления"</b> - № 170', '', '', '059d0ffdc9ac952f9eb5162d87558cea'),
(1429, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Добродетели теряются в своекорыстии, как реки в море.', '<b>"Максимы и моральные размышления"</b> - № 171', '', '', 'cb5920ef20226309ed6d337de1526fc4'),
(1430, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Если внимательно присмотреться к последствиям скуки, то  окажется,  что она заставляет отступать от долга чаще, чем даже своекорыстие.', '<b>"Максимы и моральные размышления"</b> - № 172', '', '', '49169a355d83188ad4ad2c35beb86f8c'),
(1431, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Есть две разновидности любопытства: своекорыстное - внушенное  надеждой приобрести полезные сведения, и самолюбивое - вызванное желанием узнать  то, что неизвестно другим.', '<b>"Максимы и моральные размышления"</b> - № 173', '', '', '940bac686ea74ab150c10d03850689d8'),
(1432, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Было бы куда полезнее употребить все силы нашего разума  на  то,  чтобы достойно  сносить  несчастья,  уже  случившиеся,   нежели   на   то,   чтобы предугадывать несчастья, которые еще только могут случиться.', '<b>"Максимы и моральные размышления"</b> - № 174', '', '', 'aa6587ae46760ac76648affb3104dfe0'),
(1433, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Постоянство  в  любви  -  это  вечное  непостоянство,  побуждающее  нас увлекаться  по  очереди  всеми   качествами   любимого   человека,   отдавая предпочтение то одному  из  них,  то  другому;  таким  образом,  постоянство оказывается непостоянством, но  ограниченным,  то  есть  сосредоточенным  на одном предмете.', '<b>"Максимы и моральные размышления"</b> - № 175', '', '', 'c72b7f491b8653c65bd98dd6054b2763'),
(1434, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Постоянство в любви бывает двух родов: мы постоянны или потому, что все время находим в любимом человеке новые качества,  достойные  любви,  или  же потому, что считаем постоянство долгом чести.', '<b>"Максимы и моральные размышления"</b> - № 176', '', '', 'c16a1ba36017c3d0dd81161d36c40327'),
(1435, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Постоянство  не  заслуживает  ни  похвал,  ни  порицаний,  ибо  в   нем проявляется устойчивость вкусов и чувств, не зависящая от нашей воли.', '<b>"Максимы и моральные размышления"</b> - № 177', '', '', 'd6906ac443dd378e7d0ffe9ef7e92b3e'),
(1436, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'К новым знакомствам нас обычно толкает не столько усталость  от  старых или любовь к переменам, сколько недовольство тем, что люди  хорошо  знакомые недостаточно нами восхищаются, и надежда на то, что люди мало знакомые будут восхищаться больше.', '<b>"Максимы и моральные размышления"</b> - № 178', '', '', '9c4fdf1b0c8a6ff2981d0fc0d719953b'),
(1437, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы постоянно жалуемся на друзей, чтобы заранее оправдать  непостоянство нашей дружбы.', '<b>"Максимы и моральные размышления"</b> - № 179', '', '', '369ea16e733152872d6abe179c4f5a5f'),
(1438, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Наше раскаяние -  это  обычно  не  столько  сожаление  о  зле,  которое совершили мы, сколько боязнь зла, которое могут причинить нам в ответ.', '<b>"Максимы и моральные размышления"</b> - № 180', '', '', '51c725e1757b06fcac508d0d595d6daa'),
(1439, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Иногда непостоянство происходит от легкомыслия или от  незрелости  ума, побуждающих человека соглашаться с любым чужим мнением; но есть другого рода непостоянство,  более  простительное,  ибо  его   порождает   отвращение   к окружающему.', '<b>"Максимы и моральные размышления"</b> - № 181', '', '', '499d3f157e22586f55efeaa02acf5211'),
(1440, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Пороки входят  в  состав  добродетелей,  как  яды  в  состав  лекарств; благоразумие смешивает их, ослабляет их действие и  потом  умело  пользуется ими как средством против жизненных невзгод.', '<b>"Максимы и моральные размышления"</b> - № 182', '', '', 'df690a01c5a3e82c4dcd6ff758a22398'),
(1441, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'К  чести  добродетели  следует  все  же  признать,  что  самые  большие несчастья  случаются  с  людьми  не  из-за  нее,  а  из-за  их   собственных проступков.', '<b>"Максимы и моральные размышления"</b> - № 183', '', '...собственных проступков, - Эта максима, имеющая самый общий смысл, связана, по мнению французских исследователей, с казнью  шевалье  де  Рогана (1674). возглавлявшего заговор против государства.', '9b7cff4f1c3f3dd3a61c62f03a0faccb'),
(1442, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы признаемся в своих недостатках для  того,  чтобы  этой  искренностью возместить ущерб, который они наносят нам в мнении окружающих.', '<b>"Максимы и моральные размышления"</b> - № 184', '', '', '26fe841c5e41d1ef7e87a726cbfc8b01'),
(1443, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Зло, как и добро, имеет своих героев.', '<b>"Максимы и моральные размышления"</b> - № 185', '', '', 'df3cd1e470e529cc05735d823c6bcbb6'),
(1444, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы презираем не тех, у кого есть пороки, а  тех,  у  кого  нет  никаких добродетелей.', '<b>"Максимы и моральные размышления"</b> - № 186', '', '', 'e8775fe56e174ab23ddfd29adec76f2c'),
(1445, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Видимость добродетели приносит  своекорыстию  не  меньшую  пользу,  чем порок.', '<b>"Максимы и моральные размышления"</b> - № 187', '', '', 'e1cde0edc9e174c69eef7e8e11f9629c'),
(1446, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Здоровье души не менее хрупко, чем здоровье тела, и тот, кто мнит  себя свободным от  страстей,  так  же  легко  может  им  поддаться,  как  человек цветущего здоровья - заболеть.', '<b>"Максимы и моральные размышления"</b> - № 188', '', '', 'b9e3ddfcf439678b4c9ec53e9f3a7a72'),
(1447, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'С самого рождения каждого человека природа как бы  предопределяет  меру его добродетелей и пороков.', '<b>"Максимы и моральные размышления"</b> - № 189', '', '', '0e0c33668c155577b7aa0028c3efec29'),
(1448, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Только у великих людей бывают великие пороки.', '<b>"Максимы и моральные размышления"</b> - № 190', '', '', 'b60457203b2e2e8f6a32c96f533a3b76'),
(1449, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Можно сказать, что пороки ждут  нас  на  жизненном  пути,  как  хозяева постоялых дворов, у которых приходится поочередно останавливаться,  и  я  не думаю, чтобы опыт помог нам их избегнуть, даже если бы нам было дано  пройти этот путь вторично.', '<b>"Максимы и моральные размышления"</b> - № 191', '', '', '35e42b3de69ba01003bd87b8eda292a0'),
(1450, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Когда пороки покидают нас,  мы  стараемся  уверить  себя,  что  это  мы покинули их.', '<b>"Максимы и моральные размышления"</b> - № 192', '', '', 'b3538c4ac8a20ec577bb808ed34b8d32'),
(1451, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Болезни души так же возвращаются к нам, как и болезни тела. То, что  мы принимаем  за  выздоровление,  обычно   оказывается   либо   кратковременным облегчением старого недуга, либо началом нового.', '<b>"Максимы и моральные размышления"</b> - № 193', '', '', '8d84a53271155aa2d38f0f30e7af4000'),
(1452, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Пороки души похожи на раны тела: как бы старательно их ни  лечили,  они все равно оставляют рубцы и в любую минуту могут открыться снова.', '<b>"Максимы и моральные размышления"</b> - № 194', '', '', '8887e40d040766383c40e8b696f17be0'),
(1453, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Всецело предаться одному пороку нам обычно мешает лишь то, что у нас их несколько.', '<b>"Максимы и моральные размышления"</b> - № 195', '', '', '3d421da2998b42af429e5802d6d9e6fe'),
(1454, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы легко забываем свои ошибки, когда они известны лишь нам одним.', '<b>"Максимы и моральные размышления"</b> - № 196', '', '', '5a347e3b4cc2689307e6716bf0ae3103'),
(1455, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Есть люди, в дурные дела которых невозможно поверить, пока не убедишься собственными глазами. Однако нет таких людей, дурным делам которых стоило бы удивляться после того, как мы в них уже убедились.', '<b>"Максимы и моральные размышления"</b> - № 197', '', '', '6fb9f7d2ad37c429534ec4f53115309f'),
(1456, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Мы порою восхваляем доблести одного человека,  чтобы  унизить  другого: так, например, люди меньше превозносили бы принца Конде, если бы  не  хотели опорочить маршала Тюренна, и наоборот.', '<b>"Максимы и моральные размышления"</b> - № 198', '', '', '79c885890b582ae60592c0596025b1fa'),
(1457, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Желание  прослыть  ловким  человеком  нередко  мешает  стать  ловким  в действительности.', '<b>"Максимы и моральные размышления"</b> - № 199', '', '', 'ab633c14d1aad7db7fa2841c4e3f20df'),
(1458, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Добродетель не достигала бы таких высот, если бы ей в пути не  помогало тщеславие.', '<b>"Максимы и моральные размышления"</b> - № 200', '', '', '9c3c3bfba05f88320e0be00a1ec9557c'),
(1459, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Тот, кто думает, что может обойтись без других,  сильно  ошибается;  но тот, кто думает, что другие  не  могут  обойтись  без  него,  ошибается  еще сильнее.', '<b>"Максимы и моральные размышления"</b> - № 201', '', '', '22ea56235ec4511e6989c9bf944ccb0f'),
(1460, 1, 1, 1, 1448972771, 1448972771, 124, 0, 0, 'Люди мнимо благородные скрывают свои недостатки и от других и от  себя, а люди истинно благородные прекрасно их сознают и открыто о них заявляют.', '<b>"Максимы и моральные размышления"</b> - № 202', '', '', '60b21750cc0e56df8d4d4ed86608b42c'),
(1461, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Истинно благородные люди никогда ничем не кичатся.', '<b>"Максимы и моральные размышления"</b> - № 203', '', '', 'effe6c8997fcb8392c7fc0925bee8841'),
(1462, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Строгость нрава у женщин - это белила и румяна, которыми  они  оттеняют свою красоту.', '<b>"Максимы и моральные размышления"</b> - № 204', '', '', 'cce5623ef31e2c7d8018fa6c25703f0c'),
(1463, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Целомудрие женщин - это большей частью просто забота о добром  имени  и покое.', '<b>"Максимы и моральные размышления"</b> - № 205', '', '', '4b57d93e7ec59ba6a4b40a280b5d07dd'),
(1464, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Кто стремится всегда жить на виду у  благородных  людей,  тот  поистине благородный человек.', '<b>"Максимы и моральные размышления"</b> - № 206', '', '', '0decb26441cd9d9ca2ab896e24615c41'),
(1465, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Безрассудство сопутствует нам всю жизнь; если кто-нибудь и кажется  нам мудрым, то это значит лишь, что его безрассудства соответствуют его возрасту и положению.', '<b>"Максимы и моральные размышления"</b> - № 207', '', '', '7b0cc81bb9907ee65d0523e16d37959a'),
(1466, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Есть глупцы, которые сознают свою глупость и ловко ею пользуются.', '<b>"Максимы и моральные размышления"</b> - № 208', '', '', '13341f3c8bd9d95f43999708f305580a'),
(1467, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Кто никогда не совершал безрассудств, тот не так мудр, как ему кажется.', '<b>"Максимы и моральные размышления"</b> - № 209', '', '', '718cae91a8336c708144b0e5ca6be2a8'),
(1468, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'К старости люди становятся безрассуднее - и мудрее.', '<b>"Максимы и моральные размышления"</b> - № 210', '', '', 'ba388e16fa9ef16761081dd7238bc778'),
(1469, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Иные люди похожи на песенки: они быстро выходят из моды.', '<b>"Максимы и моральные размышления"</b> - № 211', '', '', '3366b8609f7fd8ac763f0e8606fdf4df'),
(1470, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Большинство людей судит о ближних по их богатству или светским успехам.', '<b>"Максимы и моральные размышления"</b> - № 212', '', '', 'bc0489d2fbe0c01de2fb8dc06fd5eff0'),
(1471, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Жажда славы, боязнь позора,  погоня  за  богатством,  желание  устроить жизнь удобно и приятно, стремление унизить других - вот что нередко лежит  в основе доблести, столь превозносимой людьми.', '<b>"Максимы и моральные размышления"</b> - № 213', '', '', 'b099acf414740e1e178a59e853e491bc'),
(1472, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Для простого солдата доблесть - это  опасное  ремесло,  за  которое  он берется, чтобы снискать себе пропитание.', '<b>"Максимы и моральные размышления"</b> - № 214', '', '', 'e1b2a308b528077cb57902df64636fd4'),
(1473, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Высшая доблесть и  непреодолимая  трусость  -  это  крайности,  которые встречаются очень редко. Между ними на обширном  пространстве  располагаются всевозможные оттенки храбрости, такие  же  разнообразные,  как  человеческие лица и характеры. Есть люди, которые  смело  встречают  опасность  в  начале сражения, но легко охладевают и падают духом, если оно затягивается;  другие делают то, чего от них требует общественное мнение, и на этом успокаиваются. Одни не всегда  умеют  овладеть  своим  страхом,  другие  подчас  заражаются страхом окружающих, а  третьи  идут  в  бой  просто  потому,  что  не  смеют оставаться на своих местах. Иные, привыкнув к мелким опасностям,  закаляются духом для встречи с более значительными. Некоторые храбры со шпагой в руках, но пугаются мушкетного выстрела; другие же смело стоят под пулями, но боятся обнаженной шпаги. Все эти различные виды храбрости схожи между собой в  том, что ночью, - когда страх усиливается, а тьма равно  скрывает  и  хорошие,  и дурные поступки, - люди ревнивее оберегают свою жизнь. Но есть у  людей  еще один способ оберечь себя - и притом самый распространенный:  делать  меньше, чем они сделали бы, если бы знали наперед, что все сойдет  благополучно.  Из этого явствует, что страх смерти в какой-то мере ограничивает доблесть.', '<b>"Максимы и моральные размышления"</b> - № 215', '', '', '754e29f0184327864d24dfc427529842'),
(1474, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Высшая доблесть состоит в том, чтобы совершать в одиночестве то, на что люди обычно отваживаются лишь в присутствии многих свидетелей.', '<b>"Максимы и моральные размышления"</b> - № 216', '', '', '820e83985e483b20d08ccd4e0f397d41'),
(1475, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Бесстрашие  -  это   необычайная   сила   души,   возносящая   ее   над замешательством, тревогой и смятением,  порождаемыми  встречей  с  серьезной опасностью. Эта  сила  поддерживает  в  героях  спокойствие  и  помогает  им сохранять ясность ума в самых неожиданных и ужасных обстоятельствах.', '<b>"Максимы и моральные размышления"</b> - № 217', '', '', '1fcf555a9e5db7a54edbc7964efb366f'),
(1476, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Лицемерие - это дань уважения, которую порок платит добродетели.', '<b>"Максимы и моральные размышления"</b> - № 218', '', '', 'fb9070732d03fa8b051e295b7515dd6d'),
(1477, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'На войне большинство людей рискует жизнью  ровно  настолько,  насколько это необходимо, чтобы не запятнать своей  чести;  но  лишь  немногие  готовы всегда рисковать так, как этого требует цель, ради которой они идут на риск.', '<b>"Максимы и моральные размышления"</b> - № 219', '', '', 'e5f2fee6fe25480dcf00bd1918c0d954'),
(1478, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Тщеславие, стыд, а главное, темперамент - вот что обычно лежит в основе мужской доблести и женской добродетели.', '<b>"Максимы и моральные размышления"</b> - № 220', '', '', '0c80d6aec4dbae397e106201a6a9597d'),
(1479, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Все хотят снискать славу, но никто не  хочет  лишиться  жизни;  поэтому храбрецы проявляют не меньше находчивости и ума, чтобы избежать смерти,  чем крючкотворцы - чтобы приумножить состояние.', '<b>"Максимы и моральные размышления"</b> - № 221', '', '', 'cd25957fa72e659dc7ab78736fb9860f'),
(1480, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Почти всегда по отроческим склонностям человека уже  ясно,  в  чем  его слабость и что приведет к падению его тело и душу.', '<b>"Максимы и моральные размышления"</b> - № 222', '', '', 'a8b4de9d4ba7ec5a87baabc077d7bcab'),
(1481, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Благодарность подобна  честности  купца:  она  поддерживает  коммерцию. Часто мы оплачиваем ее счета не потому, что стремимся поступать справедливо, а для того, чтобы впредь люди охотнее давали нам взаймы.', '<b>"Максимы и моральные размышления"</b> - № 223', '', '', '6b607332e7377210017a417464e899fa'),
(1482, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Не всякий, кто платит долги благодарности, имеет право считать себя  на этом основании благодарным человеком.', '<b>"Максимы и моральные размышления"</b> - № 224', '', '', 'd4dddcb90554306247fedf6e87b0b769'),
(1483, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Ошибки людей в их расчетах на благодарность  за  оказанные  ими  услуги происходят оттого, что гордость дающего и  гордость  принимающего  не  могут сговориться о цене благодеяния.', '<b>"Максимы и моральные размышления"</b> - № 225', '', '', '104209a0fec46859a132ebe198ed2651'),
(1484, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Чрезмерная поспешность в расплате за оказанную услугу есть своего  рода неблагодарность.', '<b>"Максимы и моральные размышления"</b> - № 226', '', '', 'ee91382b68ef9044a962dfb7045bb8f8'),
(1485, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Счастливые люди неисправимы:  судьба  не  наказывает  их  за  грехи,  и поэтому они считают себя безгрешными.', '<b>"Максимы и моральные размышления"</b> - № 227', '', '', 'd72013ffc2044463fd4d37a61c245377'),
(1486, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Гордость не хочет быть в долгу, а самолюбие не желает расплачиваться.', '<b>"Максимы и моральные размышления"</b> - № 228', '', '', 'febeac30b918d0a9b7b35fb5c6b93243'),
(1487, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Если кто-нибудь сделает нам  добро,  мы  обязаны  терпеливо  сносить  и причиняемое этим человеком зло.', '<b>"Максимы и моральные размышления"</b> - № 229', '', '', 'bf31a7c3ac6bae820052550a67890e73'),
(1488, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Пример заразителен, поэтому все благодетели рода  человеческого  и  все злодеи  находят  подражателей.  Добрым  делам  мы   подражаем   из   чувства соревнования, дурным же - из врожденной злобности, которую стыд сдерживал, а пример выпустил на волю.', '<b>"Максимы и моральные размышления"</b> - № 230', '', '', '5f216904459dd29977c05fffbd65995b'),
(1489, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Нет ничего глупее желания всегда быть умнее всех.', '<b>"Максимы и моральные размышления"</b> - № 231', '', '', 'f132f8d769b07d5e0ca707010662f23d'),
(1490, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Чем бы мы ни объясняли наши огорчения, чаще всего  в  их  основе  лежит обманутое своекорыстие или уязвленное тщеславие.', '<b>"Максимы и моральные размышления"</b> - № 232', '', '', '8b091f0c87ba327c44bfc792473e434a'),
(1491, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Человеческое горе бывает лицемерно по-разному. Иногда, оплакивая потерю близкого  человека,  мы  в  действительности  оплакиваем  самих   себя:   мы оплакиваем наши утраченные наслаждения,  богатство,  влияние,  мы  горюем  о добром отношении к нам. Таким образом, мы проливаем слезы над участью живых, а относим их за счет мертвых. Этот род лицемерия я считаю  невинным,  ибо  в таких случаях люди обманывают не только  других,  но  и  себя.  Однако  есть лицемерие иного рода, более злостное, потому что оно сознательно вводит всех в заблуждение: я говорю о скорби некоторых людей, мечтающих  снискать  славу великим, неувядающим горем. После того как безжалостное время умерит печаль, которую эти люди некогда испытывали, они продолжают упорствовать  в  слезах, жалобах и вздохах. Они надевают на себя  личину  уныния  и  стараются  всеми своими поступками доказать, что их грусть кончится лишь вместе с жизнью. Это мелкое и утомительное тщеславие встречается обычно  у  честолюбивых  женщин. Так как их пол закрывает  им  все  пути,  ведущие  к  славе,  они  стремятся достигнуть известности, выставляя напоказ свое  безутешное  горе.  Есть  еще один неглубокий источник слез, которые легко льются и легко  высыхают:  люди плачут, чтобы прослыть чувствительными, плачут, чтобы  вызвать  сострадание, плачут, чтобы быть оплаканными, и, наконец, плачут потому,  что  не  плакать стыдно.', '<b>"Максимы и моральные размышления"</b> - № 233', '', '', 'c446f3372f5022549aeb6a5f542723cc'),
(1492, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Люди  упрямо  не  соглашаются  с  самыми  здравыми  суждениями  не   по недостатку проницательности, а из-за избытка гордости: они видят, что первые ряды в правом деле разобраны, а последние им не хочется занимать.', '<b>"Максимы и моральные размышления"</b> - № 234', '', '', '1ef3cddc7fe895cec8ee2fb1c22fe7a0'),
(1493, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Горе друзей  печалит  нас  недолго,  если  оно  доставляет  нам  случай проявить на виду у всех наше участие к ним.', '<b>"Максимы и моральные размышления"</b> - № 235', '', '', '3a0b7d66c1282e9e35b85bc1ab972781'),
(1494, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Порою может показаться, что себялюбие попадается в  сети  к  доброте  и невольно  забывает  о  себе,  когда  мы  трудимся  на  благо   ближнего.   В действительности же мы просто избираем кратчайший путь к цели, как бы отдаем деньги в рост  под  видом  подарка,  и  таким  образом  применяем  тонкий  и изысканный способ завоевать доверие окружающим.', '<b>"Максимы и моральные размышления"</b> - № 236', '', '', '8be3ec18325b1100d34e417ae23794cb'),
(1495, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Похвалы за доброту достоин лишь человек, у которого  хватает  твердости характера на то, чтобы иной раз быть злым; в противном случае  доброта  чаще всего говорит лишь о бездеятельности или о недостатке воли.', '<b>"Максимы и моральные размышления"</b> - № 237', '', '...недостатке воли. - Намек на характер Анны Австрийской.', '09177fbef0b4ce3af10f730e8db5a9f0'),
(1496, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Причинять людям зло большей частью не так опасно, как делать им слишком много добра.', '<b>"Максимы и моральные размышления"</b> - № 238', '', '', '61dedb7274b46f094a0e19ae8bc1be9a'),
(1497, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Ничто так не льстит нашему самолюбию, как доверие великих мира сего; мы принимаем его как дань  нашим  достоинствам,  не  замечая,  что  обычно  оно вызвано тщеславием или неспособностью хранить тайну.', '<b>"Максимы и моральные размышления"</b> - № 239', '', '', 'f126d979c060940f280dcc465bca97cb'),
(1498, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Привлекательность при отсутствии красоты - это особого рода  симметрия, законы которой нам неизвестны; это скрытая связь между всеми чертами лица, с одной стороны, и чертами лица, красками и общим обликом человека - с другой.', '<b>"Максимы и моральные размышления"</b> - № 240', '', '', 'a4a4111e3d30765dcf340dddc5abaf2a'),
(1499, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Кокетство - это основа характера всех женщин, только не все пускают его в ход, ибо у некоторых оно сдерживается боязнью или рассудком.', '<b>"Максимы и моральные размышления"</b> - № 241', '', '', '198031a899b6ee9b561756b62cf1e965'),
(1500, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Чаще всего тяготят окружающих те люди, которые считают, что они  никому не могут быть в тягость.', '<b>"Максимы и моральные размышления"</b> - № 242', '', '', '88af723d159ec922d5cbde9f936efed9'),
(1501, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'На свете мало недостижимых вещей; будь у нас больше  настойчивости,  мы могли бы отыскать путь почти к любой цели.', '<b>"Максимы и моральные размышления"</b> - № 243', '', '', '6346bb135b7cd86aa66718e00f17e3ab'),
(1502, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Высшая ловкость состоит в том, чтобы всему знать истинную цену.', '<b>"Максимы и моральные размышления"</b> - № 244', '', '', 'e8ca84676d2d3dc02bf22bad92e816d3'),
(1503, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Поистине ловок тот, кто умеет скрывать свою ловкость.', '<b>"Максимы и моральные размышления"</b> - № 245', '', '', '32a90753c1b6d08a41ab4f31803aeb61'),
(1504, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'То, что мы принимаем за благородство, нередко оказывается  переряженным честолюбием, которое, презирая мелкие выгоды, прямо идет к крупным.', '<b>"Максимы и моральные размышления"</b> - № 246', '', '', 'a8f16dfa5434d5366e981f810fa6d354'),
(1505, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Преданность - это в большинстве случаев уловка самолюбия, цель  которой - завоевать доверие; это способ возвыситься над другими людьми и  проникнуть в важнейшие тайны.', '<b>"Максимы и моральные размышления"</b> - № 247', '', '', 'fb70a560916d14488df19ba64f47d855'),
(1506, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Великодушие всем пренебрегает, чтобы всем завладеть.', '<b>"Максимы и моральные размышления"</b> - № 248', '', '', 'c8c392e42d5724f97af68b517ef62d50'),
(1507, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'В звуке голоса, в глазах и  во  всем  облике  говорящего  заключено  не меньше красноречия, чем в выборе слов.', '<b>"Максимы и моральные размышления"</b> - № 249', '', '', '1f51616b2483786aa1c260dcd6262f87'),
(1508, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Истинное красноречие - это умение сказать все, что нужно, и не  больше, чем нужно.', '<b>"Максимы и моральные размышления"</b> - № 250', '', '', 'e0c4832476295475f1425c65c776cc4a'),
(1509, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Одним людям идут их недостатки, а другим даже достоинства не к лицу.', '<b>"Максимы и моральные размышления"</b> - № 251', '', '', 'd7b867a3137fa8a35f0c999baa142139'),
(1510, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Вкусы меняются столь же часто, сколь редко меняются склонности.', '<b>"Максимы и моральные размышления"</b> - № 252', '', '', '2ac3794d0bcf3ef338ca7bb8eacc70e1'),
(1511, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Своекорыстие приводит в действие все добродетели и все пороки.', '<b>"Максимы и моральные размышления"</b> - № 253', '', '', 'a08333985f28646c60e0e710ee10ae7c'),
(1512, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Смирение нередко оказывается притворной  покорностью,  цель  которой  - подчинить себе других;  это  -  уловка  гордости,  принижающей  себя,  чтобы возвыситься, и хотя у гордости тысяча обличий, но  самое  искусное  и  самое обманчивое из ник - смирение.', '<b>"Максимы и моральные размышления"</b> - № 254', '', '', '7931464af61848815b747631d277102c'),
(1513, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'У всякого чувства есть свойственные лишь ему одному жесты, интонации  и мимика; впечатление от них, хорошее или дурное, приятное или  неприятное,  и служит причиной того, что люди располагают нас к себе или отталкивают.', '<b>"Максимы и моральные размышления"</b> - № 255', '', '', '696c641dcce7bf4159a2d88f0f167d89'),
(1514, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Каждый человек, кем бы он ни был, старается напустить на себя такой вид и надеть такую личину, чтобы его приняли за того,  кем  он  хочет  казаться; поэтому можно сказать, что общество состоит из одних только личин.', '<b>"Максимы и моральные размышления"</b> - № 256', '', '', 'b5f56fd6b81787ce2f9a110076ed0c7d'),
(1515, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Величавость - это непостижимая  уловка  тела,  изобретенная  для  того, чтобы скрыть недостатки ума.', '<b>"Максимы и моральные размышления"</b> - № 257', '', '', '6b6a37e625f744662b37cdcdec94affb'),
(1516, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Хороший вкус говорит не столько об уме, сколько о ясности суждений.', '<b>"Максимы и моральные размышления"</b> - № 258', '', '', '6cf6aedc66e76d8504e6e7d232e71740'),
(1517, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Счастье любви заключается в том, чтобы любить; люди  счастливее,  когда сами испытывают страсть, чем когда ее внушают.', '<b>"Максимы и моральные размышления"</b> - № 259', '', '', 'e72c07bec30d6e2a6b2c233e8b9a6d05'),
(1518, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Вежливость - это желание всегда встречать вежливое  обращение  и  слыть обходительным человеком.', '<b>"Максимы и моральные размышления"</b> - № 260', '', '', '793356ab4cc6fa5677122204f81cef82'),
(1519, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Воспитание молодых людей обычно сводится  к  поощрению  их  врожденного себялюбия.', '<b>"Максимы и моральные размышления"</b> - № 261', '', '', 'adbfcf42ee940b7bf9576a161a3a1160'),
(1520, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Ни в одной страсти себялюбие не царит так безраздельно,  как  в  любви; люди всегда готовы принести  в  жертву  покой  любимого  существа,  лишь  бы сохранить свой собственный.', '<b>"Максимы и моральные размышления"</b> - № 262', '', '', '1543766c7411b2434073af7ff04a9f31'),
(1521, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'В основе так называемой щедрости обычно лежит  тщеславие,  которое  нам дороже всего, что мы дарим.', '<b>"Максимы и моральные размышления"</b> - № 263', '', '', '66ec9730742678f61c9833e5b2f91cd0'),
(1522, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Чаще всего сострадание - это способность  увидеть  в  чужих  несчастьях свои собственные, это - предчувствие бедствий, которые  могут  постигнуть  и нас. Мы помогаем людям, чтобы они в свою очередь помогли нам; таким образом, наши услуги сводятся просто к  благодеяниям,  которые  мы  загодя  оказываем самим себе.', '<b>"Максимы и моральные размышления"</b> - № 264', '', '', 'f893fcf3235104640e246d535530d880'),
(1523, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Упрямство рождено ограниченностью нашего ума: мы неохотно  верим  тому, что выходит за пределы нашего кругозора.', '<b>"Максимы и моральные размышления"</b> - № 265', '', '', '5fd875a6f89d4420825647a8da5ca06a'),
(1524, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Ошибается тот, кто думает, будто лишь таким бурным страстям, как любовь и честолюбие, удается подчинить себе другие страсти. Самой  сильной  нередко оказывается  бездеятельная  леность:   завладевая   людскими   помыслами   и поступками, она незаметно подтачивает все их стремления и добродетели.', '<b>"Максимы и моральные размышления"</b> - № 266', '', '', 'fc1ae85b3043bf0a1daadeb725702b1a'),
(1525, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Люди потому так охотно верят дурному, не стараясь вникнуть в суть дела, что они тщеславны и ленивы. Им хочется найти  виновных,  но  они  не  желают утруждать себя разбором совершенного проступка.', '<b>"Максимы и моральные размышления"</b> - № 267', '', '', 'd24c3d86f7b666f28e0cf92d4e910480'),
(1526, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Мы по самым ничтожным поводам обвиняем судей в незнании дела и  тем  не менее охотно отдаем свою честь и доброе имя на их  суд,  хотя  все  они  нам враждебны - одни из зависти, другие  по  ограниченности,  третьи  просто  по занятости. Надеясь на то, что эти люди выскажутся в нашу пользу, мы  рискуем своим покоем и даже жизнью.', '<b>"Максимы и моральные размышления"</b> - № 268', '', '', '1c05475b0e1d8f3f1464b9da4c83e753'),
(1527, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Как бы ни был  проницателен  человек,  ему  не  постигнуть  всего  зла, которое он творит.', '<b>"Максимы и моральные размышления"</b> - № 269', '', '', '3b05c8d2e3cf17bcb1dbb67bc8c94ec5'),
(1528, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Слава,  уже  приобретенная  нами,  -  залог  той  славы,   которую   мы рассчитываем приобрести.', '<b>"Максимы и моральные размышления"</b> - № 270', '', '', '613cac0b032a528bbdc15a118d71747b'),
(1529, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Молодость - это постоянное опьянение, это горячка рассудка.', '<b>"Максимы и моральные размышления"</b> - № 271', '', '', '9928e7c581de29a820fdc84571bfcb66'),
(1530, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Тому, чьи достоинства уже награждены  подлинной  славой,  больше  всего следовало бы стыдиться усилий, которые он прилагает, чтобы ему  поставили  в заслугу всякие пустяки.', '<b>"Максимы и моральные размышления"</b> - № 272', '', '', 'abeec609a7bbdc061cfe97dd7683b38b'),
(1531, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'В свете иной раз высоко ценят людей, все достоинства которых сводятся к порокам, приятным в повседневной жизни.', '<b>"Максимы и моральные размышления"</b> - № 273', '', '', 'fffa9e40a20415ff37efd4cc2cc65f2b'),
(1532, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Очарование новизны в любви подобно  цветению  фруктовых  деревьев:  оно быстро тускнеет и больше никогда не возвращается.', '<b>"Максимы и моральные размышления"</b> - № 274', '', '', '21ca6a2f57cc53ef14fdaf73c49bbff9');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(1533, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Природное    добродушие,    которое     любит     похваляться     своей чувствительностью,   нередко   умолкает,    побежденное    самым    мелочным своекорыстием.', '<b>"Максимы и моральные размышления"</b> - № 275', '', '', '908737b1c5fc39751faa7d4b539b44f9'),
(1534, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Разлука ослабляет  легкое  увлечение,  но  усиливает  большую  страсть, подобно тому как ветер гасит свечу, но раздувает пожар.', '<b>"Максимы и моральные размышления"</b> - № 276', '', '', '57232d802a01a2292839b7b010fe792b'),
(1535, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Нередко женщины, нисколько не любя, все же воображают, будто они любят: увлечение интригой, естественное желание быть любимой, подъем душевных  сил, вызванный приключением, и боязнь обидеть отказом - все  это  приводит  их  к мысли, что  они  страстно  влюблены,  хотя  в  действительности  всего  лишь кокетничают.', '<b>"Максимы и моральные размышления"</b> - № 277', '', '', '4753e8ccefee5c152f045e1080cc6a35'),
(1536, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Люди редко бывают довольны теми, кто от их  имени  вступает  в  деловые переговоры, так как посредники, стараясь стяжать себе  добрую  славу,  почти всегда жертвуют интересами своих друзей ради успеха самих переговоров.', '<b>"Максимы и моральные размышления"</b> - № 278', '', '', 'e9220a3aee3104afce1ebdfc58d8e384'),
(1537, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Когда мы преувеличиваем привязанность к нам наших друзей,  нами  обычно руководит не столько благодарность, сколько желание выставить  напоказ  наши достоинства.', '<b>"Максимы и моральные размышления"</b> - № 279', '', '', 'a5a8c57a14d0783b9b9dcc0d39142be7'),
(1538, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Доброжелательность, с которой люди порою приветствуют тех, кто  впервые вступает в свет, обычно бывает вызвана тайной завистью к тем, кто уже  давно занимает в нем прочное положение.', '<b>"Максимы и моральные размышления"</b> - № 280', '', '', '5800636be398a86feecd067644347a62'),
(1539, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Гордость часто разжигает в нас зависть, и та же самая гордость  нередко помогает нам с ней справиться.', '<b>"Максимы и моральные размышления"</b> - № 281', '', '', '5652f6f29dbedf0ff427fa812612c9d8'),
(1540, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Ложь иной раз так ловко прикидывается истиной, что не поддаться  обману значило бы изменить здравому смыслу.', '<b>"Максимы и моральные размышления"</b> - № 282', '', '', '24d70af3e2b551e14a6ecf37de97ea35'),
(1541, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Для того, чтобы воспользоваться  хорошим  советом  со  стороны,  подчас требуется не меньше ума, чем для того, чтобы  подать  хороший  совет  самому себе.', '<b>"Максимы и моральные размышления"</b> - № 283', '', '', '074ffdbbafcd9ff1f74bd55cbc8fea26'),
(1542, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Опаснее всего те злые люди, которые не совсем лишены доброты.', '<b>"Максимы и моральные размышления"</b> - № 284', '', '', '2d075a0c8941277ab27284d2ce4289be'),
(1543, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Великодушие довольно точно  определено  своим  названием;  кроме  того, можно сказать, что оно - здравый смысл гордости и  самый  достойный  путь  к доброй славе.', '<b>"Максимы и моральные размышления"</b> - № 285', '', '', '17a3e3f61cd05e101cfcef4228a29427'),
(1544, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Мы  не  можем  вторично  полюбить  тех,  кого   однажды   действительно разлюбили.', '<b>"Максимы и моральные размышления"</b> - № 286', '', '', 'dadbab30adf0fc3c97d1c496675b8bb0'),
(1545, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Мы находим несколько решений  одного  и  того  же  вопроса  не  столько потому, что наш ум  очень  плодовит,  сколько  потому,  что  он  не  слишком прозорлив и,  вместо  того  чтобы  остановиться  на  самом  лучшем  решении, представляет нам без разбора все возможности сразу.', '<b>"Максимы и моральные размышления"</b> - № 287', '', '', '722e4ff63d22d8ba8df09d477a3cdff0'),
(1546, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'При  некоторых  обстоятельствах,  точно  так  же,  как  при   некоторых болезнях, помощь со стороны  может  иной  раз  только  повредить;  требуется большая проницательность, чтобы распознать те случаи, когда она опасна.', '<b>"Максимы и моральные размышления"</b> - № 288', '', '', '58e672db672521597ccd7c15c39ca0f2'),
(1547, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Показная простота - это утонченное лицемерие.', '<b>"Максимы и моральные размышления"</b> - № 289', '', '', 'fe29f6564b48f9b6a0822bd8b9fac7bc'),
(1548, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'В характере человека больше изъянов, чем в его уме.', '<b>"Максимы и моральные размышления"</b> - № 290', '', '', 'a4ce5e64fcd5a73d7263552edc77b922'),
(1549, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'У людских достоинств, как и у плодов, есть своя пора.', '<b>"Максимы и моральные размышления"</b> - № 291', '', '', 'efd9121676b9356cdf9512c51417821d'),
(1550, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Можно сказать, что у человеческих характеров, как и у некоторых зданий, несколько фасадов, причем не все они приятны на вид.', '<b>"Максимы и моральные размышления"</b> - № 292', '', '', 'aa09e5f51cd3d3fcebe284714d2af5c4'),
(1551, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Умеренность не имеет права хвалиться тем, что она одолевает  честолюбие и подчиняет его себе. Умеренность - это душевная бездеятельность и  леность, тогда как честолюбие - это живость и горячность,  и  они  никогда  не  живут вместе.', '<b>"Максимы и моральные размышления"</b> - № 293', '', '', '58cd09ce2459ca3809d5c0324e94b3b4'),
(1552, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Мы всегда любим тех, кто восхищается нами, но не всегда любим тех,  кем восхищаемся мы.', '<b>"Максимы и моральные размышления"</b> - № 294', '', '', '824f990e9e8ce93c52dee30beca9b184'),
(1553, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Мы редко до конца понимаем, чего мы в действительности хотим.', '<b>"Максимы и моральные размышления"</b> - № 295', '', '', '04f162d91b91b640ad8d9680acb7fe77'),
(1554, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Трудно любить тех, кого мы совсем не уважаем,  но  еще  труднее  любить тех, кого уважаем больше, чем самих себя.', '<b>"Максимы и моральные размышления"</b> - № 296', '', '', '37de2ad4ea7147a31712760c8d0c630d'),
(1555, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Соки нашего тела, совершая свой обычный и неизменный круговорот,  тайно приводят в действие и направляют нашу волю; сливаясь  в  единый  поток,  они незаметно властвуют над нами, воздействуя на все наши поступки.', '<b>"Максимы и моральные размышления"</b> - № 297', '', '', 'a525daf3bd3579bf961b73f21374cf92'),
(1556, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Признательность большинства людей порождена скрытым  желанием  добиться еще больших благодеяний.', '<b>"Максимы и моральные размышления"</b> - № 298', '', '', 'bdfeeec160bac20fc4b20c424d174ed4'),
(1557, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Почти все люди охотно расплачиваются за мелкие  одолжения,  большинство бывает  признательно  за  немаловажные,  но   почти   никто   не   чувствует благодарности за крупные.', '<b>"Максимы и моральные размышления"</b> - № 299', '', '', 'faaa6a1c1922786d95f4b43d06b437a9'),
(1558, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Иные безрассудства распространяются точно заразные болезни.', '<b>"Максимы и моральные размышления"</b> - № 300', '', '', '97caec45f62f6ff7d41598b7b25b4c0d'),
(1559, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Многие презирают жизненные  блага,  но  почти  никто  не  способен  ими поделиться.', '<b>"Максимы и моральные размышления"</b> - № 301', '', '', 'a72ce5e85dcada8680f8254d02c2f729'),
(1560, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Мы лишь тогда осмеливаемся проявлять неверие в силу и влияние  небесных светил, когда речь идет о делах несущественных.', '<b>"Максимы и моральные размышления"</b> - № 302', '', '', '3259f1fc354c12ddda3b9b839c936105'),
(1561, 1, 1, 1, 1448972772, 1448972772, 124, 0, 0, 'Какие бы похвалы нам ни расточали, мы не находим в них ничего для  себя нового.', '<b>"Максимы и моральные размышления"</b> - № 303', '', '', 'e5f3e2c7a48197cc50340d64a4acfc87'),
(1562, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Мы нередко относимся снисходительно к тем, кто тяготит нас, но  никогда не бываем снисходительны к тем, кто тяготится нами.', '<b>"Максимы и моральные размышления"</b> - № 304', '', '', 'fe3f96f12aaf9d3f9b4051c2db5db546'),
(1563, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Своекорыстие винят во всех наших преступлениях, забывая при  этом,  что оно нередко заслуживает похвалы за наши добрые дела.', '<b>"Максимы и моральные размышления"</b> - № 305', '', '', 'b988492731208e2144d6628a5010bbcc'),
(1564, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Пока человек  в  состоянии  творить  добро,  ему  не  грозит  опасность столкнуться с неблагодарностью.', '<b>"Максимы и моральные размышления"</b> - № 306', '', '', 'ab5fb12cb0b464a0de33f78bbc8e97e4'),
(1565, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Воздавать должное своим достоинствам наедине с собою столь же  разумно, сколь смехотворно превозносить их в присутствии других.', '<b>"Максимы и моральные размышления"</b> - № 307', '', '', '2859e2b3e58b40a908d6945dd2499ad2'),
(1566, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Умеренность  провозгласили  добродетелью  для  того,   чтобы   обуздать честолюбие великих людей и утешить  людей  незначительных,  обладающих  лишь скромным достоянием и скромными достоинствами.', '<b>"Максимы и моральные размышления"</b> - № 308', '', '', '69adea4a01b9da41b7d455659ffa1491'),
(1567, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Есть люди, которым на роду написано быть глупцами: они делают  глупости не только по собственному желанию, но и по воле судьбы.', '<b>"Максимы и моральные размышления"</b> - № 309', '', '', 'cd3f503ad58d5b99a28303ab03806156'),
(1568, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Бывают в жизни положения, выпутаться из которых можно только с  помощью изрядной доли безрассудства.', '<b>"Максимы и моральные размышления"</b> - № 310', '', '', 'daa969c093474db653e65ba45a3335e0'),
(1569, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Если и есть на свете люди, которые никогда не казались смешными, то это значит лишь, что никто не старался отыскать в них смешные черты.', '<b>"Максимы и моральные размышления"</b> - № 311', '', '', 'b8f2f961d552e1ab5faaa08c187b98b3'),
(1570, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Любовники только потому никогда не скучают друг с другом, что  они  все время говорят о себе.', '<b>"Максимы и моральные размышления"</b> - № 312', '', '', '5fdaaf29ba7586a3459d7f67d8128f39'),
(1571, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Почему мы запоминаем во всех подробностях то, что с нами случилось,  но неспособны запомнить, сколько раз мы рассказывали об этом одному и  тому  же лицу?', '<b>"Максимы и моральные размышления"</b> - № 313', '', '', '44cd1095465e747f2e4a6dd0a01993f8'),
(1572, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Необычайное удовольствие, с которым мы говорим о себе, должно  было  бы внушить нам подозрение, что наши собеседники его отнюдь не разделяют.', '<b>"Максимы и моральные размышления"</b> - № 314', '', '', '8517e0747a0bd79ea9bdc1e53c195ae1'),
(1573, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Нашей  полной  откровенности  с  друзьями  мешает  обычно  не   столько недоверие к ним, сколько недоверие к самим себе.', '<b>"Максимы и моральные размышления"</b> - № 315', '', '', '10e25001d771f7c4dd20eb5c71de1493'),
(1574, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Люди слабохарактерные не способны быть искренними.', '<b>"Максимы и моральные размышления"</b> - № 316', '', '', '943c343a321ced5725f26ea7f85d6375'),
(1575, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Невелика беда - услужить неблагодарному, но большое несчастье - принять услугу от подлеца.', '<b>"Максимы и моральные размышления"</b> - № 317', '', '', '4b48e52ab070a7b03b1d482edc637abe'),
(1576, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Можно излечить от безрассудства, но нельзя выпрямить кривой ум.', '<b>"Максимы и моральные размышления"</b> - № 318', '', '', '9277892e2bae7f2acf18f4c6b195867a'),
(1577, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Нам ненадолго хватило бы добрых чувств,  которые  мы  должны  питать  к нашим друзьям и благодетелям, если бы мы позволяли себе вволю говорить об их недостатках.', '<b>"Максимы и моральные размышления"</b> - № 319', '', '', '551ebf0b509bdf4055a8e6518f3e3119'),
(1578, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Восхвалять государей за достоинства, которыми они не  обладают,  значит безнаказанно наносить им оскорбление.', '<b>"Максимы и моральные размышления"</b> - № 320', '', '', '84222e671156c88f5c9f7f12b63841d8'),
(1579, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Нам легче полюбить тех,  кто  нас  ненавидит,  нежели  тех,  кто  любит сильнее, чем нам желательно.', '<b>"Максимы и моральные размышления"</b> - № 321', '', '', 'e8e314821be796e8431e40d528c50456'),
(1580, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Боится презрения лишь тот, кто его заслуживает.', '<b>"Максимы и моральные размышления"</b> - № 322', '', '', 'ff852e867affabfcf62780f410bed099'),
(1581, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Наше здравомыслие так же подвластно случаю, как и богатство.', '<b>"Максимы и моральные размышления"</b> - № 323', '', '', '5478017402d1c2771256f18087041394'),
(1582, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'В ревности больше самолюбия, чем любви.', '<b>"Максимы и моральные размышления"</b> - № 324', '', '', 'a38280bb1db09420869fe5ba78ba72b6'),
(1583, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Слабость характера нередко утешает нас  в  таких  несчастьях,  в  каких бессилен утешить разум.', '<b>"Максимы и моральные размышления"</b> - № 325', '', '', 'cafa76fac52550e7d00e25f381918e24'),
(1584, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Смешное наносит чести больший ущерб, чем само бесчестие.', '<b>"Максимы и моральные размышления"</b> - № 326', '', '', 'd7aa9d07a9b50c02b3cd02a5aedf88ec'),
(1585, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Признаваясь в маленьких недостатках, мы  тем  самым  стараемся  убедить окружающих в том, что у нас нет крупных.', '<b>"Максимы и моральные размышления"</b> - № 327', '', '', '8f4b7d596e37b6523e776854e5d0db66'),
(1586, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Зависть еще непримиримее, чем ненависть.', '<b>"Максимы и моральные размышления"</b> - № 328', '', '', '8e502f1ae419c9656fe89c252e4f8d72'),
(1587, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Иногда людям кажется, что они  ненавидят  лесть,  в  то  время  как  им ненавистна лишь та или иная ее форма.', '<b>"Максимы и моральные размышления"</b> - № 329', '', '', '87c2570e65887ab3a5a9b2b410531634'),
(1588, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Пока люди любят, они прощают.', '<b>"Максимы и моральные размышления"</b> - № 330', '', '', 'babb4e675504ed306f512657c03d0a71'),
(1589, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Труднее хранить верность той женщине,  которая  дарит  счастье,  нежели той, которая причиняет мучения.', '<b>"Максимы и моральные размышления"</b> - № 331', '', '', 'a8f8fdbde5faa4bc48530a4349269e70'),
(1590, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Женщины не сознают всей беспредельности своего кокетства.', '<b>"Максимы и моральные размышления"</b> - № 332', '', '', '76689582f87afdb4bb2aa2b9b19b1510'),
(1591, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Непреклонная строгость поведения противна женской натуре.', '<b>"Максимы и моральные размышления"</b> - № 333', '', '', '26c5261939c4100531503c49ef761b87'),
(1592, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Женщине легче преодолеть свою страсть, нежели свое кокетство.', '<b>"Максимы и моральные размышления"</b> - № 334', '', '', 'f8e3c67f3413d1aacc2e155b3cabeda5'),
(1593, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'В любви обман почти всегда заходит дальше недоверия.', '<b>"Максимы и моральные размышления"</b> - № 335', '', '', 'a92cfed230e9687edf880775444378da'),
(1594, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Бывает такая любовь, которая в высшем  своем  проявлении  не  оставляет места для ревности.', '<b>"Максимы и моральные размышления"</b> - № 336', '', '', 'adbcf0233dd4e2334cb5628343b08330'),
(1595, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Иные  достоинства  подобны  зрению  или  слуху:  люди,  лишенные   этих достоинств, не способны увидеть и оценить их в окружающих.', '<b>"Максимы и моральные размышления"</b> - № 337', '', '', 'f62ff583e0ceedfaef8dbdfc9da6417c'),
(1596, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Слишком лютая ненависть ставит нас ниже тех, кого мы ненавидим.', '<b>"Максимы и моральные размышления"</b> - № 338', '', '', '76c9c4eb03489c5194ba7aba69761c98'),
(1597, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Счастье и несчастье мы переживаем соразмерно нашему самолюбию.', '<b>"Максимы и моральные размышления"</b> - № 339', '', '', '1ba334f867563546a994e0a6841560f7'),
(1598, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Ум  у  большинства  женщин  служит  не  столько   для   укрепления   их благоразумия, сколько для оправдания их безрассудств.', '<b>"Максимы и моральные размышления"</b> - № 340', '', '', 'd30b40f1062ff348dafdca32b85b17f1'),
(1599, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Равнодушие старости не более способствует спасению души,  чем  пылкость юности.', '<b>"Максимы и моральные размышления"</b> - № 341', '', '', 'f1cdeeecfcc9f7d02c197dd7f5944907'),
(1600, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Ум и сердце человека, так же как и его речь, хранят отпечаток страны, в которой он родился.', '<b>"Максимы и моральные размышления"</b> - № 342', '', '...он родился. - Французские  исследователи  соотносят  эту  максиму либо с герцогом  д''Эперноном,  который  не  мог  избавиться  от  гасконского акцента, либо с Мазарини.', '0a8edc9c35d5867e1622b6d2e38c5fc9'),
(1601, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Чтобы стать великим человеком, нужно уметь искусно  пользоваться  всем, что предлагает судьба.', '<b>"Максимы и моральные размышления"</b> - № 343', '', '', '321f178910988a88b2562cac34e45d29'),
(1602, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Многие  люди,  подобно   растениям,   наделены   скрытыми   свойствами; обнаружить их может только случай.', '<b>"Максимы и моральные размышления"</b> - № 344', '', '', '26448e0f5c004dc9f9077c995488f0f7'),
(1603, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Только стечение обстоятельств открывает  нашу  сущность  окружающим  и, главное, нам самим.', '<b>"Максимы и моральные размышления"</b> - № 345', '', '', 'e57668ca45f4888aaf0f4eb5a34355dd'),
(1604, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Не может быть порядка в уме и сердце женщины,  если  ее  темперамент  с ними не в ладу.', '<b>"Максимы и моральные размышления"</b> - № 346', '', '', '371eb5f3f511be8ce121ab4ed484e94e'),
(1605, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Мы считаем здравомыслящими лишь тех  людей,  которые  во  всем  с  нами согласны.', '<b>"Максимы и моральные размышления"</b> - № 347', '', '', '076bc1d44c20c239b6f13ad1c2f6dcff'),
(1606, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Когда человек любит, он часто сомневается в том, во  что  больше  всего верит.', '<b>"Максимы и моральные размышления"</b> - № 348', '', '', '6bca19f21eff4d64ec7e190597831486'),
(1607, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Величайшее чудо любви в том, что она излечивает от кокетства.', '<b>"Максимы и моральные размышления"</b> - № 349', '', '', '7e0b3a186bb3efae28d331600f956387'),
(1608, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Мы потому возмущаемся людьми, которые с нами лукавят, что  они  считают себя умнее нас.', '<b>"Максимы и моральные размышления"</b> - № 350', '', '', 'a1821dfb81c80bc245be714f5e161e74'),
(1609, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Когда люди уже не любят друг друга, им трудно  найти  повод  для  того, чтобы разойтись.', '<b>"Максимы и моральные размышления"</b> - № 351', '', '', '6a71cf1aa9edf919298b4d6d8756645a'),
(1610, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Нам почти всегда  скучно  с  теми  людьми,  с  которыми  не  полагается скучать.', '<b>"Максимы и моральные размышления"</b> - № 352', '', '', 'b30a3b5db8bca7f4fe14f443f357bd90'),
(1611, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Человек истинно достойный может быть влюблен как  безумец,  но  не  как глупец.', '<b>"Максимы и моральные размышления"</b> - № 353', '', '', '7099dad7cab0d1158d0167c61cb0d8a9'),
(1612, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Иные недостатки, если  ими  умело  пользоваться,  сверкают  ярче  любых достоинств.', '<b>"Максимы и моральные размышления"</b> - № 354', '', '', 'ff38909475faf259dab62e2f1294d555'),
(1613, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Мы иногда теряем людей, о которых не столько жалеем, сколько печалимся; однако бывает и так, что мы нисколько не печалимся, хотя и жалеем об утрате.', '<b>"Максимы и моральные размышления"</b> - № 355', '', '', '7280bc14dd1a054828d91ead50a29bc4'),
(1614, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Чистосердечной  похвалой  мы  обычно  награждаем  лишь  тех,  кто  нами восхищается.', '<b>"Максимы и моральные размышления"</b> - № 356', '', '', '35dfe9a67d0377dc3180b7ab8c163be0'),
(1615, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Люди мелкого ума чувствительны к мелким обидам; люди большого  ума  все замечают и ни на что не обижаются.', '<b>"Максимы и моральные размышления"</b> - № 357', '', '', '06249d83124e7eb556ee90c8a0a435d1'),
(1616, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Истинный признак христианских добродетелей -  это  смирение;  если  его нет, все наши недостатки остаются при нас, а гордость только скрывает их  от окружающих и нередко от нас самих.', '<b>"Максимы и моральные размышления"</b> - № 358', '', '', '879a8592895bfbd6bf90da2dc3fe8fd4'),
(1617, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Неверность должна была бы убивать любовь, и не следовало  бы  ревновать тогда, когда  к  этому  есть  основания:  ревности  достоин  лишь  тот,  кто старается ее не вызывать.', '<b>"Максимы и моральные размышления"</b> - № 359', '', '', 'bd34df5b3df346fc72d47de4f19da84f'),
(1618, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Мельчайшую неверность в отношении нас мы судим куда суровее, чем  самую коварную измену в отношении других.', '<b>"Максимы и моральные размышления"</b> - № 360', '', '', '1617a13ad2c7bc5536116df7c27da1bf'),
(1619, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Ревность всегда рождается вместе с любовью, но не всегда вместе  с  нею умирает.', '<b>"Максимы и моральные размышления"</b> - № 361', '', '', '58b791fdc449265b9159bd8653fb3616'),
(1620, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Когда женщина оплакивает своего возлюбленного, это чаще  всего  говорит не о том, что она его любила, а о том,  что  она  хочет  казаться  достойной любви.', '<b>"Максимы и моральные размышления"</b> - № 362', '', '', '826b72d8262ef2a166114b4160af811a'),
(1621, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Иной раз нам не так мучительно покориться принуждению  окружающих,  как самим к чему-то себя принудить.', '<b>"Максимы и моральные размышления"</b> - № 363', '', '', '6ead13cceb96331b9a1818000bfd0ae2'),
(1622, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Всем достаточно известно, что не подобает  человеку  говорить  о  своей жене, но недостаточно известно, что еще меньше ему подобает говорить о себе.', '<b>"Максимы и моральные размышления"</b> - № 364', '', '', 'adf799d2fa0949c32bc52327b1d97a95'),
(1623, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Иные достоинства вырождаются в недостатки,  если  они  присущи  нам  от рождения,  а  другие  никогда   не   достигают   совершенства,   если,   они благоприобретенные;  так,  например,  бережливость  и  осмотрительность  нам должен внушить разум, но доброту и доблесть должна подарить природа.', '<b>"Максимы и моральные размышления"</b> - № 365', '', '', '96e15109d327119642330d6b70730d96'),
(1624, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Как бы мало мы ни доверяли нашим собеседникам, нам все же кажется,  что с нами они искреннее, чем с кем бы то ни было.', '<b>"Максимы и моральные размышления"</b> - № 366', '', '', 'bd6f046110b3ef041fd0945400dfd6b7'),
(1625, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'На  свете  мало  порядочных  женщин,  которым  не  опостылела   бы   их добродетель.', '<b>"Максимы и моральные размышления"</b> - № 367', '', '', '2f52841d450de94368b74c958228a216'),
(1626, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Почти все порядочные женщины - это нетронутые сокровища, которые потому и в неприкосновенности, что их никто не ищет.', '<b>"Максимы и моральные размышления"</b> - № 368', '', '', '4de5be5837e9b7526e5cbb923ec44ecc'),
(1627, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Усилия, которые мы прилагаем, чтобы не влюбиться, порою  причиняют  нам больше мучений, чем жестокость тех, в кого мы уже влюбились.', '<b>"Максимы и моральные размышления"</b> - № 369', '', '', 'cd488451ef6dbc18ede4b2f6a7be4fbc'),
(1628, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Трусы обычно не сознают всей силы своего страха.', '<b>"Максимы и моральные размышления"</b> - № 370', '', '', '78e84a686063fc7a4e41145541b2e0a7'),
(1629, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Тот, кого разлюбили, обычно сам виноват, что вовремя этого не заметил.', '<b>"Максимы и моральные размышления"</b> - № 371', '', '', '545255e79b403aecdc12f1cc92b47e66'),
(1630, 1, 1, 1, 1448972773, 1448972773, 124, 0, 0, 'Юношам часто кажется, что они естественны, тогда как на самом деле  они просто невоспитанны и грубы.', '<b>"Максимы и моральные размышления"</b> - № 372', '', '', '1ffdf55526f7b931cd8374eb3baa507f'),
(1631, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Иной раз, проливая слезы, мы ими обманываем  не  только  других,  но  и самих себя.', '<b>"Максимы и моральные размышления"</b> - № 373', '', '', 'fc67fdfb4ac2ef227bf69f8d52ba45dc'),
(1632, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Весьма заблуждается тот, кто думает,  будто  он  любит  свою  любовницу только за ее любовь к нему.', '<b>"Максимы и моральные размышления"</b> - № 374', '', '', 'b95836e04faa13b9af814af0f7958f26'),
(1633, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Люди  недалекие  обычно  осуждают  все,  что  выходит  за  пределы   их понимания.', '<b>"Максимы и моральные размышления"</b> - № 375', '', '', '6b3c2d72f4e961ddc2894575c891cf51'),
(1634, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Настоящая дружба не знает зависти, а настоящая любовь - кокетства.', '<b>"Максимы и моральные размышления"</b> - № 376', '', '', '6960e1af562c766c329f0d72c6e64e63'),
(1635, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Лишены прозорливости не те люди,  которые  не  достигают  цели,  а  те, которые прошли мимо нее.', '<b>"Максимы и моральные размышления"</b> - № 377', '', '', 'fa5fbbc75c3d7e786045fcd762c76379'),
(1636, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Можно дать другому разумный совет,  но  нельзя  научить  его  разумному поведению.', '<b>"Максимы и моральные размышления"</b> - № 378', '', '', '9e1971e7b487ac90d1262c3b0b2f2954'),
(1637, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Все, что перестает удаваться, перестает и привлекать.', '<b>"Максимы и моральные размышления"</b> - № 379', '', '', 'bee79b7f879cfd2861a6793ec2219ad7'),
(1638, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Как все предметы лучше всего видны на свету,  так  наши  добродетели  и пороки отчетливее всего выступают в лучах удачи.', '<b>"Максимы и моральные размышления"</b> - № 380', '', '', '9ef7812cf27fec97d58cac72cd9526b2'),
(1639, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Верность, которую удается сохранить только ценой больших усилий, ничуть не лучше измены.', '<b>"Максимы и моральные размышления"</b> - № 381', '', '', 'd493cdca3e69394f4f1ddb95424f7f03'),
(1640, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Наши поступки подобны строчкам буриме: каждый связывает их  с  чем ему заблагорассудится.', '<b>"Максимы и моральные размышления"</b> - № 382', '', '...буриме... - стихотворение на заданные рифмы.', '8ffc3cf6756e6831e501c353c16ea679'),
(1641, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Наша искренность в немалой доле вызвана желанием поговорить  о  себе  и выставить свои недостатки в благоприятном свете.', '<b>"Максимы и моральные размышления"</b> - № 383', '', '', '8e4c16343bc4968b5b892ed356532e49'),
(1642, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Нам следовало бы удивляться только нашей  способности  чему-нибудь  еще удивляться.', '<b>"Максимы и моральные размышления"</b> - № 384', '', '', 'b11682026f865ff39bf189b72b967357'),
(1643, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Одинаково трудно угодить и тому, кто любит очень сильно,  и  тому,  кто уже совсем не любит.', '<b>"Максимы и моральные размышления"</b> - № 385', '', '', 'f0185485baf08b2ce328aa6a6dd44d1c'),
(1644, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Как раз те люди, которые во что  бы  то  ни  стало  хотят  всегда  быть правыми, чаще всего бывают неправы.', '<b>"Максимы и моральные размышления"</b> - № 386', '', '', '5ec5bfc4473f6def4cdeefb976b8f05c'),
(1645, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Глупец не может быть добрым: для этого у него слишком мало мозгов.', '<b>"Максимы и моральные размышления"</b> - № 387', '', '', 'a496ed96f98949a80f019258b4b994ff'),
(1646, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Если тщеславие и не повергает в  прах  все  наши  добродетели,  то,  во всяком случае, оно их колеблет.', '<b>"Максимы и моральные размышления"</b> - № 388', '', '', 'df5ecc8de556d66dd0b0408d74038949'),
(1647, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Мы потому так нетерпимы к  чужому  тщеславию,  что  оно  уязвляет  наше собственное.', '<b>"Максимы и моральные размышления"</b> - № 389', '', '', 'b5e3f0b8d87f02c87f4b27d1ca2962b1'),
(1648, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Легче пренебречь выгодой, чем отказаться от прихоти.', '<b>"Максимы и моральные размышления"</b> - № 390', '', '', '1e2f91d9abd15a439f05974a2e1f3fd6'),
(1649, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Судьбу считают слепой главным образом те, кому она не дарует удачи.', '<b>"Максимы и моральные размышления"</b> - № 391', '', '', '085766af9ed7b62d240772f6b81ef521'),
(1650, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'С  судьбой  следует  обходиться,  как  со  здоровьем:  когда  она   нам благоприятствует  -  наслаждаться  ею,  а  когда  начинает  капризничать   - терпеливо выжидать, не прибегая без особой необходимости к сильнодействующим средствам.', '<b>"Максимы и моральные размышления"</b> - № 392', '', '', 'c7ddcb2ac1229bfe045d4d51462a216e'),
(1651, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Мещанские замашки порою скрадываются в кругу  военных,  но  они  всегда заметны при дворе.', '<b>"Максимы и моральные размышления"</b> - № 393', '', '', 'f07bbda850404c38bec0fcd9a9506ebb'),
(1652, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Можно перехитрить кого-то одного, но нельзя перехитрить всех на свете.', '<b>"Максимы и моральные размышления"</b> - № 394', '', '', '4e91545395af658560031d7e0cae86e3'),
(1653, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Порою легче стерпеть обман того, кого любишь, чем услышать от него  всю правду.', '<b>"Максимы и моральные размышления"</b> - № 395', '', '', 'f8b7be43c2830a4291a46600d30f7d86'),
(1654, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Женщина долго хранит верность первому своему любовнику, если только она не берет второго.', '<b>"Максимы и моральные размышления"</b> - № 396', '', '', '98cad437db5860732eec59b3bdad6f17'),
(1655, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Мы не дерзаем огульно утверждать, что у нас совсем  нет  пороков,  а  у наших врагов совсем нет добродетелей, но в каждом отдельном случае мы  почти готовы этому поверить.', '<b>"Максимы и моральные размышления"</b> - № 397', '', '', '2954ffbd8a1215efbe5412e449b50596'),
(1656, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Мы охотнее признаемся в лености, чем в  других  наших  недостатках;  мы внушили себе, что она проистекает из наших миролюбивых  добродетелей  и,  не нанося большого ущерба прочим достоинствам, лишь умеряет их проявление.', '<b>"Максимы и моральные размышления"</b> - № 398', '', '', '8ad007bd21deec117617746a140d8701'),
(1657, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Людям  иной  раз   присуща   величавость,   которая   не   зависит   от благосклонности судьбы: она  проявляется  в  манере  держать  себя,  которая выделяет человека и словно пророчит ему блистательное будущее, а также в той оценке, которую он невольно себе дает. Именно это качество привлекает к  нам уважение окружающих и возвышает над ними так, как не могли бы  возвысить  ни происхождение, ни сан, ни даже добродетели.', '<b>"Максимы и моральные размышления"</b> - № 399', '', '', '44dc75db9c771b62ded08174ccd470c8'),
(1658, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Достоинствам не  всегда  присуща  величавость,  но  величавости  всегда присущи хоть какие-нибудь достоинства.', '<b>"Максимы и моральные размышления"</b> - № 400', '', '', 'b840a5d4267770ea7fe3889355c29e3d'),
(1659, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Величавость так же к лицу добродетели,  как  драгоценный  убор  к  лицу красивой женщине.', '<b>"Максимы и моральные размышления"</b> - № 401', '', '', 'b299b27ec891a4411458567f1f0bd823'),
(1660, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'В волокитстве есть все что угодно, кроме любви.', '<b>"Максимы и моральные размышления"</b> - № 402', '', '', '6de20efb03dae3867917c2347d44de0a'),
(1661, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Чтобы возвысить нас, судьба порой пользуется нашими недостатками;  так, например, иные  беспокойные  люди  были  вознаграждены  по  заслугам  только потому, что все старались любой ценой отделаться от них.', '<b>"Максимы и моральные размышления"</b> - № 403', '', '...отделаться  от  них.  -  Намек  на  Великого   Конде,   которому предпочитали доверять командование армией, чем оставлять его при дворе.', '2c2c160d8ace805e62ea1ec2bafc2d99');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(1662, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'По-видимому, природа скрывает  в  глубинах  нашей  души  способности  и дарования, о которых мы и сами не подозреваем; только страсти пробуждают  их к жизни и порою сообщают нам такую проницательность и твердость,  каких  при обычных условиях мы никогда не могли бы достичь.', '<b>"Максимы и моральные размышления"</b> - № 404', '', '', 'b381f32264d17aadefdfac8a37c14c23'),
(1663, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Мы вступаем в различные возрасты нашей жизни  точно  новорожденные,  не имея за плечами никакого опыта, сколько бы нам ни было лет.', '<b>"Максимы и моральные размышления"</b> - № 405', '', '', '296983eb329124b3700cf92e29dd903a'),
(1664, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Кокетки притворяются, будто ревнуют своих любовников, желая скрыть, что они просто завидуют другим женщинам.', '<b>"Максимы и моральные размышления"</b> - № 406', '', '', '5377b95fccd413d6787e32f84dca695b'),
(1665, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Когда нам удается надуть других, они редко кажутся нам такими дураками, какими кажемся мы самим себе, когда другим удается надуть нас.', '<b>"Максимы и моральные размышления"</b> - № 407', '', '', '806e55856377fb862269962306e6fa09'),
(1666, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'В особенно смешное положение ставят себя  те  старые  женщины,  которые помнят, что когда-то были привлекательны, но забыли, что давно уже  утратили былое очарование.', '<b>"Максимы и моральные размышления"</b> - № 408', '', '', '71b1e1bf08e57bf74da16ff4c70ea4d3'),
(1667, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Нередко нам пришлось бы стыдиться своих  самых  благородных  поступков, если бы окружающим были известны наши побуждения.', '<b>"Максимы и моральные размышления"</b> - № 409', '', '', '3c237deaac6c134ebae652e56618c247'),
(1668, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Величайший  подвиг  дружбы  не  в  том,  чтобы  показать   другу   наши недостатки, а в том, чтобы открыть ему глаза на его собственные.', '<b>"Максимы и моральные размышления"</b> - № 410', '', '', '16fab56d3f9714ee32730eb916d254a3'),
(1669, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Любой наш недостаток более простителен, чем уловки, на которые мы идем, чтобы его скрыть.', '<b>"Максимы и моральные размышления"</b> - № 411', '', '', 'a991d37ee2b867f70186995e411e2f7b'),
(1670, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Каким бы тяжелым позором  мы  себя  ни  покрыли,  у  нас  почти  всегда остается возможность восстановить свое доброе имя.', '<b>"Максимы и моральные размышления"</b> - № 412', '', '', 'e975db9ededcd726d62243568a869e5a'),
(1671, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Не может долго нравиться тот, кто умен всегда на один лад.', '<b>"Максимы и моральные размышления"</b> - № 413', '', '', '49ad28403046e37f660238cc92eb1b21'),
(1672, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Дуракам и безумцам весь мир представляется в свете их сумасбродства.', '<b>"Максимы и моральные размышления"</b> - № 414', '', '', '904b4afd54501372ebea1deb0101d408'),
(1673, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Ум служит нам порою лишь для того, чтобы смело делать глупости.', '<b>"Максимы и моральные размышления"</b> - № 415', '', '', '62881e4ba4dc36a630a99e0f355aec8c'),
(1674, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Горячность, которая с годами все возрастает, уже граничит с глупостью.', '<b>"Максимы и моральные размышления"</b> - № 416', '', '', 'b7b1475b890d3f961a83d4e2971b909b'),
(1675, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Тот, кто излечивается от любви первым, - всегда излечивается полнее.', '<b>"Максимы и моральные размышления"</b> - № 417', '', '', '5a0557bc7b3bd7e07cd9f45be1420034'),
(1676, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Молодым женщинам, не желающим прослыть кокетками, и  пожилым  мужчинам, не желающим казаться смешными, следует говорить о любви так,  словно  они  к ней не причастны.', '<b>"Максимы и моральные размышления"</b> - № 418', '', '', 'fe33f8c5504a03fdc8c0dfc5a9e3a26c'),
(1677, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Мы можем казаться значительными, занимая положение, которое ниже  наших достоинств, но мы нередко кажемся ничтожными, занимая положение, слишком для нас высокое.', '<b>"Максимы и моральные размышления"</b> - № 419', '', '', '4ff533c8b32d22fd1a4a80afb1a83de1'),
(1678, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Нам часто представляется, что мы стойки в несчастии, хотя на самом деле мы только угнетены; мы переносим его, не смея на него взглянуть, как  трусы, которым так страшно защищаться, что они готовы дать себя убить.', '<b>"Максимы и моральные размышления"</b> - № 420', '', '', '6c712a5fd5b9f95c00d203699e5f464e'),
(1679, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Больше всего оживляет беседу не ум, а взаимное доверие.', '<b>"Максимы и моральные размышления"</b> - № 421', '', '', 'e1b61ac3d861a1f3316ff39b2e0f76f1'),
(1680, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Любая страсть толкает на ошибки, но на самые глупые толкает любовь.', '<b>"Максимы и моральные размышления"</b> - № 422', '', '', '85f971d46cde4a576ef34fe1f17b3bdc'),
(1681, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Как мало на свете стариков, владеющих искусством быть стариками!', '<b>"Максимы и моральные размышления"</b> - № 423', '', '', 'adc8e6954eca31c4ec52805809a845ec'),
(1682, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Нам нравится наделять себя недостатками, противоположными тем,  которые присущи нам на самом деле: слабохарактерные люди, например, любят хвастаться упрямством.', '<b>"Максимы и моральные размышления"</b> - № 424', '', '', 'dce5d0bce5ffffeb11afb73dbccddbee'),
(1683, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Проницательность придает нам  такой  всезнающий  вид,  что  она  льстит нашему тщеславию больше, чем все прочие качества ума.', '<b>"Максимы и моральные размышления"</b> - № 425', '', '', '097aa5ace793123ba555d4dab22e708f'),
(1684, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Прелесть новизны и долгая  привычка,  при  всей  их  противоположности, одинаково мешают нам видеть недостатки наших друзей.', '<b>"Максимы и моральные размышления"</b> - № 426', '', '', 'af119428609cff0a24e4c01c25a0edbb'),
(1685, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Большинство друзей внушает отвращение к  дружбе,  а  большинство  людей благочестивых - к благочестию.', '<b>"Максимы и моральные размышления"</b> - № 427', '', '', '9c07a62dbad2e1ec569022c06b617530'),
(1686, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Мы охотно прощаем нашим друзьям недостатки, которые нас не задевают.', '<b>"Максимы и моральные размышления"</b> - № 428', '', '', '8bed9b71b126104bdf7ca24cdb043125'),
(1687, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Влюбленная  женщина  скорее  простит   большую   нескромность,   нежели маленькую неверность.', '<b>"Максимы и моральные размышления"</b> - № 429', '', '', '125c77ad8d38f2a50989395f9aa55a5d'),
(1688, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'На старости любви, как и на старости лет, люди еще живут  для  скорбей, но уже не живут для наслаждений.', '<b>"Максимы и моральные размышления"</b> - № 430', '', '', 'fee8b6192e3e87558adadc0f7020d86c'),
(1689, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Ничто так не мешает естественности, как желание казаться естественным.', '<b>"Максимы и моральные размышления"</b> - № 431', '', '', '09e3de1110ab8f6b285993b1377deac0'),
(1690, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Чистосердечно  хвалить  добрые  дела  -  значит  до  некоторой  степени принимать в них участие.', '<b>"Максимы и моральные размышления"</b> - № 432', '', '', '9ebaf46d09832cf1cd7aa6541a141903'),
(1691, 1, 1, 1, 1448972774, 1448972774, 124, 0, 0, 'Вернейший признак высоких добродетелей - от самого  рождения  не  знать зависти.', '<b>"Максимы и моральные размышления"</b> - № 433', '', '', '2b8b675a2814419d18259af071f9fe33'),
(1692, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Будучи обмануты друзьями, мы можем равнодушно принимать  проявления  их дружбы, но должны сочувствовать им в их несчастьях.', '<b>"Максимы и моральные размышления"</b> - № 434', '', '', 'f41be99b96e26006effd710d4539a218'),
(1693, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Миром правят судьба и прихоть.', '<b>"Максимы и моральные размышления"</b> - № 435', '', '', '73d36846c4978f52a9fabb944c13b0ee'),
(1694, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Легче познать людей вообще, чем одного человека в частности.', '<b>"Максимы и моральные размышления"</b> - № 436', '', '', 'a5060a2aaca9d1afc8c765f1b134e2b1'),
(1695, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'О достоинствах человека нужно судить не по его хорошим качествам, а  по тому, как он ими пользуется.', '<b>"Максимы и моральные размышления"</b> - № 437', '', '', 'd790195772eabbde71d3c2633933c036'),
(1696, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Наша благодарность иногда  бывает  так  велика,  что,  расплачиваясь  с друзьями за сделанное нам добро, мы еще оставляем их у себя в долгу.', '<b>"Максимы и моральные размышления"</b> - № 438', '', '', '7ec2753baf3aae536cd4e97244fc10ec'),
(1697, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'У нас нашлось бы очень мало страстных желаний, если бы мы точно  знали, чего мы хотим.', '<b>"Максимы и моральные размышления"</b> - № 439', '', '', 'a5f6aee9e6a086e94b6f2c3f6644feb2'),
(1698, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Женщины в большинстве своем оттого так безразличны к  дружбе,  что  она кажется им пресной в сравнении с любовью.', '<b>"Максимы и моральные размышления"</b> - № 440', '', '', '031e20cc3bb3e385ce608071bef60aac'),
(1699, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'В дружбе, как и в любви, чаще доставляет счастье то, чего мы не  знаем, нежели то, что нам известно.', '<b>"Максимы и моральные размышления"</b> - № 441', '', '', '6ccb90a9c2e72146f837201e542279e0'),
(1700, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Мы стараемся вменить себе в заслугу те недостатки,  которых  не  желаем исправлять.', '<b>"Максимы и моральные размышления"</b> - № 442', '', '', '7e72ef46a852424746937b9cd5ef8841'),
(1701, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Даже самые бурные страсти порою дают нам передышку, и только  тщеславие терзает нас неотступно.', '<b>"Максимы и моральные размышления"</b> - № 443', '', '', 'abb09e5baa00d049798e4cad7cc517f8'),
(1702, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Старые безумцы еще безумнее молодых.', '<b>"Максимы и моральные размышления"</b> - № 444', '', '', '7cdc58b800266c953f8de80e960ddfec'),
(1703, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Слабохарактерность еще дальше от добродетели, чем порок.', '<b>"Максимы и моральные размышления"</b> - № 445', '', '', 'a12a729cc1f4e3046fd0739c57144874'),
(1704, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Стыд и ревность потому причиняют нам  такие  муки,  что  тут  бессильно помочь даже тщеславие.', '<b>"Максимы и моральные размышления"</b> - № 446', '', '', 'e551b3b1be17744f10b6112d1faee7e1'),
(1705, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Приличие - это наименее важный из  всех  законов  общества  и  наиболее чтимый.', '<b>"Максимы и моральные размышления"</b> - № 447', '', '', 'f2a2e17c31f66ebee8dc0b30529ce232'),
(1706, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Здравомыслящему человеку легче подчиняться сумасбродам,  чем  управлять ими.', '<b>"Максимы и моральные размышления"</b> - № 448', '', '', '7f2ea80cea3d8fc0fea0c3013cc73cb7'),
(1707, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Когда судьба возносит нас сразу на такую высоту, о которой мы не  могли и мечтать, то почти всегда оказывается,  что  мы  не  в  состоянии  достойно держать себя в новом положении.', '<b>"Максимы и моральные размышления"</b> - № 449', '', '', 'bc2b9a4026f273b8e78a53fd857d1027'),
(1708, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Наша гордость часто возрастает за счет недостатков, которые нам удалось преодолеть.', '<b>"Максимы и моральные размышления"</b> - № 450', '', '', '02d3ecec83bff831f1353e1c9510fa26'),
(1709, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Нет глупцов более несносных, чем те, которые не совсем лишены ума.', '<b>"Максимы и моральные размышления"</b> - № 451', '', '', 'b4fcb27f3e66ab3cae2815405e04e837'),
(1710, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Нет на свете человека, который не ценил бы  любое  свое  качество  куда выше, чем  подобное  же  качество  у  другого,  даже  самого  уважаемого  им человека.', '<b>"Максимы и моральные размышления"</b> - № 452', '', '', 'a6012bd0c6bed15e2affbc3a5db5d083'),
(1711, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'В серьезных делах следует заботиться не столько о том, чтобы  создавать благоприятные возможности, сколько о том, чтобы их не упускать.', '<b>"Максимы и моральные размышления"</b> - № 453', '', '', 'f152ab776015225df647f283dd789281'),
(1712, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Никто не прогадал  бы,  согласившись  на  то,  чтобы  о  нем  перестали говорить хорошо, при условии, что не станут говорить дурно.', '<b>"Максимы и моральные размышления"</b> - № 454', '', '', 'a83a24f070d5ee9bc94e5de391769bb6'),
(1713, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Как ни склонны люди к неправильным суждениям; все же несправедливость к подлинным достоинствам они проявляют реже, чем благосклонность к мнимым.', '<b>"Максимы и моральные размышления"</b> - № 455', '', '', '7ba25c88d165032df9b8e5650bea2016'),
(1714, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Глупые люди могут иной раз проявить ум,  но  к  здравому  суждению  они неспособны.', '<b>"Максимы и моральные размышления"</b> - № 456', '', '', '4f1fc51f35561ab67c99d0eaf3248211'),
(1715, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Мы выиграли бы в глазах людей, если бы являлись им  такими,  какими  мы всегда были и есть, а не прикидывались такими, какими никогда не были  и  не будем.', '<b>"Максимы и моральные размышления"</b> - № 457', '', '', '4d460308f959a23e76300bb8017d2c57'),
(1716, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Суждения наших врагов о нас ближе к истине, чем наши собственные.', '<b>"Максимы и моральные размышления"</b> - № 458', '', '', '3e3e5d3bfd5ac4c695f1fa187eb14bc6'),
(1717, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Существуют разные лекарства от любви, но нет ни одного надежного.', '<b>"Максимы и моральные размышления"</b> - № 459', '', '', '5b98bfb2ef81664670f9f1cfb065bab0'),
(1718, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Мы и не представляем себе, на что могут нас толкнуть наши страсти.', '<b>"Максимы и моральные размышления"</b> - № 460', '', '', '72e9b031cacbc83459c115c93968b0f4'),
(1719, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Старость - это тиран, который под  страхом  смерти  запрещает  нам  все наслаждения юности.', '<b>"Максимы и моральные размышления"</b> - № 461', '', '', '1e91857b30c93b83c710c1ed78c576a4'),
(1720, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Гордость,  заставляющая  нас  порицать  недостатки,  которых,  как  нам кажется, у нас  нет,  велит  нам  также  презирать  и  отсутствующие  у  нас достоинства.', '<b>"Максимы и моральные размышления"</b> - № 462', '', '', 'f3ccc10b61c3d43822ba9da6e5688367'),
(1721, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Сочувствие врагам, попавшим  в  беду,  чаще  всего  бывает  вызвано  не столько добротой, сколько гордостью: мы соболезнуем им для того,  чтобы  они поняли наше превосходство над ними.', '<b>"Максимы и моральные размышления"</b> - № 463', '', '', '78b420ca310fae1fbf3b8a8cf000892b'),
(1722, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Существует такая степень счастья и горя,  которая  выходит  за  пределы нашей способности чувствовать.', '<b>"Максимы и моральные размышления"</b> - № 464', '', '', 'b982eab2a6caa73abc6fbb243b8d0de0'),
(1723, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Насколько  преступление  легче  находит   себе   покровителей,   нежели невинность!', '<b>"Максимы и моральные размышления"</b> - № 465', '', '', '5f7dc03388cdb76331830ba690647302'),
(1724, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Все бурные страсти не к лицу женщинам, но менее других  им  не  к  лицу любовь.', '<b>"Максимы и моральные размышления"</b> - № 466', '', '', '2b89a514ba0d5d348d781489b2b2b70a'),
(1725, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Тщеславие чаще заставляет нас идти против наших склонностей, чем разум.', '<b>"Максимы и моральные размышления"</b> - № 467', '', '', 'afa307f2a1d08dbe8d796b5376afea9c'),
(1726, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Порою из дурных качеств складываются великие таланты.', '<b>"Максимы и моральные размышления"</b> - № 468', '', '', '6314e59363b123985a0285e2225e6561'),
(1727, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Мы никогда не стремимся страстно к тому, к  чему  стремимся  только разумом.', '<b>"Максимы и моральные размышления"</b> - № 469', '', '', 'a6d78670cd4c440a6106552cdbc0cba8'),
(1728, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Все наши качества, дурные, равно как  и  хорошие,  неопределенны  и сомнительны, и почти всегда они зависят от милости случая.', '<b>"Максимы и моральные размышления"</b> - № 470', '', '', 'f49e653e9d7dbb952ae2ad44de607836'),
(1729, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Когда женщина влюбляется впервые, она  любит  своего  любовника;  в дальнейшем она любит уже только любовь.', '<b>"Максимы и моральные размышления"</b> - № 471', '', '', '7c1ffa876617a3d9c1fe5a42ce61da90'),
(1730, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'У гордости, как и  у  других  страстей,  есть  свои  причуды:  люди стараются скрыть, что они ревнуют сейчас, но  хвалятся  тем,  что  ревновали когда-то и способны ревновать и впредь.', '<b>"Максимы и моральные размышления"</b> - № 472', '', '', '700def485c1e5768a7fbc30a011f2819'),
(1731, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Как  ни  редко  встречается  настоящая  любовь,  настоящая  дружба встречается еще реже.', '<b>"Максимы и моральные размышления"</b> - № 473', '', '', '7a660dc069557e226b0082d7fb8790ed'),
(1732, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Мало на свете женщин, достоинства которых пережили бы их красоту.', '<b>"Максимы и моральные размышления"</b> - № 474', '', '', '3fc02fd31837cf2316c3f623725c43f2'),
(1733, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Желание вызвать жалость или восхищение - вот что нередко составляет основу нашей откровенности.', '<b>"Максимы и моральные размышления"</b> - № 475', '', '', 'ac04eaee968e07ff7f558d68441c1d3f'),
(1734, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Наша  зависть  всегда  долговечнее  чужого  счастья,  которому  мы завидуем.', '<b>"Максимы и моральные размышления"</b> - № 476', '', '', '797abddb71fe62b47216deffdabfb0d1'),
(1735, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Твердость характера заставляет людей сопротивляться любви, но в  то же время она сообщает этому чувству пылкость и  длительность;  люди  слабые, напротив, легко загораются страстью, но  почти  никогда  не  отдаются  ей  с головой.', '<b>"Максимы и моральные размышления"</b> - № 477', '', '', '1157e96ece0351d6977c04b68bc05fca'),
(1736, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Никакому воображению не придумать такого  множества  противоречивых чувств, какие обычно уживаются в одном человеческом сердце.', '<b>"Максимы и моральные размышления"</b> - № 478', '', '', 'fcad7ef6575a7ccdff83c9418a4c4c35'),
(1737, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Истинно мягкими могут быть только  люди  с  твердым  характером:  у остальных же кажущаяся мягкость - это чаще всего  просто  слабость,  которая легко превращается в озлобленность.', '<b>"Максимы и моральные размышления"</b> - № 479', '', '', '6bdeb498f4a7bda8e23c8db6a1f0aac5'),
(1738, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Опасно упрекать в робости тех, кого хотят от нее исцелить.', '<b>"Максимы и моральные размышления"</b> - № 480', '', '', 'e3bc5f0654de166de97c61362f939d84'),
(1739, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Нет качества  более  редкого,  чем  истинная  доброта:  большинство людей, считающих себя добрыми, только снисходительны или слабы.', '<b>"Максимы и моральные размышления"</b> - № 481', '', '', '1927c32e78e1ff4cfc046de913c3a430'),
(1740, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Наш разум, по своей лености и косности, занят обычно лишь  тем,  что ему легко или приятно; эта привычка ограничивает наши познания, и никто еще не дал  себе  труда  обогатить  и  расширить  свой  разум  до пределов возможного.', '<b>"Максимы и моральные размышления"</b> - № 482', '', '', '07df147e04f9ceaa96c5e15538e9e54e'),
(1741, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Люди злословят обычно не  столько  из  желания  навредить,  сколько  из тщеславия.', '<b>"Максимы и моральные размышления"</b> - № 483', '', '', '83c7467dd365bd70712659a05fe0cf9f'),
(1742, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Пока угасающая страсть все еще волнует наше сердце, оно более склонно к новой любви, чем впоследствии, когда наступает полное исцеление.', '<b>"Максимы и моральные размышления"</b> - № 484', '', '', 'ce21950afe428138b21df48aed7e1f3d'),
(1743, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Те, кому довелось пережить большие страсти, потом всю жизнь и  радуются своему исцелению и горюют о нем.', '<b>"Максимы и моральные размышления"</b> - № 485', '', '', '176d33a63f883fabf926c6d86a7c03f0'),
(1744, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Люди независтливые встречаются  еще  реже,  чем  бескорыстные.', '<b>"Максимы и моральные размышления"</b> - № 486', '', '', '97c36e29048485310b083805e0982f90'),
(1745, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Наш  ум ленивее, чем тело.', '<b>"Максимы и моральные размышления"</b> - № 487', '', '', '28fe916dc48aae0e9f8a424bb3b73249'),
(1746, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Наше душевное спокойствие или смятение зависят не столько от  важнейших событий нашей жизни, сколько от удачного или неприятного для  нас  сочетания житейских мелочей.', '<b>"Максимы и моральные размышления"</b> - № 488', '', '', '4cfe4a859d2c8d8aa87bf76e9158d5b4'),
(1747, 1, 1, 1, 1448972775, 1448972775, 124, 0, 0, 'Как ни злы люди,  они  все  же  не  осмеливаются  открыто  преследовать добродетель. Поэтому, готовясь  напасть  на  нее,  они  притворяются,  будто считают ее лицемерной, или же приписывают ей какие-нибудь преступления.', '<b>"Максимы и моральные размышления"</b> - № 489', '', '', 'fb156939ea7228e4eed4b41eb6fe65f8'),
(1748, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Люди часто изменяют любви ради честолюбия,  но  потом  уже  никогда  не изменяют честолюбию ради любви.', '<b>"Максимы и моральные размышления"</b> - № 490', '', '', '4872d44fff34feab92e7a3dcdc02b233'),
(1749, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Непомерная скупость почти всегда ошибается в своих расчетах: она  чаще, чем все другие страсти, уходит от цели, к которой стремится,  и  оказывается во власти настоящего в ущерб будущему.', '<b>"Максимы и моральные размышления"</b> - № 491', '', '', 'a7bc2e58ff538739cb64240a86a3b319'),
(1750, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Скупость нередко приводит к  самым  противоречивым  следствиям:  многие люди  приносят  все  свое  состояние  в  жертву  отдаленным  и  сомнительным надеждам, другие же пренебрегают крупными выгодами в будущем  ради  мелочной сегодняшней наживы.', '<b>"Максимы и моральные размышления"</b> - № 492', '', '', 'c0c3d51316346cef9fe2c65c40e0aeae'),
(1751, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Людям,  видно,  мало  своих   недостатков:   они   еще.   умножают   их всевозможными  чудачествами,  которыми  словно   бы   даже   гордятся;   эти странности,  взращенные  с  таким  усердием,  становятся  в   конце   концов природными недостатками и отделаться от них уже невозможно.', '<b>"Максимы и моральные размышления"</b> - № 493', '', '', '1e73a18d53b86bd891e8f248417a91b4'),
(1752, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Насколько ясно люди понимают свои ошибки, видно из того, что, рассказывая о своем поведении, они всегда умеют выставить его  в  благоприятном свете: то самое самолюбие, которое обычно ослепляет их  ум,  в  этом  случае придает ему такую зоркость и проницательность, что им удается  ловко  утаить или смягчить любую мелочь, способную вызвать неодобрение.', '<b>"Максимы и моральные размышления"</b> - № 494', '', '', 'd01230d7cd901c109978c4abcbcbf261'),
(1753, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Впервые вступая в свет, молодые люди  должны  быть  застенчивы  или даже неловки: уверенность и  непринужденность  манер  обычно  оборачиваются наглостью.', '<b>"Максимы и моральные размышления"</b> - № 495', '', '', 'b8fedc8326174a2d2e43d8c26e292178'),
(1754, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Людские ссоры не длились бы так долго, если бы  вся  вина  была  на одной стороне.', '<b>"Максимы и моральные размышления"</b> - № 496', '', '', '22279e57dce66e6aad8031a6c393c08e'),
(1755, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Быть молодой, но некрасивой так же неутешительно для  женщины,  как быть красивой, но немолодой.', '<b>"Максимы и моральные размышления"</b> - № 497', '', '', 'e290e1f923e39776ea02262d3d560589'),
(1756, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Есть люди столь ветреные и легковесные, что у них не может быть  ни крупных недостатков, ни подлинных достоинств.', '<b>"Максимы и моральные размышления"</b> - № 498', '', '', 'dc3fa229e86ca85a3d2119d5c93a4aee'),
(1757, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Молва припоминает женщине ее первого любовника  обычно  лишь  после того, как она завела себе второго.', '<b>"Максимы и моральные размышления"</b> - № 499', '', '', '75cfaa404391b772e7668e7b34e91ca1'),
(1758, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Есть  люди,  столь  поглощенные  собой,  что,   влюбившись,   они ухитряются больше думать о собственной любви, чем о предмете своей страсти.', '<b>"Максимы и моральные размышления"</b> - № 500', '', '', '553bc4d0743821a531cca029b7693676'),
(1759, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Как ни приятна любовь, все же ее внешние проявления доставляют  нам больше радости, чем она сама.', '<b>"Максимы и моральные размышления"</b> - № 501', '', '', '2126ea70f2c3aed6dc9a7b5ba5c4f862'),
(1760, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Ум ограниченный, но здравый в конце  концов  не  так  утомителен  в собеседнике, как ум широкий, но путаный.', '<b>"Максимы и моральные размышления"</b> - № 502', '', '', 'e306b051e497a2bdf00f06592aa1f436'),
(1761, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Терзания ревности - самые мучительные из человеческих терзаний, и к тому же менее всего внушающие сочувствие тому, кто их причиняет.', '<b>"Максимы и моральные размышления"</b> - № 503', '', '', '72a87200ff091b8d86a78b72b862797c'),
(1762, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'После всех рассуждений о лицемерности многих показных  добродетелей нужно сказать несколько слов и о лицемерности презрения к смерти. Я  имею  в виду то презрение, о котором говорят безбожники, похваляясь, что черпают его не в уповании на лучшую жизнь, а в своей собственной  неустрашимости.  Между стойким приятием смерти и  презрением  к  ней  -  огромная  разница:  первое встречается довольно часто, второе же, по моему мнению, не бывает  искренним никогда. Правда, было написано множество убедительных трактатов,  в  которых доказывалось, что смерть совсем не страшна; самые слабые люди, точно так же, Как славнейшие герои, явили тысячи знаменитых примеров, подтверждающих  такой  взгляд.  Я  убежден, однако,  что  его  никогда  не  разделял  ни  один  здравомыслящий  человек. Настойчивость, которую проявляют приверженцы этого взгляда, пытаясь  внушить его другим и самим себе, уже говорит о том, что эта задача  не  из  легких. Можно по каким-либо причинам питать отвращение к жизни, но нельзя  презирать смерть. Даже люди, добровольно обрекающие себя на  нее,  отнюдь  не  считают смерть такой уж малостью; напротив, они, как и все остальные,  страшатся,  а порой и отвергают ее, если она приходит к ним не той дорогой, какую они  для нее избрали. Колебания, которым  подвержено  мужество  доблестнейших  людей, объясняется именно тем, что смерть  не  всегда  рисуется  их  воображению  с одинаковой яркостью. Все дело в том, что они  презирают  смерть,  пока  не постигли ее, но, постигнув,  поддаются  страху.  Следует  всячески  избегать мыслей о ней и обо всем, что ее окружает, иначе она покажется нам величайшим бедствием. Самые смелые и самые разумные люди - это те, которые  под  любыми благовидными предлогами стараются не думать о смерти. Всякий, кому  довелось узнать ее такой, какова она в действительности, понимает,  что  она  ужасна. Единственным  источником  стойкости  для  философов  всех  времен   являлась неизбежность смерти. Они считали необходимым с готовностью идти  туда,  куда не могли не идти, и, будучи не в состоянии навеки сохранить свою жизнь,  изо всех сил старались увековечить хотя бы свою славу и спасти от крушения  все, что возможно. Ограничимся же тем, что ради сохранения нашего достоинства  не станем даже самим себе признаваться в наших мыслях о смерти и  возложим  все надежды на бодрость нашего духа, а не на шаткие рассуждения о том,  будто  к ней следует приближаться безбоязненно. Желание стяжать  себе  славу  стойкой смертью, утешительные мысли о печали окружающих, надежда оставить после себя доброе имя, уверенность в освобождении от жизненных тягот и прихотей  судьбы - все это недурные средства, но ни одно из них нельзя считать  надежным.  От них не больше проку, чем от деревянной изгороди для  солдат,  которым  нужно перебежать поле под огнем врага. Пока изгородь далеко,  людям  кажется,  что она может их защитить, но по мере приближения к ней они  начинают  понимать, что защита эта непрочна.  Было  бы  слишком  самонадеянно  с  нашей  стороны думать, что смерть и вблизи покажется нам  такой  же,  какой  мы  видели  ее издали, и что наши чувства, имя которым  -  слабость,  достаточно  закалены, чтобы позволить нам бестрепетно пройти через самое тяжкое из всех испытаний. Равным образом, и на себялюбие может рассчитывать  лишь  тот,  кто  его  не понимает: оно не способно заставить нас легко отнестись к  событию,  которое ему же несет гибель.  Наконец,  разум,  в  котором  многие  надеются  найти поддержку, слишком слаб, чтобы при встрече  со  смертью  мы  могли  на  него опереться. Наоборот, он особенно часто предает  нас  и,  вместо  того  чтобы научить презрению к смерти, ярко освещает все, что есть  в  ней  ужасного  и отталкивающего. Единственное, что  в  его  силах,  -  это  посоветовать  нам отвратить от нее взоры и сосредоточить их на чем-нибудь другом. Катон и Брут обратились к возвышенным  помыслам, а не так давно некий лакей удовольствовался тем, что пустился в пляс на  том самом эшафоте, где его должны были колесовать. Невзирая на то,  что  способы различны, - результат один и тот же. Хотя разница между  великими  людьми  и людьми заурядными огромна, те и другие, как явствует из множества  примеров, нередко принимают смерть одинаково. Впрочем, есть и отличие: у великих людей презрение к смерти вызвано ослепляющей их любовью к славе, а у людей простых - ограниченностью, которая не позволяет им постичь всю глубину ожидающего их несчастья и дает возможность думать о вещах посторонних.', '<b>"Максимы и моральные размышления"</b> - № 504', '', '', '03905cd8969f53c213f94440671b8fa2'),
(1763, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Дарования, которыми господь наделил  людей,  так  же  разнообразны  как деревья, которыми он украсил землю, и каждое обладает особенными  свойствами и приносит лишь ему присущие плоды. Потому-то лучшее грушевое дерево никогда не родит даже дрянных яблок, а самый даровитый человек  пасует  перед  делом хотя и заурядным, но дающимся только тому  кто  к  этому  делу  способен.  И потому сочинять афоризмы, не имея хоть небольшого таланта к  занятию  такого рода, не менее смехотворно, чем ожидать, что на  грядке,  где  не  высажены луковицы, зацветут тюльпаны.', '<b>"Максимы и моральные размышления"</b> - № 505', '', '', '21126ed812c497e0c561a7e56875b0c0'),
(1764, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Разновидностей тщеславия столько, что и считать не  стоит.', '<b>"Максимы и моральные размышления"</b> - № 506', '', '', '8a308e40e42acd86c8bfdae0da44c1a9'),
(1765, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Свет  полон горошин, которые издеваются над бобами.', '<b>"Максимы и моральные размышления"</b> - № 507', '', '', '2148a4a10262f19a2518de5e61e32869'),
(1766, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Кто  слишком  высоко  ценит  благородство  своего   происхождения,   то недостаточно ценит дела, которые некогда легли в его основу.', '<b>"Максимы и моральные размышления"</b> - № 508', '', '', '1feccedb758707d49b82985fc64d06d1'),
(1767, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'В наказание за первородный грех бог дозволил человеку сотворить кумир из себялюбия, чтобы оно терзало его на всех жизненных путях.', '<b>"Максимы и моральные размышления"</b> - № 509', '', '', '4abbe1cbf7d9ea62fcb796937bd14cd7');
INSERT INTO `mx_love__aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `aut_id`, `lib_id`, `prim_id`, `text`, `prim_str`, `lib_str`, `dop`, `hash`) VALUES
(1768, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Своекорыстие - душа нашего сознания: подобно тому, как тело, лишенное души, не видит, не слышит, не сознает, не чувствует и не движется,  так  и сознание, разлученное,  если  дозволено  употребить  такое  выражение,  со своекорыстием, не видит, не слышит, не чувствует и не действует.  Потому-то и человек, который во имя своей выгоды скитается по морям  и  землям,  вдруг как бы цепенеет, едва речь заходит о выгоде ближнего;  потому-то  внезапно погружаются в дремоту и словно отлетают в иной мир те, кому мы рассказываем о своих делах, и так же внезапно  просыпаются,  стоит  их  почуять  в  нашем рассказе нечто, хотя бы отдаленно их затрагивающее. Вот и получается, что наш собеседник то теряет сознание то приходит в себя, смотря по тому, идет ли дело о его выгоде или, напротив, не имеет к нему никакого касательства.', '<b>"Максимы и моральные размышления"</b> - № 510', '', '', 'bfec92bdc102edaf75703113e2edde4f'),
(1769, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Мы всего боимся, как и положено смертным,  и  всего  хотим,  как  будто награждены бессмертием.', '<b>"Максимы и моральные размышления"</b> - № 511', '', '', '4e767f2b3bbc10255ff8803e9b941ff6'),
(1770, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Порой кажется, что сам дьявол придумал  поставить  леность  на  рубежах наших добродетелей.', '<b>"Максимы и моральные размышления"</b> - № 512', '', '', 'c9a3be44e223de87d52f929fca39f66b'),
(1771, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Мы потому готовы поверить любым рассказам о недостатках наших  ближних, что всего легче верить желаемому.', '<b>"Максимы и моральные размышления"</b> - № 513', '', '', '75198f434ae817ec548cc4e69e4c6f2b'),
(1772, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Исцеляет от ревности только полная уверенность в том,  чего  мы  больше всего боялись, потому что вместе с нею приходит конец или нашей  любви,  или жизни; что и говорить, лекарство жестокое, но менее жестокое, чем  недоверие и подозрение.', '<b>"Максимы и моральные размышления"</b> - № 514', '', '', 'ab642818a68c86418931bafeaa59313e'),
(1773, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Где надежда, там и боязнь: боязнь всегда полна надежды, надежда  всегда полна боязни..', '<b>"Максимы и моральные размышления"</b> - № 515', '', '', '3dbf10854acd240b207a1ff2081870e2'),
(1774, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Не следует обижаться на людей,  утаивших  от  нас  правду:  мы  и  сами постоянно утаиваем ее от себя.', '<b>"Максимы и моральные размышления"</b> - № 516', '', '', '8ba9e3c799581c123dbef8229615ee58'),
(1775, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Мы  чаще  всего  потому  превратно  судим  о  сентенциях,  доказывающих лживость людских  добродетелей,  что  наши  собственные  добродетели  всегда кажутся нам истинными.', '<b>"Максимы и моральные размышления"</b> - № 517', '', '', '24a5227536403d6f9bda9a7b1b9d2bd3'),
(1776, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Преданность властям предержащим - лишь другая личина себялюбия.', '<b>"Максимы и моральные размышления"</b> - № 518', '', '', '4c246c5f34b4f052c9f1067966b21e8a'),
(1777, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Где конец добру, там начало злу, а где конец злу, там начало добру.', '<b>"Максимы и моральные размышления"</b> - № 519', '', '', '361d337d526585ca26e316eff24c53cd'),
(1778, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Философы порицают богатство лишь потому, что мы плохо им распоряжаемся. От нас одних зависит и приобретать, и пускать его в ход, не служа  при  этом пороку. Вместо  того,  чтобы  с  помощью  богатства  поддерживать  и  питать злодеяния, как с помощью дров питают  пламя,  мы  могли  бы  отдать  его  на служение добродетелям, придав им тем самым и блеск, и привлекательность.', '<b>"Максимы и моральные размышления"</b> - № 520', '', '', '15ffe22357f62a37c442e3f7c1359cf0'),
(1779, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Крушение всех надежд человека приятно и его друзьям и недругам.', '<b>"Максимы и моральные размышления"</b> - № 521', '', '', '3031ccf2e4f8ca60521388f25cce4afd'),
(1780, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Поскольку всех счастливее в этом мире тот, кто довольствуется малым, то власть имущих и честолюбцев надо считать самыми несчастными  людьми,  потому что для счастья им нужно несметное множество благ.', '<b>"Максимы и моральные размышления"</b> - № 522', '', '', '54fdeaf0e420680f3d808343558d15af'),
(1781, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Человек  ныне  не  таков,  каким  был  создан,  и  вот  убедительнейшее доказательство этому: чем разумнее он становится, тем больше стыдится в душе сумасбродства, низости и порочности своих чувств и наклонностей.', '<b>"Максимы и моральные размышления"</b> - № 523', '', '', 'c9b7455aee96af7c733c0319103fa419'),
(1782, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Сентенции, обнажающие человеческое сердце,  вызывают  такое  возмущение потому, что людям боязно предстать перед светом во всей своей наготе.', '<b>"Максимы и моральные размышления"</b> - № 524', '', '', 'fddcef129b2ba0084382e60e3eede15b'),
(1783, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Люди, которых мы любим, почти всегда более  властны  над  нашей  душой, нежели мы сами.', '<b>"Максимы и моральные размышления"</b> - № 525', '', '', '271a9ce1569e3a5aa37a62d3065ba674'),
(1784, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Мы часто клеймим чужие недостатки, но  редко,  пользуясь  их  примером, исправляем свои.', '<b>"Максимы и моральные размышления"</b> - № 526', '', '', '8e61db830fbfcda9639f7b7be16972b2'),
(1785, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Человек  так  жалок,   что,   посвятив   себя   единственной   цели   - удовлетворению своих страстей, беспрестанно сетует на их тиранство; не желая выносить их гнет, он вместе с тем не желает и сделать усилие, чтобы сбросить его; ненавидя страсти,  не  менее  ненавидит  и  лекарства,  их  исцеляющие; восставая против терзаний недуга, восстает и против тягот лечения.', '<b>"Максимы и моральные размышления"</b> - № 527', '', '', '0e5b825c65c114f8c0fa8aac074cd4f1'),
(1786, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Когда мы радуемся или печалимся, наши  чувства  соразмерны  не  столько удачам или  бедам,  доставшимся  нам  на  долю,  сколько  нашей  способности чувствовать.', '<b>"Максимы и моральные размышления"</b> - № 528', '', '', '679c9df4575b592763cf8450e61de602'),
(1787, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Хитрость - признак недалекого ума.', '<b>"Максимы и моральные размышления"</b> - № 529', '', '', '40c56dfdbefde420fbc61d66dcab8442'),
(1788, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Мы расточаем похвалы только затем, чтобы извлечь потом из них выгоду.', '<b>"Максимы и моральные размышления"</b> - № 530', '', '', '33d8e53199ad1c2945a4ebd3ce3789da'),
(1789, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Людские страсти - это всего лишь разные склонности людского себялюбия.', '<b>"Максимы и моральные размышления"</b> - № 531', '', '', '5e3a2e52d5e28025c15efa4a54a439ac'),
(1790, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Окончательно соскучившись, мы перестаем скучать.', '<b>"Максимы и моральные размышления"</b> - № 532', '', '', '3b2d7d2d98ea41eec78ec7a735cc2104'),
(1791, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Люди хвалят или бранят чаще всего то, что принято хвалить или бранить.', '<b>"Максимы и моральные размышления"</b> - № 533', '', '', 'ae20c0f0fbc009b22159b0baa2f42bc8'),
(1792, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Множество людей притязают  на  благочестие,  но  никого  не  привлекает смирение.', '<b>"Максимы и моральные размышления"</b> - № 534', '', '', '6d26fcdbce5db5c8473038ed404b8698'),
(1793, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Физический труд помогает забывать о  нравственных  страданиях;  поэтому бедняки - счастливые люди.', '<b>"Максимы и моральные размышления"</b> - № 535', '', '', '75d2b547481c8cfb6a20020a06330e23'),
(1794, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Истинному самобичеванию подвергает себя лишь тот, кто никого об этом не оповещает; в противном случае все облегчается тщеславием.', '<b>"Максимы и моральные размышления"</b> - № 536', '', '', 'df02276ba16c21deaa2ee62a66bc3f6b'),
(1795, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Смирение - это угодный богу алтарь для наших жертвоприношений.', '<b>"Максимы и моральные размышления"</b> - № 537', '', '', 'b594b96f65d6bb387df645b8f83c4038'),
(1796, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Мудрец счастлив, довольствуясь  немногим,  а  глупцу  всего  мало:  вот почему почти все люди несчастны.', '<b>"Максимы и моральные размышления"</b> - № 538', '', '', '7705e88ab6b2af0274d75fd775ab7fc3'),
(1797, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Нас  мучит  не  столько  жажда  счастья,   сколько   желание   прослыть счастливцами.', '<b>"Максимы и моральные размышления"</b> - № 539', '', '', 'ecd44489f4d8f9f9676f7d0ff22dcb4b'),
(1798, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Легче убить желание в зародыше, чем потом ублаготворять все вожделения, им рожденные.', '<b>"Максимы и моральные размышления"</b> - № 540', '', '', 'bf3f7f8c4abfd198e921d1832fa2bef8'),
(1799, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Ясный разум дает душе то, что здоровье - телу.', '<b>"Максимы и моральные размышления"</b> - № 541', '', '', '5c516289f61591e42a87eddaa175cf62'),
(1800, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Так как великие мира сего не могут дать человеку ни телесного здоровья, ни душевного покоя, то все их благодеяния он всегда  оплачивает  по  слишком дорогой цене.', '<b>"Максимы и моральные размышления"</b> - № 542', '', '', 'eb255de3f0254105c4e5baa0498463d8'),
(1801, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Прежде чем сильно чего-то  пожелать,  следует  осведомиться,  очень  ли счастлив нынешний обладатель желаемого.', '<b>"Максимы и моральные размышления"</b> - № 543', '', '', '69deb82dc7cee3a38c8df731a4e89c36'),
(1802, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Истинный друг - величайшее из земных благ, хотя как раз за этим  благом мы меньше всего гонимся.', '<b>"Максимы и моральные размышления"</b> - № 544', '', '', '618a780618c8fdd8686e9b059f6f060f'),
(1803, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Любовники начинают видеть недостатки  своих  любовниц,  лишь  когда  их увлечению приходит конец.', '<b>"Максимы и моральные размышления"</b> - № 545', '', '', '0ecf0d960f507328c3d9dd6efecf6d69'),
(1804, 1, 1, 1, 1448972776, 1448972776, 124, 0, 0, 'Благоразумие и любовь не созданы друг для  друга:  по  мере  того,  как растет любовь, уменьшается благоразумие.', '<b>"Максимы и моральные размышления"</b> - № 546', '', '', '394b5c7f13eadf06695ac6641f948017'),
