INSERT INTO `mx_love__cat_aph` (`id`, `status`, `author_id`, `updater_id`, `created_at`, `updated_at`, `root`, `lft`, `rgt`, `level`, `name`, `slug`, `intro`, `intro2`, `img`, `title`, `keywords`, `description`) VALUES
(1, 1, 1, 1, 1451236044, 1451236044, 1, 1, 66, 1, NULL, NULL, NULL, NULL, '', NULL, NULL, NULL),
(2, 1, 1, 1, 1451236044, 1451236044, 0, 3, 4, 3, 'Ассирия', 'assiriya', 'древнее государство в северном Двуречье (на территории современного Ирака)', '14-9 вв. до н. э.- 605 г. до н. э.', '/love/cat/assiriya.jpg', NULL, NULL, NULL),
(3, 1, 1, 1, 1451236044, 1451236044, 0, 50, 59, 2, 'Священные писания', 'svyaschennye-pisaniya', 'основополагающие тексты какой-либо религии, за которыми, как правило, признаётся божественное происхождение.', NULL, '/love/cat/book.jpg', NULL, NULL, NULL),
(4, 1, 1, 1, 1451236044, 1451236044, 0, 42, 43, 2, 'Классицизм', 'klassicizm', '(фр. classicisme, от лат. classicus — образцовый) — художественный стиль и эстетическое направление в европейском искусстве', ' XVII — XVIII вв.', '/love/cat/classicizm.jpg', NULL, NULL, NULL),
(5, 1, 1, 1, 1451236044, 1451236044, 0, 6, 23, 2, 'Древняя Греция', 'drevnyaya-greciya', 'принятое в историографии обозначение раннеклассовых обществ на юге Балканского полуострова, островах Эгейского моря, побережье Фракии и западной береговой полосе Малой Азии', 'III — I тыс. до н. э.', '/love/cat/ellada.jpg', NULL, NULL, NULL),
(6, 1, 1, 1, 1451236044, 1451236044, 0, 44, 45, 2, 'Просвещение', 'prosveschenie', 'идейное течение, основанное на убеждении в решающей роли разума и науки в познании «естественного порядка», соответствующего подлинной природе человека и общества', 'XVII — XVIII вв.', '/love/cat/prosveshenie.jpg', NULL, NULL, NULL),
(7, 1, 1, 1, 1451236044, 1451236044, 0, 38, 39, 2, 'Средние века', 'srednie-veka', 'принятое в исторической науке обозначение периода, следующего за историей древнего мира и предшествующего новой истории.', 'с 476 года - середина XVII в', '/love/cat/middle.jpg', NULL, NULL, NULL),
(8, 1, 1, 1, 1451236044, 1451236044, 0, 48, 49, 2, 'Модернизм', 'modernizm', '(итал. modernismo «современное течение») — направление в искусстве и литературе, характеризующееся разрывом с предшествующим историческим опытом художественного творчества, стремлением утвердить новые нетрадиционные начала в искусстве', 'XX  — XXI века', '/love/cat/modernizm.jpg', NULL, NULL, NULL),
(9, 1, 1, 1, 1451236044, 1451236044, 0, 40, 41, 2, 'Возрождение', 'vozrozhdenie', '(фр. Renaissance, итал. Rinascimento) - эпоха в истории культуры Европы, пришедшая на смену культуре Средних веков и предшествующая культуре нового времени', ' XIII — XVI века', '/love/cat/renessanse.jpg', NULL, NULL, NULL),
(10, 1, 1, 1, 1451236044, 1451236044, 0, 46, 47, 2, '19 - век Романтизм', 'romantizm', 'идейное и художественное направление в европейской и американской духовной культуре, представляющее собой реакцию на Просвещение и стимулированный им научно-технический прогресс. Характеризуется утверждением самоценности духовно-творческой жизни личности.', 'XVIII — XX века', '/love/cat/romantizm.jpg', NULL, NULL, NULL),
(11, 1, 1, 1, 1451236044, 1451236044, 0, 24, 37, 2, 'Древний Рим', 'drevnij-rim-aph', 'Согласно преданию, город Рим основан братьями Ромулом и Ремом ок. 754/753 до н. э.', '754/753 г. до н. э. - 476 г.', '/love/cat/rome.jpg', NULL, NULL, NULL),
(12, 1, 1, 1, 1451236044, 1451236044, 0, 60, 61, 2, 'Отцы Церкви, богословы', 'otcy-cerkvi-bogoslovy', 'почётный титул, используемый с конца IV века применительно к группе выдающихся церковных деятелей и писателей прошлого, чей авторитет имел особый вес в формировании догматики, составлению канона — списка Священных книг Библии', 'в Православии - Святые Отцы', '/love/cat/saint.jpg', NULL, NULL, NULL),
(13, 1, 1, 1, 1451236044, 1451236044, 0, 62, 63, 2, 'Русское наследие', 'russkoe-nasledie', 'афоризмы отечественных мыслителей всех эпох', NULL, '/love/cat/russian.jpg', NULL, NULL, NULL),
(14, 1, 1, 1, 1451236044, 1451236044, 0, 64, 65, 2, 'Неизвестные авторы', 'neizvestnye-avtory', 'афоризмы, авторов которых не удалось установить', NULL, '/love/cat/noname.jpg', NULL, NULL, NULL),
(15, 1, 1, 1, 1451236044, 1451236044, 0, 7, 8, 3, 'Писатели, поэты', 'pisateli-poety', 'Писатели, поэты', '', '/love/cat/aphwrt.jpg', NULL, NULL, NULL),
(16, 1, 1, 1, 1451236044, 1451236044, 0, 9, 10, 3, 'Семь мудрецов', 'sem-mudrecov', 'древнегреческие авторы кратких сентенций («гном») на темы житейской мудрости (напр., «познай самого себя» Фалеса, «ничего слишком» Солона и т. п.). Состав «Семи мудрецов» в различных источниках варьируется (всего 17 имен в разных комбинациях)', NULL, '/love/cat/semmudrecov.jpg', NULL, NULL, NULL),
