(2, 'test2', 'test11', '<p>Demo for plugin Code Highlighting Hello, world!</p>\r\n\r\n<p>[code]</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; /**<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; * @var string шаблон<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; */</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; public $tpl = &quot;form&quot;;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; /**<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; * @var array параметры \\yii\\widgets\\ActiveForm по умолчанию<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; */</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; protected $defaultFormOptions = [&#39;enableAjaxValidation&#39; =&gt; true, &#39;enableClientValidation&#39; =&gt; false];</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; /**<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; * @var string идентификатор виджета<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; */</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; protected $id;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; protected $_tplDir;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; public function init()<br />\r\n&nbsp;&nbsp;&nbsp; {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $model = $this-&gt;model;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;id = strtolower(self::FORM_ID_PREF . str_replace(&quot;\\\\&quot;, &quot;-&quot;, $model::className()));</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; BootstrapPluginAsset::register($this-&gt;view);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; }</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; public function run()<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $formOptions = array_merge($this-&gt;defaultFormOptions, $this-&gt;formOptions);</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $this-&gt;render($this-&gt;tpl, [<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &quot;model&quot; =&gt; $this-&gt;model,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &quot;formOptions&quot; =&gt; $formOptions,<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &quot;id&quot; =&gt; $this-&gt;id<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ]<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; );<br />\r\n&nbsp;&nbsp;&nbsp; }</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; /**<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; * @var array дирректории, где хранятся шаблоны<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; */<br />\r\n&nbsp;&nbsp;&nbsp; public function getTplDir()<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($this-&gt;_tplDir === null) {</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $widgetTpl = [$this-&gt;viewPath . DIRECTORY_SEPARATOR . &#39;tpl&#39; . DIRECTORY_SEPARATOR];</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (is_array($this-&gt;model-&gt;tplDir)) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; foreach ($this-&gt;model-&gt;tplDir as $dir) {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $modelTpl[] = Yii::getAlias($dir);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } else {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $modelTpl = [Yii::getAlias($this-&gt;model-&gt;tplDir)];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p>\r\n\r\n<p>/* $modelTpl = [Yii::$app-&gt;controller-&gt;module-&gt;basePath.DIRECTORY_SEPARATOR.&#39;views&#39;.DIRECTORY_SEPARATOR.Yii::$app-&gt;controller-&gt;id.DIRECTORY_SEPARATOR.&#39;tpl&#39;.DIRECTORY_SEPARATOR];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; */<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;_tplDir = Array